#_(ns org.bitcoinj.core.listeners #_"DownloadProgressTracker"
    (:import [java.util Date Locale]
             [java.util.concurrent ExecutionException])
    (:import [com.google.common.util.concurrent ListenableFuture SettableFuture]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core Block FilteredBlock Peer Utils]))

;;;
 ; <p>An implementation of {@link AbstractPeerDataEventListener} that listens to chain download events and tracks
 ; progress as a percentage.  The default implementation prints progress to stdout, but you can subclass it and
 ; override the progress method to update a GUI instead.</p>
 ;;
#_public
(§ class #_"DownloadProgressTracker" (§ extends #_"AbstractPeerDataEventListener")
    #_private
    #_static
    (§ def- #_"Logger" (§ name DownloadProgressTracker/log) (LoggerFactory/getLogger (§ klass #_"DownloadProgressTracker")))

    #_private
    (§ field- #_"int" (§ name originalBlocksLeft) -1)
    #_private
    (§ field- #_"int" (§ name lastPercent) 0)
    #_private
    (§ field- #_"SettableFuture<Long>" (§ name future) (SettableFuture/create))
    #_private
    (§ field- #_"boolean" (§ name caughtUp) false)

    #_override
    #_public
    (§ method #_"void" (§ fn onChainDownloadStarted) [#_"Peer" __peer, #_"int" __blocksLeft])
    (§ block
        (§ if (and (< 0 __blocksLeft) (== originalBlocksLeft -1)))
        (§ block
            (.. this (startDownload __blocksLeft))
        )
        ;; Only mark this the first time, because this method can be called more than once during a chain download
        ;; if we switch peers during it.
        (§ if (== originalBlocksLeft -1))
        (§ block
            (§ ass (§ name originalBlocksLeft) __blocksLeft)
        )
        (§ else )
        (§ block
            (.. DownloadProgressTracker/log (info "Chain download switched to {}", __peer))
        )
        (§ if (== __blocksLeft 0))
        (§ block
            (.. this (doneDownload))
            (.. future (set (.. __peer (getBestHeight))))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn onBlocksDownloaded) [#_"Peer" __peer, #_"Block" __block, #_nilable #_"FilteredBlock" __filteredBlock, #_"int" __blocksLeft])
    (§ block
        (§ if caughtUp)
        (§ block
            (§ return nil)
        )

        (§ if (== __blocksLeft 0))
        (§ block
            (§ ass (§ name caughtUp) true)
            (.. this (doneDownload))
            (.. future (set (.. __peer (getBestHeight))))
        )

        (§ if (or (< __blocksLeft 0) (<= originalBlocksLeft 0)))
        (§ block
            (§ return nil)
        )

        (§ let #_"double" __pct (- 100.0 (* 100.0 (/ __blocksLeft (§ cast #_"double" originalBlocksLeft)))))
        (§ block
            (§ if (!= (§ cast #_"int" __pct) lastPercent))
            (§ block
                (.. this (progress __pct, __blocksLeft, (§ new #_"Date" (* (.. __block (getTimeSeconds)) 1000))))
                (§ ass (§ name lastPercent) (§ cast #_"int" __pct))
            )
            nil
        )
    )

    ;;;
     ; Called when download progress is made.
     ;
     ; @param pct The percentage of chain downloaded, estimated.
     ; @param date The date of the last block downloaded.
     ;;
    #_protected
    (§ method #_"void" (§ fn progress) [#_"double" __pct, #_"int" __blocksSoFar, #_"Date" __date])
    (§ block
        (.. DownloadProgressTracker/log (info (String/format Locale/US, "Chain download %d%% done with %d blocks to go, block date %s", (§ cast #_"int" __pct), __blocksSoFar, (Utils/dateTimeFormat __date))))
        nil
    )

    ;;;
     ; Called when download is initiated.
     ;
     ; @param blocks The number of blocks to download, estimated.
     ;;
    #_protected
    (§ method #_"void" (§ fn startDownload) [#_"int" __blocks])
    (§ block
        (.. DownloadProgressTracker/log (info (str "Downloading block chain of size " __blocks ". " (if (< 1000 __blocks) "This may take a while." ""))))
        nil
    )

    ;;;
     ; Called when we are done downloading the block chain.
     ;;
    #_protected
    (§ method #_"void" (§ fn doneDownload) [])
    (§ block
        nil
    )

    ;;;
     ; Wait for the chain to be downloaded.
     ;;
    #_public
    (§ method #_"void" (§ fn await) [])
        (§ throws #_"InterruptedException")
    (§ block
        (§ try )
        (§ block
            (.. future (get))
        )
        (§ catch #_"ExecutionException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e))
        )
        nil
    )

    ;;;
     ; Returns a listenable future that completes with the height of the best chain (as reported by the peer) once chain
     ; download seems to be finished.
     ;;
    #_public
    (§ method #_"ListenableFuture<Long>" (§ fn getFuture) [])
    (§ block
        future
    )
)

