    #_override
    #_public
    (§ method #_"boolean" (§ fn isEncrypted) [])
    (§ block
        (and (nil? priv) (or (.. super (isEncrypted)) (and (some? parent) (.. parent (isEncrypted)))))
    )

    ;;;
     ; Returns this keys {@link org.bitcoinj.crypto.KeyCrypter} <b>or</b> the keycrypter of its parent key.
     ;;
    #_override
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        (§ if (some? keyCrypter))
        (§ block
            (§ return keyCrypter)
        )
        (§ if (some? parent))
        (§ block
            (§ return (.. parent (getKeyCrypter)))
        )

        nil
    )

    #_override
    #_public
    (§ method #_"ECDSASignature" (§ fn sign) [#_"Sha256Hash" __input, #_nilable #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
