        (§ if (.. this (isEncrypted)))
        (§ block
            ;; If the key is encrypted, ECKey.sign will decrypt it first before rerunning sign.  Decryption walks
            ;; the key heirarchy to find the private key (see below), so, we can just run the inherited method.
            (§ return (.. super (sign __input, __aesKey)))
        )
        (§ else )
        (§ block
            ;; If it's not encrypted, derive the private via the parents.
            (§ let #_"BigInteger" __privateKey (.. this (findOrDerivePrivateKey)))
            (§ block
                ;; This key is a part of a public-key only heirarchy and cannot be used for signing.
                (§ if (nil? __privateKey))
                (§ block
                    (§ throw (§ new #_"MissingPrivateKeyException"))
                )

                (§ return (.. super (doSign __input, __privateKey)))
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKey" (§ fn decrypt) [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull __keyCrypter)
        ;; Check that the keyCrypter matches the one used to encrypt the keys, if set.
        (§ if (and (some? (.. this keyCrypter)) (not (.. this keyCrypter (equals __keyCrypter)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "The keyCrypter being used to decrypt the key is different to the one that was used to encrypt it"))
        )

        (§ let #_"BigInteger" __privKey (.. this (findOrDeriveEncryptedPrivateKey __keyCrypter, __aesKey)))
        (§ block
            (§ let #_"DeterministicKey" __key (§ new #_"DeterministicKey" childNumberPath, chainCode, __privKey, parent))
            (§ block
                (§ if (not (Arrays/equals (.. __key (getPubKey)), (.. this (getPubKey)))))
                (§ block
                    (§ throw (§ new #_"KeyCrypterException" "Provided AES key is wrong"))
                )

                (§ if (nil? parent))
                (§ block
                    (.. __key (setCreationTimeSeconds (.. this (getCreationTimeSeconds))))
                )
                __key
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKey" (§ fn decrypt) [#_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ cast #_"DeterministicKey" (.. super (decrypt __aesKey)))
    )

    ;; For when a key is encrypted, either decrypt our encrypted private key bytes, or work up the tree asking parents
    ;; to decrypt and re-derive.
    #_private
    (§ method- #_"BigInteger" (§ fn findOrDeriveEncryptedPrivateKey) [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])
    (§ block
        (§ if (some? encryptedPrivateKey))
        (§ block
            (§ return (§ new #_"BigInteger" 1, (.. __keyCrypter (decrypt encryptedPrivateKey, __aesKey))))
        )

        ;; Otherwise we don't have it, but maybe we can figure it out from our parents.  Walk up the tree looking for
        ;; the first key that has some encrypted private key data.
        (§ let #_"DeterministicKey" __cursor parent)
        (§ block
            (§ while (some? __cursor))
            (§ block
                (§ if (!= (.. __cursor encryptedPrivateKey) nil))
                (§ block
                    (§ break )
                )
                (§ ass __cursor (.. __cursor parent))
            )
            (§ if (nil? __cursor))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "Neither this key nor its parents have an encrypted private key"))
            )

            (§ let #_"byte[]" __parentalPrivateKeyBytes (.. __keyCrypter (decrypt (.. __cursor encryptedPrivateKey), __aesKey)))
            (§ block
                (.. this (derivePrivateKeyDownwards __cursor, __parentalPrivateKeyBytes))
            )
        )
    )

    #_private
    (§ method- #_"DeterministicKey" (§ fn findParentWithPrivKey) [])
    (§ block
        (§ let #_"DeterministicKey" __cursor this)
        (§ block
            (§ while (some? __cursor))
            (§ block
                (§ if (!= (.. __cursor priv) nil))
                (§ block
                    (§ break )
                )
                (§ ass __cursor (.. __cursor parent))
            )
            __cursor
        )
    )

    #_nilable
    #_private
    (§ method- #_"BigInteger" (§ fn findOrDerivePrivateKey) [])
    (§ block
        (§ let #_"DeterministicKey" __cursor (.. this (findParentWithPrivKey)))
        (§ block
            (§ if (nil? __cursor))
            (§ block
                (§ return nil)
            )

            (.. this (derivePrivateKeyDownwards __cursor, (.. __cursor priv (toByteArray))))
        )
    )

    #_private
    (§ method- #_"BigInteger" (§ fn derivePrivateKeyDownwards) [#_"DeterministicKey" __cursor, #_"byte[]" __parentalPrivateKeyBytes])
    (§ block
        (§ let #_"DeterministicKey" __downCursor (§ new #_"DeterministicKey" (.. __cursor childNumberPath), (.. __cursor chainCode), (.. __cursor pub), (§ new #_"BigInteger" 1, __parentalPrivateKeyBytes), (.. __cursor parent)))
        (§ block
            ;; Now we have to rederive the keys along the path back to ourselves.  That path can be found by just truncating
            ;; our path with the length of the parents path.
            (§ let #_"ImmutableList<ChildNumber>" __path (.. childNumberPath (subList (.. __cursor (getPath) (size)), (.. childNumberPath (size)))))
            (§ block
                (§ for (§ var #_"ChildNumber" __num) :for __path)
                (§ block
                    (§ ass __downCursor (HDKeyDerivation/deriveChildKey __downCursor, __num))
                )
                ;; downCursor is now the same key as us, but with private key bytes.
                ;; If it's not, it means we tried decrypting with an invalid password and earlier checks e.g. for padding didn't
                ;; catch it.
                (§ if (not (.. __downCursor pub (equals pub))))
                (§ block
                    (§ throw (§ new #_"KeyCrypterException" "Could not decrypt bytes"))
                )

                (Preconditions/checkNotNull (.. __downCursor priv))
            )
        )
    )

    ;;;
     ; Derives a child at the given index using hardened derivation.  Note: <code>index</code>
     ; is not the "i" value.  If you want the softened derivation, then use instead
     ; <code>HDKeyDerivation.deriveChildKey(this, new ChildNumber(child, false))</code>.
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn derive) [#_"int" __child])
    (§ block
        (HDKeyDerivation/deriveChildKey this, (§ new #_"ChildNumber" __child, true))
    )

    ;;;
     ; Returns the private key of this deterministic key.  Even if this object isn't storing the private key,
     ; it can be re-derived by walking up to the parents if necessary and this is what will happen.
     ; @throws java.lang.IllegalStateException if the parents are encrypted or a watching chain.
     ;;
    #_override
    #_public
    (§ method #_"BigInteger" (§ fn getPrivKey) [])
    (§ block
        (§ let #_"BigInteger" __key (.. this (findOrDerivePrivateKey)))
        (§ block
            (Preconditions/checkState (some? __key), "Private key bytes not available")
            __key
        )
    )

    #_public
    (§ method #_"byte[]" (§ fn serializePublic) [#_"NetworkParameters" __params])
    (§ block
        (.. this (serialize __params, true))
    )

    #_public
    (§ method #_"byte[]" (§ fn serializePrivate) [#_"NetworkParameters" __params])
    (§ block
        (.. this (serialize __params, false))
    )

    #_private
    (§ method- #_"byte[]" (§ fn serialize) [#_"NetworkParameters" __params, #_"boolean" __pub])
    (§ block
        (§ let #_"ByteBuffer" __ser (ByteBuffer/allocate 78))
        (§ block
            (.. __ser (putInt (if __pub (.. __params (getBip32HeaderPub)) (.. __params (getBip32HeaderPriv)))))
            (.. __ser (put (§ cast #_"byte" (.. this (getDepth)))))
            (.. __ser (putInt (.. this (getParentFingerprint))))
            (.. __ser (putInt (.. this (getChildNumber) (i))))
            (.. __ser (put (.. this (getChainCode))))
            (.. __ser (put (if __pub (.. this (getPubKey)) (.. this (getPrivKeyBytes33)))))
            (Preconditions/checkState (== (.. __ser (position)) 78))
            (.. __ser (array))
        )
    )

    #_public
    (§ method #_"String" (§ fn serializePubB58) [#_"NetworkParameters" __params])
    (§ block
        (DeterministicKey/toBase58 (.. this (serialize __params, true)))
    )

    #_public
    (§ method #_"String" (§ fn serializePrivB58) [#_"NetworkParameters" __params])
    (§ block
        (DeterministicKey/toBase58 (.. this (serialize __params, false)))
    )

    #_static
    (§ defn #_"String" (§ fn DeterministicKey/toBase58) [#_"byte[]" __ser])
    (§ block
        (Base58/encode (DeterministicKey/addChecksum __ser))
    )

    ;;; Deserialize a base-58-encoded HD Key with no parent. ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn DeterministicKey/deserializeB58) [#_"String" __base58, #_"NetworkParameters" __params])
    (§ block
        (DeterministicKey/deserializeB58 nil, __base58, __params)
    )

    ;;;
     ; Deserialize a base-58-encoded HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ; @throws IllegalArgumentException if the base58 encoded key could not be parsed.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn DeterministicKey/deserializeB58) [#_nilable #_"DeterministicKey" __parent, #_"String" __base58, #_"NetworkParameters" __params])
    (§ block
        (DeterministicKey/deserialize __params, (Base58/decodeChecked __base58), __parent)
    )

    ;;;
     ; Deserialize an HD Key with no parent.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn DeterministicKey/deserialize) [#_"NetworkParameters" __params, #_"byte[]" __serializedKey])
    (§ block
        (DeterministicKey/deserialize __params, __serializedKey, nil)
    )

    ;;;
     ; Deserialize an HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn DeterministicKey/deserialize) [#_"NetworkParameters" __params, #_"byte[]" __serializedKey, #_nilable #_"DeterministicKey" __parent])
    (§ block
        (§ let #_"ByteBuffer" __buffer (ByteBuffer/wrap __serializedKey))
        (§ block
            (§ let #_"int" __header (.. __buffer (getInt)))
            (§ block
                (§ if (and (!= __header (.. __params (getBip32HeaderPriv))) (!= __header (.. __params (getBip32HeaderPub)))))
                (§ block
                    (§ throw (§ new #_"IllegalArgumentException" (str "Unknown header bytes: " (.. (DeterministicKey/toBase58 __serializedKey) (substring 0, 4)))))
                )

                (§ let #_"boolean" __pub (== __header (.. __params (getBip32HeaderPub))))
                (§ block
                    (§ let #_"int" __depth (& (.. __buffer (get)) 0xff)) ;; convert signed byte to positive int since depth cannot be negative
                    (§ block
                        (§ let #_"int" __parentFingerprint (.. __buffer (getInt)))
                        (§ block
                            (§ let #_"int" __i (.. __buffer (getInt)))
                            (§ block
                                (§ let #_"ChildNumber" __childNumber (§ new #_"ChildNumber" __i))
                                (§ block
                                    (§ let #_"ImmutableList<ChildNumber>" __path)
                                    (§ block
                                        (§ if (some? __parent))
                                        (§ block
                                            (§ if (== __parentFingerprint 0))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Parent was provided but this key doesn't have one"))
                                            )
                                            (§ if (!= (.. __parent (getFingerprint)) __parentFingerprint))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Parent fingerprints don't match"))
                                            )
                                            (§ ass __path (HDUtils/append (.. __parent (getPath)), __childNumber))
                                            (§ if (!= (.. __path (size)) __depth))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Depth does not match"))
                                            )
                                        )
                                        (§ else )
                                        (§ block
                                            ;; We have been given a key that is not a root key, yet we lack the object representing the parent.
                                            ;; This can happen when deserializing an account key for a watching wallet.  In this case, we assume that
                                            ;; the client wants to conceal the key's position in the hierarchy.  The path is truncated at the
                                            ;; parent's node.
                                            (§ if (<= 1 __depth))
                                            (§ block
                                                (§ ass __path (ImmutableList/of __childNumber))
                                            )
                                            (§ else )
                                            (§ block
                                                (§ ass __path (ImmutableList/of))
                                            )
                                        )
                                        (§ let #_"byte[]" __chainCode (§ new #_"byte[]" (§ count 32)))
                                        (§ block
                                            (.. __buffer (get __chainCode))
                                            (§ let #_"byte[]" __data (§ new #_"byte[]" (§ count 33)))
                                            (§ block
                                                (.. __buffer (get __data))
                                                (Preconditions/checkArgument (not (.. __buffer (hasRemaining))), "Found unexpected data in key")
                                                (§ if __pub)
                                                (§ block
                                                    (§ return (§ new #_"DeterministicKey" __path, __chainCode, (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), __data), __parent, __depth, __parentFingerprint))
                                                )
                                                (§ else )
                                                (§ block
                                                    (§ return (§ new #_"DeterministicKey" __path, __chainCode, (§ new #_"BigInteger" 1, __data), __parent, __depth, __parentFingerprint))
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree
     ; in which case the time is stored alongside the key as per normal, see {@link org.bitcoinj.core.ECKey#getCreationTimeSeconds()}.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        (§ if (some? parent))
        (§ block
            (§ return (.. parent (getCreationTimeSeconds)))
        )
        (§ else )
        (§ block
            (§ return (.. super (getCreationTimeSeconds)))
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree.
     ; Thus, setting the creation time on a leaf is forbidden.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" __newCreationTimeSeconds])
    (§ block
        (§ if (some? parent))
        (§ block
            (§ throw (§ new #_"IllegalStateException" "Creation time can only be set on root keys."))
        )
        (§ else )
        (§ block
            (.. super (setCreationTimeSeconds __newCreationTimeSeconds))
        )
        nil
    )

    ;;;
     ; Verifies equality of all fields but NOT the parent pointer (thus the same key derived in two separate heirarchy
     ; objects will equal each other.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"DeterministicKey" __other (§ cast #_"DeterministicKey" __o))
        (§ block
            (and (.. super (equals __other)) (Arrays/equals (.. this chainCode), (.. __other chainCode)) (Objects/equal (.. this childNumberPath), (.. __other childNumberPath)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (.. super (hashCode)), (Arrays/hashCode chainCode), childNumberPath)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"MoreObjects.ToStringHelper" __helper (.. (MoreObjects/toStringHelper this) (omitNullValues)))
        (§ block
            (.. __helper (add "pub", (.. Utils/HEX (encode (.. pub (getEncoded))))))
            (.. __helper (add "chainCode", (.. Utils/HEX (encode chainCode))))
            (.. __helper (add "path", (.. this (getPathAsString))))
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. __helper (add "creationTimeSeconds", creationTimeSeconds))
            )
            (.. __helper (add "isEncrypted", (.. this (isEncrypted))))
            (.. __helper (add "isPubKeyOnly", (.. this (isPubKeyOnly))))
            (.. __helper (toString))
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn formatKeyWithAddress) [#_"boolean" __includePrivateKeys, #_"StringBuilder" __sb, #_"NetworkParameters" __params])
    (§ block
        (§ let #_"Address" __address (.. this (toAddress __params)))
        (§ block
            (.. __sb (append "  addr:") (append __address))
            (.. __sb (append "  hash160:") (append (.. Utils/HEX (encode (.. this (getPubKeyHash))))))
            (.. __sb (append "  (") (append (.. this (getPathAsString))) (append ")\n"))
            (§ if __includePrivateKeys)
            (§ block
                (.. __sb (append "  ") (append (.. this (toStringWithPrivate __params))) (append "\n"))
            )
            nil
        )
    )
)

#_(ns org.bitcoinj.crypto #_"EncryptableItem"
   (:require [org.bitcoinj.wallet Protos]))

;;;
 ; Provides a uniform way to access something that can be optionally encrypted with a
 ; {@link org.bitcoinj.crypto.KeyCrypter}, yielding an {@link org.bitcoinj.crypto.EncryptedData},
 ; and which can have a creation time associated with it.
