#_public
(§ class #_"EncryptedData"
    #_public
    (§ field #_"byte[]" (§ name initialisationVector))
    #_public
    (§ field #_"byte[]" (§ name encryptedBytes))

    #_public
    (§ constructor #_"EncryptedData" [#_"byte[]" __initialisationVector, #_"byte[]" __encryptedBytes])
    (§ block
        (§ ass (§ name (.. this initialisationVector)) (Arrays/copyOf __initialisationVector, (.. __initialisationVector length)))
        (§ ass (§ name (.. this encryptedBytes)) (Arrays/copyOf __encryptedBytes, (.. __encryptedBytes length)))
        this
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"EncryptedData" __other (§ cast #_"EncryptedData" __o))
        (§ block
            (and (Arrays/equals encryptedBytes, (.. __other encryptedBytes)) (Arrays/equals initialisationVector, (.. __other initialisationVector)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (Arrays/hashCode encryptedBytes), (Arrays/hashCode initialisationVector))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "EncryptedData [initialisationVector=" (Arrays/toString initialisationVector) ", encryptedPrivateKey=" (Arrays/toString encryptedBytes) "]")
    )
)

