#_(ns org.bitcoinj.crypto #_"HDKeyDerivation"
    (:import [java.math *]
             [java.nio *]
             [java.security *]
             [java.util *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect *]
             [org.spongycastle.math.ec *])
   (:require [org.bitcoinj.core *]))

;;;
 ; Implementation of the <a href="https://github.com/bitcoin/bips/blob/master/bip-0032.mediawiki">BIP 32</a>
 ; deterministic wallet child key generation algorithm.
 ;;
#_public
(§ class #_"HDKeyDerivation"
    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )

        (§ ass (§ name HDKeyDerivation/RAND_INT) (§ new #_"BigInteger" 256, (§ new #_"SecureRandom")))
    )

    ;; Some arbitrary random number.  Doesn't matter what it is.
    #_private
    #_static
    (§ def- #_"BigInteger" (§ name HDKeyDerivation/RAND_INT))

    #_private
    (§ constructor- #_"HDKeyDerivation" [])
    (§ block
        this
    )

    ;;;
     ; Child derivation may fail (although with extremely low probability); in such case it is re-attempted.
     ; This is the maximum number of re-attempts (to avoid an infinite loop in case of bugs etc.)
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name HDKeyDerivation/MAX_CHILD_DERIVATION_ATTEMPTS) 100)

    ;;;
     ; Generates a new deterministic key from the given seed, which can be any arbitrary byte array.
     ; However resist the temptation to use a string as the seed - any key derived from a password
     ; is likely to be weak and easily broken by attackers (this is not theoretical, people have had
     ; money stolen that way).  This method checks that the given seed is at least 64 bits long.
     ;
     ; @throws HDDerivationException if generated master key is invalid (private key 0 or >= n).
     ; @throws IllegalArgumentException if the seed is less than 8 bytes and could be brute forced.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/createMasterPrivateKey) [#_"byte[]" __seed])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (< 8 (.. __seed length)), "Seed is too short and could be brute forced")

        ;; Calculate I = HMAC-SHA512(key="Bitcoin seed", msg=S).
        (§ let #_"byte[]" __i (HDUtils/hmacSha512 (HDUtils/createHmacSha512Digest (.. "Bitcoin seed" (getBytes))), __seed))
        (§ block
            ;; Split I into two 32-byte sequences, Il and Ir.
            ;; Use Il as master secret key, and Ir as master chain code.
            (Preconditions/checkState (== (.. __i length) 64), (.. __i length))

            (§ let #_"byte[]" __il (Arrays/copyOfRange __i, 0, 32))
            (§ block
                (§ let #_"byte[]" __ir (Arrays/copyOfRange __i, 32, 64))
                (§ block
                    (Arrays/fill __i, (§ cast #_"byte" 0))
                    (§ let #_"DeterministicKey" __masterPrivKey (HDKeyDerivation/createMasterPrivKeyFromBytes __il, __ir))
                    (§ block
                        (Arrays/fill __il, (§ cast #_"byte" 0))
                        (Arrays/fill __ir, (§ cast #_"byte" 0))

                        ;; Child deterministic keys will chain up to their parents to find the keys.
                        (.. __masterPrivKey (setCreationTimeSeconds (Utils/currentTimeSeconds)))
                        __masterPrivKey
                    )
                )
            )
        )
    )

    ;;;
     ; @throws HDDerivationException if privKeyBytes is invalid (0 or >= n).
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/createMasterPrivKeyFromBytes) [#_"byte[]" __privKeyBytes, #_"byte[]" __chainCode])
        (§ throws #_"HDDerivationException")
    (§ block
        (§ let #_"BigInteger" __priv (§ new #_"BigInteger" 1, __privKeyBytes))
        (§ block
            (HDKeyDerivation/assertNonZero __priv, "Generated master key is invalid.")
            (HDKeyDerivation/assertLessThanN __priv, "Generated master key is invalid.")
            (§ new #_"DeterministicKey" (ImmutableList/of #_"ImmutableList<ChildNumber>"), __chainCode, __priv, nil)
        )
    )

    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/createMasterPubKeyFromBytes) [#_"byte[]" __pubKeyBytes, #_"byte[]" __chainCode])
    (§ block
        (§ new #_"DeterministicKey" (ImmutableList/of #_"ImmutableList<ChildNumber>"), __chainCode, (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), __pubKeyBytes), nil, nil)
    )

    ;;;
     ; Derives a key given the "extended" child number, i.e. the 0x80000000 bit of the value that you
     ; pass for <code>childNumber</code> will determine whether to use hardened derivation or not.
     ; Consider whether your code would benefit from the clarity of the equivalent, but explicit, form
     ; of this method that takes a <code>ChildNumber</code> rather than an <code>int</code>, for example:
     ; <code>deriveChildKey(parent, new ChildNumber(childNumber, true))</code>
     ; where the value of the hardened bit of <code>childNumber</code> is zero.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/deriveChildKey) [#_"DeterministicKey" __parent, #_"int" __childNumber])
    (§ block
        (HDKeyDerivation/deriveChildKey __parent, (§ new #_"ChildNumber" __childNumber))
    )

    ;;;
     ; Derives a key of the "extended" child number, i.e. with the 0x80000000 bit specifying whether
     ; to use hardened derivation or not.  If derivation fails, tries a next child.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/deriveThisOrNextChildKey) [#_"DeterministicKey" __parent, #_"int" __childNumber])
    (§ block
        (§ let #_"ChildNumber" __child (§ new #_"ChildNumber" __childNumber))
        (§ block
            (§ let #_"boolean" __isHardened (.. __child (isHardened)))
            (§ block
                (§ for (§ var #_"int" __i 0) :for (< __i HDKeyDerivation/MAX_CHILD_DERIVATION_ATTEMPTS) :for (§ recur (inc __i)))
                (§ block
                    (§ try )
                    (§ block
                        (§ ass __child (§ new #_"ChildNumber" (+ (.. __child (num)) __i), __isHardened))
                        (§ return (HDKeyDerivation/deriveChildKey __parent, __child))
                    )
                    (§ catch #_"HDDerivationException" (§ name _))
                    (§ block
                    )
                )
                (§ throw (§ new #_"HDDerivationException" "Maximum number of child derivation attempts reached, this is probably an indication of a bug."))
            )
        )
    )

    ;;;
     ; @throws HDDerivationException if private derivation is attempted for a public-only parent key, or
     ; if the resulting derived key is invalid (e.g. private key == 0).
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn HDKeyDerivation/deriveChildKey) [#_"DeterministicKey" __parent, #_"ChildNumber" __childNumber])
        (§ throws #_"HDDerivationException")
    (§ block
        (§ if (not (.. __parent (hasPrivKey))))
        (§ block
            (§ let #_"RawKeyBytes" __rawKey (HDKeyDerivation/deriveChildKeyBytesFromPublic __parent, __childNumber, PublicDeriveMode/NORMAL))
            (§ block
                (§ return (§ new #_"DeterministicKey" (HDUtils/append (.. __parent (getPath)), __childNumber), (.. __rawKey chainCode), (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), (.. __rawKey keyBytes)), nil, __parent))
            )
        )
        (§ else )
        (§ block
            (§ let #_"RawKeyBytes" __rawKey (HDKeyDerivation/deriveChildKeyBytesFromPrivate __parent, __childNumber))
            (§ block
                (§ return (§ new #_"DeterministicKey" (HDUtils/append (.. __parent (getPath)), __childNumber), (.. __rawKey chainCode), (§ new #_"BigInteger" 1, (.. __rawKey keyBytes)), __parent))
            )
        )
    )

    #_public
    #_static
    (§ defn #_"RawKeyBytes" (§ fn HDKeyDerivation/deriveChildKeyBytesFromPrivate) [#_"DeterministicKey" __parent, #_"ChildNumber" __childNumber])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (.. __parent (hasPrivKey)), "Parent key must have private key bytes for this method.")

        (§ let #_"byte[]" __parentPublicKey (.. __parent (getPubKeyPoint) (getEncoded true)))
        (§ block
            (Preconditions/checkState (== (.. __parentPublicKey length) 33), (str "Parent pubkey must be 33 bytes, but is " (.. __parentPublicKey length)))

            (§ let #_"ByteBuffer" __data (ByteBuffer/allocate 37))
            (§ block
                (.. __data (put (if (.. __childNumber (isHardened)) (.. __parent (getPrivKeyBytes33)) __parentPublicKey)))
                (.. __data (putInt (.. __childNumber (i))))

                (§ let #_"byte[]" __i (HDUtils/hmacSha512 (.. __parent (getChainCode)), (.. __data (array))))
                (§ block
                    (Preconditions/checkState (== (.. __i length) 64), (.. __i length))

                    (§ let #_"byte[]" __il (Arrays/copyOfRange __i, 0, 32))
                    (§ block
                        (§ let #_"byte[]" __chainCode (Arrays/copyOfRange __i, 32, 64))
                        (§ block
                            (§ let #_"BigInteger" __ilInt (§ new #_"BigInteger" 1, __il))
                            (§ block
                                (HDKeyDerivation/assertLessThanN __ilInt, "Illegal derived key: I_L >= n")

                                (§ let #_"BigInteger" __priv (.. __parent (getPrivKey)))
                                (§ block
                                    (§ let #_"BigInteger" __ki (.. __priv (add __ilInt) (mod (.. ECKey/CURVE (getN)))))
                                    (§ block
                                        (HDKeyDerivation/assertNonZero __ki, "Illegal derived key: derived private key equals 0.")

                                        (§ new #_"RawKeyBytes" (.. __ki (toByteArray)), __chainCode)
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_public
    (§ enum #_"PublicDeriveMode"
        (§ item NORMAL)
        (§ item WITH_INVERSION)
    )

    #_public
    #_static
    (§ defn #_"RawKeyBytes" (§ fn HDKeyDerivation/deriveChildKeyBytesFromPublic) [#_"DeterministicKey" __parent, #_"ChildNumber" __childNumber, #_"PublicDeriveMode" __mode])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (not (.. __childNumber (isHardened))), "Can't use private derivation with public keys only.")

        (§ let #_"byte[]" __parentPublicKey (.. __parent (getPubKeyPoint) (getEncoded true)))
        (§ block
            (Preconditions/checkState (== (.. __parentPublicKey length) 33), (str "Parent pubkey must be 33 bytes, but is " (.. __parentPublicKey length)))

            (§ let #_"ByteBuffer" __data (ByteBuffer/allocate 37))
            (§ block
                (.. __data (put __parentPublicKey))
                (.. __data (putInt (.. __childNumber (i))))

                (§ let #_"byte[]" __i (HDUtils/hmacSha512 (.. __parent (getChainCode)), (.. __data (array))))
                (§ block
                    (Preconditions/checkState (== (.. __i length) 64), (.. __i length))

                    (§ let #_"byte[]" __il (Arrays/copyOfRange __i, 0, 32))
                    (§ block
                        (§ let #_"byte[]" __chainCode (Arrays/copyOfRange __i, 32, 64))
                        (§ block
                            (§ let #_"BigInteger" __ilInt (§ new #_"BigInteger" 1, __il))
                            (§ block
                                (HDKeyDerivation/assertLessThanN __ilInt, "Illegal derived key: I_L >= n")

                                (§ let #_"BigInteger" __N (.. ECKey/CURVE (getN)))
                                (§ block
                                    (§ let #_"ECPoint" __Ki)
                                    (§ block
                                        (§ switch __mode)
                                        (§ block
                                            (§ case NORMAL)
                                            (§ block
                                                (§ ass __Ki (.. (ECKey/publicPointFromPrivate __ilInt) (add (.. __parent (getPubKeyPoint)))))
                                                (§ break )
                                            )
                                            (§ case WITH_INVERSION)
                                            (§ block
                                                ;; This trick comes from Gregory Maxwell.  Check the homomorphic properties of our curve hold.  The
                                                ;; below calculations should be redundant and give the same result as NORMAL but if the precalculated
                                                ;; tables have taken a bit flip will yield a different answer.  This mode is used when vending a key
                                                ;; to perform a last-ditch sanity check trying to catch bad RAM.
                                                (§ ass __Ki (ECKey/publicPointFromPrivate (.. __ilInt (add HDKeyDerivation/RAND_INT) (mod __N))))
                                                (§ let #_"BigInteger" __additiveInverse (.. HDKeyDerivation/RAND_INT (negate) (mod __N)))
                                                (§ block
                                                    (§ ass __Ki (Ki/add (ECKey/publicPointFromPrivate __additiveInverse)))
                                                    (§ ass __Ki (Ki/add (.. __parent (getPubKeyPoint))))
                                                    (§ break )
                                                )
                                            )
                                            (§ default )
                                            (§ block
                                                (§ throw (§ new #_"AssertionError"))
                                            )
                                        )

                                        (HDKeyDerivation/assertNonInfinity __Ki, "Illegal derived key: derived public key equals infinity.")
                                        (§ new #_"RawKeyBytes" (Ki/getEncoded true), __chainCode)
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn HDKeyDerivation/assertNonZero) [#_"BigInteger" __integer, #_"String" __errorMessage])
    (§ block
        (§ if (.. __integer (equals BigInteger/ZERO)))
        (§ block
            (§ throw (§ new #_"HDDerivationException" __errorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn HDKeyDerivation/assertNonInfinity) [#_"ECPoint" __point, #_"String" __errorMessage])
    (§ block
        (§ if (.. __point (equals (.. ECKey/CURVE (getCurve) (getInfinity)))))
        (§ block
            (§ throw (§ new #_"HDDerivationException" __errorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn HDKeyDerivation/assertLessThanN) [#_"BigInteger" __integer, #_"String" __errorMessage])
    (§ block
        (§ if (> (.. __integer (compareTo (.. ECKey/CURVE (getN)))) 0))
        (§ block
            (§ throw (§ new #_"HDDerivationException" __errorMessage))
        )
        nil
    )

    #_public
    #_static
    (§ class #_"RawKeyBytes"
        #_public
        (§ field #_"byte[]" (§ name keyBytes))
        #_public
        (§ field #_"byte[]" (§ name chainCode))

        #_public
        (§ constructor #_"RawKeyBytes" [#_"byte[]" __keyBytes, #_"byte[]" __chainCode])
        (§ block
            (§ ass (§ name (.. this keyBytes)) __keyBytes)
            (§ ass (§ name (.. this chainCode)) __chainCode)
            this
        )
    )
)

#_(ns org.bitcoinj.crypto #_"HDUtils"
    (:import [java.nio ByteBuffer]
             [java.util ArrayList Arrays Collections List])
    (:import [com.google.common.base Joiner]
             [com.google.common.collect ImmutableList Iterables]
             [org.spongycastle.crypto.digests SHA512Digest]
             [org.spongycastle.crypto.macs HMac]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core ECKey]))

;;;
 ; Static utilities used in BIP 32 Hierarchical Deterministic Wallets (HDW).
 ;;
#_public
(§ class #_"HDUtils"
    #_private
    #_static
    (§ def- #_"Joiner" (§ name HDUtils/PATH_JOINER) (Joiner/on "/"))

    #_static
    (§ defn #_"HMac" (§ fn HDUtils/createHmacSha512Digest) [#_"byte[]" __key])
    (§ block
        (§ let #_"SHA512Digest" __digest (§ new #_"SHA512Digest"))
        (§ block
            (§ let #_"HMac" __hMac (§ new #_"HMac" __digest))
            (§ block
                (.. __hMac (init (§ new #_"KeyParameter" __key)))
                __hMac
            )
        )
    )

    #_static
    (§ defn #_"byte[]" (§ fn HDUtils/hmacSha512) [#_"HMac" __hmacSha512, #_"byte[]" __input])
    (§ block
        (.. __hmacSha512 (reset))
        (.. __hmacSha512 (update __input, 0, (.. __input length)))
        (§ let #_"byte[]" __out (§ new #_"byte[]" (§ count 64)))
        (§ block
            (.. __hmacSha512 (doFinal __out, 0))
            __out
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn HDUtils/hmacSha512) [#_"byte[]" __key, #_"byte[]" __data])
    (§ block
        (HDUtils/hmacSha512 (HDUtils/createHmacSha512Digest __key), __data)
    )

    #_static
    (§ defn #_"byte[]" (§ fn HDUtils/toCompressed) [#_"byte[]" __uncompressedPoint])
    (§ block
        (.. ECKey/CURVE (getCurve) (decodePoint __uncompressedPoint) (getEncoded true))
    )

    #_static
    (§ defn #_"byte[]" (§ fn HDUtils/longTo4ByteArray) [#_"long" __n])
    (§ block
        (§ let #_"byte[]" __bytes (Arrays/copyOfRange (.. (ByteBuffer/allocate 8) (putLong __n) (array)), 4, 8))
        (§ block
            (§ assert (== (.. __bytes length) 4) :assert (.. __bytes length))
            __bytes
        )
    )

    ;;; Append a derivation level to an existing path. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" (§ fn HDUtils/append) [#_"List<ChildNumber>" __path, #_"ChildNumber" __childNumber])
    (§ block
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll __path) (add __childNumber) (build))
    )

    ;;; Concatenate two derivation paths. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" (§ fn HDUtils/concat) [#_"List<ChildNumber>" __path, #_"List<ChildNumber>" __path2])
    (§ block
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll __path) (addAll __path2) (build))
    )

    ;;; Convert to a string path, starting with "M/". ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn HDUtils/formatPath) [#_"List<ChildNumber>" __path])
    (§ block
        (.. HDUtils/PATH_JOINER (join (Iterables/concat (Collections/singleton "M"), __path)))
    )

    ;;;
     ; The path is a human-friendly representation of the deterministic path.  For example:
     ;
     ; "44H / 0H / 0H / 1 / 1"
     ;
     ; Where a letter "H" means hardened key.  Spaces are ignored.
     ;;
    #_public
    #_static
    (§ defn #_"List<ChildNumber>" (§ fn HDUtils/parsePath) [#_non-nil #_"String" __path])
    (§ block
        (§ let #_"String[]" __parsedNodes (.. __path (replace "M", "") (split "/")))
        (§ block
            (§ let #_"List<ChildNumber>" __nodes (§ new #_"ArrayList<>"))
            (§ block

                (§ for (§ var #_"String" __n) :for __parsedNodes)
                (§ block
                    (§ ass __n (.. __n (replaceAll " ", "")))
                    (§ if (!= (.. __n (length)) 0))
                    (§ block
                        (§ let #_"boolean" __isHard (.. __n (endsWith "H")))
                        (§ block
                            (§ if __isHard)
                            (§ block
                                (§ ass __n (.. __n (substring 0, (- (.. __n (length)) 1))))
                            )
                            (§ let #_"int" __nodeNumber (Integer/parseInt __n))
                            (§ block
                                (.. __nodes (add (§ new #_"ChildNumber" __nodeNumber, __isHard)))
                            )
                        )
                    )
                )

                __nodes
            )
        )
    )
)

#_(ns org.bitcoinj.crypto #_"KeyCrypter"
    (:import [java.io Serializable])
    (:import [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.wallet.Protos.Wallet EncryptionType]))

;;;
 ; <p>A KeyCrypter can be used to encrypt and decrypt a message.  The sequence of events to encrypt and then decrypt
 ; a message are as follows:</p>
 ;
 ; <p>(1) Ask the user for a password.  deriveKey() is then called to create an KeyParameter.  This contains the AES
 ; key that will be used for encryption.</p>
 ; <p>(2) Encrypt the message using encrypt(), providing the message bytes and the KeyParameter from (1).  This returns
 ; an EncryptedData which contains the encryptedPrivateKey bytes and an initialisation vector.</p>
 ; <p>(3) To decrypt an EncryptedData, repeat step (1) to get a KeyParameter, then call decrypt().</p>
 ;
 ; <p>There can be different algorithms used for encryption/ decryption so the getUnderstoodEncryptionType is used
 ; to determine whether any given KeyCrypter can understand the type of encrypted data you have.</p>
 ;;
#_public
(§ interface #_"KeyCrypter" (§ extends #_"Serializable")
    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    (§ method #_"EncryptionType" (§ fn getUnderstoodEncryptionType) [])

    ;;;
     ; Create a KeyParameter (which typically contains an AES key).
     ; @param password
     ; @return the KeyParameter which typically contains the AES key to use for encrypting and decrypting.
     ; @throws KeyCrypterException
     ;;
    (§ method #_"KeyParameter" (§ fn deriveKey) [#_"CharSequence" __password])
        (§ throws #_"KeyCrypterException")

    ;;;
     ; Decrypt the provided encrypted bytes, converting them into unencrypted bytes.
     ;
     ; @throws KeyCrypterException if decryption was unsuccessful.
     ;;
    (§ method #_"byte[]" (§ fn decrypt) [#_"EncryptedData" __encryptedBytesToDecode, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")

    ;;;
     ; Encrypt the supplied bytes, converting them into ciphertext.
     ;
     ; @return an encryptedPrivateKey containing the encrypted bytes and an initialisation vector.
     ; @throws KeyCrypterException if encryption was unsuccessful.
     ;;
    (§ method #_"EncryptedData" (§ fn encrypt) [#_"byte[]" __plainBytes, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
)

#_(ns org.bitcoinj.crypto #_"KeyCrypterException")

;;;
 ; <p>Exception to provide the following:</p>
 ; <ul>
 ; <li>Provision of encryption / decryption exception.</li>
 ; </ul>
 ; <p>This base exception acts as a general failure mode not attributable to a specific cause (other than
 ; that reported in the exception message).  Since this is in English, it may not be worth reporting directly
 ; to the user other than as part of a "general failure to parse" response.</p>
 ;;
#_public
(§ class #_"KeyCrypterException" (§ extends #_"RuntimeException")
    #_public
    (§ constructor #_"KeyCrypterException" [#_"String" __s])
    (§ block
        (§ super __s)
        this
    )

    #_public
    (§ constructor #_"KeyCrypterException" [#_"String" __s, #_"Throwable" __throwable])
    (§ block
        (§ super __s, __throwable)
        this
    )
)

#_(ns org.bitcoinj.crypto #_"KeyCrypterScrypt"
    (:import [java.security SecureRandom]
             [java.util Arrays])
    (:import [com.google.common.base Objects Preconditions Stopwatch]
             [com.google.protobuf ByteString]
             [com.lambdaworks.crypto SCrypt]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto BufferedBlockCipher]
             [org.spongycastle.crypto.engines AESFastEngine]
             [org.spongycastle.crypto.modes CBCBlockCipher]
             [org.spongycastle.crypto.paddings PaddedBufferedBlockCipher]
             [org.spongycastle.crypto.params KeyParameter ParametersWithIV])
   (:require [org.bitcoinj.core Utils]
             [org.bitcoinj.wallet Protos]
             [org.bitcoinj.wallet.Protos ScryptParameters]
             [org.bitcoinj.wallet.Protos.Wallet EncryptionType]))

;;;
 ; <p>This class encrypts and decrypts byte arrays and strings using scrypt as the
 ; key derivation function and AES for the encryption.</p>
 ;
 ; <p>You can use this class to:</p>
 ;
 ; <p>1. Using a user password, create an AES key that can encrypt and decrypt your private keys.
 ; To convert the password to the AES key, scrypt is used.  This is an algorithm resistant
 ; to brute force attacks.  You can use the ScryptParameters to tune how difficult you
 ; want this to be generation to be.</p>
 ;
 ; <p>2. Using the AES Key generated above, you then can encrypt and decrypt any bytes using
 ; the AES symmetric cipher.  Eight bytes of salt is used to prevent dictionary attacks.</p>
 ;;
#_public
(§ class #_"KeyCrypterScrypt" (§ implements #_"KeyCrypter")
    #_private
    #_static
    (§ def- #_"Logger" (§ name KeyCrypterScrypt/log) (LoggerFactory/getLogger (§ klass #_"KeyCrypterScrypt")))

    ;;;
     ; Key length in bytes.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name KeyCrypterScrypt/KEY_LENGTH) 32) ;; = 256 bits.

    ;;;
     ; The size of an AES block in bytes.
     ; This is also the length of the initialisation vector.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name KeyCrypterScrypt/BLOCK_LENGTH) 16) ;; = 128 bits.

    ;;;
     ; The length of the salt used.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name KeyCrypterScrypt/SALT_LENGTH) 8)

    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )

        (§ ass (§ name KeyCrypterScrypt/SECURE_RANDOM) (§ new #_"SecureRandom"))
    )

    #_private
    #_static
    (§ def- #_"SecureRandom" (§ name KeyCrypterScrypt/SECURE_RANDOM))

    ;;; Returns SALT_LENGTH (8) bytes of random data. ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn KeyCrypterScrypt/randomSalt) [])
    (§ block
        (§ let #_"byte[]" __salt (§ new #_"byte[]" (§ count KeyCrypterScrypt/SALT_LENGTH)))
        (§ block
            (.. KeyCrypterScrypt/SECURE_RANDOM (nextBytes __salt))
            __salt
        )
    )

    ;; Scrypt parameters.
    #_private
    (§ field- #_"ScryptParameters" (§ name scryptParameters))

    ;;;
     ; Encryption/Decryption using default parameters and a random salt.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [])
    (§ block
        (§ let #_"Protos.ScryptParameters.Builder" __builder (.. (Protos/ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (KeyCrypterScrypt/randomSalt)))))
        (§ block
            (§ ass (§ name (.. this scryptParameters)) (.. __builder (build)))
            this
        )
    )

    ;;;
     ; Encryption/Decryption using custom number of iterations parameters and a random salt.
     ; As of August 2016, a useful value for mobile devices is 4096 (derivation takes about 1 second).
     ;
     ; @param iterations Number of scrypt iterations.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [#_"int" __iterations])
    (§ block
        (§ let #_"Protos.ScryptParameters.Builder" __builder (.. (Protos/ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (KeyCrypterScrypt/randomSalt))) (setN __iterations)))
        (§ block
            (§ ass (§ name (.. this scryptParameters)) (.. __builder (build)))
            this
        )
    )

    ;;;
     ; Encryption/ Decryption using specified Scrypt parameters.
     ;
     ; @param scryptParameters ScryptParameters to use.
     ; @throws NullPointerException if the scryptParameters or any of its N, R or P is null.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [#_"ScryptParameters" __scryptParameters])
    (§ block
        (§ ass (§ name (.. this scryptParameters)) (Preconditions/checkNotNull __scryptParameters))

        ;; Check there is a non-empty salt.  Some early MultiBit wallets has a missing salt, so it is not a hard fail.
        (§ if (or (nil? (.. __scryptParameters (getSalt))) (nil? (.. __scryptParameters (getSalt) (toByteArray))) (== (.. __scryptParameters (getSalt) (toByteArray) length) 0)))
        (§ block
            (.. KeyCrypterScrypt/log (warn "You are using a ScryptParameters with no salt. Your encryption may be vulnerable to a dictionary attack."))
        )
        this
    )

    ;;;
     ; Generate AES key.
     ;
     ; This is a very slow operation compared to encrypt/ decrypt so it is normally worth caching the result.
     ;
     ; @param password The password to use in key generation.
     ; @return the KeyParameter containing the created AES key.
     ; @throws KeyCrypterException
     ;;
    #_override
    #_public
    (§ method #_"KeyParameter" (§ fn deriveKey) [#_"CharSequence" __password])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"byte[]" __passwordBytes nil)
        (§ block
            (§ try )
            (§ block
                (§ ass __passwordBytes (KeyCrypterScrypt/convertToByteArray __password))
                (§ let #_"byte[]" __salt (§ new #_"byte[]" (§ count 0)))
                (§ block
                    (§ if (!= (.. scryptParameters (getSalt)) nil))
                    (§ block
                        (§ ass __salt (.. scryptParameters (getSalt) (toByteArray)))
                    )
                    (§ else )
                    (§ block
                        ;; Warn the user that they are not using a salt.  Some early MultiBit wallets had a blank salt.
                        (.. KeyCrypterScrypt/log (warn "You are using a ScryptParameters with no salt. Your encryption may be vulnerable to a dictionary attack."))
                    )

                    (§ let #_"Stopwatch" __watch (Stopwatch/createStarted))
                    (§ block
                        (§ let #_"byte[]" __keyBytes (SCrypt/scrypt __passwordBytes, __salt, (§ cast #_"int" (.. scryptParameters (getN))), (.. scryptParameters (getR)), (.. scryptParameters (getP)), KeyCrypterScrypt/KEY_LENGTH))
                        (§ block
                            (.. __watch (stop))
                            (.. KeyCrypterScrypt/log (info "Deriving key took {} for {} scrypt iterations.", __watch, (.. scryptParameters (getN))))
                            (§ return (§ new #_"KeyParameter" __keyBytes))
                        )
                    )
                )
            )
            (§ catch #_"Exception" __e)
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "Could not generate key from password and salt.", __e))
            )
            (§ finally )
            (§ block
                ;; Zero the password bytes.
                (§ if (some? __passwordBytes))
                (§ block
                    (java.util.Arrays/fill __passwordBytes, (§ cast #_"byte" 0))
                )
            )
        )
    )

    ;;;
     ; Password based encryption using AES - CBC 256 bits.
     ;;
    #_override
    #_public
    (§ method #_"EncryptedData" (§ fn encrypt) [#_"byte[]" __plainBytes, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull __plainBytes)
        (Preconditions/checkNotNull __aesKey)

        (§ try )
        (§ block
            ;; Generate iv - each encryption call has a different iv.
            (§ let #_"byte[]" __iv (§ new #_"byte[]" (§ count KeyCrypterScrypt/BLOCK_LENGTH)))
            (§ block
                (.. KeyCrypterScrypt/SECURE_RANDOM (nextBytes __iv))

                (§ let #_"ParametersWithIV" __keyWithIv (§ new #_"ParametersWithIV" __aesKey, __iv))
                (§ block

                    ;; Encrypt using AES.
                    (§ let #_"BufferedBlockCipher" __cipher (§ new #_"PaddedBufferedBlockCipher" (§ new #_"CBCBlockCipher" (§ new #_"AESFastEngine"))))
                    (§ block
                        (.. __cipher (init true, __keyWithIv))
                        (§ let #_"byte[]" __encryptedBytes (§ new #_"byte[]" (§ count (.. __cipher (getOutputSize (.. __plainBytes length))))))
                        (§ block
                            (§ let #_"int" __length1 (.. __cipher (processBytes __plainBytes, 0, (.. __plainBytes length), __encryptedBytes, 0)))
                            (§ block
                                (§ let #_"int" __length2 (.. __cipher (doFinal __encryptedBytes, __length1)))
                                (§ block

                                    (§ return (§ new #_"EncryptedData" __iv, (Arrays/copyOf __encryptedBytes, (+ __length1 __length2))))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" __e)
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Could not encrypt bytes.", __e))
        )
    )

    ;;;
     ; Decrypt bytes previously encrypted with this class.
     ;
     ; @param dataToDecrypt The data to decrypt.
     ; @param aesKey The AES key to use for decryption.
     ; @return the decrypted bytes.
     ; @throws KeyCrypterException if bytes could not be decrypted.
     ;;
    #_override
    #_public
    (§ method #_"byte[]" (§ fn decrypt) [#_"EncryptedData" __dataToDecrypt, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull __dataToDecrypt)
        (Preconditions/checkNotNull __aesKey)

        (§ try )
        (§ block
            (§ let #_"ParametersWithIV" __keyWithIv (§ new #_"ParametersWithIV" (§ new #_"KeyParameter" (.. __aesKey (getKey))), (.. __dataToDecrypt initialisationVector)))
            (§ block

                ;; Decrypt the message.
                (§ let #_"BufferedBlockCipher" __cipher (§ new #_"PaddedBufferedBlockCipher" (§ new #_"CBCBlockCipher" (§ new #_"AESFastEngine"))))
                (§ block
                    (.. __cipher (init false, __keyWithIv))

                    (§ let #_"byte[]" __cipherBytes (.. __dataToDecrypt encryptedBytes))
                    (§ block
                        (§ let #_"byte[]" __decryptedBytes (§ new #_"byte[]" (§ count (.. __cipher (getOutputSize (.. __cipherBytes length))))))
                        (§ block
                            (§ let #_"int" __length1 (.. __cipher (processBytes __cipherBytes, 0, (.. __cipherBytes length), __decryptedBytes, 0)))
                            (§ block
                                (§ let #_"int" __length2 (.. __cipher (doFinal __decryptedBytes, __length1)))
                                (§ block

                                    (§ return (Arrays/copyOf __decryptedBytes, (+ __length1 __length2)))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" __e)
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Could not decrypt bytes", __e))
        )
    )

    ;;;
     ; Convert a CharSequence (which are UTF16) into a byte array.
     ;
     ; Note: a String.getBytes() is not used to avoid creating a String of the password in the JVM.
     ;;
    #_private
    #_static
    (§ defn- #_"byte[]" (§ fn KeyCrypterScrypt/convertToByteArray) [#_"CharSequence" __charSequence])
    (§ block
        (Preconditions/checkNotNull __charSequence)

        (§ let #_"byte[]" __byteArray (§ new #_"byte[]" (§ count (.. __charSequence (length)) << 1)))
        (§ block
            (§ for (§ var #_"int" __i 0) :for (< __i (.. __charSequence (length))) :for (§ recur (inc __i)))
            (§ block
                (§ let #_"int" __bytePosition (<< __i 1))
                (§ block
                    (§ ass (§ name (§ ai __byteArray __bytePosition)) (§ cast #_"byte" (>> (& (.. __charSequence (charAt __i)) 0xff00) 8)))
                    (§ ass (§ name (§ ai __byteArray (inc __bytePosition))) (§ cast #_"byte" (& (.. __charSequence (charAt __i)) 0x00ff)))
                )
            )
            __byteArray
        )
    )

    #_public
    (§ method #_"ScryptParameters" (§ fn getScryptParameters) [])
    (§ block
        scryptParameters
    )

    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    #_override
    #_public
    (§ method #_"EncryptionType" (§ fn getUnderstoodEncryptionType) [])
    (§ block
        EncryptionType/ENCRYPTED_SCRYPT_AES
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "AES-" (* KeyCrypterScrypt/KEY_LENGTH 8) "-CBC, Scrypt (N: " (.. scryptParameters (getN)) ")")
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode scryptParameters)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (Objects/equal scryptParameters, (.. (§ cast #_"KeyCrypterScrypt" __o) scryptParameters))
    )
)

