#_(ns org.bitcoinj.crypto #_"LinuxSecureRandom"
    (:import [java.io *]
             [java.security *])
    (:import [org.slf4j Logger LoggerFactory]))

;;;
 ; A SecureRandom implementation that is able to override the standard JVM provided implementation, and which simply
 ; serves random numbers by reading /dev/urandom.  That is, it delegates to the kernel on UNIX systems and is unusable
 ; on other platforms.  Attempts to manually set the seed are ignored.  There is no difference between seed bytes and
 ; non-seed bytes, they are all from the same source.
 ;;
#_public
(§ class #_"LinuxSecureRandom" (§ extends #_"SecureRandomSpi")
    #_private
    #_static
    (§ def- #_"FileInputStream" (§ name LinuxSecureRandom/URANDOM))

    #_private
    #_static
    (§ class #_"LinuxSecureRandomProvider" (§ extends #_"Provider")
        #_public
        (§ constructor #_"LinuxSecureRandomProvider" [])
        (§ block
            (§ super "LinuxSecureRandom", 1.0, "A Linux specific random number provider that uses /dev/urandom")

            (.. this (put "SecureRandom.LinuxSecureRandom", (.. (§ klass #_"LinuxSecureRandom") (getName))))
            this
        )
    )

    #_private
    #_static
    (§ def- #_"Logger" (§ name LinuxSecureRandom/log) (LoggerFactory/getLogger (§ klass #_"LinuxSecureRandom")))

    #_static
    (§ block
        (§ try )
        (§ block
            (§ let #_"File" __file (§ new #_"File" "/dev/urandom"))
            (§ block
                ;; This stream is deliberately leaked.
                (§ ass (§ name LinuxSecureRandom/URANDOM) (§ new #_"FileInputStream" __file))
                (§ if (== (.. LinuxSecureRandom/URANDOM (read)) -1))
                (§ block
                    (§ throw (§ new #_"RuntimeException" "/dev/urandom not readable?"))
                )

                ;; Now override the default SecureRandom implementation with this one.
                (§ let #_"int" __position (Security/insertProviderAt (§ new #_"LinuxSecureRandomProvider"), 1))
                (§ block

                    (§ if (!= __position -1))
                    (§ block
                        (.. LinuxSecureRandom/log (info "Secure randomness will be read from {} only.", __file))
                    )
                    (§ else )
                    (§ block
                        (.. LinuxSecureRandom/log (info "Randomness is already secure."))
                    )
                )
            )
        )
        (§ catch #_"FileNotFoundException" __e)
        (§ block
            ;; Should never happen.
            (.. LinuxSecureRandom/log (error "/dev/urandom does not appear to exist or is not openable"))
            (§ throw (§ new #_"RuntimeException" __e))
        )
        (§ catch #_"IOException" __e)
        (§ block
            (.. LinuxSecureRandom/log (error "/dev/urandom does not appear to be readable"))
            (§ throw (§ new #_"RuntimeException" __e))
        )
    )

    #_private
    (§ field- #_"DataInputStream" (§ name dis))

    #_public
    (§ constructor #_"LinuxSecureRandom" [])
    (§ block
        ;; DataInputStream is not thread safe, so each random object has its own.
        (§ ass (§ name dis) (§ new #_"DataInputStream" LinuxSecureRandom/URANDOM))
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn engineSetSeed) [#_"byte[]" __bytes])
    (§ block
        ;; Ignore.
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn engineNextBytes) [#_"byte[]" __bytes])
    (§ block
        (§ try )
        (§ block
            (.. dis (readFully __bytes)) ;; This will block until all the bytes can be read.
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Fatal error.  Do not attempt to recover from this.
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"byte[]" (§ fn engineGenerateSeed) [#_"int" __i])
    (§ block
        (§ let #_"byte[]" __bits (§ new #_"byte[]" (§ count __i)))
        (§ block
            (.. this (engineNextBytes __bits))
            __bits
        )
    )
)

#_(ns org.bitcoinj.crypto #_"MnemonicCode"
    (:import [java.io BufferedReader FileNotFoundException IOException InputStream InputStreamReader]
             [java.security MessageDigest]
             [java.util ArrayList Collections List])
    (:import [com.google.common.base Stopwatch]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core Sha256Hash Utils]))

;;;
 ; A MnemonicCode object may be used to convert between binary seed values and lists of words per
 ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0039.mediawiki">the BIP 39 specification</a>.
 ;;

#_public
(§ class #_"MnemonicCode"
    #_private
    #_static
    (§ def- #_"Logger" (§ name MnemonicCode/log) (LoggerFactory/getLogger (§ klass #_"MnemonicCode")))

    #_private
    (§ field- #_"ArrayList<String>" (§ name wordList))

    #_private
    #_static
    (§ def- #_"String" (§ name MnemonicCode/BIP39_ENGLISH_RESOURCE_NAME) "mnemonic/wordlist/english.txt")
    #_private
    #_static
    (§ def- #_"String" (§ name MnemonicCode/BIP39_ENGLISH_SHA256) "ad90bf3beb7b0eb7e5acd74727dc0da96e0a280a258354e7293fb7e211ac03db")

    ;;; UNIX time for when the BIP39 standard was finalised.  This can be used as a default seed birthday. ;;
    #_public
    #_static
    (§ def #_"long" (§ name MnemonicCode/BIP39_STANDARDISATION_TIME_SECS) 1381276800)

    #_private
    #_static
    (§ def- #_"int" (§ name MnemonicCode/PBKDF2_ROUNDS) 2048)

    #_public
    #_static
    (§ def #_"MnemonicCode" (§ name MnemonicCode/INSTANCE))

    #_static
    (§ block
        (§ try )
        (§ block
            (§ ass (§ name MnemonicCode/INSTANCE) (§ new #_"MnemonicCode"))
        )
        (§ catch #_"FileNotFoundException" __e)
        (§ block
            ;; We expect failure on Android.  The developer has to set INSTANCE themselves.
            (§ if (not (Utils/isAndroidRuntime)))
            (§ block
                (.. MnemonicCode/log (error "Could not find word list", __e))
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (.. MnemonicCode/log (error "Failed to load word list", __e))
        )
    )

    ;;; Initialise from the included word list.  Won't work on Android. ;;
    #_public
    (§ constructor #_"MnemonicCode" [])
        (§ throws #_"IOException")
    (§ block
        (§ this (MnemonicCode/openDefaultWords), MnemonicCode/BIP39_ENGLISH_SHA256)
        this
    )

    #_private
    #_static
    (§ defn- #_"InputStream" (§ fn MnemonicCode/openDefaultWords) [])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"InputStream" __stream (.. (§ klass #_"MnemonicCode") (getResourceAsStream MnemonicCode/BIP39_ENGLISH_RESOURCE_NAME)))
        (§ block
            (§ if (nil? __stream))
            (§ block
                (§ throw (§ new #_"FileNotFoundException" MnemonicCode/BIP39_ENGLISH_RESOURCE_NAME))
            )
            __stream
        )
    )

    ;;;
     ; Creates an MnemonicCode object, initializing with words read from the supplied input stream.
     ; If a wordListDigest is supplied, the digest of the words will be checked.
     ;;
    #_public
    (§ constructor #_"MnemonicCode" [#_"InputStream" __wordstream, #_"String" __wordListDigest])
        (§ throws #_"IOException", #_"IllegalArgumentException")
    (§ block
        (§ let #_"BufferedReader" __br (§ new #_"BufferedReader" (§ new #_"InputStreamReader" __wordstream, "UTF-8")))
        (§ block
            (§ ass (§ name (.. this wordList)) (§ new #_"ArrayList<>" 2048))
            (§ let #_"MessageDigest" __md (Sha256Hash/newDigest))
            (§ block
                (§ let #_"String" __word)
                (§ block
                    (§ while (!= (§ ass __word (.. __br (readLine))) nil))
                    (§ block
                        (.. __md (update (.. __word (getBytes))))
                        (.. this wordList (add __word))
                    )
                    (.. __br (close))

                    (§ if (!= (.. this wordList (size)) 2048))
                    (§ block
                        (§ throw (§ new #_"IllegalArgumentException" "input stream did not contain 2048 words"))
                    )

                    ;; If a wordListDigest is supplied, check to make sure it matches.
                    (§ if (some? __wordListDigest))
                    (§ block
                        (§ let #_"byte[]" __digest (.. __md (digest)))
                        (§ block
                            (§ let #_"String" __hexdigest (.. Utils/HEX (encode __digest)))
                            (§ block
                                (§ if (not (.. __hexdigest (equals __wordListDigest))))
                                (§ block
                                    (§ throw (§ new #_"IllegalArgumentException" "wordlist digest mismatch"))
                                )
                            )
                        )
                    )
                    this
                )
            )
        )
    )

    ;;;
     ; Gets the word list this code uses.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn getWordList) [])
    (§ block
        wordList
    )

    ;;;
     ; Convert mnemonic word list to seed.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn MnemonicCode/toSeed) [#_"List<String>" __words, #_"String" __passphrase])
    (§ block
        ;; To create binary seed from mnemonic, we use PBKDF2 function with mnemonic sentence (in UTF-8) used as a password
        ;; and string "mnemonic" + passphrase (again in UTF-8) used as a salt.  Iteration count is set to 4096 and HMAC-SHA512
        ;; is used as a pseudo-random function.  Desired length of the derived key is 512 bits (= 64 bytes).

        (§ let #_"String" __pass (.. Utils/SPACE_JOINER (join __words)))
        (§ block
            (§ let #_"String" __salt (str "mnemonic" __passphrase))
            (§ block

                (§ let #_"Stopwatch" __watch (Stopwatch/createStarted))
                (§ block
                    (§ let #_"byte[]" __seed (PBKDF2SHA512/derive __pass, __salt, MnemonicCode/PBKDF2_ROUNDS, 64))
                    (§ block
                        (.. __watch (stop))
                        (.. MnemonicCode/log (info "PBKDF2 took {}", __watch))
                        __seed
                    )
                )
            )
        )
    )

    ;;;
     ; Convert mnemonic word list to original entropy value.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn toEntropy) [#_"List<String>" __words])
        (§ throws #_"MnemonicException.MnemonicLengthException", #_"MnemonicException.MnemonicWordException", #_"MnemonicException.MnemonicChecksumException")
    (§ block
        (§ if (< 0 (% (.. __words (size)) 3)))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Word list size must be multiple of three words."))
        )

        (§ if (== (.. __words (size)) 0))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Word list is empty."))
        )

        ;; Look up all the words in the list and construct the concatenation of the original entropy and the checksum.

        (§ let #_"int" __concatLenBits (* (.. __words (size)) 11))
        (§ block
            (§ let #_"boolean[]" __concatBits (§ new #_"boolean[]" (§ count __concatLenBits)))
            (§ block
                (§ let #_"int" __wordindex 0)
                (§ block
                    (§ for (§ var #_"String" __word) :for __words)
                    (§ block
                        ;; Find the words index in the wordlist.
                        (§ let #_"int" __ndx (Collections/binarySearch (.. this wordList), __word))
                        (§ block
                            (§ if (< __ndx 0))
                            (§ block
                                (§ throw (§ new #_"MnemonicException.MnemonicWordException" __word))
                            )

                            ;; Set the next 11 bits to the value of the index.
                            (§ for (§ var #_"int" __i 0) :for (< __i 11) :for (§ recur (inc __i)))
                            (§ block
                                (§ ass (§ name (§ ai __concatBits (+ (* __wordindex 11) __i))) (!= (& __ndx (<< 1 (- 10 __i))) 0))
                            )
                            (§ ass __wordindex (inc __wordindex))
                        )
                    )

                    (§ let #_"int" __checksumLengthBits (/ __concatLenBits 33))
                    (§ block
                        (§ let #_"int" __entropyLengthBits (- __concatLenBits __checksumLengthBits))
                        (§ block

                            ;; Extract original entropy as bytes.
                            (§ let #_"byte[]" __entropy (§ new #_"byte[]" (§ count __entropyLengthBits / 8)))
                            (§ block
                                (§ for (§ var #_"int" __i 0) :for (< __i (.. __entropy length)) :for (§ recur (inc __i)))
                                (§ block
                                    (§ for (§ var #_"int" __j 0) :for (< __j 8) :for (§ recur (inc __j)))
                                    (§ block
                                        (§ if (§ ai __concatBits (+ (* __i 8) __j)))
                                        (§ block
                                            (§ ass (§ name (§ ai __entropy __i)) (| (§ ai __entropy __i) (<< 1 (- 7 __j))))
                                        )
                                    )
                                )

                                ;; Take the digest of the entropy.
                                (§ let #_"byte[]" __hash (Sha256Hash/hash __entropy))
                                (§ block
                                    (§ let #_"boolean[]" __hashBits (MnemonicCode/bytesToBits __hash))
                                    (§ block

                                        ;; Check all the checksum bits.
                                        (§ for (§ var #_"int" __i 0) :for (< __i __checksumLengthBits) :for (§ recur (inc __i)))
                                        (§ block
                                            (§ if (!= (§ ai __concatBits (+ __entropyLengthBits __i)) (§ ai __hashBits __i)))
                                            (§ block
                                                (§ throw (§ new #_"MnemonicException.MnemonicChecksumException"))
                                            )
                                        )

                                        __entropy
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Convert entropy data to mnemonic word list.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn toMnemonic) [#_"byte[]" __entropy])
        (§ throws #_"MnemonicException.MnemonicLengthException")
    (§ block
        (§ if (< 0 (% (.. __entropy length) 4)))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Entropy length not multiple of 32 bits."))
        )

        (§ if (== (.. __entropy length) 0))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Entropy is empty."))
        )

        ;; We take initial entropy of ENT bits and compute its checksum by taking first ENT / 32 bits of its SHA256 hash.

        (§ let #_"byte[]" __hash (Sha256Hash/hash __entropy))
        (§ block
            (§ let #_"boolean[]" __hashBits (MnemonicCode/bytesToBits __hash))
            (§ block

                (§ let #_"boolean[]" __entropyBits (MnemonicCode/bytesToBits __entropy))
                (§ block
                    (§ let #_"int" __checksumLengthBits (/ (.. __entropyBits length) 32))
                    (§ block

                        ;; We append these bits to the end of the initial entropy.
                        (§ let #_"boolean[]" __concatBits (§ new #_"boolean[]" (§ count (.. __entropyBits length) + __checksumLengthBits)))
                        (§ block
                            (System/arraycopy __entropyBits, 0, __concatBits, 0, (.. __entropyBits length))
                            (System/arraycopy __hashBits, 0, __concatBits, (.. __entropyBits length), __checksumLengthBits)

                            ;; Next we take these concatenated bits and split them into groups of 11 bits.  Each group encodes number from 0-2047
                            ;; which is a position in a wordlist.  We convert numbers into words and use joined words as mnemonic sentence.

                            (§ let #_"ArrayList<String>" __words (§ new #_"ArrayList<>"))
                            (§ block
                                (§ let #_"int" __nwords (/ (.. __concatBits length) 11))
                                (§ block
                                    (§ for (§ var #_"int" __i 0) :for (< __i __nwords) :for (§ recur (inc __i)))
                                    (§ block
                                        (§ let #_"int" __index 0)
                                        (§ block
                                            (§ for (§ var #_"int" __j 0) :for (< __j 11) :for (§ recur (inc __j)))
                                            (§ block
                                                (§ ass __index (<< __index 1))
                                                (§ if (§ ai __concatBits (+ (* __i 11) __j)))
                                                (§ block
                                                    (§ ass __index (| __index 0x1))
                                                )
                                            )
                                            (.. __words (add (.. this wordList (get __index))))
                                        )
                                    )

                                    __words
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Check to see if a mnemonic word list is valid.
     ;;
    #_public
    (§ method #_"void" (§ fn check) [#_"List<String>" __words])
        (§ throws #_"MnemonicException")
    (§ block
        (.. this (toEntropy __words))
        nil
    )

    #_private
    #_static
    (§ defn- #_"boolean[]" (§ fn MnemonicCode/bytesToBits) [#_"byte[]" __data])
    (§ block
        (§ let #_"boolean[]" __bits (§ new #_"boolean[]" (§ count (.. __data length) * 8)))
        (§ block
            (§ for (§ var #_"int" __i 0) :for (< __i (.. __data length)) :for (§ recur (inc __i)))
            (§ block
                (§ for (§ var #_"int" __j 0) :for (< __j 8) :for (§ recur (inc __j)))
                (§ block
                    (§ ass (§ name (§ ai __bits (+ (* __i 8) __j))) (!= (& (§ ai __data __i) (<< 1 (- 7 __j))) 0))
                )
            )
            __bits
        )
    )
)

#_(ns org.bitcoinj.crypto #_"MnemonicException")

;;;
 ; Exceptions thrown by the MnemonicCode module.
 ;;
#_public
(§ class #_"MnemonicException" (§ extends #_"Exception")
    #_public
