)

#_(ns org.bitcoinj.crypto #_"PBKDF2SHA512"
    (:import [java.io ByteArrayOutputStream]
             [java.nio ByteBuffer ByteOrder]
             [javax.crypto Mac]
             [javax.crypto.spec SecretKeySpec]))

;;;
 ; This is a clean-room implementation of PBKDF2 using RFC 2898 as a reference.
 ;
 ; RFC 2898: http://tools.ietf.org/html/rfc2898#section-5.2
 ;
 ; This code passes all RFC 6070 test vectors: http://tools.ietf.org/html/rfc6070
 ;
 ; http://cryptofreek.org/2012/11/29/pbkdf2-pure-java-implementation/
 ; Modified to use SHA-512 - Ken Sedgwick ken@bonsai.com
 ;;
#_public
(§ class #_"PBKDF2SHA512"
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn PBKDF2SHA512/derive) [#_"String" __P, #_"String" __S, #_"int" __c, #_"int" __dkLen])
    (§ block
        (§ let #_"ByteArrayOutputStream" __baos (§ new #_"ByteArrayOutputStream"))
        (§ block

            (§ try )
            (§ block
                (§ let #_"int" __hLen 20)
                (§ block

                    (§ if (< (* (dec (Math/pow 2, 32)) __hLen) __dkLen))
                    (§ block
                        (§ throw (§ new #_"IllegalArgumentException" "derived key too long"))
                    )

                    (§ let #_"int" __l (§ cast #_"int" (Math/ceil (/ (§ cast #_"double" __dkLen) (§ cast #_"double" __hLen)))))
                    (§ block
                    ;; int r = dkLen - (l - 1) * hLen;

                        (§ for (§ var #_"int" __i 1) :for (<= __i __l) :for (§ recur (inc __i)))
                        (§ block
                            (§ let #_"byte[]" __T (PBKDF2SHA512/F __P, __S, __c, __i))
                            (§ block
                                (.. __baos (write __T))
                            )
                        )
                    )
                )
            )
            (§ catch #_"Exception" __e)
            (§ block
                (§ throw (§ new #_"RuntimeException" __e))
            )

            (§ let #_"byte[]" __baDerived (§ new #_"byte[]" (§ count __dkLen)))
            (§ block
                (System/arraycopy (.. __baos (toByteArray)), 0, __baDerived, 0, (.. __baDerived length))

                __baDerived
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"byte[]" (§ fn PBKDF2SHA512/F) [#_"String" __P, #_"String" __S, #_"int" __c, #_"int" __i])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"byte[]" __U_LAST nil)
        (§ block
            (§ let #_"byte[]" __U_XOR nil)
            (§ block

                (§ let #_"SecretKeySpec" __key (§ new #_"SecretKeySpec" (.. __P (getBytes "UTF-8")), "HmacSHA512"))
                (§ block
                    (§ let #_"Mac" __mac (Mac/getInstance (.. __key (getAlgorithm))))
                    (§ block
                        (.. __mac (init __key))

                        (§ for (§ var #_"int" __j 0) :for (< __j __c) :for (§ recur (inc __j)))
                        (§ block
                            (§ if (== __j 0))
                            (§ block
                                (§ let #_"byte[]" __baS (.. __S (getBytes "UTF-8")))
                                (§ block
                                    (§ let #_"byte[]" __baI (PBKDF2SHA512/INT __i))
                                    (§ block
                                        (§ let #_"byte[]" __baU (§ new #_"byte[]" (§ count (.. __baS length) + (.. __baI length))))
                                        (§ block

                                            (System/arraycopy __baS, 0, __baU, 0, (.. __baS length))
                                            (System/arraycopy __baI, 0, __baU, (.. __baS length), (.. __baI length))

                                            (§ ass __U_XOR (.. __mac (doFinal __baU)))
                                            (§ ass __U_LAST __U_XOR)
                                            (.. __mac (reset))
                                        )
                                    )
                                )
                            )
                            (§ else )
                            (§ block
                                (§ let #_"byte[]" __baU (.. __mac (doFinal __U_LAST)))
                                (§ block
                                    (.. __mac (reset))

                                    (§ for (§ var #_"int" __k 0) :for (< __k (.. __U_XOR length)) :for (§ recur (inc __k)))
                                    (§ block
                                        (§ ass (§ name (§ ai __U_XOR __k)) (§ cast #_"byte" (bit-xor (§ ai __U_XOR __k) (§ ai __baU __k))))
                                    )

                                    (§ ass __U_LAST __baU)
                                )
                            )
                        )

                        __U_XOR
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"byte[]" (§ fn PBKDF2SHA512/INT) [#_"int" __i])
    (§ block
        (§ let #_"ByteBuffer" __bb (ByteBuffer/allocate 4))
        (§ block
            (.. __bb (order ByteOrder/BIG_ENDIAN))
            (.. __bb (putInt __i))

            (.. __bb (array))
        )
    )
)

#_(ns org.bitcoinj.crypto #_"TransactionSignature"
    (:import [java.io ByteArrayOutputStream IOException]
             [java.math BigInteger])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core ECKey Transaction VerificationException]
             [org.bitcoinj.core.Transaction SigHash]))

;;;
 ; A TransactionSignature wraps an {@link org.bitcoinj.core.ECKey.ECDSASignature} and adds methods for handling
 ; the additional SIGHASH mode byte that is used.
 ;;
#_public
(§ class #_"TransactionSignature" (§ extends #_"ECDSASignature")
    ;;;
     ; A byte that controls which parts of a transaction are signed.  This is exposed because signatures
     ; parsed off the wire may have sighash flags that aren't "normal" serializations of the enum values.
     ; Because Bitcoin Core works via bit testing, we must not lose the exact value when round-tripping
     ; otherwise we'll fail to verify signature hashes.
     ;;
    #_public
    (§ field #_"int" (§ name sighashFlags))

    ;;; Constructs a signature with the given components and SIGHASH_ALL. ;;
    #_public
    (§ constructor #_"TransactionSignature" [#_"BigInteger" __r, #_"BigInteger" __s])
    (§ block
        (§ this __r, __s, (.. Transaction/SigHash/ALL value))
        this
    )

    ;;; Constructs a signature with the given components and raw sighash flag bytes (needed for rule compatibility). ;;
    #_public
    (§ constructor #_"TransactionSignature" [#_"BigInteger" __r, #_"BigInteger" __s, #_"int" __sighashFlags])
    (§ block
        (§ super __r, __s)
        (§ ass (§ name (.. this sighashFlags)) __sighashFlags)
        this
    )

    ;;; Constructs a transaction signature based on the ECDSA signature. ;;
    #_public
    (§ constructor #_"TransactionSignature" [#_"ECDSASignature" __signature, #_"Transaction.SigHash" __mode, #_"boolean" __anyoneCanPay])
    (§ block
        (§ super (.. __signature r), (.. __signature s))
        (§ ass (§ name sighashFlags) (TransactionSignature/calcSigHashValue __mode, __anyoneCanPay))
        this
    )

    ;;;
     ; Returns a dummy invalid signature whose R/S values are set such that they will take up the same number of
     ; encoded bytes as a real signature.  This can be useful when you want to fill out a transaction to be of
     ; the right size (e.g. for fee calculations) but don't have the requisite signing key yet and will fill out
     ; the real signature later.
     ;;
    #_public
    #_static
    (§ defn #_"TransactionSignature" (§ fn TransactionSignature/dummy) [])
    (§ block
        (§ let #_"BigInteger" __val ECKey/HALF_CURVE_ORDER)
        (§ block
            (§ new #_"TransactionSignature" __val, __val)
        )
    )

    ;;; Calculates the byte used in the protocol to represent the combination of mode and anyoneCanPay. ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn TransactionSignature/calcSigHashValue) [#_"Transaction.SigHash" __mode, #_"boolean" __anyoneCanPay])
    (§ block
        ;; Enforce compatibility since this code was made before the SigHash enum was updated.
        (Preconditions/checkArgument (or (== __mode SigHash/ALL) (== __mode SigHash/NONE) (== __mode SigHash/SINGLE)))

        (§ let #_"int" __sighashFlags (.. __mode value))
        (§ block
            (§ if __anyoneCanPay)
            (§ block
                (§ ass __sighashFlags (| __sighashFlags (.. Transaction/SigHash/ANYONECANPAY value)))
            )
            __sighashFlags
        )
    )

    ;;;
     ; Returns true if the given signature is has canonical encoding, and will thus be accepted as standard by
     ; Bitcoin Core.  DER and the SIGHASH encoding allow for quite some flexibility in how the same structures
     ; are encoded, and this can open up novel attacks in which a man in the middle takes a transaction and then
     ; changes its signature such that the transaction hash is different but it's still valid.  This can confuse
     ; wallets and generally violates people's mental model of how Bitcoin should work, thus non-canonical
     ; signatures are now not relayed by default.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn TransactionSignature/isEncodingCanonical) [#_"byte[]" __signature])
    (§ block
        ;; See Bitcoin Core's IsCanonicalSignature, https://bitcointalk.org/index.php?topic=8392.msg127623#msg127623
        ;; A canonical signature exists of: <30> <total len> <02> <len R> <R> <02> <len S> <S> <hashtype>
        ;; Where R and S are not negative (their first byte has its highest bit not set), and not
        ;; excessively padded (do not start with a 0 byte, unless an otherwise negative number follows,
        ;; in which case a single 0 byte is necessary and even required).
        (§ if (not (<= 9 (.. __signature length) 73)))
        (§ block
            (§ return false)
        )

        (§ let #_"int" __hashType (& (& (§ ai __signature (dec (.. __signature length))) 0xff) (bit-not (.. Transaction/SigHash/ANYONECANPAY value)))) ;; mask the byte to prevent sign-extension hurting us
        (§ block
            (§ if (not (<= (.. Transaction/SigHash/ALL value) __hashType (.. Transaction/SigHash/SINGLE value))))
            (§ block
                (§ return false)
            )

            ;;                   "wrong type"                  "wrong length marker"
            (§ if (or (!= (& 0xff (§ ai __signature 0)) 0x30) (!= (& 0xff (§ ai __signature 1)) (- (.. __signature length) 3))))
            (§ block
                (§ return false)
            )

            (§ let #_"int" __lenR (& 0xff (§ ai __signature 3)))
            (§ block
                (§ if (or (<= (.. __signature length) (+ 5 __lenR)) (== __lenR 0)))
                (§ block
                    (§ return false)
                )
                (§ let #_"int" __lenS (& 0xff (§ ai __signature (+ 5 __lenR))))
                (§ block
                    (§ if (or (!= (+ __lenR __lenS 7) (.. __signature length)) (== __lenS 0)))
                    (§ block
                        (§ return false)
                    )

                    ;;    R value type mismatch          R value negative
                    (§ if (or (!= (§ ai __signature (- 4 2)) 0x02) (== (& 0x80 (§ ai __signature 4)) 0x80)))
                    (§ block
                        (§ return false)
                    )
                    (§ if (and (< 1 __lenR) (== (§ ai __signature 4) 0x00) (!= (& 0x80 (§ ai __signature (+ 4 1))) 0x80)))
                    (§ block
                        (§ return false) ;; R value excessively padded
                    )

                    ;;       S value type mismatch                    S value negative
                    (§ if (or (!= (§ ai __signature (+ 6 __lenR -2)) 0x02) (== (& 0x80 (§ ai __signature (+ 6 __lenR))) 0x80)))
                    (§ block
                        (§ return false)
                    )
                    (§ if (and (< 1 __lenS) (== (§ ai __signature (+ 6 __lenR)) 0x00) (!= (& 0x80 (§ ai __signature (+ 6 __lenR 1))) 0x80)))
                    (§ block
                        (§ return false) ;; S value excessively padded
                    )

                    true
                )
            )
        )
    )

    #_public
    (§ method #_"boolean" (§ fn anyoneCanPay) [])
    (§ block
        (!= (& sighashFlags (.. Transaction/SigHash/ANYONECANPAY value)) 0)
    )

    #_public
    (§ method #_"Transaction.SigHash" (§ fn sigHashMode) [])
    (§ block
        (§ let #_"int" __mode (& sighashFlags 0x1f))
        (§ block

            (§ if (== __mode (.. Transaction/SigHash/NONE value)))
            (§ block
                (§ return Transaction/SigHash/NONE)
            )
            (§ if (== __mode (.. Transaction/SigHash/SINGLE value)))
            (§ block
                (§ return Transaction/SigHash/SINGLE)
            )

            Transaction/SigHash/ALL
        )
    )

    ;;;
     ; What we get back from the signer are the two components of a signature, r and s.  To get a flat byte stream
     ; of the type used by Bitcoin we have to encode them using DER encoding, which is just a way to pack the two
     ; components into a structure, and then we append a byte to the end for the sighash flags.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn encodeToBitcoin) [])
    (§ block
        (§ try )
        (§ block
            (§ let #_"ByteArrayOutputStream" __bos (.. this (derByteStream)))
            (§ block
                (.. __bos (write sighashFlags))
                (§ return (.. __bos (toByteArray)))
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
    )

    #_override
    #_public
    (§ method #_"ECDSASignature" (§ fn toCanonicalised) [])
    (§ block
        (§ new #_"TransactionSignature" (.. super (toCanonicalised)), (.. this (sigHashMode)), (.. this (anyoneCanPay)))
    )

    ;;;
     ; Returns a decoded signature.
     ;
     ; @param requireCanonicalEncoding if the encoding of the signature must be canonical.
     ; @throws RuntimeException if the signature is invalid or unparseable in some way.
     ; @deprecated use {@link #decodeFromBitcoin(byte[], boolean, boolean)} instead.
     ;;
    #_deprecated
    #_public
    #_static
    (§ defn #_"TransactionSignature" (§ fn TransactionSignature/decodeFromBitcoin) [#_"byte[]" __bytes, #_"boolean" __requireCanonicalEncoding])
        (§ throws #_"VerificationException")
    (§ block
        (TransactionSignature/decodeFromBitcoin __bytes, __requireCanonicalEncoding, false)
    )

    ;;;
     ; Returns a decoded signature.
     ;
     ; @param requireCanonicalEncoding if the encoding of the signature must be canonical.
     ; @param requireCanonicalSValue if the S-value must be canonical (below half the order of the curve).
     ; @throws RuntimeException if the signature is invalid or unparseable in some way.
     ;;
    #_public
    #_static
    (§ defn #_"TransactionSignature" (§ fn TransactionSignature/decodeFromBitcoin) [#_"byte[]" __bytes, #_"boolean" __requireCanonicalEncoding, #_"boolean" __requireCanonicalSValue])
        (§ throws #_"VerificationException")
    (§ block
        ;; Bitcoin encoding is DER signature + sighash byte.
        (§ if (and __requireCanonicalEncoding (not (TransactionSignature/isEncodingCanonical __bytes))))
        (§ block
            (§ throw (§ new #_"VerificationException" "Signature encoding is not canonical."))
        )

        (§ let #_"ECDSASignature" __sig)
        (§ block
            (§ try )
            (§ block
                (§ ass __sig (ECDSASignature/decodeFromDER __bytes))
            )
            (§ catch #_"IllegalArgumentException" __e)
            (§ block
                (§ throw (§ new #_"VerificationException" "Could not decode DER", __e))
            )
            (§ if (and __requireCanonicalSValue (not (.. __sig (isCanonical)))))
            (§ block
                (§ throw (§ new #_"VerificationException" "S-value is not canonical."))
            )

            ;; In Bitcoin, any value of the final byte is valid, but not necessarily canonical.  See javadocs
            ;; for isEncodingCanonical to learn more about this.  So we must store the exact byte found.
            (§ new #_"TransactionSignature" (.. __sig r), (.. __sig s), (§ ai __bytes (dec (.. __bytes length))))
        )
    )
)

#_(ns org.bitcoinj.kits #_"WalletAppKit"
    (:import [java.io *]
             [java.net *]
             [java.nio.channels *]
             [java.util *]
             [java.util.concurrent *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect *]
             [com.google.common.util.concurrent *]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core.listeners *]
             [org.bitcoinj.core *]
             [org.bitcoinj.net.discovery *]
             [org.bitcoinj.store *]
             [org.bitcoinj.wallet *]))

;;;
 ; <p>Utility class that wraps the boilerplate needed to set up a new SPV bitcoinj app.  Instantiate it with a directory
 ; and file prefix, optionally configure a few things, then use startAsync and optionally awaitRunning.  The object will
 ; construct and configure a {@link BlockChain}, {@link SPVBlockStore}, {@link Wallet} and {@link PeerGroup}.  Depending
 ; on the value of the blockingStartup property, startup will be considered complete once the block chain has fully
 ; synchronized, so it can take a while.</p>
 ;
 ; <p>To add listeners and modify the objects that are constructed, you can either do that by overriding the
 ; {@link #onSetupCompleted()} method (which will run on a background thread) and make your changes there,
 ; or by waiting for the service to start and then accessing the objects from wherever you want.  However, you cannot
 ; access the objects this class creates until startup is complete.</p>
 ;
 ; <p>The asynchronous design of this class may seem puzzling (just use {@link #awaitRunning()} if you don't want that).
 ; It is to make it easier to fit bitcoinj into GUI apps, which require a high degree of responsiveness on their main
 ; thread which handles all the animation and user interaction.  Even when blockingStart is false, initializing bitcoinj
 ; means doing potentially blocking file IO, generating keys and other potentially intensive operations.  By running it
 ; on a background thread, there's no risk of accidentally causing UI lag.</p>
 ;
 ; <p>Note that {@link #awaitRunning()} can throw an unchecked {@link java.lang.IllegalStateException}
 ; if anything goes wrong during startup - you should probably handle it and use {@link Exception#getCause()} to figure
 ; out what went wrong more precisely.  Same thing if you just use the {@link #startAsync()} method.</p>
 ;;
#_public
(§ class #_"WalletAppKit" (§ extends #_"AbstractIdleService")
    #_protected
    #_static
    (§ def #_"Logger" (§ name WalletAppKit/log) (LoggerFactory/getLogger (§ klass #_"WalletAppKit")))

    #_protected
    (§ field #_"String" (§ name filePrefix))
    #_protected
    (§ field #_"NetworkParameters" (§ name params))
    #_protected
    #_volatile
    (§ field #_"BlockChain" (§ name vChain))
    #_protected
    #_volatile
    (§ field #_"BlockStore" (§ name vStore))
    #_protected
    #_volatile
    (§ field #_"Wallet" (§ name vWallet))
    #_protected
    #_volatile
    (§ field #_"PeerGroup" (§ name vPeerGroup))

    #_protected
    (§ field #_"File" (§ name directory))
    #_protected
    #_volatile
    (§ field #_"File" (§ name vWalletFile))

    #_protected
    (§ field #_"boolean" (§ name useAutoSave) true)
    #_protected
    (§ field #_"PeerAddress[]" (§ name peerAddresses))
    #_protected
    (§ field #_"DownloadProgressTracker" (§ name downloadListener))
    #_protected
    (§ field #_"boolean" (§ name autoStop) true)
    #_protected
    (§ field #_"InputStream" (§ name checkpoints))
    #_protected
    (§ field #_"boolean" (§ name blockingStartup) true)
    #_protected
    (§ field #_"String" (§ name userAgent))
    #_protected
    (§ field #_"String" (§ name version))
    #_protected
    (§ field #_"WalletProtobufSerializer.WalletFactory" (§ name walletFactory))
    #_nilable
    #_protected
    (§ field #_"DeterministicSeed" (§ name restoreFromSeed))
    #_nilable
    #_protected
    (§ field #_"PeerDiscovery" (§ name discovery))

    #_protected
    #_volatile
    (§ field #_"Context" (§ name context))

    ;;;
     ; Creates a new WalletAppKit, with a newly created {@link Context}.  Files will be stored in the given directory.
     ;;
    #_public
    (§ constructor #_"WalletAppKit" [#_"NetworkParameters" __params, #_"File" __directory, #_"String" __filePrefix])
    (§ block
        (§ this (§ new #_"Context" __params), __directory, __filePrefix)
        this
    )

    ;;;
     ; Creates a new WalletAppKit, with the given {@link Context}.  Files will be stored in the given directory.
     ;;
    #_public
    (§ constructor #_"WalletAppKit" [#_"Context" __context, #_"File" __directory, #_"String" __filePrefix])
    (§ block
        (§ ass (§ name (.. this context)) __context)
        (§ ass (§ name (.. this params)) (Preconditions/checkNotNull (.. __context (getParams))))
        (§ ass (§ name (.. this directory)) (Preconditions/checkNotNull __directory))
        (§ ass (§ name (.. this filePrefix)) (Preconditions/checkNotNull __filePrefix))
        this
    )

    ;;; Will only connect to the given addresses.  Cannot be called after startup. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setPeerNodes) [#_"PeerAddress..." __addresses])
    (§ block
        (Preconditions/checkState (== (state) State/NEW), "Cannot call after startup")
        (§ ass (§ name (.. this peerAddresses)) __addresses)
        this
    )

    ;;; Will only connect to localhost.  Cannot be called after startup. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn connectToLocalHost) [])
    (§ block
        (§ try )
        (§ block
            (§ let #_"InetAddress" __localHost (InetAddress/getLocalHost))
            (§ block
                (§ return (.. this (setPeerNodes (§ new #_"PeerAddress" params, __localHost, (.. params (getPort))))))
            )
        )
        (§ catch #_"UnknownHostException" __e)
        (§ block
            ;; Borked machine with no loopback adapter configured properly.
            (§ throw (§ new #_"RuntimeException" __e))
        )
    )

    ;;; If true, the wallet will save itself to disk automatically whenever it changes. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setAutoSave) [#_"boolean" __value])
    (§ block
        (Preconditions/checkState (== (state) State/NEW), "Cannot call after startup")
        (§ ass (§ name useAutoSave) __value)
        this
    )

    ;;;
     ; If you want to learn about the sync process, you can provide a listener here.  For instance,
     ; a {@link org.bitcoinj.core.DownloadProgressTracker} is a good choice.  This has no effect unless
     ; setBlockingStartup(false) has been called too, due to some missing implementation code.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setDownloadListener) [#_"DownloadProgressTracker" __listener])
    (§ block
        (§ ass (§ name (.. this downloadListener)) __listener)
        this
    )

    ;;; If true, will register a shutdown hook to stop the library.  Defaults to true. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setAutoStop) [#_"boolean" __autoStop])
    (§ block
        (§ ass (§ name (.. this autoStop)) __autoStop)
        this
    )

    ;;;
     ; If set, the file is expected to contain a checkpoints file calculated with BuildCheckpoints.
     ; It makes initial block sync faster for new users - please refer to the documentation on the
     ; bitcoinj website (https://bitcoinj.github.io/speeding-up-chain-sync) for further details.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setCheckpoints) [#_"InputStream" __checkpoints])
    (§ block
        (§ if (!= (.. this checkpoints) nil))
        (§ block
            (Utils/closeUnchecked (.. this checkpoints))
        )
        (§ ass (§ name (.. this checkpoints)) (Preconditions/checkNotNull __checkpoints))
        this
    )

    ;;;
     ; If true (the default) then the startup of this service won't be considered complete until the network has been
     ; brought up, peer connections established and the block chain synchronised.  Therefore {@link #awaitRunning()} can
     ; potentially take a very long time.  If false, then startup is considered complete once the network activity
     ; begins and peer connections/block chain sync will continue in the background.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setBlockingStartup) [#_"boolean" __blockingStartup])
    (§ block
        (§ ass (§ name (.. this blockingStartup)) __blockingStartup)
        this
    )

    ;;;
     ; Sets the string that will appear in the subver field of the version message.
     ; @param userAgent A short string that should be the name of your app, e.g. "My Wallet".
     ; @param version A short string that contains the version number, e.g. "1.0-BETA".
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setUserAgent) [#_"String" __userAgent, #_"String" __version])
    (§ block
        (§ ass (§ name (.. this userAgent)) (Preconditions/checkNotNull __userAgent))
        (§ ass (§ name (.. this version)) (Preconditions/checkNotNull __version))
        this
    )

    ;;;
     ; Sets a wallet factory which will be used when the kit creates a new wallet.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setWalletFactory) [#_"WalletProtobufSerializer.WalletFactory" __walletFactory])
    (§ block
        (§ ass (§ name (.. this walletFactory)) __walletFactory)
        this
    )

    ;;;
     ; If a seed is set here then any existing wallet that matches the file name will be renamed to a backup name,
     ; the chain file will be deleted, and the wallet object will be instantiated with the given seed instead of
     ; a fresh one being created.  This is intended for restoring a wallet from the original seed.  To implement
     ; restore, you would shut down the existing appkit, if any, then recreate it with the seed given by the user,
     ; then start up the new kit.  The next time your app starts it should work as normal (that is, don't keep
     ; calling this each time).
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn restoreWalletFromSeed) [#_"DeterministicSeed" __seed])
    (§ block
        (§ ass (§ name (.. this restoreFromSeed)) __seed)
        this
    )

    ;;;
     ; Sets the peer discovery class to use.  If none is provided then DNS is used, which is a reasonable default.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setDiscovery) [#_nilable #_"PeerDiscovery" __discovery])
    (§ block
        (§ ass (§ name (.. this discovery)) __discovery)
        this
    )

    ;;;
     ; Override this to use a {@link BlockStore} that isn't the default of {@link SPVBlockStore}.
     ;;
    #_protected
    (§ method #_"BlockStore" (§ fn provideBlockStore) [#_"File" __file])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ new #_"SPVBlockStore" params, __file)
    )

    ;;;
     ; This method is invoked on a background thread after all objects are initialised, but before the peer group
     ; or block chain download is started.  You can tweak the objects configuration here.
     ;;
    #_protected
    (§ method #_"void" (§ fn onSetupCompleted) [])
    (§ block
        nil
    )

    ;;;
     ; Tests to see if the spvchain file has an operating system file lock on it.  Useful for checking if your app
     ; is already running.  If another copy of your app is running and you start the appkit anyway, an exception will
     ; be thrown during the startup process.  Returns false if the chain file does not exist or is a directory.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isChainFileLocked) [])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"RandomAccessFile" __file2 nil)
        (§ block
            (§ try )
            (§ block
                (§ let #_"File" __file (§ new #_"File" directory, (str filePrefix ".spvchain")))
                (§ block
                    (§ if (not (.. __file (exists))))
                    (§ block
                        (§ return false)
                    )
                    (§ if (.. __file (isDirectory)))
                    (§ block
                        (§ return false)
                    )
                    (§ ass __file2 (§ new #_"RandomAccessFile" __file, "rw"))
                    (§ let #_"FileLock" __lock (.. __file2 (getChannel) (tryLock)))
                    (§ block
                        (§ if (nil? __lock))
                        (§ block
                            (§ return true)
                        )
                        (.. __lock (release))
                        (§ return false)
                    )
                )
            )
            (§ finally )
            (§ block
                (§ if (some? __file2))
                (§ block
                    (.. __file2 (close))
                )
            )
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn startUp) [])
        (§ throws #_"Exception")
    (§ block
        ;; Runs in a separate thread.
        (Context/propagate context)
        (§ if (and (not (.. directory (exists))) (not (.. directory (mkdirs)))))
        (§ block
            (§ throw (§ new #_"IOException" (str "Could not create directory " (.. directory (getAbsolutePath)))))
        )

        (.. WalletAppKit/log (info "Starting up with directory = {}", directory))
        (§ try )
        (§ block
            (§ let #_"File" __chainFile (§ new #_"File" directory, (str filePrefix ".spvchain")))
            (§ block
                (§ let #_"boolean" __chainFileExists (.. __chainFile (exists)))
                (§ block
                    (§ ass (§ name vWalletFile) (§ new #_"File" directory, (str filePrefix ".wallet")))
                    (§ let #_"boolean" __shouldReplayWallet (or (and (.. vWalletFile (exists)) (not __chainFileExists)) (some? restoreFromSeed)))
                    (§ block
                        (§ ass (§ name vWallet) (.. this (createOrLoadWallet __shouldReplayWallet)))

                        ;; Initiate Bitcoin network objects (block store, blockchain and peer group).
                        (§ ass (§ name vStore) (.. this (provideBlockStore __chainFile)))
                        (§ if (or (not __chainFileExists) (some? restoreFromSeed)))
                        (§ block
                            (§ if (and (nil? checkpoints) (not (Utils/isAndroidRuntime))))
                            (§ block
                                (§ ass (§ name checkpoints) (CheckpointManager/openStream params))
                            )

                            (§ if (some? checkpoints))
                            (§ block
                                ;; Initialize the chain file with a checkpoint to speed up first-run sync.
                                (§ let #_"long" __time)
                                (§ block
                                    (§ if (some? restoreFromSeed))
                                    (§ block
                                        (§ ass __time (.. restoreFromSeed (getCreationTimeSeconds)))
                                        (§ if __chainFileExists)
                                        (§ block
                                            (.. WalletAppKit/log (info "Deleting the chain file in preparation from restore."))
                                            (.. vStore (close))
                                            (§ if (not (.. __chainFile (delete))))
                                            (§ block
                                                (§ throw (§ new #_"IOException" "Failed to delete chain file in preparation for restore."))
                                            )

                                            (§ ass (§ name vStore) (§ new #_"SPVBlockStore" params, __chainFile))
                                        )
                                    )
                                    (§ else )
                                    (§ block
                                        (§ ass __time (.. vWallet (getEarliestKeyCreationTime)))
                                    )
                                    (§ if (< 0 __time))
                                    (§ block
                                        (CheckpointManager/checkpoint params, checkpoints, vStore, __time)
                                    )
                                    (§ else )
                                    (§ block
                                        (.. WalletAppKit/log (warn "Creating a new uncheckpointed block store due to a wallet with a creation time of zero: this will result in a very slow chain sync"))
                                    )
                                )
                            )
                            (§ elseif __chainFileExists)
                            (§ block
                                (.. WalletAppKit/log (info "Deleting the chain file in preparation from restore."))
                                (.. vStore (close))
                                (§ if (not (.. __chainFile (delete))))
                                (§ block
                                    (§ throw (§ new #_"IOException" "Failed to delete chain file in preparation for restore."))
                                )

                                (§ ass (§ name vStore) (§ new #_"SPVBlockStore" params, __chainFile))
                            )
                        )
                        (§ ass (§ name vChain) (§ new #_"BlockChain" params, vStore))
                        (§ ass (§ name vPeerGroup) (.. this (createPeerGroup)))
                        (§ if (!= (.. this userAgent) nil))
                        (§ block
                            (.. vPeerGroup (setUserAgent userAgent, version))
                        )

                        ;; Set up peer addresses or discovery first, so if wallet extensions try to broadcast a transaction
                        ;; before we're actually connected the broadcast waits for an appropriate number of connections.
                        (§ if (some? peerAddresses))
                        (§ block
                            (§ for (§ var #_"PeerAddress" __addr) :for peerAddresses)
                            (§ block
                                (.. vPeerGroup (addAddress __addr))
                            )
                            (.. vPeerGroup (setMaxConnections (.. peerAddresses length)))
                            (§ ass (§ name peerAddresses) nil)
                        )
                        (§ else )
                        (§ block
                            (.. vPeerGroup (addPeerDiscovery (or discovery (§ new #_"DnsDiscovery" params))))
                        )
                        (.. vChain (addWallet vWallet))
                        (.. vPeerGroup (addWallet vWallet))
                        (.. this (onSetupCompleted))

                        (§ if blockingStartup)
                        (§ block
                            (.. vPeerGroup (start))
                            ;; Make sure we shut down cleanly.
                            (.. this (installShutdownHook))

                            ;; TODO: Be able to use the provided download listener when doing a blocking startup.
                            (§ let #_"DownloadProgressTracker" __listener (§ new #_"DownloadProgressTracker"))
                            (§ block
                                (.. vPeerGroup (startBlockChainDownload __listener))
                                (.. __listener (await))
                            )
                        )
                        (§ else )
                        (§ block
                            (Futures/addCallback (.. vPeerGroup (startAsync)), (§ new #_"FutureCallback")
                            (§ anon
                                #_override
                                #_public
                                (§ method #_"void" (§ fn onSuccess) [#_nilable #_"Object" __result])
                                (§ block
                                    (§ let #_"DownloadProgressTracker" __l (or downloadListener (§ new #_"DownloadProgressTracker")))
                                    (§ block
                                        (.. vPeerGroup (startBlockChainDownload __l))
                                        nil
                                    )
                                )

                                #_override
                                #_public
                                (§ method #_"void" (§ fn onFailure) [#_"Throwable" __t])
                                (§ block
                                    (§ throw (§ new #_"RuntimeException" __t))
                                )
                            ))
                        )
                    )
                )
            )
        )
        (§ catch #_"BlockStoreException" __e)
        (§ block
            (§ throw (§ new #_"IOException" __e))
        )
        nil
    )

    #_private
    (§ method- #_"Wallet" (§ fn createOrLoadWallet) [#_"boolean" __shouldReplayWallet])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"Wallet" __wallet)
        (§ block

            (.. this (maybeMoveOldWalletOutOfTheWay))

            (§ if (.. vWalletFile (exists)))
            (§ block
                (§ ass __wallet (.. this (loadWallet __shouldReplayWallet)))
            )
            (§ else )
            (§ block
                (§ ass __wallet (.. this (createWallet)))
                (.. __wallet (freshReceiveKey))

                ;; Currently the only way we can be sure that an extension is aware of its containing wallet is
                ;; by deserializing the extension.
                ;; Hence, we first save and then load wallet to ensure any extensions are correctly initialized.
                (.. __wallet (saveToFile vWalletFile))
                (§ ass __wallet (.. this (loadWallet false)))
            )

            (§ if useAutoSave)
            (§ block
                (.. this (setupAutoSave __wallet))
            )

            __wallet
        )
    )

    #_protected
    (§ method #_"void" (§ fn setupAutoSave) [#_"Wallet" __wallet])
    (§ block
        (.. __wallet (autosaveToFile vWalletFile, 5, TimeUnit/SECONDS, nil))
        nil
    )

    #_private
    (§ method- #_"Wallet" (§ fn loadWallet) [#_"boolean" __shouldReplayWallet])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"Wallet" __wallet)
        (§ block
            (§ let #_"FileInputStream" __walletStream (§ new #_"FileInputStream" vWalletFile))
            (§ block
                (§ try )
                (§ block
                    (§ let #_"Protos.Wallet" __proto (WalletProtobufSerializer/parseToProto __walletStream))
                    (§ block
                        (§ let #_"WalletProtobufSerializer" __serializer)
                        (§ block
                            (§ if (some? walletFactory))
                            (§ block
                                (§ ass __serializer (§ new #_"WalletProtobufSerializer" walletFactory))
                            )
                            (§ else )
                            (§ block
                                (§ ass __serializer (§ new #_"WalletProtobufSerializer"))
                            )
                            (§ ass __wallet (.. __serializer (readWallet params, __proto)))
                            (§ if __shouldReplayWallet)
                            (§ block
                                (.. __wallet (reset))
                            )
                        )
                    )
                )
                (§ finally )
                (§ block
                    (.. __walletStream (close))
                )
                __wallet
            )
        )
    )

    #_protected
    (§ method #_"Wallet" (§ fn createWallet) [])
    (§ block
        (§ let #_"KeyChainGroup" __kcg)
        (§ block
            (§ if (some? restoreFromSeed))
            (§ block
                (§ ass __kcg (§ new #_"KeyChainGroup" params, restoreFromSeed))
            )
            (§ else )
            (§ block
                (§ ass __kcg (§ new #_"KeyChainGroup" params))
            )
            (if (some? walletFactory) (.. walletFactory (create params, __kcg)) (§ new #_"Wallet" params, __kcg)) ;; default
        )
    )

    #_private
    (§ method- #_"void" (§ fn maybeMoveOldWalletOutOfTheWay) [])
    (§ block
        (§ if (nil? restoreFromSeed))
        (§ block
            (§ return nil)
        )
        (§ if (not (.. vWalletFile (exists))))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" __counter 1)
        (§ block
            (§ let #_"File" __newName)
            (§ block
                (§ do )
                (§ block
                    (§ ass __newName (§ new #_"File" (.. vWalletFile (getParent)), (str "Backup " __counter " for " (.. vWalletFile (getName)))))
                    (§ ass __counter (inc __counter))
                )
                (§ again (.. __newName (exists)))

                (.. WalletAppKit/log (info "Renaming old wallet file {} to {}", vWalletFile, __newName))

                ;; This should not happen unless something is really messed up.
                (§ if (not (.. vWalletFile (renameTo __newName))))
                (§ block
                    (§ throw (§ new #_"RuntimeException" "Failed to rename wallet for restore"))
                )
                nil
            )
        )
    )

    #_protected
    (§ method #_"PeerGroup" (§ fn createPeerGroup) [])
        (§ throws #_"TimeoutException")
    (§ block
        (§ new #_"PeerGroup" params, vChain)
    )

    #_private
    (§ method- #_"void" (§ fn installShutdownHook) [])
    (§ block
        (§ if autoStop)
        (§ block
            (.. (Runtime/getRuntime) (addShutdownHook (§ new #_"Thread")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (§ try )
                    (§ block
                        (.. (§ dhis WalletAppKit) (stopAsync))
                        (.. (§ dhis WalletAppKit) (awaitTerminated))
                    )
                    (§ catch #_"Exception" __e)
                    (§ block
                        (§ throw (§ new #_"RuntimeException" __e))
                    )
                    nil
                )
            )))
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn shutDown) [])
        (§ throws #_"Exception")
    (§ block
        ;; Runs in a separate thread.
        (§ try )
        (§ block
            (Context/propagate context)
            (.. vPeerGroup (stop))
            (.. vWallet (saveToFile vWalletFile))
            (.. vStore (close))

            (§ ass (§ name vPeerGroup) nil)
            (§ ass (§ name vWallet) nil)
            (§ ass (§ name vStore) nil)
            (§ ass (§ name vChain) nil)
        )
        (§ catch #_"BlockStoreException" __e)
        (§ block
            (§ throw (§ new #_"IOException" __e))
        )
        nil
    )

    #_public
    (§ method #_"NetworkParameters" (§ fn params) [])
    (§ block
        params
    )

    #_public
    (§ method #_"BlockChain" (§ fn chain) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vChain
    )

    #_public
    (§ method #_"BlockStore" (§ fn store) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vStore
    )

    #_public
    (§ method #_"Wallet" (§ fn wallet) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vWallet
    )

    #_public
    (§ method #_"PeerGroup" (§ fn peerGroup) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vPeerGroup
    )

    #_public
    (§ method #_"File" (§ fn directory) [])
    (§ block
        directory
    )
)

#_(ns org.bitcoinj.net #_"AbstractTimeoutHandler"
    (:import [java.util Timer TimerTask]))

;;;
 ; <p>A base class which provides basic support for socket timeouts.  It is used instead of integrating timeouts into the
 ; NIO select thread both for simplicity and to keep code shared between NIO and blocking sockets as much as possible.
 ; </p>
 ;;
#_public
#_abstract
(§ class #_"AbstractTimeoutHandler"
    ;; TimerTask and timeout value which are added to a timer to kill the connection on timeout.
    #_private
    (§ field- #_"TimerTask" (§ name timeoutTask))
    #_private
    (§ field- #_"long" (§ name timeoutMillis) 0)
