;;;
 ; <p>A reusable object that will calculate, given a list of {@link org.bitcoinj.core.PeerFilterProvider}s, a merged
 ; {@link org.bitcoinj.core.BloomFilter} and earliest key time for all of them.
 ; Used by the {@link org.bitcoinj.core.PeerGroup} class internally.</p>
 ;
 ; <p>Thread safety: threading here can be complicated.  Each filter provider is given a begin event, which may acquire
 ; a lock (and is guaranteed to receive an end event).  This class is mostly thread unsafe and is meant to be used from
 ; a single thread only, PeerGroup ensures this by only accessing it from the dedicated PeerGroup thread.  PeerGroup
 ; does not hold any locks whilst this object is used, relying on the single thread to prevent multiple filters being
 ; calculated in parallel, thus a filter provider can do things like make blocking calls into PeerGroup from a separate
 ; thread.  However the bloomFilterFPRate property IS thread safe, for convenience.</p>
 ;;
#_public
(§ class #_"FilterMerger"
    ;; We use a constant tweak to avoid giving up privacy when we regenerate our filter with new keys.
    #_private
    (§ field- #_"long" (§ name bloomFilterTweak) (§ cast #_"long" (* (Math/random) Long/MAX_VALUE)))

    #_private
    #_volatile
    (§ field- #_"double" (§ name vBloomFilterFPRate))
    #_private
    (§ field- #_"int" (§ name lastBloomFilterElementCount))
    #_private
    (§ field- #_"BloomFilter" (§ name lastFilter))

    #_public
    (§ constructor #_"FilterMerger" [#_"double" __bloomFilterFPRate])
    (§ block
        (§ ass (§ name (.. this vBloomFilterFPRate)) __bloomFilterFPRate)
        this
    )

    #_public
    #_static
    (§ class #_"Result"
        #_public
        (§ field #_"BloomFilter" (§ name filter))
        #_public
        (§ field #_"long" (§ name earliestKeyTimeSecs))
        #_public
        (§ field #_"boolean" (§ name changed))
    )

    #_public
    (§ method #_"Result" (§ fn calculate) [#_"ImmutableList<PeerFilterProvider>" __providers])
    (§ block
        (§ let #_"LinkedList<PeerFilterProvider>" __begunProviders (Lists/newLinkedList))
        (§ block
            (§ try )
            (§ block
                ;; All providers must be in a consistent, unchanging state because the filter is a merged one that's
                ;; large enough for all providers elements: if a provider were to get more elements in the middle of the
                ;; calculation, we might assert or calculate the filter wrongly.  Most providers use a lock here but
                ;; snapshotting required state is also a legitimate strategy.
                (§ for (§ var #_"PeerFilterProvider" __provider) :for __providers)
                (§ block
                    (.. __provider (beginBloomFilterCalculation))
                    (.. __begunProviders (add __provider))
                )
                (§ let #_"Result" __result (§ new #_"Result"))
                (§ block
                    (§ ass (§ name (.. __result earliestKeyTimeSecs)) Long/MAX_VALUE)
                    (§ let #_"int" __elements 0)
                    (§ block
                        (§ let #_"boolean" __requiresUpdateAll false)
                        (§ block
                            (§ for (§ var #_"PeerFilterProvider" __p) :for __providers)
                            (§ block
                                (§ ass (§ name (.. __result earliestKeyTimeSecs)) (Math/min (.. __result earliestKeyTimeSecs), (.. __p (getEarliestKeyCreationTime))))
                                (§ ass __elements (+ __elements (.. __p (getBloomFilterElementCount))))
                            )

                            (§ if (< 0 __elements))
                            (§ block
                                ;; We stair-step our element count so that we avoid creating a filter with different parameters
                                ;; as much as possible as that results in a loss of privacy.
                                ;; The constant 100 here is somewhat arbitrary, but makes sense for small to medium wallets -
                                ;; it will likely mean we never need to create a filter with different parameters.
                                (§ ass (§ name lastBloomFilterElementCount) (if (< lastBloomFilterElementCount __elements) (+ __elements 100) lastBloomFilterElementCount))
                                (§ let #_"BloomFilter.BloomUpdate" __bloomFlags (if __requiresUpdateAll BloomFilter/BloomUpdate/UPDATE_ALL BloomFilter/BloomUpdate/UPDATE_P2PUBKEY_ONLY))
                                (§ block
                                    (§ let #_"double" __fpRate vBloomFilterFPRate)
                                    (§ block
                                        (§ let #_"BloomFilter" __filter (§ new #_"BloomFilter" lastBloomFilterElementCount, __fpRate, bloomFilterTweak, __bloomFlags))
                                        (§ block
                                            (§ for (§ var #_"PeerFilterProvider" __p) :for __providers)
                                            (§ block
                                                (.. __filter (merge (.. __p (getBloomFilter lastBloomFilterElementCount, __fpRate, bloomFilterTweak))))
                                            )

                                            (§ ass (§ name (.. __result changed)) (not (.. __filter (equals lastFilter))))
                                            (§ ass (§ name (.. __result filter)) (§ ass (§ name lastFilter) __filter))
                                        )
                                    )
                                )
                            )
                            ;; Now adjust the earliest key time backwards by a week to handle the case of clock drift.  This can occur
                            ;; both in block header timestamps and if the users clock was out of sync when the key was first created
                            ;; (to within a small amount of tolerance).
                            (§ ass (§ name (.. __result earliestKeyTimeSecs)) (- earliestKeyTimeSecs (* 86400 7)))
                            (§ return __result)
                        )
                    )
                )
            )
            (§ finally )
            (§ block
                (§ for (§ var #_"PeerFilterProvider" __provider) :for __begunProviders)
                (§ block
                    (.. __provider (endBloomFilterCalculation))
                )
            )
        )
    )

    #_public
    (§ method #_"void" (§ fn setBloomFilterFPRate) [#_"double" __bloomFilterFPRate])
    (§ block
        (§ ass (§ name (.. this vBloomFilterFPRate)) __bloomFilterFPRate)
        nil
    )

    #_public
    (§ method #_"double" (§ fn getBloomFilterFPRate) [])
    (§ block
        vBloomFilterFPRate
    )

    #_public
    (§ method #_"BloomFilter" (§ fn getLastFilter) [])
    (§ block
        lastFilter
    )
)

