                    (§ let #_"SettableFuture<SocketAddress>" __future (SettableFuture/create))
                    (§ block

                        (§ constructor #_"PendingConnect" [#_"SocketChannel" __sc, #_"StreamConnection" __connection, #_"SocketAddress" __address])
