            ;; Create a ConnectionHandler and hook everything together.
            (§ let #_"PendingConnect" __data (§ cast #_"PendingConnect" (.. __key (attachment))))
            (§ block
                (§ let #_"StreamConnection" __connection (.. __data connection))
                (§ block
                    (§ let #_"SocketChannel" __sc (§ cast #_"SocketChannel" (.. __key (channel))))
                    (§ block
                        (§ let #_"ConnectionHandler" __handler (§ new #_"ConnectionHandler" __connection, __key, connectedHandlers))
