        (§ ass (§ name (.. this connectionFactory)) __connectionFactory)

        (§ ass (§ name sc) (ServerSocketChannel/open))
        (.. sc (configureBlocking false))
        (.. sc (socket) (bind __bindAddress))
        (§ ass (§ name selector) (.. (SelectorProvider/provider) (openSelector)))
        (.. sc (register selector, SelectionKey/OP_ACCEPT))
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn run) [])
        (§ throws #_"Exception")
    (§ block
        (§ try )
        (§ block
            (§ while (.. this (isRunning)))
            (§ block
                (.. selector (select))

                (§ let #_"Iterator<SelectionKey>" __keyIterator (.. selector (selectedKeys) (iterator)))
                (§ block
                    (§ while (.. __keyIterator (hasNext)))
                    (§ block
                        (§ let #_"SelectionKey" __key (.. __keyIterator (next)))
                        (§ block
                            (.. __keyIterator (remove))

                            (ConnectionHandler/handleKey selector, __key)
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" __e)
        (§ block
            (.. NioServer/log (error "Error trying to open/read from connection: {}", __e))
        )
        (§ finally )
        (§ block
            ;; Go through and close everything, without letting IOExceptions get in our way.
            (§ for (§ var #_"SelectionKey" __key) :for (.. selector (keys)))
            (§ block
                (§ try )
                (§ block
                    (.. __key (channel) (close))
                )
                (§ catch #_"IOException" __e)
                (§ block
                    (.. NioServer/log (error "Error closing channel", __e))
                )
                (§ try )
                (§ block
                    (.. __key (cancel))
                    (ConnectionHandler/handleKey selector, __key)
                )
                (§ catch #_"IOException" __e)
                (§ block
                    (.. NioServer/log (error "Error closing selection key", __e))
                )
            )
            (§ try )
            (§ block
                (.. selector (close))
            )
            (§ catch #_"IOException" __e)
            (§ block
                (.. NioServer/log (error "Error closing server selector", __e))
            )
            (§ try )
            (§ block
                (.. sc (close))
            )
            (§ catch #_"IOException" __e)
            (§ block
                (.. NioServer/log (error "Error closing server channel", __e))
            )
        )
        nil
    )

    ;;;
     ; Invoked by the Execution service when it's time to stop.
     ; Calling this method directly will NOT stop the service, call
     ; {@link com.google.common.util.concurrent.AbstractExecutionThreadService#stop()} instead.
     ;;
