    #_nilable
    (§ method #_"StreamConnection" (§ fn getNewConnection) [#_"InetAddress" __inetAddress, #_"int" __port])
)

#_(ns org.bitcoinj.net.discovery #_"DnsDiscovery"
    (:import [java.net *]
             [java.util *]
             [java.util.concurrent *])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.utils *]))

;;;
 ; <p>Supports peer discovery through DNS.</p>
 ;
 ; <p>Failure to resolve individual host names will not cause an Exception to be thrown.
 ; However, if all hosts passed fail to resolve a PeerDiscoveryException will be thrown during getPeers().</p>
 ;
 ; <p>DNS seeds do not attempt to enumerate every peer on the network.
 ; {@link DnsDiscovery#getPeers(long, java.util.concurrent.TimeUnit)} will return up to 30 random peers
 ; from the set of those returned within the timeout period.  If you want more peers to connect to,
 ; you need to discover them via other means (like addr broadcasts).</p>
 ;;
#_public
(§ class #_"DnsDiscovery" (§ extends #_"MultiplexingDiscovery")
    ;;;
     ; Supports finding peers through DNS A records.  Community run DNS entry points will be used.
     ;
     ; @param netParams Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"DnsDiscovery" [#_"NetworkParameters" __netParams])
    (§ block
        (§ this (.. __netParams (getDnsSeeds)), __netParams)
        this
    )

    ;;;
     ; Supports finding peers through DNS A records.
     ;
     ; @param dnsSeeds Host names to be examined for seed addresses.
     ; @param params Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"DnsDiscovery" [#_"String[]" __dnsSeeds, #_"NetworkParameters" __params])
    (§ block
        (§ super __params, (DnsDiscovery/buildDiscoveries __params, __dnsSeeds))
        this
    )

    #_private
    #_static
    (§ defn- #_"List<PeerDiscovery>" (§ fn DnsDiscovery/buildDiscoveries) [#_"NetworkParameters" __params, #_"String[]" __seeds])
    (§ block
        (§ let #_"List<PeerDiscovery>" __discoveries (§ new #_"ArrayList<>"))
        (§ block
            (§ if (some? __seeds))
            (§ block
                (§ for (§ var #_"String" __seed) :for __seeds)
                (§ block
                    (.. __discoveries (add (§ new #_"DnsSeedDiscovery" __params, __seed)))
                )
            )
            __discoveries
        )
    )

    #_override
    #_protected
    (§ method #_"ExecutorService" (§ fn createExecutor) [])
    (§ block
        ;; Attempted workaround for reported bugs on Linux in which gethostbyname does not appear to be properly
        ;; thread safe and can cause segfaults on some libc versions.
        (§ if (.. (System/getProperty "os.name") (toLowerCase) (contains "linux")))
        (§ block
            (§ return (Executors/newSingleThreadExecutor (§ new #_"ContextPropagatingThreadFactory" "DNS seed lookups")))
        )
        (§ else )
        (§ block
            (§ return (Executors/newFixedThreadPool (.. seeds (size)), (§ new #_"DaemonThreadFactory" "DNS seed lookups")))
        )
    )

    ;;; Implements discovery from a single DNS host. ;;
    #_public
    #_static
    (§ class #_"DnsSeedDiscovery" (§ implements #_"PeerDiscovery")
        #_private
        (§ field- #_"String" (§ name hostname))
        #_private
        (§ field- #_"NetworkParameters" (§ name params))

        #_public
        (§ constructor #_"DnsSeedDiscovery" [#_"NetworkParameters" __params, #_"String" __hostname])
        (§ block
            (§ ass (§ name (.. this hostname)) __hostname)
            (§ ass (§ name (.. this params)) __params)
            this
        )

        #_override
        #_public
        (§ method #_"InetSocketAddress[]" (§ fn getPeers) [#_"long" __services, #_"long" __timeoutValue, #_"TimeUnit" __timeoutUnit])
            (§ throws #_"PeerDiscoveryException")
        (§ block
            (§ if (!= __services 0))
            (§ block
                (§ throw (§ new #_"PeerDiscoveryException" (str "DNS seeds cannot filter by services: " __services)))
            )

            (§ try )
            (§ block
                (§ let #_"InetAddress[]" __response (InetAddress/getAllByName hostname))
                (§ block
                    (§ let #_"InetSocketAddress[]" __result (§ new #_"InetSocketAddress[]" (§ count (.. __response length))))
                    (§ block
                        (§ for (§ var #_"int" __i 0) :for (< __i (.. __response length)) :for (§ recur (inc __i)))
                        (§ block
                            (§ ass (§ name (§ ai __result __i)) (§ new #_"InetSocketAddress" (§ ai __response __i), (.. params (getPort))))
                        )
                        (§ return __result)
                    )
                )
            )
            (§ catch #_"UnknownHostException" __e)
            (§ block
                (§ throw (§ new #_"PeerDiscoveryException" __e))
            )
        )

        #_override
        #_public
        (§ method #_"void" (§ fn shutdown) [])
        (§ block
            nil
        )

        #_override
        #_public
        (§ method #_"String" (§ fn toString) [])
        (§ block
            hostname
        )
    )
)

#_(ns org.bitcoinj.net.discovery #_"MultiplexingDiscovery"
    (:import [java.net InetSocketAddress]
             [java.util ArrayList Collections List]
             [java.util.concurrent *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core NetworkParameters VersionMessage]
             [org.bitcoinj.net.discovery.DnsDiscovery DnsSeedDiscovery]
             [org.bitcoinj.utils *]))

;;;
 ; MultiplexingDiscovery queries multiple PeerDiscovery objects, shuffles their responses and then returns the results,
 ; thus selecting randomly between them and reducing the influence of any particular seed.  Any that don't respond
 ; within the timeout are ignored.  Backends are queried in parallel.  Backends may block.
 ;;
#_public
(§ class #_"MultiplexingDiscovery" (§ implements #_"PeerDiscovery")
    #_private
    #_static
    (§ def- #_"Logger" (§ name MultiplexingDiscovery/log) (LoggerFactory/getLogger (§ klass #_"MultiplexingDiscovery")))

    #_protected
    (§ field #_"List<PeerDiscovery>" (§ name seeds))
    #_protected
    (§ field #_"NetworkParameters" (§ name netParams))
    #_private
    #_volatile
    (§ field- #_"ExecutorService" (§ name vThreadPool))

    ;;;
     ; Builds a suitable set of peer discoveries.  Will query them in parallel before producing a merged response.
     ; If specific services are required, DNS is not used as the protocol can't handle it.
     ; @param params Network to use.
     ; @param services Required services as a bitmask, e.g. {@link VersionMessage#NODE_NETWORK}.
     ;;
    #_public
    #_static
    (§ defn #_"MultiplexingDiscovery" (§ fn MultiplexingDiscovery/forServices) [#_"NetworkParameters" __params, #_"long" __services])
    (§ block
        (§ let #_"List<PeerDiscovery>" __discoveries (Lists/newArrayList))
        (§ block
            ;; Also use DNS seeds if there is no specific service requirement.
            (§ if (== __services 0))
            (§ block
                (§ let #_"String[]" __dnsSeeds (.. __params (getDnsSeeds)))
                (§ block
                    (§ if (some? __dnsSeeds))
                    (§ block
                        (§ for (§ var #_"String" __dnsSeed) :for __dnsSeeds)
                        (§ block
                            (.. __discoveries (add (§ new #_"DnsSeedDiscovery" __params, __dnsSeed)))
                        )
                    )
                )
            )
            (§ new #_"MultiplexingDiscovery" __params, __discoveries)
        )
    )

    ;;;
     ; Will query the given seeds in parallel before producing a merged response.
     ;;
    #_public
    (§ constructor #_"MultiplexingDiscovery" [#_"NetworkParameters" __params, #_"List<PeerDiscovery>" __seeds])
    (§ block
        (Preconditions/checkArgument (not (.. __seeds (isEmpty))))

        (§ ass (§ name (.. this netParams)) __params)
        (§ ass (§ name (.. this seeds)) __seeds)
        this
    )

    #_override
    #_public
    (§ method #_"InetSocketAddress[]" (§ fn getPeers) [#_"long" __services, #_"long" __timeoutValue, #_"TimeUnit" __timeoutUnit])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        (§ ass (§ name vThreadPool) (.. this (createExecutor)))
        (§ try )
        (§ block
            (§ let #_"List<Callable<InetSocketAddress[]>>" __tasks (Lists/newArrayList))
            (§ block
                (§ for (§ var PeerDiscovery seed) :for seeds)
                (§ block
                    (.. __tasks (add (§ new #_"Callable<InetSocketAddress[]>"
                    (§ anon
                        #_override
                        #_public
                        (§ method #_"InetSocketAddress[]" (§ fn call) [])
                            (§ throws #_"Exception")
                        (§ block
                            (.. seed (getPeers __services, __timeoutValue, __timeoutUnit))
                        )
                    ))))
                )
                (§ let #_"List<Future<InetSocketAddress[]>>" __futures (.. vThreadPool (invokeAll __tasks, __timeoutValue, __timeoutUnit)))
                (§ block
                    (§ let #_"ArrayList<InetSocketAddress>" __addrs (Lists/newArrayList))
                    (§ block
                        (§ for (§ var #_"int" __i 0) :for (< __i (.. __futures (size))) :for (§ recur (inc __i)))
                        (§ block
                            (§ let #_"Future<InetSocketAddress[]>" __future (.. __futures (get __i)))
                            (§ block
                                (§ if (.. __future (isCancelled)))
                                (§ block
                                    (.. MultiplexingDiscovery/log (warn "Seed {}: timed out", (.. seeds (get __i))))
                                    (§ continue ) ;; Timed out.
                                )
                                (§ let #_"InetSocketAddress[]" __inetAddresses)
                                (§ block
                                    (§ try )
                                    (§ block
                                        (§ ass __inetAddresses (.. __future (get)))
                                    )
                                    (§ catch #_"ExecutionException" __e)
                                    (§ block
                                        (.. MultiplexingDiscovery/log (warn "Seed {}: failed to look up: {}", (.. seeds (get __i)), (.. __e (getMessage))))
                                        (§ continue )
                                    )
                                    (Collections/addAll __addrs, __inetAddresses)
                                )
                            )
                        )
                        (§ if (== (.. __addrs (size)) 0))
                        (§ block
                            (§ throw (§ new #_"PeerDiscoveryException" (str "No peer discovery returned any results in " (.. __timeoutUnit (toMillis __timeoutValue)) "ms. Check internet connection?")))
                        )

                        (Collections/shuffle __addrs)
                        (.. vThreadPool (shutdownNow))
                        (§ return (.. __addrs (toArray (§ new #_"InetSocketAddress[]" (§ count (.. __addrs (size)))))))
                    )
                )
            )
        )
        (§ catch #_"InterruptedException" __e)
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" __e))
        )
        (§ finally )
        (§ block
            (.. vThreadPool (shutdown))
        )
    )

    #_protected
    (§ method #_"ExecutorService" (§ fn createExecutor) [])
    (§ block
        (Executors/newFixedThreadPool (.. seeds (size)), (§ new #_"ContextPropagatingThreadFactory" "Multiplexing discovery"))
    )

    #_override
    #_public
    (§ method #_"void" (§ fn shutdown) [])
    (§ block
        (§ let #_"ExecutorService" __tp vThreadPool)
        (§ block
            (§ if (some? __tp))
            (§ block
                (.. __tp (shutdown))
            )
            nil
        )
    )
)

