#_(ns org.bitcoinj.net.discovery #_"SeedPeers"
    (:import [java.net InetAddress InetSocketAddress UnknownHostException]
             [java.util.concurrent TimeUnit])
   (:require [org.bitcoinj.core NetworkParameters]))

;;;
 ; SeedPeers stores a pre-determined list of Bitcoin node addresses.  These nodes are selected based on
 ; being active on the network for a long period of time.  The intention is to be a last resort way of finding
 ; a connection to the network, in case IRC and DNS fail.  The list comes from the Bitcoin C++ source code.
 ;;
#_public
(§ class #_"SeedPeers" (§ implements #_"PeerDiscovery")
    #_private
    (§ field- #_"NetworkParameters" (§ name params))
    #_private
    (§ field- #_"int[]" (§ name seedAddrs))
    #_private
    (§ field- #_"int" (§ name pnseedIndex))

    ;;;
     ; Supports finding peers by IP addresses.
     ;
     ; @param params Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"SeedPeers" [#_"NetworkParameters" __params])
    (§ block
        (§ this (.. __params (getAddrSeeds)), __params)
        this
    )

    ;;;
     ; Supports finding peers by IP addresses.
     ;
     ; @param seedAddrs IP addresses for seed addresses.
     ; @param params Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"SeedPeers" [#_"int[]" __seedAddrs, #_"NetworkParameters" __params])
    (§ block
        (§ ass (§ name (.. this seedAddrs)) __seedAddrs)
        (§ ass (§ name (.. this params)) __params)
        this
    )

    ;;;
     ; Acts as an iterator, returning the address of each node in the list sequentially.
     ; Once all the list has been iterated, null will be returned for each subsequent query.
     ;
     ; @return InetSocketAddress - the address/port of the next node.
     ; @throws PeerDiscoveryException
     ;;
    #_nilable
    #_public
    (§ method #_"InetSocketAddress" (§ fn getPeer) [])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        (§ try )
        (§ block
            (§ return (.. this (nextPeer)))
        )
        (§ catch #_"UnknownHostException" __e)
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" __e))
        )
    )

    #_nilable
    #_private
    (§ method- #_"InetSocketAddress" (§ fn nextPeer) [])
        (§ throws #_"UnknownHostException", #_"PeerDiscoveryException")
    (§ block
        (§ if (or (nil? seedAddrs) (== (.. seedAddrs length) 0)))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" "No IP address seeds configured; unable to find any peers"))
        )

        (§ if (<= (.. seedAddrs length) pnseedIndex))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" __i pnseedIndex)
        (§ block
            (§ ass (§ name pnseedIndex) (inc pnseedIndex))
            (§ new #_"InetSocketAddress" (.. this (convertAddress (§ ai seedAddrs __i))), (.. params (getPort)))
        )
    )

    ;;;
     ; Returns an array containing all the Bitcoin nodes within the list.
     ;;
    #_override
    #_public
    (§ method #_"InetSocketAddress[]" (§ fn getPeers) [#_"long" __services, #_"long" __timeoutValue, #_"TimeUnit" __timeoutUnit])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        (§ if (!= __services 0))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" (str "Pre-determined peers cannot be filtered by services: " __services)))
        )

        (§ try )
        (§ block
            (§ return (.. this (allPeers)))
        )
        (§ catch #_"UnknownHostException" __e)
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" __e))
        )
    )

    #_private
    (§ method- #_"InetSocketAddress[]" (§ fn allPeers) [])
        (§ throws #_"UnknownHostException")
    (§ block
        (§ let #_"InetSocketAddress[]" __addresses (§ new #_"InetSocketAddress[]" (§ count (.. seedAddrs length))))
        (§ block
            (§ for (§ var #_"int" __i 0) :for (< __i (.. seedAddrs length)) :for (§ recur (inc __i)))
            (§ block
                (§ ass (§ name (§ ai __addresses __i)) (§ new #_"InetSocketAddress" (.. this (convertAddress (§ ai seedAddrs __i))), (.. params (getPort))))
            )
            __addresses
        )
    )

    #_private
    (§ method- #_"InetAddress" (§ fn convertAddress) [#_"int" __seed])
        (§ throws #_"UnknownHostException")
    (§ block
        (§ let #_"byte[]" __v4addr (§ new #_"byte[]" (§ count 4)))
        (§ block
            (§ ass (§ name (§ ai __v4addr 0)) (§ cast #_"byte" (& 0xff __seed)))
            (§ ass (§ name (§ ai __v4addr 1)) (§ cast #_"byte" (& 0xff (>> __seed 8))))
            (§ ass (§ name (§ ai __v4addr 2)) (§ cast #_"byte" (& 0xff (>> __seed 16))))
            (§ ass (§ name (§ ai __v4addr 3)) (§ cast #_"byte" (& 0xff (>> __seed 24))))
            (InetAddress/getByAddress __v4addr)
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn shutdown) [])
    (§ block
        nil
    )
)

#_(ns org.bitcoinj.params #_"AbstractBitcoinNetParams"
    (:import [java.math BigInteger]
             [java.util.concurrent TimeUnit])
    (:import [com.google.common.base Preconditions Stopwatch]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core BitcoinSerializer Block Coin NetworkParameters Sha256Hash StoredBlock Transaction Utils VerificationException]
             [org.bitcoinj.utils MonetaryFormat]
             [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Parameters for Bitcoin-like networks.
 ;;
#_public
#_abstract
(§ class #_"AbstractBitcoinNetParams" (§ extends #_"NetworkParameters")
    ;;;
     ; Scheme part for Bitcoin URIs.
     ;;
    #_public
    #_static
    (§ def #_"String" (§ name AbstractBitcoinNetParams/BITCOIN_SCHEME) "bitcoin")
    #_public
    #_static
    (§ def #_"int" (§ name AbstractBitcoinNetParams/REWARD_HALVING_INTERVAL) 210000)

    #_private
    #_static
    (§ def- #_"Logger" (§ name AbstractBitcoinNetParams/log) (LoggerFactory/getLogger (§ klass #_"AbstractBitcoinNetParams")))

    #_public
    (§ constructor #_"AbstractBitcoinNetParams" [])
    (§ block
        (§ super )
        this
    )

    ;;;
     ; Checks if we are at a reward halving point.
     ; @param height The height of the previous stored block.
     ; @return if this is a reward halving point.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isRewardHalvingPoint) [#_"int" __height])
    (§ block
        (== (% (inc __height) AbstractBitcoinNetParams/REWARD_HALVING_INTERVAL) 0)
    )

    ;;;
     ; Checks if we are at a difficulty transition point.
     ; @param height The height of the previous stored block.
     ; @return if this is a difficulty transition point.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isDifficultyTransitionPoint) [#_"int" __height])
    (§ block
        (== (% (inc __height) (.. this (getInterval))) 0)
    )

    #_override
    #_public
    (§ method #_"void" (§ fn checkDifficultyTransitions) [#_"StoredBlock" __storedPrev, #_"Block" __nextBlock, #_"BlockStore" __blockStore])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (§ let #_"Block" __prev (.. __storedPrev (getHeader)))
        (§ block

            ;; Is this supposed to be a difficulty transition point?
            (§ if (not (.. this (isDifficultyTransitionPoint (.. __storedPrev (getHeight))))))
            (§ block
                ;; No ... so check the difficulty didn't actually change.
                (§ if (!= (.. __nextBlock (getDifficultyTarget)) (.. __prev (getDifficultyTarget))))
                (§ block
                    (§ throw (§ new #_"VerificationException" (str "Unexpected change in difficulty at height " (.. __storedPrev (getHeight)) ": " (Long/toHexString (.. __nextBlock (getDifficultyTarget))) " vs " (Long/toHexString (.. __prev (getDifficultyTarget))))))
                )
                (§ return nil)
            )

            ;; We need to find a block far back in the chain.  It's OK that this is expensive because it only occurs every
            ;; two weeks after the initial block chain download.
            (§ let #_"Stopwatch" __watch (Stopwatch/createStarted))
            (§ block

                (§ let #_"Sha256Hash" __hash (.. __prev (getHash)))
                (§ block
                    (§ let #_"StoredBlock" __cursor nil)
                    (§ block
                        (§ let #_"int" __interval (.. this (getInterval)))
                        (§ block
                            (§ for (§ var #_"int" __i 0) :for (< __i __interval) :for (§ recur (inc __i)))
                            (§ block
                                (§ ass __cursor (.. __blockStore (get __hash)))
                                (§ if (nil? __cursor))
                                (§ block
                                    ;; This should never happen.  If it does, it means we are following an incorrect or busted chain.
                                    (§ throw (§ new #_"VerificationException" (str "Difficulty transition point but we did not find a way back to the last transition point. Not found: " __hash)))
                                )
                                (§ ass __hash (.. __cursor (getHeader) (getPrevBlockHash)))
                            )
                            (Preconditions/checkState (and (some? __cursor) (.. this (isDifficultyTransitionPoint (- (.. __cursor (getHeight)) 1)))), "Didn't arrive at a transition point.")

                            (.. __watch (stop))
                            (§ if (< 50 (.. __watch (elapsed TimeUnit/MILLISECONDS))))
                            (§ block
                                (.. AbstractBitcoinNetParams/log (info "Difficulty transition traversal took {}", __watch))
                            )

                            (§ let #_"Block" __blockIntervalAgo (.. __cursor (getHeader)))
                            (§ block
                                (§ let #_"int" __timespan (§ cast #_"int" (- (.. __prev (getTimeSeconds)) (.. __blockIntervalAgo (getTimeSeconds)))))
                                (§ block
                                    ;; Limit the adjustment step.
                                    (§ let #_"int" __targetTimespan (.. this (getTargetTimespan)))
                                    (§ block
                                        (§ if (< __timespan (/ __targetTimespan 4)))
                                        (§ block
                                            (§ ass __timespan (/ __targetTimespan 4))
                                        )
                                        (§ if (> __timespan (* __targetTimespan 4)))
                                        (§ block
                                            (§ ass __timespan (* __targetTimespan 4))
                                        )

                                        (§ let #_"BigInteger" __newTarget (Utils/decodeCompactBits (.. __prev (getDifficultyTarget))))
                                        (§ block
                                            (§ ass __newTarget (.. __newTarget (multiply (BigInteger/valueOf __timespan))))
                                            (§ ass __newTarget (.. __newTarget (divide (BigInteger/valueOf __targetTimespan))))

                                            (§ if (< 0 (.. __newTarget (compareTo (.. this (getMaxTarget))))))
                                            (§ block
                                                (.. AbstractBitcoinNetParams/log (info "Difficulty hit proof of work limit: {}", (.. __newTarget (toString 16))))
                                                (§ ass __newTarget (.. this (getMaxTarget)))
                                            )

                                            (§ let #_"int" __accuracyBytes (- (§ cast #_"int" (>>> (.. __nextBlock (getDifficultyTarget)) 24)) 3))
                                            (§ block
                                                (§ let #_"long" __receivedTargetCompact (.. __nextBlock (getDifficultyTarget)))
                                                (§ block

                                                    ;; The calculated difficulty is to a higher precision than received, so reduce here.
                                                    (§ let #_"BigInteger" __mask (.. (BigInteger/valueOf 0xffffff) (shiftLeft (* __accuracyBytes 8))))
                                                    (§ block
                                                        (§ ass __newTarget (.. __newTarget (and __mask)))
                                                        (§ let #_"long" __newTargetCompact (Utils/encodeCompactBits __newTarget))
                                                        (§ block

                                                            (§ if (!= __newTargetCompact __receivedTargetCompact))
                                                            (§ block
                                                                (§ throw (§ new #_"VerificationException" (str "Network provided difficulty bits do not match what was calculated: " (Long/toHexString __newTargetCompact) " vs " (Long/toHexString __receivedTargetCompact))))
                                                            )
                                                            nil
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"Coin" (§ fn getMaxMoney) [])
    (§ block
        NetworkParameters/MAX_MONEY
    )

    #_override
    #_public
    (§ method #_"Coin" (§ fn getMinNonDustOutput) [])
    (§ block
        Transaction/MIN_NONDUST_OUTPUT
    )

    #_override
    #_public
    (§ method #_"MonetaryFormat" (§ fn getMonetaryFormat) [])
    (§ block
        (§ new #_"MonetaryFormat")
    )

    #_override
    #_public
    (§ method #_"int" (§ fn getProtocolVersionNum) [#_"ProtocolVersion" __version])
    (§ block
        (.. __version (getBitcoinProtocolVersion))
    )

    #_override
    #_public
    (§ method #_"BitcoinSerializer" (§ fn getSerializer) [#_"boolean" __parseRetain])
    (§ block
        (§ new #_"BitcoinSerializer" this, __parseRetain)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn getUriScheme) [])
    (§ block
        AbstractBitcoinNetParams/BITCOIN_SCHEME
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn hasMaxMoney) [])
    (§ block
        true
    )
)

