#_(ns org.bitcoinj.params #_"Networks"
    (:import [java.util Collection Set])
    (:import [com.google.common.collect ImmutableSet Lists])
   (:require [org.bitcoinj.core NetworkParameters]))

;;;
 ; Utility class that holds all the registered NetworkParameters types used for Address auto discovery.
 ; By default only MainNetParams and TestNet3Params are used.  If you want to use UnitTestParams, use
 ; the register and unregister the TestNet3Params as they don't have their own address version/type code.
 ;;
#_public
(§ class #_"Networks"
    ;;; Registered networks. ;;
    #_private
    #_static
    (§ def- #_"Set<? extends NetworkParameters>" (§ name Networks/NETWORKS) (ImmutableSet/of (TestNet3Params/get), (MainNetParams/get)))

    #_public
    #_static
    (§ defn #_"Set<? extends NetworkParameters>" (§ fn Networks/get) [])
    (§ block
        Networks/NETWORKS
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn Networks/register) [#_"NetworkParameters" __network])
    (§ block
        (Networks/register (Lists/newArrayList __network))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn Networks/register) [#_"Collection<? extends NetworkParameters>" __networks])
    (§ block
        (§ let #_"ImmutableSet.Builder<NetworkParameters>" __builder (ImmutableSet/builder))
        (§ block
            (.. __builder (addAll Networks/NETWORKS))
            (.. __builder (addAll __networks))
            (§ ass (§ name Networks/NETWORKS) (.. __builder (build)))
            nil
        )
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn Networks/unregister) [#_"NetworkParameters" __network])
    (§ block
        (§ if (.. Networks/NETWORKS (contains __network)))
        (§ block
            (§ let #_"ImmutableSet.Builder<NetworkParameters>" __builder (ImmutableSet/builder))
            (§ block

                (§ for (§ var #_"NetworkParameters" __parameters) :for Networks/NETWORKS)
                (§ block
                    (§ if (not (.. __parameters (equals __network))))
                    (§ block
                        (.. __builder (add __parameters))
                    )
                )

                (§ ass (§ name Networks/NETWORKS) (.. __builder (build)))
            )
        )
        nil
    )
)

#_(ns org.bitcoinj.params #_"TestNet3Params"
    (:import [java.math BigInteger]
             [java.util Date])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Block NetworkParameters StoredBlock Utils VerificationException]
             [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Parameters for the testnet, a separate public instance of Bitcoin that has relaxed rules suitable for development
 ; and testing of applications and new Bitcoin versions.
 ;;
#_public
(§ class #_"TestNet3Params" (§ extends #_"AbstractBitcoinNetParams")
    #_public
    #_static
    (§ def #_"int" (§ name TestNet3Params/TESTNET_MAJORITY_WINDOW) 100)
    #_public
    #_static
    (§ def #_"int" (§ name TestNet3Params/TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED) 75)
    #_public
    #_static
    (§ def #_"int" (§ name TestNet3Params/TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE) 51)

    #_public
    (§ constructor #_"TestNet3Params" [])
    (§ block
        (§ super )

        (§ ass (§ name id) NetworkParameters/ID_TESTNET)
        (§ ass (§ name packetMagic) 0x0b110907)
        (§ ass (§ name interval) NetworkParameters/INTERVAL)
        (§ ass (§ name targetTimespan) NetworkParameters/TARGET_TIMESPAN)
        (§ ass (§ name maxTarget) (Utils/decodeCompactBits 0x1d00ffff))
        (§ ass (§ name port) 18333)
        (§ ass (§ name addressHeader) 111)
        (§ ass (§ name p2shHeader) 196)
        (§ ass (§ name acceptableAddressCodes) (§ new #_"int[]" (§ coll addressHeader, p2shHeader )))
        (.. genesisBlock (setTime 1296688602))
        (.. genesisBlock (setDifficultyTarget 0x1d00ffff))
        (.. genesisBlock (setNonce 414098458))
        (§ ass (§ name spendableCoinbaseDepth) 100)
        (§ ass (§ name subsidyDecreaseBlockCount) 210000)
        (§ let #_"String" __genesisHash (.. genesisBlock (getHashAsString)))
        (§ block
            (Preconditions/checkState (.. __genesisHash (equals "000000000933ea01ad0ee984209779baaec3ced90fa3f408719526f8d77f4943")))
            (§ ass (§ name alertSigningKey) (.. Utils/HEX (decode "04302390343f91cc401d56d68b123028bf52e5fca1939df127f63c6467cdf9c8e2c14b61104cf817d0b780da337893ecc4aaff1309e536162dabbdb45200ca2b0a")))

            (§ ass (§ name dnsSeeds) (§ new #_"String[]"
            (§ coll
                "testnet-seed.bitcoin.jonasschnelli.ch", ;; Jonas Schnelli
                "testnet-seed.bluematt.me",              ;; Matt Corallo
                "testnet-seed.bitcoin.petertodd.org",    ;; Peter Todd
                "testnet-seed.bitcoin.schildbach.de",    ;; Andreas Schildbach
            )))
            (§ ass (§ name addrSeeds) nil)
            (§ ass (§ name bip32HeaderPub) 0x043587cf)
            (§ ass (§ name bip32HeaderPriv) 0x04358394)

            (§ ass (§ name majorityEnforceBlockUpgrade) TestNet3Params/TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE)
            (§ ass (§ name majorityRejectBlockOutdated) TestNet3Params/TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED)
            (§ ass (§ name majorityWindow) TestNet3Params/TESTNET_MAJORITY_WINDOW)
            this
        )
    )

    #_private
    #_static
    (§ def- #_"TestNet3Params" (§ name TestNet3Params/INSTANCE))

    #_public
    #_static
    #_synchronized
    (§ defn #_"TestNet3Params" (§ fn TestNet3Params/get) [])
    (§ block
        (§ if (nil? TestNet3Params/INSTANCE))
        (§ block
            (§ ass (§ name TestNet3Params/INSTANCE) (§ new #_"TestNet3Params"))
        )
        TestNet3Params/INSTANCE
    )

    #_override
    #_public
    (§ method #_"String" (§ fn getPaymentProtocolId) [])
    (§ block
        NetworkParameters/PAYMENT_PROTOCOL_ID_TESTNET
    )

    ;; February 16th 2012
    #_private
    #_static
    (§ def- #_"Date" (§ name TestNet3Params/TESTNET_DIFF_DATE) (§ new #_"Date" 1329264000000))

    #_override
    #_public
    (§ method #_"void" (§ fn checkDifficultyTransitions) [#_"StoredBlock" __storedPrev, #_"Block" __nextBlock, #_"BlockStore" __blockStore])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (§ if (and (not (.. this (isDifficultyTransitionPoint (.. __storedPrev (getHeight))))) (.. __nextBlock (getTime) (after TestNet3Params/TESTNET_DIFF_DATE))))
        (§ block
            (§ let #_"Block" __prev (.. __storedPrev (getHeader)))
            (§ block

                ;; After 15th February 2012 the rules on the testnet change to avoid people running up the difficulty
                ;; and then leaving, making it too hard to mine a block.  On non-difficulty transition points, easy
                ;; blocks are allowed if there has been a span of 20 minutes without one.
                (§ let #_"long" __timeDelta (- (.. __nextBlock (getTimeSeconds)) (.. __prev (getTimeSeconds))))
                (§ block
                    ;; There is an integer underflow bug in bitcoin-qt that means mindiff blocks are accepted when time
                    ;; goes backwards.
                    (§ if (and (<= 0 __timeDelta) (<= __timeDelta (* 2 NetworkParameters/TARGET_SPACING))))
                    (§ block
                        ;; Walk backwards until we find a block that doesn't have the easiest proof of work, then check
                        ;; that difficulty is equal to that one.
                        (§ let #_"StoredBlock" __cursor __storedPrev)
                        (§ block
                            (§ while (and (not (.. __cursor (getHeader) (equals (.. this (getGenesisBlock))))) (!= (% (.. __cursor (getHeight)) (.. this (getInterval))) 0) (.. __cursor (getHeader) (getDifficultyTargetAsInteger) (equals (.. this (getMaxTarget))))))
                            (§ block
                                (§ ass __cursor (.. __cursor (getPrev __blockStore)))
                            )
                            (§ let #_"BigInteger" __cursorTarget (.. __cursor (getHeader) (getDifficultyTargetAsInteger)))
                            (§ block
                                (§ let #_"BigInteger" __newTarget (.. __nextBlock (getDifficultyTargetAsInteger)))
                                (§ block
                                    (§ if (not (.. __cursorTarget (equals __newTarget))))
                                    (§ block
                                        (§ throw (§ new #_"VerificationException" (str "Testnet block transition that is not allowed: " (Long/toHexString (.. __cursor (getHeader) (getDifficultyTarget))) " vs " (Long/toHexString (.. __nextBlock (getDifficultyTarget))))))
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ else )
        (§ block
            (.. super (checkDifficultyTransitions __storedPrev, __nextBlock, __blockStore))
        )
        nil
    )
)

