#_(ns org.bitcoinj.script #_"Script"
    (:import [java.io ByteArrayInputStream ByteArrayOutputStream IOException OutputStream]
             [java.math BigInteger]
             [java.security MessageDigest NoSuchAlgorithmException]
             [java.util *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto.digests RIPEMD160Digest])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.crypto TransactionSignature]
             [org.bitcoinj.script ScriptOpCodes]))

;; TODO: Redesign this entire API to be more type safe and organised.

;;;
 ; <p>Programs embedded inside transactions that control redemption of payments.</p>
 ;
 ; <p>Bitcoin transactions don't specify what they do directly.  Instead
 ; <a href="https://en.bitcoin.it/wiki/Script">a small binary stack language</a> is used to define programs that
 ; when evaluated, return whether the transaction "accepts" or rejects the other transactions connected to it.</p>
 ;
 ; <p>In SPV mode, scripts are not run, because that would require all transactions to be available and lightweight
 ; clients don't have that data.  In full mode, this class is used to run the interpreted language.  It also has
 ; static methods for building scripts.</p>
 ;;
#_public
(§ class #_"Script"
    ;;; Enumeration to encapsulate the type of this script. ;;
    #_public
    (§ enum #_"ScriptType"
        ;; Do NOT change the ordering of the following definitions because their ordinals are stored in databases.
        (§ item NO_TYPE)
        (§ item P2PKH)
        (§ item PUB_KEY)
        (§ item P2SH)
    )

    ;;; Flags to pass to {@link Script#correctlySpends(Transaction, long, Script, Set)}.
     ; Note currently only P2SH, DERSIG and NULLDUMMY are actually supported.
     ;;
    #_public
    (§ enum #_"VerifyFlag"
        (§ item P2SH) ;; Enable BIP16-style subscript evaluation.
        (§ item STRICTENC) ;; Passing a non-strict-DER signature or one with undefined hashtype to a checksig operation causes script failure.
        (§ item DERSIG) ;; Passing a non-strict-DER signature to a checksig operation causes script failure (softfork safe, BIP66 rule 1).
        (§ item LOW_S) ;; Passing a non-strict-DER signature or one with S > order/2 to a checksig operation causes script failure.
        (§ item NULLDUMMY) ;; Verify dummy stack item consumed by CHECKMULTISIG is of zero-length.
        (§ item SIGPUSHONLY) ;; Using a non-push operator in the scriptSig causes script failure (softfork safe, BIP62 rule 2).
        (§ item MINIMALDATA) ;; Require minimal encodings for all push operations.
        (§ item DISCOURAGE_UPGRADABLE_NOPS) ;; Discourage use of NOPs reserved for upgrades (NOP1-10).
        (§ item CLEANSTACK) ;; Require that only a single stack element remains after evaluation.
        (§ item CHECKLOCKTIMEVERIFY) ;; Enable CHECKLOCKTIMEVERIFY operation.
        (§ item CHECKSEQUENCEVERIFY) ;; Enable CHECKSEQUENCEVERIFY operation.
    )
    #_public
    #_static
    (§ def #_"EnumSet<VerifyFlag>" (§ name Script/ALL_VERIFY_FLAGS) (EnumSet/allOf (§ klass #_"VerifyFlag")))

    #_private
    #_static
    (§ def- #_"Logger" (§ name Script/log) (LoggerFactory/getLogger (§ klass #_"Script")))

    #_public
    #_static
    (§ def #_"long" (§ name Script/MAX_SCRIPT_ELEMENT_SIZE) 520) ;; bytes
    #_private
    #_static
    (§ def- #_"int" (§ name Script/MAX_OPS_PER_SCRIPT) 201)
    #_private
    #_static
    (§ def- #_"int" (§ name Script/MAX_STACK_SIZE) 1000)
    #_private
    #_static
    (§ def- #_"int" (§ name Script/MAX_PUBKEYS_PER_MULTISIG) 20)
    #_private
    #_static
    (§ def- #_"int" (§ name Script/MAX_SCRIPT_SIZE) 10000)
    #_public
    #_static
    (§ def #_"int" (§ name Script/SIG_SIZE) 75)
    ;;; Max number of sigops allowed in a standard p2sh redeem script. ;;
    #_public
    #_static
    (§ def #_"int" (§ name Script/MAX_P2SH_SIGOPS) 15)

    ;; The program is a set of chunks where each element is either [opcode] or [data, data, data ...].
    #_protected
    (§ field #_"List<ScriptChunk>" (§ name chunks))
    ;; Unfortunately, scripts are not ever re-serialized or canonicalized when used in signature hashing.
    ;; Thus we must preserve the exact bytes that we read off the wire, along with the parsed form.
    #_protected
    (§ field #_"byte[]" (§ name program))

    ;; Creation time of the associated keys in seconds since the epoch.
    #_private
    (§ field- #_"long" (§ name creationTimeSeconds))

    ;;; Creates an empty script that serializes to nothing. ;;
    #_private
    (§ constructor- #_"Script" [])
    (§ block
        (§ ass (§ name chunks) (Lists/newArrayList))
        this
    )

    ;; Used from ScriptBuilder.
    (§ constructor #_"Script" [#_"List<ScriptChunk>" __chunks])
    (§ block
        (§ ass (§ name (.. this chunks)) (Collections/unmodifiableList (§ new #_"ArrayList<>" __chunks)))
        (§ ass (§ name creationTimeSeconds) (Utils/currentTimeSeconds))
        this
    )

    ;;;
     ; Construct a Script that copies and wraps the programBytes array.
     ; The array is parsed and checked for syntactic validity.
     ; @param programBytes Array of program bytes from a transaction.
     ;;
    #_public
    (§ constructor #_"Script" [#_"byte[]" __programBytes])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name program) __programBytes)
        (.. this (parse __programBytes))
        (§ ass (§ name creationTimeSeconds) 0)
        this
    )

    #_public
    (§ constructor #_"Script" [#_"byte[]" __programBytes, #_"long" __creationTimeSeconds])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name program) __programBytes)
        (.. this (parse __programBytes))
        (§ ass (§ name (.. this creationTimeSeconds)) __creationTimeSeconds)
        this
    )

    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        creationTimeSeconds
    )

    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" __creationTimeSeconds])
    (§ block
        (§ ass (§ name (.. this creationTimeSeconds)) __creationTimeSeconds)
        nil
    )

    ;;;
     ; Returns the program opcodes as a string, for example "[1234] DUP HASH160".
     ;;
    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (.. Utils/SPACE_JOINER (join chunks))
    )

    ;;; Returns the serialized program as a newly created byte array. ;;
    #_public
    (§ method #_"byte[]" (§ fn getProgram) [])
    (§ block
        (§ try )
        (§ block
            ;; Don't round-trip as Bitcoin Core doesn't and it would introduce a mismatch.
            (§ if (some? program))
            (§ block
                (§ return (Arrays/copyOf program, (.. program length)))
            )

            (§ let #_"ByteArrayOutputStream" __bos (§ new #_"ByteArrayOutputStream"))
            (§ block
                (§ for (§ var #_"ScriptChunk" __chunk) :for chunks)
                (§ block
                    (.. __chunk (write __bos))
                )
                (§ ass (§ name program) (.. __bos (toByteArray)))
                (§ return program)
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
    )

    ;;; Returns an immutable list of the scripts parsed form.  Each chunk is either an opcode or data element. ;;
    #_public
    (§ method #_"List<ScriptChunk>" (§ fn getChunks) [])
    (§ block
        (Collections/unmodifiableList chunks)
    )

    #_private
    #_static
    (§ def- #_"ScriptChunk[]" (§ name Script/STANDARD_TRANSACTION_SCRIPT_CHUNKS)
    (§ coll
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_DUP, nil, 0)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_HASH160, nil, 1)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_EQUALVERIFY, nil, 23)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_CHECKSIG, nil, 24)
    ))

    ;;;
     ; <p>To run a script, first we parse it which breaks it up into chunks representing pushes of data
     ; or logical opcodes.  Then we can run the parsed chunks.</p>
     ;
     ; <p>The reason for this split, instead of just interpreting directly, is to make it easier
     ; to reach into a programs structure and pull out bits of data without having to run it.
     ; This is necessary to render the to/from addresses of transactions in a user interface.
     ; Bitcoin Core does something similar.</p>
     ;;
    #_private
    (§ method- #_"void" (§ fn parse) [#_"byte[]" __program])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name chunks) (§ new #_"ArrayList<>" 5)) ;; Common size.
        (§ let #_"ByteArrayInputStream" __bis (§ new #_"ByteArrayInputStream" __program))
        (§ block
            (§ let #_"int" __initialSize (.. __bis (available)))
            (§ block
                (§ while (< 0 (.. __bis (available))))
                (§ block
                    (§ let #_"int" __startLocationInProgram (- __initialSize (.. __bis (available))))
                    (§ block
                        (§ let #_"int" __opcode (.. __bis (read)))
                        (§ block

                            (§ let #_"long" __dataToRead -1)
                            (§ block
                                (§ if (< -1 __opcode ScriptOpCodes/OP_PUSHDATA1))
                                (§ block
                                    ;; Read some bytes of data, where how many is the opcode value itself.
                                    (§ ass __dataToRead __opcode)
                                )
                                (§ elseif (== __opcode ScriptOpCodes/OP_PUSHDATA1))
                                (§ block
                                    (§ if (< (.. __bis (available)) 1))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass __dataToRead (.. __bis (read)))
                                )
                                (§ elseif (== __opcode ScriptOpCodes/OP_PUSHDATA2))
                                (§ block
                                    ;; Read a short, then read that many bytes of data.
                                    (§ if (< (.. __bis (available)) 2))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass __dataToRead (| (.. __bis (read)) (<< (.. __bis (read)) 8)))
                                )
                                (§ elseif (== __opcode ScriptOpCodes/OP_PUSHDATA4))
                                (§ block
                                    ;; Read a uint32, then read that many bytes of data.
                                    ;; Though this is allowed, because its value cannot be > 520, it should never actually be used.
                                    (§ if (< (.. __bis (available)) 4))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass __dataToRead (| (§ cast #_"long" (.. __bis (read))) (<< (§ cast #_"long" (.. __bis (read))) 8) (<< (§ cast #_"long" (.. __bis (read))) 16) (<< (§ cast #_"long" (.. __bis (read))) 24)))
                                )

                                (§ let #_"ScriptChunk" __chunk)
                                (§ block
                                    (§ if (== __dataToRead -1))
                                    (§ block
                                        (§ ass __chunk (§ new #_"ScriptChunk" __opcode, nil, __startLocationInProgram))
                                    )
                                    (§ else )
                                    (§ block
                                        (§ if (< (.. __bis (available)) __dataToRead))
                                        (§ block
                                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_BAD_OPCODE, "Push of data element that is larger than remaining data"))
                                        )

                                        (§ let #_"byte[]" __data (§ new #_"byte[]" (§ count (§ cast #_"int" __dataToRead))))
                                        (§ block
                                            (Preconditions/checkState (or (== __dataToRead 0) (== (.. __bis (read __data, 0, (§ cast #_"int" __dataToRead))) __dataToRead)))
                                            (§ ass __chunk (§ new #_"ScriptChunk" __opcode, __data, __startLocationInProgram))
                                        )
                                    )
                                    ;; Save some memory by eliminating redundant copies of the same chunk objects.
                                    (§ for (§ var #_"ScriptChunk" __c) :for Script/STANDARD_TRANSACTION_SCRIPT_CHUNKS)
                                    (§ block
                                        (§ if (.. __c (equals __chunk)))
                                        (§ block
                                            (§ ass __chunk __c)
                                        )
                                    )
                                    (.. chunks (add __chunk))
                                )
                            )
                        )
                    )
                )
                nil
            )
        )
    )

    ;;;
     ; Returns true if this script is of the form <pubkey> OP_CHECKSIG.  This form was originally intended for
     ; transactions where the peers talked to each other directly via TCP/IP, but has fallen out of favor with time
     ; due to that mode of operation being susceptible to man-in-the-middle attacks.  It is still used in coinbase
     ; outputs and can be useful more exotic types of transaction, but today most payments are to addresses.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isSentToRawPubKey) [])
    (§ block
        (and (== (.. chunks (size)) 2) (.. chunks (get 1) (equalsOpCode ScriptOpCodes/OP_CHECKSIG)) (not (.. chunks (get 0) (isOpCode))) (< 1 (.. chunks (get 0) data length)))
    )

    ;;;
     ; Returns true if this script is of the form DUP HASH160 <pubkey hash> EQUALVERIFY CHECKSIG, i.e. payment to an
     ; address like 1VayNert3x1KzbpzMGt2qdqrAThiRovi8.  This form was originally intended for the case where you wish
     ; to send somebody money with a written code because their node is offline, but over time has become the standard
     ; way to make payments due to the short and recognizable base58 form addresses come in.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isSentToAddress) [])
    (§ block
        (and (== (.. chunks (size)) 5) (.. chunks (get 0) (equalsOpCode ScriptOpCodes/OP_DUP)) (.. chunks (get 1) (equalsOpCode ScriptOpCodes/OP_HASH160)) (== (.. chunks (get 2) data length) Address/LENGTH) (.. chunks (get 3) (equalsOpCode ScriptOpCodes/OP_EQUALVERIFY)) (.. chunks (get 4) (equalsOpCode ScriptOpCodes/OP_CHECKSIG)))
    )

    ;;;
     ; <p>If a program matches the standard template DUP HASH160 &lt;pubkey hash&gt; EQUALVERIFY CHECKSIG,
     ; then this function retrieves the third element.
     ; In this case, this is useful for fetching the destination address of a transaction.</p>
     ;
     ; <p>If a program matches the standard template HASH160 &lt;script hash&gt; EQUAL,
     ; then this function retrieves the second element.
     ; In this case, this is useful for fetching the hash of the redeem script of a transaction.</p>
     ;
     ; <p>Otherwise it throws a ScriptException.</p>
     ;
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKeyHash) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (.. this (isSentToAddress)))
        (§ block
            (§ return (.. chunks (get 2) data))
        )
        (§ if (.. this (isPayToScriptHash)))
        (§ block
            (§ return (.. chunks (get 1) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Script not in the standard scriptPubKey form"))
    )

    ;;;
     ; Returns the public key in this script.  If a script contains two constants and nothing else, it is assumed
     ; to be a scriptSig (input) for a pay-to-address output and the second constant is returned (the first is the
     ; signature).  If a script contains a constant and an OP_CHECKSIG opcode, the constant is returned as it is
     ; assumed to be a direct pay-to-key scriptPubKey (output) and the first constant is the public key.
     ;
     ; @throws ScriptException if the script is none of the named forms.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (!= (.. chunks (size)) 2))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not of right size, expecting 2 but got " (.. chunks (size)))))
        )

        (§ let #_"ScriptChunk" __chunk0 (.. chunks (get 0)))
        (§ block
            (§ let #_"byte[]" __chunk0data (.. __chunk0 data))
            (§ block
                (§ let #_"ScriptChunk" __chunk1 (.. chunks (get 1)))
                (§ block
                    (§ let #_"byte[]" __chunk1data (.. __chunk1 data))
                    (§ block

                        ;; If we have two large constants assume the input to a pay-to-address output.
                        (§ if (and (some? __chunk0data) (< 2 (.. __chunk0data length)) (some? __chunk1data) (< 2 (.. __chunk1data length))))
                        (§ block
                            (§ return __chunk1data)
                        )

                        ;; A large constant followed by an OP_CHECKSIG is the key.
                        (§ if (and (.. __chunk1 (equalsOpCode ScriptOpCodes/OP_CHECKSIG)) (some? __chunk0data) (< 2 (.. __chunk0data length))))
                        (§ block
                            (§ return __chunk0data)
                        )

                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script did not match expected form: " this)))
                    )
                )
            )
        )
    )

    ;;;
     ; Retrieves the sender public key from a LOCKTIMEVERIFY transaction.
     ;
     ; @throws ScriptException
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getCLTVPaymentChannelSenderPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (.. this (isSentToCLTVPaymentChannel)))
        (§ block
            (§ return (.. chunks (get 8) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMVERIFY transaction: " this)))
    )

    ;;;
     ; Retrieves the recipient public key from a LOCKTIMEVERIFY transaction.
     ;
     ; @throws ScriptException
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getCLTVPaymentChannelRecipientPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (.. this (isSentToCLTVPaymentChannel)))
        (§ block
            (§ return (.. chunks (get 1) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMVERIFY transaction: " this)))
    )

    #_public
    (§ method #_"BigInteger" (§ fn getCLTVPaymentChannelExpiry) [])
    (§ block
        (§ if (.. this (isSentToCLTVPaymentChannel)))
        (§ block
            (§ return (Script/castToBigInteger (.. chunks (get 4) data), 5, false))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMEVERIFY transaction: " this)))
    )

    ;;;
     ; For 2-element [input] scripts assumes that the paid-to-address can be derived from the public key.
     ; The concept of a "from address" isn't well defined in Bitcoin and you should not assume the sender
     ; of a transaction can actually receive coins on it. This method may be removed in future.
     ;;
    #_deprecated
    #_public
    (§ method #_"Address" (§ fn getFromAddress) [#_"NetworkParameters" __params])
        (§ throws #_"ScriptException")
    (§ block
        (§ new #_"Address" __params, (Utils/sha256hash160 (.. this (getPubKey))))
    )

    ;;;
     ; Gets the destination address from this script, if it's in the required form (see getPubKey).
     ;;
    #_public
    (§ method #_"Address" (§ fn getToAddress) [#_"NetworkParameters" __params])
        (§ throws #_"ScriptException")
    (§ block
        (.. this (getToAddress __params, false))
    )

    ;;;
     ; Gets the destination address from this script, if it's in the required form (see getPubKey).
     ;
     ; @param forcePayToPubKey If true, allow payToPubKey to be casted to the corresponding address.
     ;                         This is useful if you prefer showing addresses rather than pubkeys.
     ;;
    #_public
    (§ method #_"Address" (§ fn getToAddress) [#_"NetworkParameters" __params, #_"boolean" __forcePayToPubKey])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (.. this (isSentToAddress)))
        (§ block
            (§ return (§ new #_"Address" __params, (.. this (getPubKeyHash))))
        )
        (§ if (.. this (isPayToScriptHash)))
        (§ block
            (§ return (Address/fromP2SHScript __params, this))
        )
        (§ if (and __forcePayToPubKey (.. this (isSentToRawPubKey))))
        (§ block
            (§ return (.. (ECKey/fromPublicOnly (.. this (getPubKey))) (toAddress __params)))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Cannot cast this script to a pay-to-address type"))
    )

    ;;;
     ; Writes out the given byte buffer to the output stream with the correct opcode prefix.
     ; To write an integer call writeBytes(out, Utils.reverseBytes(Utils.encodeMPI(val, false))).
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn Script/writeBytes) [#_"OutputStream" __os, #_"byte[]" __buf])
        (§ throws #_"IOException")
    (§ block
        (§ if (< (.. __buf length) ScriptOpCodes/OP_PUSHDATA1))
        (§ block
            (.. __os (write (.. __buf length)))
            (.. __os (write __buf))
        )
        (§ elseif (< (.. __buf length) 256))
        (§ block
            (.. __os (write ScriptOpCodes/OP_PUSHDATA1))
            (.. __os (write (.. __buf length)))
            (.. __os (write __buf))
        )
        (§ elseif (< (.. __buf length) 65536))
        (§ block
            (.. __os (write ScriptOpCodes/OP_PUSHDATA2))
            (.. __os (write (& 0xff (.. __buf length))))
            (.. __os (write (& 0xff (>> (.. __buf length) 8))))
            (.. __os (write __buf))
        )
        (§ else )
        (§ block
            (§ throw (§ new #_"RuntimeException" "Unimplemented"))
        )
        nil
    )

    ;;; Creates a program that requires at least N of the given keys to sign, using OP_CHECKMULTISIG. ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Script/createMultiSigOutputScript) [#_"int" __threshold, #_"List<ECKey>" __pubkeys])
    (§ block
        (Preconditions/checkArgument (< 0 __threshold))
        (Preconditions/checkArgument (<= __threshold (.. __pubkeys (size))))
        (Preconditions/checkArgument (<= (.. __pubkeys (size)) 16)) ;; That's the max we can represent with a single opcode.

        (§ if (< 3 (.. __pubkeys (size))))
        (§ block
            (.. Script/log (warn "Creating a multi-signature output that is non-standard: {} pubkeys, should be <= 3", (.. __pubkeys (size))))
        )

        (§ try )
        (§ block
            (§ let #_"ByteArrayOutputStream" __bits (§ new #_"ByteArrayOutputStream"))
            (§ block
                (.. __bits (write (Script/encodeToOpN __threshold)))
                (§ for (§ var #_"ECKey" __key) :for __pubkeys)
                (§ block
                    (Script/writeBytes __bits, (.. __key (getPubKey)))
                )
                (.. __bits (write (Script/encodeToOpN (.. __pubkeys (size)))))
                (.. __bits (write ScriptOpCodes/OP_CHECKMULTISIG))
                (§ return (.. __bits (toByteArray)))
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Script/createInputScript) [#_"byte[]" __signature, #_"byte[]" __pubkey])
    (§ block
        (§ try )
        (§ block
            ;; TODO: Do this by creating a Script *first* then having the script reassemble itself into bytes.
            (§ let #_"ByteArrayOutputStream" __bits (§ new #_"UnsafeByteArrayOutputStream" (+ (.. __signature length) (.. __pubkey length) 2)))
            (§ block
                (Script/writeBytes __bits, __signature)
                (Script/writeBytes __bits, __pubkey)
                (§ return (.. __bits (toByteArray)))
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e))
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Script/createInputScript) [#_"byte[]" __signature])
    (§ block
        (§ try )
        (§ block
            ;; TODO: Do this by creating a Script *first* then having the script reassemble itself into bytes.
            (§ let #_"ByteArrayOutputStream" __bits (§ new #_"UnsafeByteArrayOutputStream" (+ (.. __signature length) 2)))
            (§ block
                (Script/writeBytes __bits, __signature)
                (§ return (.. __bits (toByteArray)))
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e))
        )
    )

    ;;;
     ; Creates an incomplete scriptSig that, once filled with signatures, can redeem output containing this scriptPubKey.
     ; Instead of the signatures resulting script has OP_0.
     ; Having incomplete input script allows to pass around partially signed tx.
     ; It is expected that this program later on will be updated with proper signatures.
     ;;
    #_public
    (§ method #_"Script" (§ fn createEmptyInputScript) [#_nilable #_"ECKey" __key, #_nilable #_"Script" __redeemScript])
    (§ block
        (§ if (.. this (isSentToAddress)))
        (§ block
            (Preconditions/checkArgument (some? __key), "Key required to create pay-to-address input script")
            (§ return (ScriptBuilder/createInputScript nil, __key))
        )

        (§ if (.. this (isSentToRawPubKey)))
        (§ block
            (§ return (ScriptBuilder/createInputScript nil))
        )

        (§ if (.. this (isPayToScriptHash)))
        (§ block
            (Preconditions/checkArgument (some? __redeemScript), "Redeem script required to create P2SH input script")
            (§ return (ScriptBuilder/createP2SHMultiSigInputScript nil, __redeemScript))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Do not understand script type: " this)))
    )

    ;;;
     ; Returns a copy of the given scriptSig with the signature inserted in the given position.
     ;;
    #_public
    (§ method #_"Script" (§ fn getScriptSigWithSignature) [#_"Script" __scriptSig, #_"byte[]" __sigBytes, #_"int" __index])
    (§ block
        (§ let #_"int" __sigsPrefixCount 0)
        (§ block
            (§ let #_"int" __sigsSuffixCount 0)
            (§ block
                (§ if (.. this (isPayToScriptHash)))
                (§ block
                    (§ ass __sigsPrefixCount 1) ;; OP_0 <sig>* <redeemScript>
                    (§ ass __sigsSuffixCount 1)
                )
                (§ elseif (.. this (isSentToMultiSig)))
                (§ block
                    (§ ass __sigsPrefixCount 1) ;; OP_0 <sig>*
                )
                (§ elseif (.. this (isSentToAddress)))
                (§ block
                    (§ ass __sigsSuffixCount 1) ;; <sig> <pubkey>
                )
                (ScriptBuilder/updateScriptWithSignature __scriptSig, __sigBytes, __index, __sigsPrefixCount, __sigsSuffixCount)
            )
        )
    )

    ;;;
     ; Returns the index where a signature by the key should be inserted.
     ; Only applicable to a P2SH scriptSig.
     ;;
    #_public
    (§ method #_"int" (§ fn getSigInsertionIndex) [#_"Sha256Hash" __hash, #_"ECKey" __signingKey])
    (§ block
        ;; Iterate over existing signatures, skipping the initial OP_0, the final redeem script
        ;; and any placeholder OP_0 sigs.
        (§ let #_"List<ScriptChunk>" __existingChunks (.. chunks (subList 1, (- (.. chunks (size)) 1))))
        (§ block
            (§ let #_"ScriptChunk" __redeemScriptChunk (.. chunks (get (- (.. chunks (size)) 1))))
            (§ block
                (Preconditions/checkNotNull (.. __redeemScriptChunk data))
                (§ let #_"Script" __redeemScript (§ new #_"Script" (.. __redeemScriptChunk data)))
                (§ block

                    (§ let #_"int" __sigCount 0)
                    (§ block
                        (§ let #_"int" __myIndex (.. __redeemScript (findKeyInRedeem __signingKey)))
                        (§ block
                            (§ for (§ var #_"ScriptChunk" __chunk) :for __existingChunks)
                            (§ block
                                (§ if (== (.. __chunk opcode) ScriptOpCodes/OP_0))
                                (§ block
                                    ;; OP_0, skip
                                )
                                (§ else )
                                (§ block
                                    (Preconditions/checkNotNull (.. __chunk data))
                                    (§ if (< __myIndex (.. __redeemScript (findSigInRedeem (.. __chunk data), __hash))))
                                    (§ block
                                        (§ return __sigCount)
                                    )
                                    (§ ass __sigCount (inc __sigCount))
                                )
                            )
                            __sigCount
                        )
                    )
                )
            )
        )
    )

    #_private
    (§ method- #_"int" (§ fn findKeyInRedeem) [#_"ECKey" __key])
    (§ block
        (Preconditions/checkArgument (.. chunks (get 0) (isOpCode))) ;; P2SH scriptSig

        (§ let #_"int" __numKeys (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
        (§ block
            (§ for (§ var #_"int" __i 0) :for (< __i __numKeys) :for (§ recur (inc __i)))
            (§ block
                (§ if (Arrays/equals (.. chunks (get (+ 1 __i)) data), (.. __key (getPubKey))))
                (§ block
                    (§ return __i)
                )
            )

            (§ throw (§ new #_"IllegalStateException" (str "Could not find matching key " __key " in script " this)))
        )
    )

    ;;;
     ; Returns a list of the keys required by this script, assuming a multi-sig script.
     ;
     ; @throws ScriptException if the script type is not understood or is pay to address or is P2SH (run this method on the "Redeem script" instead).
     ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getPubKeys) [])
    (§ block
        (§ if (not (.. this (isSentToMultiSig))))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Only usable for multisig scripts."))
        )

        (§ let #_"ArrayList<ECKey>" __result (Lists/newArrayList))
        (§ block
            (§ let #_"int" __numKeys (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
            (§ block
                (§ for (§ var #_"int" __i 0) :for (< __i __numKeys) :for (§ recur (inc __i)))
                (§ block
                    (.. __result (add (ECKey/fromPublicOnly (.. chunks (get (+ 1 __i)) data))))
                )
                __result
            )
        )
    )

    #_private
    (§ method- #_"int" (§ fn findSigInRedeem) [#_"byte[]" __signatureBytes, #_"Sha256Hash" __hash])
    (§ block
        (Preconditions/checkArgument (.. chunks (get 0) (isOpCode))) ;; P2SH scriptSig

        (§ let #_"int" __numKeys (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
        (§ block
            (§ let #_"TransactionSignature" __signature (TransactionSignature/decodeFromBitcoin __signatureBytes, true))
            (§ block
                (§ for (§ var #_"int" __i 0) :for (< __i __numKeys) :for (§ recur (inc __i)))
                (§ block
                    (§ if (.. (ECKey/fromPublicOnly (.. chunks (get (inc __i)) data)) (verify __hash, __signature)))
                    (§ block
                        (§ return __i)
                    )
                )

                (§ throw (§ new #_"IllegalStateException" (str "Could not find matching key for signature on " __hash " sig " (.. Utils/HEX (encode __signatureBytes)))))
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"int" (§ fn Script/getSigOpCount) [#_"List<ScriptChunk>" __chunks, #_"boolean" __accurate])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"int" __sigOps 0)
        (§ block
            (§ let #_"int" __lastOpCode ScriptOpCodes/OP_INVALIDOPCODE)
            (§ block
                (§ for (§ var #_"ScriptChunk" __chunk) :for __chunks)
                (§ block
                    (§ if (.. __chunk (isOpCode)))
                    (§ block
                        (§ switch (.. __chunk opcode))
                        (§ block
                            (§ case ScriptOpCodes/OP_CHECKSIG)
                            (§ case ScriptOpCodes/OP_CHECKSIGVERIFY)
                            (§ block
                                (§ ass __sigOps (inc __sigOps))
                                (§ break )
                            )
                            (§ case ScriptOpCodes/OP_CHECKMULTISIG)
                            (§ case ScriptOpCodes/OP_CHECKMULTISIGVERIFY)
                            (§ block
                                (§ if (and __accurate (<= ScriptOpCodes/OP_1 __lastOpCode ScriptOpCodes/OP_16)))
                                (§ block
                                    (§ ass __sigOps (+ __sigOps (Script/decodeFromOpN __lastOpCode)))
                                )
                                (§ else )
                                (§ block
                                    (§ ass __sigOps (+ __sigOps 20))
                                )
                                (§ break )
                            )
                            (§ default )
                            (§ block
                                (§ break )
                            )
                        )
                        (§ ass __lastOpCode (.. __chunk opcode))
                    )
                )
                __sigOps
            )
        )
    )

    #_static
    (§ defn #_"int" (§ fn Script/decodeFromOpN) [#_"int" __opcode])
    (§ block
        (Preconditions/checkArgument (or (== __opcode ScriptOpCodes/OP_0) (== __opcode ScriptOpCodes/OP_1NEGATE) (<= ScriptOpCodes/OP_1 __opcode ScriptOpCodes/OP_16)), "decodeFromOpN called on non OP_N opcode")

        (§ if (== __opcode ScriptOpCodes/OP_0))
        (§ block
            (§ return 0)
        )
        (§ if (== __opcode ScriptOpCodes/OP_1NEGATE))
        (§ block
            (§ return -1)
        )

        (- (inc __opcode) ScriptOpCodes/OP_1)
    )

    #_static
    (§ defn #_"int" (§ fn Script/encodeToOpN) [#_"int" __value])
    (§ block
        (Preconditions/checkArgument (<= -1 __value 16), (str "encodeToOpN called for " __value " which we cannot encode in an opcode."))

        (§ if (== __value 0))
        (§ block
            (§ return ScriptOpCodes/OP_0)
        )
        (§ if (== __value -1))
        (§ block
            (§ return ScriptOpCodes/OP_1NEGATE)
        )

        (+ (dec __value) ScriptOpCodes/OP_1)
    )

    ;;;
     ; Gets the count of regular SigOps in the script program (counting multisig ops as 20).
     ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn Script/getSigOpCount) [#_"byte[]" __program])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"Script" __script (§ new #_"Script"))
        (§ block
            (§ try )
            (§ block
                (.. __script (parse __program))
            )
            (§ catch #_"ScriptException" (§ name _))
            (§ block
                ;; Ignore errors and count up to the parse-able length.
