                        (§ block
                            (§ for (§ var #_"int" __i 0) :for (< __i __sigCount) :for (§ recur (inc __i)))
                            (§ block
                                (.. __sigs (add (.. __stack (pollLast))))
                            )

                            (§ let #_"byte[]" __prog (.. __script (getProgram)))
                            (§ block
                                (§ let #_"byte[]" __connectedScript (Arrays/copyOfRange __prog, __lastCodeSepLocation, (.. __prog length)))
                                (§ block

                                    (§ for (§ var #_"byte[]" __sig) :for __sigs)
                                    (§ block
                                        (§ let #_"UnsafeByteArrayOutputStream" __outStream (§ new #_"UnsafeByteArrayOutputStream" (inc (.. __sig length))))
                                        (§ block
                                            (§ try )
                                            (§ block
                                                (Script/writeBytes __outStream, __sig)
                                            )
                                            (§ catch #_"IOException" __e)
                                            (§ block
                                                (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
                                            )
                                            (§ ass __connectedScript (Script/removeAllInstancesOf __connectedScript, (.. __outStream (toByteArray))))
                                        )
                                    )

                                    (§ let #_"boolean" __valid true)
                                    (§ block
                                        (§ while (< 0 (.. __sigs (size))))
                                        (§ block
                                            (§ let #_"byte[]" __pubKey (.. __pubkeys (pollFirst)))
                                            (§ block
                                                ;; We could reasonably move this out of the loop, but because signature verification is significantly
                                                ;; more expensive than hashing, its not a big deal.
                                                (§ try )
                                                (§ block
                                                    (§ let #_"TransactionSignature" __sig (TransactionSignature/decodeFromBitcoin (.. __sigs (getFirst)), __requireCanonical))
                                                    (§ block
                                                        (§ let #_"Sha256Hash" __hash (.. __txContainingThis (hashForSignature __index, __connectedScript, (§ cast #_"byte" (.. __sig sighashFlags)))))
                                                        (§ block
                                                            (§ if (ECKey/verify (.. __hash (getBytes)), __sig, __pubKey))
                                                            (§ block
                                                                (.. __sigs (pollFirst))
                                                            )
                                                        )
                                                    )
                                                )
                                                (§ catch #_"Exception" (§ name _))
                                                (§ block
                                                    ;; There is (at least) one exception that could be hit here (EOFException, if the sig is too short).
                                                    ;; Because I can't verify there aren't more, we use a very generic Exception catch.
                                                )

                                                (§ if (< (.. __pubkeys (size)) (.. __sigs (size))))
                                                (§ block
                                                    (§ ass __valid false)
                                                    (§ break )
                                                )
                                            )
                                        )

                                        ;; We uselessly remove a stack object to emulate a Bitcoin Core bug.
                                        (§ let #_"byte[]" __nullDummy (.. __stack (pollLast)))
                                        (§ block
                                            (§ if (and (.. __verifyFlags (contains VerifyFlag/NULLDUMMY)) (< 0 (.. __nullDummy length))))
                                            (§ block
                                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_NULLFAIL, (str "OP_CHECKMULTISIG(VERIFY) with non-null nulldummy: " (Arrays/toString __nullDummy))))
                                            )

                                            (§ if (== __opcode ScriptOpCodes/OP_CHECKMULTISIG))
                                            (§ block
                                                (.. __stack (add (if __valid (§ new #_"byte[]" (§ coll 1 )) (§ new #_"byte[]"))))
                                            )
                                            (§ elseif (and (== __opcode ScriptOpCodes/OP_CHECKMULTISIGVERIFY) (not __valid)))
                                            (§ block
                                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_NULLFAIL, "Script failed OP_CHECKMULTISIGVERIFY"))
                                            )

                                            __opCount
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Verifies that this script (interpreted as a scriptSig) correctly spends the given scriptPubKey, enabling all validation rules.
     ; @param txContainingThis The transaction in which this input scriptSig resides.
     ;                         Accessing txContainingThis from another thread while this method runs results in undefined behavior.
     ; @param scriptSigIndex The index in txContainingThis of the scriptSig (note: NOT the index of the scriptPubKey).
     ; @param scriptPubKey The connected scriptPubKey containing the conditions needed to claim the value.
     ; @deprecated Use {@link #correctlySpends(org.bitcoinj.core.Transaction, long, org.bitcoinj.script.Script, java.util.Set)}
     ; instead so that verification flags do not change as new verification options are added.
     ;;
    #_deprecated
    #_public
    (§ method #_"void" (§ fn correctlySpends) [#_"Transaction" __txContainingThis, #_"long" __scriptSigIndex, #_"Script" __scriptPubKey])
        (§ throws #_"ScriptException")
    (§ block
        (.. this (correctlySpends __txContainingThis, __scriptSigIndex, __scriptPubKey, Script/ALL_VERIFY_FLAGS))
