    )

    ;;;
     ; Verifies that this script (interpreted as a scriptSig) correctly spends the given scriptPubKey.
     ; @param txContainingThis The transaction in which this input scriptSig resides.
     ;                         Accessing txContainingThis from another thread while this method runs results in undefined behavior.
     ; @param scriptSigIndex The index in txContainingThis of the scriptSig (note: NOT the index of the scriptPubKey).
     ; @param scriptPubKey The connected scriptPubKey containing the conditions needed to claim the value.
     ; @param verifyFlags Each flag enables one validation rule.  If in doubt, use {@link #correctlySpends(Transaction, long, Script)}
     ;                    which sets all flags.
     ;;
    #_public
    (§ method #_"void" (§ fn correctlySpends) [#_"Transaction" __txContainingThis, #_"long" __scriptSigIndex, #_"Script" __scriptPubKey, #_"Set<VerifyFlag>" __verifyFlags])
        (§ throws #_"ScriptException")
    (§ block
        ;; Clone the transaction because executing the script involves editing it, and if we die, we'll leave
        ;; the tx half broken (also it's not so thread safe to work on it directly).
        (§ try )
        (§ block
            (§ ass __txContainingThis (.. __txContainingThis (getParams) (getDefaultSerializer) (makeTransaction (.. __txContainingThis (bitcoinSerialize)))))
        )
        (§ catch #_"ProtocolException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Should not happen unless we were given a totally broken transaction.
        )

        (§ if (or (< Script/MAX_SCRIPT_SIZE (.. this (getProgram) length)) (< Script/MAX_SCRIPT_SIZE (.. __scriptPubKey (getProgram) length))))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SCRIPT_SIZE, "Script larger than 10,000 bytes"))
        )

        (§ let #_"LinkedList<byte[]>" __stack (§ new #_"LinkedList<>"))
        (§ block
            (§ let #_"LinkedList<byte[]>" __p2shStack nil)
            (§ block

                (Script/executeScript __txContainingThis, __scriptSigIndex, this, __stack, __verifyFlags)
                (§ if (.. __verifyFlags (contains VerifyFlag/P2SH)))
                (§ block
                    (§ ass __p2shStack (§ new #_"LinkedList<>" __stack))
                )
                (Script/executeScript __txContainingThis, __scriptSigIndex, __scriptPubKey, __stack, __verifyFlags)

                (§ if (== (.. __stack (size)) 0))
                (§ block
                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "Stack empty at end of script execution."))
                )

                (§ if (not (Script/castToBool (.. __stack (pollLast)))))
                (§ block
                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, (str "Script resulted in a non-true stack: " __stack)))
                )

                ;; P2SH is pay to script hash.  It means that the scriptPubKey has a special form which is a valid
                ;; program but it has "useless" form that if evaluated as a normal program always returns true.
                ;; Instead, miners recognize it as special based on its template - it provides a hash of the real scriptPubKey
                ;; and that must be provided by the input.  The goal of this bizarre arrangement is twofold:
                ;;
                ;; (1) You can sum up a large, complex script (like a CHECKMULTISIG script) with an address that's the same
                ;;     size as a regular address.  This means it doesn't overload scannable QR codes/NFC tags or become
                ;;     un-wieldy to copy/paste.
                ;; (2) It allows the working set to be smaller: nodes perform best when they can store as many unspent outputs
                ;;     in RAM as possible, so if the outputs are made smaller and the inputs get bigger, then it's better for
                ;;     overall scalability and performance.

                ;; TODO: Check if we can take out enforceP2SH if there's a checkpoint at the enforcement block.
                (§ if (and (.. __verifyFlags (contains VerifyFlag/P2SH)) (.. __scriptPubKey (isPayToScriptHash))))
                (§ block
                    (§ for (§ var #_"ScriptChunk" __chunk) :for chunks)
                    (§ block
                        (§ if (and (.. __chunk (isOpCode)) (< ScriptOpCodes/OP_16 (.. __chunk opcode))))
                        (§ block
                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_PUSHONLY, "Attempted to spend a P2SH scriptPubKey with a script that contained script ops"))
                        )
                    )

                    (§ let #_"byte[]" __scriptPubKeyBytes (.. __p2shStack (pollLast)))
                    (§ block
                        (§ let #_"Script" __scriptPubKeyP2SH (§ new #_"Script" __scriptPubKeyBytes))
                        (§ block

                            (Script/executeScript __txContainingThis, __scriptSigIndex, __scriptPubKeyP2SH, __p2shStack, __verifyFlags)

                            (§ if (== (.. __p2shStack (size)) 0))
                            (§ block
                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "P2SH stack empty at end of script execution."))
                            )

                            (§ if (not (Script/castToBool (.. __p2shStack (pollLast)))))
                            (§ block
                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "P2SH script execution resulted in a non-true stack"))
                            )
                        )
                    )
                )
                nil
            )
        )
    )

    ;; Utility that doesn't copy for internal use.
    #_private
    (§ method- #_"byte[]" (§ fn getQuickProgram) [])
    (§ block
        (or program (.. this (getProgram)))
    )

    ;;;
     ; Get the {@link org.bitcoinj.script.Script.ScriptType}.
     ; @return The script type.
     ;;
    #_public
    (§ method #_"ScriptType" (§ fn getScriptType) [])
    (§ block
        (§ if (.. this (isSentToAddress)))
        (§ block
            (§ return ScriptType/P2PKH)
        )
        (§ if (.. this (isSentToRawPubKey)))
        (§ block
            (§ return ScriptType/PUB_KEY)
        )
        (§ if (.. this (isPayToScriptHash)))
        (§ block
            (§ return ScriptType/P2SH)
        )

        ScriptType/NO_TYPE
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (Arrays/equals (.. this (getQuickProgram)), (.. (§ cast #_"Script" __o) (getQuickProgram)))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Arrays/hashCode (.. this (getQuickProgram)))
    )
)

#_(ns org.bitcoinj.script #_"ScriptBuilder"
    (:import [java.math BigInteger]
             [java.util ArrayList Arrays Collections List Stack])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists])
   (:require [org.bitcoinj.core Address ECKey Utils]
             [org.bitcoinj.crypto TransactionSignature]
             [org.bitcoinj.script ScriptOpCodes]))

;;;
 ; <p>Tools for the construction of commonly used script types.  You don't normally need this as it's hidden
 ; behind convenience methods on {@link org.bitcoinj.core.Transaction}, but they are useful when working with
 ; the protocol at a lower level.</p>
 ;;
#_public
(§ class #_"ScriptBuilder"
    #_private
    (§ field- #_"List<ScriptChunk>" (§ name chunks))

    ;;; Creates a fresh ScriptBuilder with an empty program. ;;
    #_public
    (§ constructor #_"ScriptBuilder" [])
    (§ block
        (§ ass (§ name chunks) (Lists/newLinkedList))
        this
    )

    ;;; Creates a fresh ScriptBuilder with the given program as the starting point. ;;
    #_public
    (§ constructor #_"ScriptBuilder" [#_"Script" __template])
    (§ block
        (§ ass (§ name chunks) (§ new #_"ArrayList<>" (.. __template (getChunks))))
        this
    )

    ;;; Adds the given chunk to the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn addChunk) [#_"ScriptChunk" __chunk])
    (§ block
        (.. this (addChunk (.. chunks (size)), __chunk))
    )

    ;;; Adds the given chunk at the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn addChunk) [#_"int" __index, #_"ScriptChunk" __chunk])
    (§ block
        (.. chunks (add __index, __chunk))
        this
    )

    ;;; Adds the given opcode to the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn op) [#_"int" __opcode])
    (§ block
        (.. this (op (.. chunks (size)), __opcode))
    )

    ;;; Adds the given opcode to the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn op) [#_"int" __index, #_"int" __opcode])
    (§ block
        (Preconditions/checkArgument (< ScriptOpCodes/OP_PUSHDATA4 __opcode))
        (.. this (addChunk __index, (§ new #_"ScriptChunk" __opcode, nil)))
    )

    ;;; Adds a copy of the given byte array as a data element (i.e. PUSHDATA) at the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn data) [#_"byte[]" __data])
    (§ block
        (if (== (.. __data length) 0) (.. this (smallNum 0)) (.. this (data (.. chunks (size)), __data)))
    )

    ;;; Adds a copy of the given byte array as a data element (i.e. PUSHDATA) at the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn data) [#_"int" __index, #_"byte[]" __data])
    (§ block
        ;; implements BIP62
        (§ let #_"byte[]" __copy (Arrays/copyOf __data, (.. __data length)))
        (§ block
            (§ let #_"int" __opcode)
            (§ block
                (§ if (== (.. __data length) 0))
                (§ block
                    (§ ass __opcode ScriptOpCodes/OP_0)
                )
                (§ elseif (== (.. __data length) 1))
                (§ block
                    (§ let #_"byte" __b (§ ai __data 0))
                    (§ block
                        (§ if (<= 1 __b 16))
                        (§ block
                            (§ ass __opcode (Script/encodeToOpN __b))
                        )
                        (§ else )
                        (§ block
                            (§ ass __opcode 1)
                        )
                    )
                )
                (§ elseif (< (.. __data length) ScriptOpCodes/OP_PUSHDATA1))
                (§ block
                    (§ ass __opcode (.. __data length))
                )
                (§ elseif (< (.. __data length) 256))
                (§ block
                    (§ ass __opcode ScriptOpCodes/OP_PUSHDATA1)
                )
                (§ elseif (< (.. __data length) 65536))
                (§ block
                    (§ ass __opcode ScriptOpCodes/OP_PUSHDATA2)
                )
                (§ else )
                (§ block
                    (§ throw (§ new #_"RuntimeException" "Unimplemented"))
                )
                (.. this (addChunk __index, (§ new #_"ScriptChunk" __opcode, __copy)))
            )
        )
    )

    ;;;
     ; Adds the given number to the end of the program.
     ; Automatically uses shortest encoding possible.
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn number) [#_"long" __num])
    (§ block
        (if (<= 0 __num 16) (.. this (smallNum (§ cast #_"int" __num))) (.. this (bigNum __num)))
    )

    ;;;
     ; Adds the given number to the given index in the program.
     ; Automatically uses shortest encoding possible.
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn number) [#_"int" __index, #_"long" __num])
    (§ block
        (if (<= 0 __num 16) (.. this (smallNum __index, (§ cast #_"int" __num))) (.. this (bigNum __index, __num)))
    )

    ;;;
     ; Adds the given number as a OP_N opcode to the end of the program.
     ; Only handles values 0-16 inclusive.
     ;
     ; @see #number(int)
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn smallNum) [#_"int" __num])
    (§ block
        (.. this (smallNum (.. chunks (size)), __num))
    )

    ;;; Adds the given number as a push data chunk.
     ; This is intended to use for negative numbers or values > 16, and although
     ; it will accept numbers in the range 0-16 inclusive, the encoding would be
     ; considered non-standard.
     ;
     ; @see #number(int)
     ;;
    #_protected
    (§ method #_"ScriptBuilder" (§ fn bigNum) [#_"long" __num])
    (§ block
        (.. this (bigNum (.. chunks (size)), __num))
    )

    ;;;
     ; Adds the given number as a OP_N opcode to the given index in the program.
     ; Only handles values 0-16 inclusive.
     ;
     ; @see #number(int)
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn smallNum) [#_"int" __index, #_"int" __num])
    (§ block
        (Preconditions/checkArgument (<= 0 __num), "Cannot encode negative numbers with smallNum")
        (Preconditions/checkArgument (<= __num 16), "Cannot encode numbers larger than 16 with smallNum")

        (.. this (addChunk __index, (§ new #_"ScriptChunk" (Script/encodeToOpN __num), nil)))
    )

    ;;;
     ; Adds the given number as a push data chunk to the given index in the program.
     ; This is intended to use for negative numbers or values > 16, and although
     ; it will accept numbers in the range 0-16 inclusive, the encoding would be
     ; considered non-standard.
     ;
     ; @see #number(int)
     ;;
    #_protected
    (§ method #_"ScriptBuilder" (§ fn bigNum) [#_"int" __index, #_"long" __num])
    (§ block
        (§ let #_"byte[]" __data)
        (§ block

            (§ if (== __num 0))
            (§ block
                (§ ass __data (§ new #_"byte[]" (§ count 0)))
            )
            (§ else )
            (§ block
                (§ let #_"Stack<Byte>" __result (§ new #_"Stack<>"))
                (§ block
                    (§ let #_"boolean" __neg (< __num 0))
                    (§ block
                        (§ let #_"long" __absvalue (Math/abs __num))
                        (§ block

                            (§ while (!= __absvalue 0))
                            (§ block
                                (.. __result (push (§ cast #_"byte" (& __absvalue 0xff))))
                                (§ ass __absvalue (>> __absvalue 8))
                            )

                            (§ if (!= (& (.. __result (peek)) 0x80) 0))
                            (§ block
                                ;; The most significant byte is >= 0x80, so push an extra byte that
                                ;; contains just the sign of the value.
                                (.. __result (push (§ cast #_"byte" (if __neg 0x80 0))))
                            )
                            (§ elseif __neg)
                            (§ block
                                ;; The most significant byte is < 0x80 and the value is negative,
                                ;; set the sign bit so it is subtracted and interpreted as a
                                ;; negative when converting back to an integral.
                                (.. __result (push (§ cast #_"byte" (| (.. __result (pop)) 0x80))))
                            )

                            (§ ass __data (§ new #_"byte[]" (§ count (.. __result (size)))))
                            (§ for (§ var #_"int" __byteIdx 0) :for (< __byteIdx (.. __data length)) :for (§ recur (inc __byteIdx)))
                            (§ block
                                (§ ass (§ name (§ ai __data __byteIdx)) (.. __result (get __byteIdx)))
                            )
                        )
                    )
                )
            )

            ;; At most the encoded value could take up to 8 bytes, so we don't need
            ;; to use OP_PUSHDATA opcodes.
            (.. this (addChunk __index, (§ new #_"ScriptChunk" (.. __data length), __data)))
        )
    )

    ;;; Creates a new immutable Script based on the state of the builder. ;;
    #_public
    (§ method #_"Script" (§ fn build) [])
    (§ block
        (§ new #_"Script" chunks)
    )

    ;;; Creates a scriptPubKey that encodes payment to the given address. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createOutputScript) [#_"Address" __to])
    (§ block
        (§ if (.. __to (isP2SHAddress)))
        (§ block
            ;; OP_HASH160 <scriptHash> OP_EQUAL
            (§ return (.. (§ new #_"ScriptBuilder") (op ScriptOpCodes/OP_HASH160) (data (.. __to (getHash160))) (op ScriptOpCodes/OP_EQUAL) (build)))
        )
        (§ else )
        (§ block
            ;; OP_DUP OP_HASH160 <pubKeyHash> OP_EQUALVERIFY OP_CHECKSIG
            (§ return (.. (§ new #_"ScriptBuilder") (op ScriptOpCodes/OP_DUP) (op ScriptOpCodes/OP_HASH160) (data (.. __to (getHash160))) (op ScriptOpCodes/OP_EQUALVERIFY) (op ScriptOpCodes/OP_CHECKSIG) (build)))
        )
    )

    ;;; Creates a scriptPubKey that encodes payment to the given raw public key. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createOutputScript) [#_"ECKey" __key])
    (§ block
        (.. (§ new #_"ScriptBuilder") (data (.. __key (getPubKey))) (op ScriptOpCodes/OP_CHECKSIG) (build))
    )

    ;;;
     ; Creates a scriptSig that can redeem a pay-to-address output.
     ; If given signature is null, incomplete scriptSig will be created with OP_0 instead of signature.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createInputScript) [#_nilable #_"TransactionSignature" __signature, #_"ECKey" __pubKey])
    (§ block
        (§ let #_"byte[]" __pubkeyBytes (.. __pubKey (getPubKey)))
        (§ block
            (§ let #_"byte[]" __sigBytes (if (some? __signature) (.. __signature (encodeToBitcoin)) (§ new #_"byte[]")))
            (§ block
                (.. (§ new #_"ScriptBuilder") (data __sigBytes) (data __pubkeyBytes) (build))
            )
        )
    )

    ;;;
     ; Creates a scriptSig that can redeem a pay-to-pubkey output.
     ; If given signature is null, incomplete scriptSig will be created with OP_0 instead of signature.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createInputScript) [#_nilable #_"TransactionSignature" __signature])
    (§ block
        (§ let #_"byte[]" __sigBytes (if (some? __signature) (.. __signature (encodeToBitcoin)) (§ new #_"byte[]")))
        (§ block
            (.. (§ new #_"ScriptBuilder") (data __sigBytes) (build))
        )
    )

    ;;; Creates a program that requires at least N of the given keys to sign, using OP_CHECKMULTISIG. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createMultiSigOutputScript) [#_"int" __threshold, #_"List<ECKey>" __pubkeys])
    (§ block
        (Preconditions/checkArgument (< 0 __threshold))
        (Preconditions/checkArgument (<= __threshold (.. __pubkeys (size))))
        (Preconditions/checkArgument (<= (.. __pubkeys (size)) 16)) ;; That's the max we can represent with a single opcode.

        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (smallNum __threshold))
            (§ for (§ var #_"ECKey" __key) :for __pubkeys)
            (§ block
                (.. __builder (data (.. __key (getPubKey))))
            )
            (.. __builder (smallNum (.. __pubkeys (size))))
            (.. __builder (op ScriptOpCodes/OP_CHECKMULTISIG))
            (.. __builder (build))
        )
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createMultiSigInputScript) [#_"List<TransactionSignature>" __signatures])
    (§ block
        (§ let #_"List<byte[]>" __sigs (§ new #_"ArrayList<>" (.. __signatures (size))))
        (§ block
            (§ for (§ var #_"TransactionSignature" __signature) :for __signatures)
            (§ block
                (.. __sigs (add (.. __signature (encodeToBitcoin))))
            )

            (ScriptBuilder/createMultiSigInputScriptBytes __sigs, nil)
        )
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createMultiSigInputScript) [#_"TransactionSignature..." __signatures])
    (§ block
        (ScriptBuilder/createMultiSigInputScript (Arrays/asList __signatures))
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program, using pre-encoded signatures. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createMultiSigInputScriptBytes) [#_"List<byte[]>" __signatures])
    (§ block
        (ScriptBuilder/createMultiSigInputScriptBytes __signatures, nil)
    )

    ;;;
     ; Create a program that satisfies a pay-to-script hashed OP_CHECKMULTISIG program.
     ; If given signature list is null, incomplete scriptSig will be created with OP_0 instead of signatures.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createP2SHMultiSigInputScript) [#_nilable #_"List<TransactionSignature>" __signatures, #_"Script" __multisigProgram])
    (§ block
        (§ let #_"List<byte[]>" __sigs (§ new #_"ArrayList<>"))
        (§ block
            (§ if (nil? __signatures))
            (§ block
                ;; Create correct number of empty signatures.
                (§ let #_"int" __numSigs (.. __multisigProgram (getNumberOfSignaturesRequiredToSpend)))
                (§ block
                    (§ for (§ var #_"int" __i 0) :for (< __i __numSigs) :for (§ recur (inc __i)))
                    (§ block
                        (.. __sigs (add (§ new #_"byte[]")))
                    )
                )
            )
            (§ else )
            (§ block
                (§ for (§ var #_"TransactionSignature" __signature) :for __signatures)
                (§ block
                    (.. __sigs (add (.. __signature (encodeToBitcoin))))
                )
            )
            (ScriptBuilder/createMultiSigInputScriptBytes __sigs, (.. __multisigProgram (getProgram)))
        )
    )

    ;;;
     ; Create a program that satisfies an OP_CHECKMULTISIG program, using pre-encoded signatures.
     ; Optionally, appends the script program bytes if spending a P2SH output.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createMultiSigInputScriptBytes) [#_"List<byte[]>" __signatures, #_nilable #_"byte[]" __multisigProgramBytes])
    (§ block
        (Preconditions/checkArgument (<= (.. __signatures (size)) 16))

        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (smallNum 0)) ;; Work around a bug in CHECKMULTISIG that is now a required part of the protocol.
            (§ for (§ var #_"byte[]" __signature) :for __signatures)
            (§ block
                (.. __builder (data __signature))
            )
            (§ if (some? __multisigProgramBytes))
            (§ block
                (.. __builder (data __multisigProgramBytes))
            )
            (.. __builder (build))
        )
    )

    ;;;
     ; Returns a copy of the given scriptSig with the signature inserted in the given position.
     ;
     ; This function assumes that any missing sigs have OP_0 placeholders.  If given scriptSig
     ; already has all the signatures in place, IllegalArgumentException will be thrown.
     ;
     ; @param targetIndex Where to insert the signature.
     ; @param sigsPrefixCount How many items to copy verbatim (e.g. initial OP_0 for multisig).
     ; @param sigsSuffixCount How many items to copy verbatim at end (e.g. redeemScript for P2SH).
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/updateScriptWithSignature) [#_"Script" __scriptSig, #_"byte[]" __signature, #_"int" __targetIndex, #_"int" __sigsPrefixCount, #_"int" __sigsSuffixCount])
    (§ block
        (§ let #_"List<ScriptChunk>" __inputChunks (.. __scriptSig (getChunks)))
        (§ block
            (§ let #_"int" __totalChunks (.. __inputChunks (size)))
            (§ block

                ;; Check if we have a place to insert, otherwise just return given scriptSig unchanged.
                ;; We assume here that OP_0 placeholders always go after the sigs, so
                ;; to find if we have sigs missing, we can just check the chunk in latest sig position.
                (§ let #_"boolean" __hasMissingSigs (.. __inputChunks (get (- __totalChunks __sigsSuffixCount 1)) (equalsOpCode ScriptOpCodes/OP_0)))
                (§ block
                    (Preconditions/checkArgument __hasMissingSigs, "ScriptSig is already filled with signatures")

                    ;; copy the prefix
                    (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
                    (§ block
                        (§ for (§ var #_"ScriptChunk" __chunk) :for (.. __inputChunks (subList 0, __sigsPrefixCount)))
                        (§ block
                            (.. __builder (addChunk __chunk))
                        )

                        ;; Copy the sigs.
                        (§ let #_"int" __pos 0)
                        (§ block
                            (§ let #_"boolean" __inserted false)
                            (§ block
                                (§ for (§ var #_"ScriptChunk" __chunk) :for (.. __inputChunks (subList __sigsPrefixCount, (- __totalChunks __sigsSuffixCount))))
                                (§ block
                                    (§ if (== __pos __targetIndex))
                                    (§ block
                                        (§ ass __inserted true)
                                        (.. __builder (data __signature))
                                        (§ ass __pos (inc __pos))
                                    )
                                    (§ if (not (.. __chunk (equalsOpCode ScriptOpCodes/OP_0))))
                                    (§ block
                                        (.. __builder (addChunk __chunk))
                                        (§ ass __pos (inc __pos))
                                    )
                                )

                                ;; Add OP_0's if needed, since we skipped them in the previous loop.
                                (§ while (< __pos (- __totalChunks __sigsPrefixCount __sigsSuffixCount)))
                                (§ block
                                    (§ if (== __pos __targetIndex))
                                    (§ block
                                        (§ ass __inserted true)
                                        (.. __builder (data __signature))
                                    )
                                    (§ else )
                                    (§ block
                                        (.. __builder (addChunk (§ new #_"ScriptChunk" ScriptOpCodes/OP_0, nil)))
                                    )
                                    (§ ass __pos (inc __pos))
                                )

                                ;; Copy the suffix.
                                (§ for (§ var #_"ScriptChunk" __chunk) :for (.. __inputChunks (subList (- __totalChunks __sigsSuffixCount), __totalChunks)))
                                (§ block
                                    (.. __builder (addChunk __chunk))
                                )

                                (Preconditions/checkState __inserted)
                                (.. __builder (build))
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Creates a scriptPubKey that sends to the given script hash.  Read
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0016.mediawiki">BIP 16</a>
     ; to learn more about this kind of script.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createP2SHOutputScript) [#_"byte[]" __hash])
    (§ block
        (Preconditions/checkArgument (== (.. __hash length) 20))

        (.. (§ new #_"ScriptBuilder") (op ScriptOpCodes/OP_HASH160) (data __hash) (op ScriptOpCodes/OP_EQUAL) (build))
    )

    ;;;
     ; Creates a scriptPubKey for the given redeem script.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createP2SHOutputScript) [#_"Script" __redeemScript])
    (§ block
        (§ let #_"byte[]" __hash (Utils/sha256hash160 (.. __redeemScript (getProgram))))
        (§ block
            (ScriptBuilder/createP2SHOutputScript __hash)
        )
    )

    ;;;
     ; Creates a P2SH output script with given public keys and threshold.
     ; Given public keys will be placed in redeem script in the lexicographical sorting order.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createP2SHOutputScript) [#_"int" __threshold, #_"List<ECKey>" __pubkeys])
    (§ block
        (§ let #_"Script" __redeemScript (ScriptBuilder/createRedeemScript __threshold, __pubkeys))
        (§ block
            (ScriptBuilder/createP2SHOutputScript __redeemScript)
        )
    )

    ;;;
     ; Creates redeem script with given public keys and threshold.
     ; Given public keys will be placed in redeem script in the lexicographical sorting order.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createRedeemScript) [#_"int" __threshold, #_"List<ECKey>" __pubkeys])
    (§ block
        (§ ass __pubkeys (§ new #_"ArrayList<>" __pubkeys))
        (Collections/sort __pubkeys, ECKey/PUBKEY_COMPARATOR)
        (ScriptBuilder/createMultiSigOutputScript __threshold, __pubkeys)
    )

    ;;;
     ; Creates a script of the form OP_RETURN [data].  This feature allows you to attach
     ; a small piece of data (like a hash of something stored elsewhere) to a zero valued
     ; output which can never be spent and thus does not pollute the ledger.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createOpReturnScript) [#_"byte[]" __data])
    (§ block
        (Preconditions/checkArgument (<= (.. __data length) 80))

        (.. (§ new #_"ScriptBuilder") (op ScriptOpCodes/OP_RETURN) (data __data) (build))
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelOutput) [#_"BigInteger" __time, #_"ECKey" __from, #_"ECKey" __to])
    (§ block
        (§ let #_"byte[]" __timeBytes (Utils/reverseBytes (Utils/encodeMPI __time, false)))
        (§ block
            (§ if (< 5 (.. __timeBytes length)))
            (§ block
                (§ throw (§ new #_"RuntimeException" "Time too large to encode as 5-byte int"))
            )

            (.. (§ new #_"ScriptBuilder") (op ScriptOpCodes/OP_IF) (data (.. __to (getPubKey))) (op ScriptOpCodes/OP_CHECKSIGVERIFY) (op ScriptOpCodes/OP_ELSE) (data __timeBytes) (op ScriptOpCodes/OP_CHECKLOCKTIMEVERIFY) (op ScriptOpCodes/OP_DROP) (op ScriptOpCodes/OP_ENDIF) (data (.. __from (getPubKey))) (op ScriptOpCodes/OP_CHECKSIG) (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelRefund) [#_"TransactionSignature" __signature])
    (§ block
        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (data (.. __signature (encodeToBitcoin))))
            (.. __builder (data (§ new #_"byte[]" (§ coll 0 )))) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. __builder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelP2SHRefund) [#_"TransactionSignature" __signature, #_"Script" __redeemScript])
    (§ block
        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (data (.. __signature (encodeToBitcoin))))
            (.. __builder (data (§ new #_"byte[]" (§ coll 0 )))) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. __builder (data (.. __redeemScript (getProgram))))
            (.. __builder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelP2SHInput) [#_"byte[]" __from, #_"byte[]" __to, #_"Script" __redeemScript])
    (§ block
        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (data __from))
            (.. __builder (data __to))
            (.. __builder (smallNum 1)) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. __builder (data (.. __redeemScript (getProgram))))
            (.. __builder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelInput) [#_"TransactionSignature" __from, #_"TransactionSignature" __to])
    (§ block
        (ScriptBuilder/createCLTVPaymentChannelInput (.. __from (encodeToBitcoin)), (.. __to (encodeToBitcoin)))
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn ScriptBuilder/createCLTVPaymentChannelInput) [#_"byte[]" __from, #_"byte[]" __to])
    (§ block
        (§ let #_"ScriptBuilder" __builder (§ new #_"ScriptBuilder"))
        (§ block
            (.. __builder (data __from))
            (.. __builder (data __to))
            (.. __builder (smallNum 1)) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. __builder (build))
        )
    )
)

#_(ns org.bitcoinj.script #_"ScriptChunk"
    (:import [java.io IOException OutputStream]
             [java.util Arrays])
    (:import [com.google.common.base Objects Preconditions])
   (:require [org.bitcoinj.core Utils]
             [org.bitcoinj.script ScriptOpCodes]))

;;;
 ; A script element that is either a data push (signature, pubkey, etc.) or a non-push (logic, numeric, etc.) operation.
 ;;
#_public
(§ class #_"ScriptChunk"
    ;;; Operation to be executed.  Opcodes are defined in {@link ScriptOpCodes}. ;;
    #_public
    (§ field #_"int" (§ name opcode))
    ;;;
     ; For push operations, this is the vector to be pushed on the stack.
     ; For {@link ScriptOpCodes#OP_0}, the vector is empty.
     ; Null for non-push operations.
     ;;
    #_nilable
    #_public
    (§ field #_"byte[]" (§ name data))
    #_private
    (§ field- #_"int" (§ name startLocationInProgram))

    #_public
    (§ constructor #_"ScriptChunk" [#_"int" __opcode, #_"byte[]" __data])
    (§ block
        (§ this __opcode, __data, -1)
        this
    )

    #_public
    (§ constructor #_"ScriptChunk" [#_"int" __opcode, #_"byte[]" __data, #_"int" __startLocationInProgram])
    (§ block
        (§ ass (§ name (.. this opcode)) __opcode)
        (§ ass (§ name (.. this data)) __data)
        (§ ass (§ name (.. this startLocationInProgram)) __startLocationInProgram)
        this
    )

    #_public
    (§ method #_"boolean" (§ fn equalsOpCode) [#_"int" __opcode])
    (§ block
        (== __opcode (.. this opcode))
    )

    ;;;
     ; If this chunk is a single byte of non-pushdata content (could be OP_RESERVED or some invalid Opcode).
     ;;
    #_public
    (§ method #_"boolean" (§ fn isOpCode) [])
    (§ block
        (< ScriptOpCodes/OP_PUSHDATA4 opcode)
    )

    ;;;
     ; Returns true if this chunk is pushdata content, including the single-byte pushdatas.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPushData) [])
    (§ block
        (<= opcode ScriptOpCodes/OP_16)
    )

    #_public
    (§ method #_"int" (§ fn getStartLocationInProgram) [])
    (§ block
        (Preconditions/checkState (<= 0 startLocationInProgram))

        startLocationInProgram
    )

    ;;; If this chunk is an OP_N opcode returns the equivalent integer value. ;;
    #_public
    (§ method #_"int" (§ fn decodeOpN) [])
    (§ block
        (Preconditions/checkState (.. this (isOpCode)))

        (Script/decodeFromOpN opcode)
    )

    ;;;
     ; Called on a pushdata chunk, returns true if it uses the smallest possible way (according to BIP62) to push the data.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isShortestPossiblePushData) [])
    (§ block
        (Preconditions/checkState (.. this (isPushData)))

        (§ if (nil? data))
        (§ block
            (§ return true) ;; OP_N
        )
        (§ if (== (.. data length) 0))
        (§ block
            (§ return (== opcode ScriptOpCodes/OP_0))
        )
        (§ if (== (.. data length) 1))
        (§ block
            (§ let #_"byte" __b (§ ai data 0))
            (§ block
                (§ if (<= 0x01 __b 0x10))
                (§ block
                    (§ return (== opcode (dec (+ ScriptOpCodes/OP_1 __b))))
                )
                (§ if (== (& __b 0xff) 0x81))
                (§ block
                    (§ return (== opcode ScriptOpCodes/OP_1NEGATE))
                )
            )
        )
        (§ if (< (.. data length) ScriptOpCodes/OP_PUSHDATA1))
        (§ block
            (§ return (== opcode (.. data length)))
        )
        (§ if (< (.. data length) 256))
        (§ block
            (§ return (== opcode ScriptOpCodes/OP_PUSHDATA1))
        )
        (§ if (< (.. data length) 65536))
        (§ block
            (§ return (== opcode ScriptOpCodes/OP_PUSHDATA2))
        )

        ;; Can never be used, but implemented for completeness.
        (== opcode ScriptOpCodes/OP_PUSHDATA4)
    )

    #_public
    (§ method #_"void" (§ fn write) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        (§ if (.. this (isOpCode)))
        (§ block
            (Preconditions/checkState (nil? data))
            (.. __stream (write opcode))
        )
        (§ elseif (some? data))
        (§ block
            (§ if (< opcode ScriptOpCodes/OP_PUSHDATA1))
            (§ block
                (Preconditions/checkState (== (.. data length) opcode))
                (.. __stream (write opcode))
            )
            (§ elseif (== opcode ScriptOpCodes/OP_PUSHDATA1))
            (§ block
                (Preconditions/checkState (<= (.. data length) 0xff))
                (.. __stream (write ScriptOpCodes/OP_PUSHDATA1))
                (.. __stream (write (.. data length)))
            )
            (§ elseif (== opcode ScriptOpCodes/OP_PUSHDATA2))
            (§ block
                (Preconditions/checkState (<= (.. data length) 0xffff))
                (.. __stream (write ScriptOpCodes/OP_PUSHDATA2))
                (.. __stream (write (& 0xff (.. data length))))
                (.. __stream (write (& 0xff (>> (.. data length) 8))))
            )
            (§ elseif (== opcode ScriptOpCodes/OP_PUSHDATA4))
            (§ block
                (Preconditions/checkState (<= (.. data length) Script/MAX_SCRIPT_ELEMENT_SIZE))
                (.. __stream (write ScriptOpCodes/OP_PUSHDATA4))
                (Utils/uint32ToByteStreamLE (.. data length), __stream)
            )
            (§ else )
            (§ block
                (§ throw (§ new #_"RuntimeException" "Unimplemented"))
            )
            (.. __stream (write data))
        )
        (§ else )
        (§ block
            (.. __stream (write opcode)) ;; smallNum
        )
        nil
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder"))
        (§ block
            (§ if (.. this (isOpCode)))
            (§ block
                (.. __sb (append (ScriptOpCodes/getOpCodeName opcode)))
            )
            (§ elseif (some? data)) ;; Data chunk.
            (§ block
                (.. __sb (append (ScriptOpCodes/getPushDataName opcode)) (append "[") (append (.. Utils/HEX (encode data))) (append "]"))
            )
            (§ else ) ;; Small num.
            (§ block
                (.. __sb (append (Script/decodeFromOpN opcode)))
            )
            (.. __sb (toString))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"ScriptChunk" __other (§ cast #_"ScriptChunk" __o))
        (§ block
            (and (== opcode (.. __other opcode)) (== startLocationInProgram (.. __other startLocationInProgram)) (Arrays/equals data, (.. __other data)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode opcode, startLocationInProgram, (Arrays/hashCode data))
    )
)

