    (§ defn #_"String" (§ fn ScriptOpCodes/getOpCodeName) [#_"int" __opcode])
    (§ block
        (§ if (.. ScriptOpCodes/OP_CODE_MAP (containsKey __opcode)))
        (§ block
            (§ return (.. ScriptOpCodes/OP_CODE_MAP (get __opcode)))
        )

        (str "NON_OP(" __opcode ")")
    )

