            (§ block
                (§ return nil)
            )

            (§ if (or (nil? username) (nil? password)))
            (§ block
                (.. conn (set (DriverManager/getConnection connectionURL)))
            )
            (§ else )
            (§ block
                (§ let #_"Properties" __props (§ new #_"Properties"))
                (§ block
                    (.. __props (setProperty "user", (.. this username)))
                    (.. __props (setProperty "password", (.. this password)))
                    (.. conn (set (DriverManager/getConnection connectionURL, __props)))
                )
            )
            (.. allConnections (add (.. conn (get))))
            (§ let #_"Connection" __connection (.. conn (get)))
            (§ block
                ;; Set the schema if one is needed.
                (§ if (some? schemaName))
                (§ block
                    (§ let #_"Statement" __s (.. __connection (createStatement)))
                    (§ block
                        (§ for (§ var #_"String" __sql) :for (.. this (getCreateSchemeSQL)))
                        (§ block
                            (.. __s (execute __sql))
                        )
