    )

    ;;;
     ; Initialise the store state from the database.
     ; @throws java.sql.SQLException if there is a database error.
     ; @throws BlockStoreException if there is a block store error.
     ;;
    #_private
    (§ method- #_"void" (§ fn initFromDatabase) [])
        (§ throws #_"SQLException", #_"BlockStoreException")
    (§ block
        (§ let #_"PreparedStatement" __ps (.. conn (get) (prepareStatement (.. this (getSelectSettingsSQL)))))
        (§ block
            (§ let #_"ResultSet" __rs)
            (§ block
                (.. __ps (setString 1, DatabaseFullPrunedBlockStore/CHAIN_HEAD_SETTING))
                (§ ass __rs (.. __ps (executeQuery)))
                (§ if (not (.. __rs (next))))
                (§ block
                    (§ throw (§ new #_"BlockStoreException" "corrupt database block store - no chain head pointer"))
                )

                (§ let #_"Sha256Hash" __hash (Sha256Hash/wrap (.. __rs (getBytes 1))))
                (§ block
                    (.. __rs (close))
                    (§ ass (§ name (.. this chainHeadBlock)) (.. this (get __hash)))
                    (§ ass (§ name (.. this chainHeadHash)) __hash)
                    (§ if (== (.. this chainHeadBlock) nil))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - head block not found"))
                    )

                    (.. __ps (setString 1, DatabaseFullPrunedBlockStore/VERIFIED_CHAIN_HEAD_SETTING))
                    (§ ass __rs (.. __ps (executeQuery)))
                    (§ if (not (.. __rs (next))))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - no verified chain head pointer"))
                    )

                    (§ ass __hash (Sha256Hash/wrap (.. __rs (getBytes 1))))
                    (.. __rs (close))
                    (.. __ps (close))
                    (§ ass (§ name (.. this verifiedChainHeadBlock)) (.. this (get __hash)))
                    (§ ass (§ name (.. this verifiedChainHeadHash)) __hash)
                    (§ if (== (.. this verifiedChainHeadBlock) nil))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - verified head block not found"))
                    )
                    nil
                )
            )
        )
    )

    #_protected
    (§ method #_"void" (§ fn putUpdateStoredBlock) [#_"StoredBlock" __storedBlock, #_"boolean" __wasUndoable])
        (§ throws #_"SQLException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"PreparedStatement" __ps (.. conn (get) (prepareStatement (.. this (getInsertHeadersSQL)))))
            (§ block
                ;; We skip the first 4 bytes because (on mainnet) the minimum target has 4 0-bytes.
                (§ let #_"byte[]" __hashBytes (§ new #_"byte[]" (§ count 28)))
                (§ block
                    (System/arraycopy (.. __storedBlock (getHeader) (getHash) (getBytes)), 4, __hashBytes, 0, 28)
                    (.. __ps (setBytes 1, __hashBytes))
                    (.. __ps (setBytes 2, (.. __storedBlock (getChainWork) (toByteArray))))
                    (.. __ps (setInt 3, (.. __storedBlock (getHeight))))
                    (.. __ps (setBytes 4, (.. __storedBlock (getHeader) (cloneAsHeader) (unsafeBitcoinSerialize))))
                    (.. __ps (setBoolean 5, __wasUndoable))
                    (.. __ps (executeUpdate))
                    (.. __ps (close))
