            (§ let #_"PreparedStatement" __ps (.. conn (get) (prepareStatement (.. this (getUpdateHeadersSQL)))))
            (§ block
                (.. __ps (setBoolean 1, true))
                ;; We skip the first 4 bytes because (on mainnet) the minimum target has 4 0-bytes.
                (§ let #_"byte[]" __hashBytes (§ new #_"byte[]" (§ count 28)))
                (§ block
                    (System/arraycopy (.. __storedBlock (getHeader) (getHash) (getBytes)), 4, __hashBytes, 0, 28)
                    (.. __ps (setBytes 2, __hashBytes))
                    (.. __ps (executeUpdate))
                    (.. __ps (close))
                )
            )
        )
        nil
    )

