;;;
 ; A full pruned block store using the H2 pure-java embedded database.
 ;
 ; Note that because of the heavy delete load on the database, during IBD,
 ; you may see the database files grow quite large (around 1.5G).
 ; H2 automatically frees some space at shutdown, so close()ing the database
 ; decreases the space usage somewhat (to only around 1.3G).
 ;;
#_public
(§ class #_"H2FullPrunedBlockStore" (§ extends #_"DatabaseFullPrunedBlockStore")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/H2_DUPLICATE_KEY_ERROR_CODE) "23505")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/DATABASE_DRIVER_CLASS) "org.h2.Driver")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX) "jdbc:h2:")

    ;; create table SQL
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_SETTINGS_TABLE) (str "CREATE TABLE settings ("
              "name VARCHAR(32) NOT NULL CONSTRAINT settings_pk PRIMARY KEY, "
              "value BLOB"
              ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_HEADERS_TABLE) (str "CREATE TABLE headers ("
              "hash BINARY(28) NOT NULL CONSTRAINT headers_pk PRIMARY KEY, "
              "chainwork BLOB NOT NULL, "
              "height INT NOT NULL, "
              "header BLOB NOT NULL, "
              "wasundoable BOOL NOT NULL"
              ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_UNDOABLE_TABLE) (str "CREATE TABLE undoableblocks ("
              "hash BINARY(28) NOT NULL CONSTRAINT undoableblocks_pk PRIMARY KEY, "
              "height INT NOT NULL, "
              "txoutchanges BLOB, "
              "transactions BLOB"
              ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_OPEN_OUTPUT_TABLE) (str "CREATE TABLE openoutputs ("
              "hash BINARY(32) NOT NULL, "
              "index INT NOT NULL, "
              "height INT NOT NULL, "
              "value BIGINT NOT NULL, "
              "scriptbytes BLOB NOT NULL, "
              "toaddress VARCHAR(35), "
              "addresstargetable TINYINT, "
              "coinbase BOOLEAN, "
              "PRIMARY KEY (hash, index)"
              ")"))

    ;; Some indexes to speed up inserts.
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_OUTPUTS_ADDRESS_MULTI_INDEX) "CREATE INDEX openoutputs_hash_index_height_toaddress_idx ON openoutputs (hash, index, height, toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_OUTPUTS_TOADDRESS_INDEX) "CREATE INDEX openoutputs_toaddress_idx ON openoutputs (toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX) "CREATE INDEX openoutputs_addresstargetable_idx ON openoutputs (addresstargetable)")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_OUTPUTS_HASH_INDEX) "CREATE INDEX openoutputs_hash_idx ON openoutputs (hash)")
    #_private
    #_static
    (§ def- #_"String" (§ name H2FullPrunedBlockStore/CREATE_UNDOABLE_TABLE_INDEX) "CREATE INDEX undoableblocks_height_idx ON undoableblocks (height)")

    ;;;
     ; Creates a new H2FullPrunedBlockStore, with given credentials for H2 database.
     ; @param params A copy of the NetworkParameters used.
     ; @param dbName The path to the database on disk.
     ; @param username The username to use in the database.
     ; @param password The username's password to use in the database.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"H2FullPrunedBlockStore" [#_"NetworkParameters" __params, #_"String" __dbName, #_"String" __username, #_"String" __password, #_"int" __fullStoreDepth])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super __params, (str H2FullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX __dbName ";create=true;LOCK_TIMEOUT=60000;DB_CLOSE_ON_EXIT=FALSE"), __fullStoreDepth, __username, __password, nil)
        this
    )

    ;;;
     ; Creates a new H2FullPrunedBlockStore.
     ; @param params A copy of the NetworkParameters used.
     ; @param dbName The path to the database on disk.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"H2FullPrunedBlockStore" [#_"NetworkParameters" __params, #_"String" __dbName, #_"int" __fullStoreDepth])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this __params, __dbName, nil, nil, __fullStoreDepth)
        this
    )

    ;;;
     ; Creates a new H2FullPrunedBlockStore with the given cache size.
     ; @param params A copy of the NetworkParameters used.
     ; @param dbName The path to the database on disk.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param cacheSize The number of kilobytes to dedicate to H2 Cache.  The default value of 16MB (16384) is a safe bet
     ;                  to achieve good performance/cost when importing blocks from disk, past 32MB makes little sense,
     ;                  and below 4MB sees a sharp drop in performance.
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"H2FullPrunedBlockStore" [#_"NetworkParameters" __params, #_"String" __dbName, #_"int" __fullStoreDepth, #_"int" __cacheSize])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this __params, __dbName, __fullStoreDepth)

        (§ try )
        (§ block
            (§ let #_"Statement" __s (.. conn (get) (createStatement)))
            (§ block
                (.. __s (executeUpdate (str "SET CACHE_SIZE " __cacheSize)))
                (.. __s (close))
            )
        )
        (§ catch #_"SQLException" __e)
        (§ block
            (§ throw (§ new #_"BlockStoreException" __e))
        )
        this
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDuplicateKeyErrorCode) [])
    (§ block
        H2FullPrunedBlockStore/H2_DUPLICATE_KEY_ERROR_CODE
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateTablesSQL) [])
    (§ block
        (§ let #_"List<String>" __sql (§ new #_"ArrayList<>"))
        (§ block
            (.. __sql (add H2FullPrunedBlockStore/CREATE_SETTINGS_TABLE))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_HEADERS_TABLE))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_UNDOABLE_TABLE))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_OPEN_OUTPUT_TABLE))
            __sql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateIndexesSQL) [])
    (§ block
        (§ let #_"List<String>" __sql (§ new #_"ArrayList<>"))
        (§ block
            (.. __sql (add H2FullPrunedBlockStore/CREATE_UNDOABLE_TABLE_INDEX))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_OUTPUTS_ADDRESS_MULTI_INDEX))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_OUTPUTS_HASH_INDEX))
            (.. __sql (add H2FullPrunedBlockStore/CREATE_OUTPUTS_TOADDRESS_INDEX))
            __sql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateSchemeSQL) [])
    (§ block
        ;; do nothing
        (Collections/emptyList)
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDatabaseDriverClass) [])
    (§ block
        H2FullPrunedBlockStore/DATABASE_DRIVER_CLASS
    )
)

#_(ns org.bitcoinj.store #_"MemoryBlockStore"
    (:import [java.util LinkedHashMap Map])
   (:require [org.bitcoinj.core *]))

;;;
 ; Keeps {@link org.bitcoinj.core.StoredBlock}s in memory.  Used primarily for unit testing.
 ;;
#_public
(§ class #_"MemoryBlockStore" (§ implements #_"BlockStore")
    #_private
    (§ field- #_"LinkedHashMap<Sha256Hash, StoredBlock>" (§ name blockMap) (§ new #_"LinkedHashMap<Sha256Hash, StoredBlock>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, StoredBlock>" __eldest])
            (§ block
                (< 5000 (.. blockMap (size)))
            )
        )))
    #_private
    (§ field- #_"StoredBlock" (§ name chainHead))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))

    #_public
    (§ constructor #_"MemoryBlockStore" [#_"NetworkParameters" __params])
    (§ block
        ;; Insert the genesis block.
        (§ try )
        (§ block
            (§ let #_"Block" __genesisHeader (.. __params (getGenesisBlock) (cloneAsHeader)))
            (§ block
                (§ let #_"StoredBlock" __storedGenesis (§ new #_"StoredBlock" __genesisHeader, (.. __genesisHeader (getWork)), 0))
                (§ block
                    (.. this (put __storedGenesis))
                    (.. this (setChainHead __storedGenesis))
                    (§ ass (§ name (.. this params)) __params)
                )
            )
        )
        (§ catch #_"BlockStoreException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
        (§ catch #_"VerificationException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
        this
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" __block])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (§ let #_"Sha256Hash" __hash (.. __block (getHeader) (getHash)))
        (§ block
            (.. blockMap (put __hash, __block))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (.. blockMap (get __hash))
    )

    #_override
    #_public
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        chainHead
    )

    #_override
    #_public
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" __chainHead])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ if (nil? blockMap))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "MemoryBlockStore is closed"))
        )

        (§ ass (§ name (.. this chainHead)) __chainHead)
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (§ ass (§ name blockMap) nil)
        nil
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )
)

#_(ns org.bitcoinj.store #_"MemoryFullPrunedBlockStore"
    (:import [java.util *])
    (:import [com.google.common.base Objects Preconditions]
             [com.google.common.collect Lists])
   (:require [org.bitcoinj.core *]))

;;;
 ; Used as a key for memory map (to avoid having to think about NetworkParameters,
 ; which is required for {@link TransactionOutPoint}.
 ;;
(§ class #_"StoredTransactionOutPoint"
    ;;; Hash of the transaction to which we refer. ;;
    (§ field #_"Sha256Hash" (§ name hash))
    ;;; Which output of that transaction we are talking about. ;;
    (§ field #_"long" (§ name index))

    (§ constructor #_"StoredTransactionOutPoint" [#_"Sha256Hash" __hash, #_"long" __index])
    (§ block
        (§ ass (§ name (.. this hash)) __hash)
        (§ ass (§ name (.. this index)) __index)
        this
    )

    (§ constructor #_"StoredTransactionOutPoint" [#_"UTXO" __out])
    (§ block
        (§ ass (§ name (.. this hash)) (.. __out (getHash)))
        (§ ass (§ name (.. this index)) (.. __out (getIndex)))
        this
    )

    ;;;
     ; The hash of the transaction to which we refer.
     ;;
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        hash
    )

    ;;;
     ; The index of the output in transaction to which we refer.
     ;;
    (§ method #_"long" (§ fn getIndex) [])
    (§ block
        index
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (.. this (getIndex)), (.. this (getHash)))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "Stored transaction out point: " hash ":" index)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"StoredTransactionOutPoint" __other (§ cast #_"StoredTransactionOutPoint" __o))
        (§ block
            (and (== (.. this (getIndex)) (.. __other (getIndex))) (Objects/equal (.. this (getHash)), (.. __other (getHash))))
        )
    )
)

;;;
 ; A HashMap<KeyType, ValueType> that is DB transaction-aware.
 ; This class is not thread-safe.
 ;;
(§ class #_"TransactionalHashMap<KeyType, ValueType>"
    (§ field #_"ThreadLocal<HashMap<KeyType, ValueType>>" (§ name tempMap))
    (§ field #_"ThreadLocal<HashSet<KeyType>>" (§ name tempSetRemoved))
    #_private
    (§ field- #_"ThreadLocal<Boolean>" (§ name inTransaction))

    (§ field #_"HashMap<KeyType, ValueType>" (§ name map))

    #_public
    (§ constructor #_"TransactionalHashMap" [])
    (§ block
        (§ ass (§ name tempMap) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name tempSetRemoved) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name inTransaction) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name map) (§ new #_"HashMap<>"))
        this
    )

    #_public
    (§ method #_"void" (§ fn beginDatabaseBatchWrite) [])
    (§ block
        (.. inTransaction (set true))
        nil
    )

    #_public
    (§ method #_"void" (§ fn commitDatabaseBatchWrite) [])
    (§ block
        (§ if (!= (.. tempSetRemoved (get)) nil))
        (§ block
            (§ for (§ var #_"KeyType" __key) :for (.. tempSetRemoved (get)))
            (§ block
                (.. map (remove __key))
            )
        )
        (§ if (!= (.. tempMap (get)) nil))
        (§ block
            (§ for (§ var #_"Map.Entry<KeyType, ValueType>" __entry) :for (.. tempMap (get) (entrySet)))
            (§ block
                (.. map (put (.. __entry (getKey)), (.. __entry (getValue))))
            )
        )
        (.. this (abortDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" (§ fn abortDatabaseBatchWrite) [])
    (§ block
        (.. inTransaction (set false))
        (.. tempSetRemoved (remove))
        (.. tempMap (remove))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn get) [#_"KeyType" __key])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ if (!= (.. tempMap (get)) nil))
            (§ block
                (§ let #_"ValueType" __value (.. tempMap (get) (get __key)))
                (§ block
                    (§ if (some? __value))
                    (§ block
                        (§ return __value)
                    )
                )
            )
            (§ if (and (some? (.. tempSetRemoved (get))) (.. tempSetRemoved (get) (contains __key))))
            (§ block
                (§ return nil)
            )
        )
        (.. map (get __key))
    )

    #_public
    (§ method #_"List<ValueType>" (§ fn values) [])
    (§ block
        (§ let #_"List<ValueType>" __valueTypes (§ new #_"ArrayList<>"))
        (§ block
            (§ for (§ var #_"KeyType" __keyType) :for (.. map (keySet)))
            (§ block
                (.. __valueTypes (add (.. this (get __keyType))))
            )
            __valueTypes
        )
    )

    #_public
    (§ method #_"void" (§ fn put) [#_"KeyType" __key, #_"ValueType" __value])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ if (!= (.. tempSetRemoved (get)) nil))
            (§ block
                (.. tempSetRemoved (get) (remove __key))
            )
            (§ if (== (.. tempMap (get)) nil))
            (§ block
                (.. tempMap (set (§ new #_"HashMap<KeyType, ValueType>")))
            )
            (.. tempMap (get) (put __key, __value))
        )
        (§ else )
        (§ block
            (.. map (put __key, __value))
        )
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn remove) [#_"KeyType" __key])
    (§ block
        (§ if (.. Boolean/TRUE (equals (.. inTransaction (get)))))
        (§ block
            (§ let #_"ValueType" __retVal (.. map (get __key)))
            (§ block
                (§ if (some? __retVal))
                (§ block
                    (§ if (== (.. tempSetRemoved (get)) nil))
                    (§ block
                        (.. tempSetRemoved (set (§ new #_"HashSet<KeyType>")))
                    )
                    (.. tempSetRemoved (get) (add __key))
                )
                (§ if (!= (.. tempMap (get)) nil))
                (§ block
                    (§ let #_"ValueType" __tempVal (.. tempMap (get) (remove __key)))
                    (§ block
                        (§ if (some? __tempVal))
                        (§ block
                            (§ return __tempVal)
                        )
                    )
                )
                (§ return __retVal)
            )
        )
        (§ else )
        (§ block
            (§ return (.. map (remove __key)))
        )
    )
)

;;;
 ; A Map with multiple key types that is DB per-thread-transaction-aware.
 ; However, this class is not thread-safe.
 ; @param <UniqueKeyType> Is a key that must be unique per object.
 ; @param <MultiKeyType> Is a key that can have multiple values.
 ;;
(§ class #_"TransactionalMultiKeyHashMap<UniqueKeyType, MultiKeyType, ValueType>"
    (§ field #_"TransactionalHashMap<UniqueKeyType, ValueType>" (§ name mapValues))
    (§ field #_"HashMap<MultiKeyType, Set<UniqueKeyType>>" (§ name mapKeys))

    #_public
    (§ constructor #_"TransactionalMultiKeyHashMap" [])
    (§ block
        (§ ass (§ name mapValues) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name mapKeys) (§ new #_"HashMap<>"))
        this
    )

    #_public
    (§ method #_"void" (§ fn beginTransaction) [])
    (§ block
        (.. mapValues (beginDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" (§ fn commitTransaction) [])
    (§ block
        (.. mapValues (commitDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" (§ fn abortTransaction) [])
    (§ block
        (.. mapValues (abortDatabaseBatchWrite))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn get) [#_"UniqueKeyType" __key])
    (§ block
        (.. mapValues (get __key))
    )

    #_public
    (§ method #_"void" (§ fn put) [#_"UniqueKeyType" __uniqueKey, #_"MultiKeyType" __multiKey, #_"ValueType" __value])
    (§ block
        (.. mapValues (put __uniqueKey, __value))
        (§ let #_"Set<UniqueKeyType>" __set (.. mapKeys (get __multiKey)))
        (§ block
            (§ if (nil? __set))
            (§ block
                (§ ass __set (§ new #_"HashSet<>"))
                (.. __set (add __uniqueKey))
                (.. mapKeys (put __multiKey, __set))
            )
            (§ else )
            (§ block
                (.. __set (add __uniqueKey))
            )
            nil
        )
    )

    #_nilable
    #_public
    (§ method #_"ValueType" (§ fn removeByUniqueKey) [#_"UniqueKeyType" __key])
    (§ block
        (.. mapValues (remove __key))
    )

    #_public
    (§ method #_"void" (§ fn removeByMultiKey) [#_"MultiKeyType" __key])
    (§ block
        (§ let #_"Set<UniqueKeyType>" __set (.. mapKeys (remove __key)))
        (§ block
            (§ if (some? __set))
            (§ block
                (§ for (§ var #_"UniqueKeyType" __uniqueKey) :for __set)
                (§ block
                    (.. this (removeByUniqueKey __uniqueKey))
                )
            )
            nil
        )
    )
)

;;;
 ; Keeps {@link StoredBlock}s, {@link StoredUndoableBlock}s and {@link org.bitcoinj.core.UTXO}s in memory.
 ; Used primarily for unit testing.
 ;;
#_public
(§ class #_"MemoryFullPrunedBlockStore" (§ implements #_"FullPrunedBlockStore")
    #_protected
    #_static
    (§ class #_"StoredBlockAndWasUndoableFlag"
        #_public
        (§ field #_"StoredBlock" (§ name block))
        #_public
        (§ field #_"boolean" (§ name wasUndoable))
        #_public
        (§ constructor #_"StoredBlockAndWasUndoableFlag" [#_"StoredBlock" __block, #_"boolean" __wasUndoable])
        (§ block
            (§ ass (§ name (.. this block)) __block)
            (§ ass (§ name (.. this wasUndoable)) __wasUndoable)
            this
        )
    )
    #_private
    (§ field- #_"TransactionalHashMap<Sha256Hash, StoredBlockAndWasUndoableFlag>" (§ name blockMap))
    #_private
    (§ field- #_"TransactionalMultiKeyHashMap<Sha256Hash, Integer, StoredUndoableBlock>" (§ name fullBlockMap))
    ;; TODO: Use something more suited to remove-heavy use?
    #_private
    (§ field- #_"TransactionalHashMap<StoredTransactionOutPoint, UTXO>" (§ name transactionOutputMap))
    #_private
    (§ field- #_"StoredBlock" (§ name chainHead))
    #_private
    (§ field- #_"StoredBlock" (§ name verifiedChainHead))
    #_private
    (§ field- #_"int" (§ name fullStoreDepth))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))

    ;;;
     ; Set up the MemoryFullPrunedBlockStore.
     ; @param params The network parameters of this block store - used to get genesis block.
     ; @param fullStoreDepth The depth of blocks to keep FullStoredBlocks instead of StoredBlocks.
     ;;
    #_public
    (§ constructor #_"MemoryFullPrunedBlockStore" [#_"NetworkParameters" __params, #_"int" __fullStoreDepth])
    (§ block
        (§ ass (§ name blockMap) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name fullBlockMap) (§ new #_"TransactionalMultiKeyHashMap<>"))
        (§ ass (§ name transactionOutputMap) (§ new #_"TransactionalHashMap<>"))
        (§ ass (§ name (.. this fullStoreDepth)) (if (< 0 __fullStoreDepth) __fullStoreDepth 1))
        ;; Insert the genesis block.
        (§ try )
        (§ block
            (§ let #_"StoredBlock" __storedGenesisHeader (§ new #_"StoredBlock" (.. __params (getGenesisBlock) (cloneAsHeader)), (.. __params (getGenesisBlock) (getWork)), 0))
            (§ block
                ;; The coinbase in the genesis block is not spendable.
                (§ let #_"List<Transaction>" __genesisTransactions (Lists/newLinkedList))
                (§ block
                    (§ let #_"StoredUndoableBlock" __storedGenesis (§ new #_"StoredUndoableBlock" (.. __params (getGenesisBlock) (getHash)), __genesisTransactions))
                    (§ block
                        (.. this (put __storedGenesisHeader, __storedGenesis))
                        (.. this (setChainHead __storedGenesisHeader))
                        (.. this (setVerifiedChainHead __storedGenesisHeader))
                        (§ ass (§ name (.. this params)) __params)
                    )
                )
            )
        )
        (§ catch #_"BlockStoreException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
        (§ catch #_"VerificationException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
        )
        this
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" __block])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"Sha256Hash" __hash (.. __block (getHeader) (getHash)))
        (§ block
            (.. blockMap (put __hash, (§ new #_"StoredBlockAndWasUndoableFlag" __block, false)))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn put) [#_"StoredBlock" __storedBlock, #_"StoredUndoableBlock" __undoableBlock])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"Sha256Hash" __hash (.. __storedBlock (getHeader) (getHash)))
        (§ block
            (.. fullBlockMap (put __hash, (.. __storedBlock (getHeight)), __undoableBlock))
            (.. blockMap (put __hash, (§ new #_"StoredBlockAndWasUndoableFlag" __storedBlock, true)))
            nil
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"StoredBlockAndWasUndoableFlag" __storedBlock (.. blockMap (get __hash)))
        (§ block
            (when (some? __storedBlock) (.. __storedBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getOnceUndoableStoredBlock) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ let #_"StoredBlockAndWasUndoableFlag" __storedBlock (.. blockMap (get __hash)))
        (§ block
            (when (and (some? __storedBlock) (.. __storedBlock wasUndoable)) (.. __storedBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"StoredUndoableBlock" (§ fn getUndoBlock) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull fullBlockMap, "MemoryFullPrunedBlockStore is closed")
        (.. fullBlockMap (get __hash))
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        chainHead
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" __chainHead])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ ass (§ name (.. this chainHead)) __chainHead)
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"StoredBlock" (§ fn getVerifiedChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        verifiedChainHead
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setVerifiedChainHead) [#_"StoredBlock" __chainHead])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull blockMap, "MemoryFullPrunedBlockStore is closed")
        (§ ass (§ name (.. this verifiedChainHead)) __chainHead)
        (§ if (< (.. this chainHead (getHeight)) (.. __chainHead (getHeight))))
        (§ block
            (.. this (setChainHead __chainHead))
        )
        ;; Potential leak here if not all blocks get setChainHead'd.
        ;; Though the FullPrunedBlockStore allows for this, the current AbstractBlockChain will not do it.
        (.. fullBlockMap (removeByMultiKey (- (.. __chainHead (getHeight)) fullStoreDepth)))
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (§ ass (§ name blockMap) nil)
        (§ ass (§ name fullBlockMap) nil)
        (§ ass (§ name transactionOutputMap) nil)
        nil
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    (§ method #_"UTXO" (§ fn getTransactionOutput) [#_"Sha256Hash" __hash, #_"long" __index])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (.. transactionOutputMap (get (§ new #_"StoredTransactionOutPoint" __hash, __index)))
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn addUnspentTransactionOutput) [#_"UTXO" __out])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (.. transactionOutputMap (put (§ new #_"StoredTransactionOutPoint" __out), __out))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn removeUnspentTransactionOutput) [#_"UTXO" __out])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull transactionOutputMap, "MemoryFullPrunedBlockStore is closed")
        (§ if (== (.. transactionOutputMap (remove (§ new #_"StoredTransactionOutPoint" __out))) nil))
        (§ block
            (§ throw (§ new #_"BlockStoreException" "Tried to remove a UTXO from MemoryFullPrunedBlockStore that it didn't have!"))
        )
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn beginDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (beginDatabaseBatchWrite))
        (.. fullBlockMap (beginTransaction))
        (.. transactionOutputMap (beginDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn commitDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (commitDatabaseBatchWrite))
        (.. fullBlockMap (commitTransaction))
        (.. transactionOutputMap (commitDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn abortDatabaseBatchWrite) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockMap (abortDatabaseBatchWrite))
        (.. fullBlockMap (abortTransaction))
        (.. transactionOutputMap (abortDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn hasUnspentOutputs) [#_"Sha256Hash" __hash, #_"int" __numOutputs])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ for (§ var #_"int" __i 0) :for (< __i __numOutputs) :for (§ recur (inc __i)))
        (§ block
            (§ if (!= (.. this (getTransactionOutput __hash, __i)) nil))
            (§ block
                (§ return true)
            )
        )
        false
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )
)

#_(ns org.bitcoinj.store #_"PostgresFullPrunedBlockStore"
    (:import [java.io ByteArrayOutputStream IOException]
             [java.sql PreparedStatement ResultSet SQLException Types]
             [java.util ArrayList List])
    (:import [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]))

;;;
 ; <p>A full pruned block store using the Postgres database engine.  As an added bonus an address index is calculated,
 ; so you can use {@link #calculateBalanceForAddress(org.bitcoinj.core.Address)} to quickly look up
 ; the quantity of bitcoins controlled by that address.</p>
 ;;
#_public
(§ class #_"PostgresFullPrunedBlockStore" (§ extends #_"DatabaseFullPrunedBlockStore")
    #_private
    #_static
    (§ def- #_"Logger" (§ name PostgresFullPrunedBlockStore/log) (LoggerFactory/getLogger (§ klass #_"PostgresFullPrunedBlockStore")))

    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/POSTGRES_DUPLICATE_KEY_ERROR_CODE) "23505")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/DATABASE_DRIVER_CLASS) "org.postgresql.Driver")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX) "jdbc:postgresql://")

    ;; create table SQL
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_SETTINGS_TABLE) (str "CREATE TABLE settings ("
            "name character varying(32) NOT NULL, "
            "value bytea, "
            "CONSTRAINT setting_pk PRIMARY KEY (name)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_HEADERS_TABLE) (str "CREATE TABLE headers ("
            "hash bytea NOT NULL, "
            "chainwork bytea NOT NULL, "
            "height integer NOT NULL, "
            "header bytea NOT NULL, "
            "wasundoable boolean NOT NULL, "
            "CONSTRAINT headers_pk PRIMARY KEY (hash)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE) (str "CREATE TABLE undoableblocks ("
            "hash bytea NOT NULL, "
            "height integer NOT NULL, "
            "txoutchanges bytea, "
            "transactions bytea, "
            "CONSTRAINT undoableblocks_pk PRIMARY KEY (hash)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_OPEN_OUTPUT_TABLE) (str "CREATE TABLE openoutputs ("
            "hash bytea NOT NULL, "
            "index integer NOT NULL, "
            "height integer NOT NULL, "
            "value bigint NOT NULL, "
            "scriptbytes bytea NOT NULL, "
            "toaddress character varying(35), "
            "addresstargetable smallint, "
            "coinbase boolean, "
            "CONSTRAINT openoutputs_pk PRIMARY KEY (hash,index)"
            ")"))

    ;; Some indexes to speed up inserts.
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESS_MULTI_INDEX) "CREATE INDEX openoutputs_hash_index_num_height_toaddress_idx ON openoutputs USING btree (hash, index, height, toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_OUTPUTS_TOADDRESS_INDEX) "CREATE INDEX openoutputs_toaddress_idx ON openoutputs USING btree (toaddress)")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX) "CREATE INDEX openoutputs_addresstargetable_idx ON openoutputs USING btree (addresstargetable)")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_OUTPUTS_HASH_INDEX) "CREATE INDEX openoutputs_hash_idx ON openoutputs USING btree (hash)")
    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE_INDEX) "CREATE INDEX undoableblocks_height_idx ON undoableBlocks USING btree (height)")

    #_private
    #_static
    (§ def- #_"String" (§ name PostgresFullPrunedBlockStore/SELECT_UNDOABLEBLOCKS_EXISTS_SQL) "select 1 from undoableblocks where hash = ?")

    ;;;
     ; Creates a new PostgresFullPrunedBlockStore.
     ;
     ; @param params A copy of the NetworkParameters used.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param hostname The hostname of the database to connect to.
     ; @param dbName The database to connect to.
     ; @param username The database username.
     ; @param password The password to the database.
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"PostgresFullPrunedBlockStore" [#_"NetworkParameters" __params, #_"int" __fullStoreDepth, #_"String" __hostname, #_"String" __dbName, #_"String" __username, #_"String" __password])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super __params, (str PostgresFullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX __hostname "/" __dbName), __fullStoreDepth, __username, __password, nil)
        this
    )

    ;;;
     ; <p>Create a new PostgresFullPrunedBlockStore, storing the tables in the schema specified.  You may want to
     ; specify a schema to avoid name collisions, or just to keep the database better organized.  The schema is not
     ; required, and if one is not provided than the default schema for the username will be used.  See
     ; <a href="http://www.postgres.org/docs/9.3/static/ddl-schemas.html">the postgres schema docs</a>
     ; for more on schemas.</p>
     ;
     ; @param params A copy of the NetworkParameters used.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param hostname The hostname of the database to connect to.
     ; @param dbName The database to connect to.
     ; @param username The database username.
     ; @param password The password to the database.
     ; @param schemaName The name of the schema to put the tables in.  May be null if no schema is being used.
     ; @throws BlockStoreException if the database fails to open for any reason.
     ;;
    #_public
    (§ constructor #_"PostgresFullPrunedBlockStore" [#_"NetworkParameters" __params, #_"int" __fullStoreDepth, #_"String" __hostname, #_"String" __dbName, #_"String" __username, #_"String" __password, #_nilable #_"String" __schemaName])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super __params, (str PostgresFullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX __hostname "/" __dbName), __fullStoreDepth, __username, __password, __schemaName)
        this
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDuplicateKeyErrorCode) [])
    (§ block
        PostgresFullPrunedBlockStore/POSTGRES_DUPLICATE_KEY_ERROR_CODE
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateTablesSQL) [])
    (§ block
        (§ let #_"List<String>" __sql (§ new #_"ArrayList<>"))
        (§ block
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_SETTINGS_TABLE))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_HEADERS_TABLE))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_OPEN_OUTPUT_TABLE))
            __sql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateIndexesSQL) [])
    (§ block
        (§ let #_"List<String>" __sql (§ new #_"ArrayList<>"))
        (§ block
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE_INDEX))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESS_MULTI_INDEX))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_OUTPUTS_HASH_INDEX))
            (.. __sql (add PostgresFullPrunedBlockStore/CREATE_OUTPUTS_TOADDRESS_INDEX))
            __sql
        )
    )

    #_override
    #_protected
    (§ method #_"List<String>" (§ fn getCreateSchemeSQL) [])
    (§ block
        (§ let #_"List<String>" __sql (§ new #_"ArrayList<>"))
        (§ block
            (.. __sql (add (str "CREATE SCHEMA IF NOT EXISTS " schemaName)))
            (.. __sql (add (str "set search_path to '" schemaName "'")))
            __sql
        )
    )

    #_override
    #_protected
    (§ method #_"String" (§ fn getDatabaseDriverClass) [])
    (§ block
        PostgresFullPrunedBlockStore/DATABASE_DRIVER_CLASS
    )

    #_override
    #_public
    (§ method #_"void" (§ fn put) [#_"StoredBlock" __storedBlock, #_"StoredUndoableBlock" __undoableBlock])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. this (maybeConnect))
        ;; We skip the first 4 bytes because (on mainnet) the minimum target has 4 0-bytes.
        (§ let #_"byte[]" __hashBytes (§ new #_"byte[]" (§ count 28)))
        (§ block
            (System/arraycopy (.. __storedBlock (getHeader) (getHash) (getBytes)), 4, __hashBytes, 0, 28)
            (§ let #_"int" __height (.. __storedBlock (getHeight)))
            (§ block
                (§ let #_"byte[]" __transactions nil)
                (§ block
                    (§ let #_"byte[]" __txOutChanges nil)
                    (§ block
                        (§ try )
                        (§ block
                            (§ let #_"ByteArrayOutputStream" __bos (§ new #_"ByteArrayOutputStream"))
                            (§ block
                                (§ if (!= (.. __undoableBlock (getTxOutChanges)) nil))
                                (§ block
                                    (.. __undoableBlock (getTxOutChanges) (serializeToStream __bos))
                                    (§ ass __txOutChanges (.. __bos (toByteArray)))
                                )
                                (§ else )
                                (§ block
                                    (§ let #_"int" __numTxn (.. __undoableBlock (getTransactions) (size)))
                                    (§ block
                                        (.. __bos (write (& 0xff __numTxn)))
                                        (.. __bos (write (& 0xff (>> __numTxn 8))))
                                        (.. __bos (write (& 0xff (>> __numTxn 16))))
                                        (.. __bos (write (& 0xff (>> __numTxn 24))))
                                        (§ for (§ var #_"Transaction" __tx) :for (.. __undoableBlock (getTransactions)))
                                        (§ block
                                            (.. __tx (bitcoinSerialize __bos))
                                        )
                                        (§ ass __transactions (.. __bos (toByteArray)))
                                    )
                                )
                                (.. __bos (close))
                            )
                        )
                        (§ catch #_"IOException" __e)
                        (§ block
                            (§ throw (§ new #_"BlockStoreException" __e))
                        )

                        (§ try )
                        (§ block
                            (§ if (.. PostgresFullPrunedBlockStore/log (isDebugEnabled)))
                            (§ block
                                (.. PostgresFullPrunedBlockStore/log (debug (str "Looking for undoable block with hash: " (.. Utils/HEX (encode __hashBytes)))))
                            )

                            (§ let #_"PreparedStatement" __findS (.. conn (get) (prepareStatement PostgresFullPrunedBlockStore/SELECT_UNDOABLEBLOCKS_EXISTS_SQL)))
                            (§ block
                                (.. __findS (setBytes 1, __hashBytes))

                                (§ let #_"ResultSet" __rs (.. __findS (executeQuery)))
                                (§ block
                                    (§ if (.. __rs (next)))
                                    (§ block
                                        ;; We already have this output, update it.
                                        (.. __findS (close))

                                        ;; Postgres insert-or-updates are very complex (and finnicky).
                                        ;; This level of transaction isolation seems to work for bitcoinj.
                                        (§ let #_"PreparedStatement" __ps (.. conn (get) (prepareStatement (.. this (getUpdateUndoableBlocksSQL)))))
                                        (§ block
                                            (.. __ps (setBytes 3, __hashBytes))

                                            (§ if (.. PostgresFullPrunedBlockStore/log (isDebugEnabled)))
                                            (§ block
                                                (.. PostgresFullPrunedBlockStore/log (debug (str "Updating undoable block with hash: " (.. Utils/HEX (encode __hashBytes)))))
                                            )

                                            (§ if (nil? __transactions))
                                            (§ block
                                                (.. __ps (setBytes 1, __txOutChanges))
                                                (.. __ps (setNull 2, Types/BINARY))
                                            )
                                            (§ else )
                                            (§ block
                                                (.. __ps (setNull 1, Types/BINARY))
                                                (.. __ps (setBytes 2, __transactions))
                                            )
                                            (.. __ps (executeUpdate))
                                            (.. __ps (close))

                                            (§ return nil)
                                        )
                                    )

                                    (§ let #_"PreparedStatement" __ps (.. conn (get) (prepareStatement (.. this (getInsertUndoableBlocksSQL)))))
                                    (§ block
                                        (.. __ps (setBytes 1, __hashBytes))
                                        (.. __ps (setInt 2, __height))

                                        (§ if (.. PostgresFullPrunedBlockStore/log (isDebugEnabled)))
                                        (§ block
                                            (.. PostgresFullPrunedBlockStore/log (debug (str "Inserting undoable block with hash: " (.. Utils/HEX (encode __hashBytes))  " at height " __height)))
                                        )

                                        (§ if (nil? __transactions))
                                        (§ block
                                            (.. __ps (setBytes 3, __txOutChanges))
                                            (.. __ps (setNull 4, Types/BINARY))
                                        )
                                        (§ else )
                                        (§ block
                                            (.. __ps (setNull 3, Types/BINARY))
                                            (.. __ps (setBytes 4, __transactions))
                                        )
                                        (.. __ps (executeUpdate))
                                        (.. __ps (close))
                                        (§ try )
                                        (§ block
                                            (.. this (putUpdateStoredBlock __storedBlock, true))
                                        )
                                        (§ catch #_"SQLException" __e)
                                        (§ block
                                            (§ throw (§ new #_"BlockStoreException" __e))
                                        )
                                    )
                                )
                            )
                        )
                        (§ catch #_"SQLException" __e)
                        (§ block
                            (§ if (not (.. __e (getSQLState) (equals PostgresFullPrunedBlockStore/POSTGRES_DUPLICATE_KEY_ERROR_CODE))))
                            (§ block
                                (§ throw (§ new #_"BlockStoreException" __e))
                            )
                        )
                        nil
                    )
                )
            )
        )
    )
)

#_(ns org.bitcoinj.store #_"SPVBlockStore"
    (:import [java.io *]
             [java.nio *]
             [java.nio.channels *]
             [java.util *]
             [java.util.concurrent.locks *])
    (:import [com.google.common.base Charsets Preconditions]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.utils *]))

;; TODO: Lose the mmap in this class.  There are too many platform bugs that require odd workarounds.

;;;
 ; An SPVBlockStore holds a limited number of block headers in a memory mapped ring buffer.  With such a store,
 ; you may not be able to process very deep re-orgs and could be disconnected from the chain (requiring a replay),
 ; but as they are virtually unheard of this is not a significant risk.
 ;;
#_public
(§ class #_"SPVBlockStore" (§ implements #_"BlockStore")
    #_private
    #_static
    (§ def- #_"Logger" (§ name SPVBlockStore/log) (LoggerFactory/getLogger (§ klass #_"SPVBlockStore")))

    ;;; The default number of headers that will be stored in the ring buffer. ;;
    #_public
    #_static
    (§ def #_"int" (§ name SPVBlockStore/DEFAULT_CAPACITY) 5000)
    #_public
    #_static
    (§ def #_"String" (§ name SPVBlockStore/HEADER_MAGIC) "SPVB")

    #_protected
    #_volatile
    (§ field #_"MappedByteBuffer" (§ name buffer))
    #_protected
    (§ field #_"int" (§ name capacity))
    #_protected
    (§ field #_"NetworkParameters" (§ name params))

    #_protected
    (§ field #_"ReentrantLock" (§ name lock) (Threading/lock "SPVBlockStore"))

    ;; The entire ring-buffer is mmapped and accessing it should be as fast as accessing regular memory once it's
    ;; faulted in.  Unfortunately, in theory practice and theory are the same.  In practice they aren't.
    ;;
    ;; MMapping a file in Java does not give us a byte[] as you may expect but rather a ByteBuffer, and whilst on
    ;; the OpenJDK/Oracle JVM calls into the get() methods are compiled down to inlined native code on Android each
    ;; get() call is actually a full-blown JNI method under the hood, meaning it's unbelievably slow.  The caches
    ;; below let us stay in the JIT-compiled Java world without expensive JNI transitions and make a 10x difference!
    #_protected
    (§ field #_"LinkedHashMap<Sha256Hash, StoredBlock>" (§ name blockCache) (§ new #_"LinkedHashMap<Sha256Hash, StoredBlock>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, StoredBlock>" __entry])
            (§ block
                (< 2050 (.. this (size))) ;; Slightly more than the difficulty transition period.
            )
        )))

    ;; Use a separate cache to track get() misses.  This is to efficiently handle the case of an unconnected block
    ;; during chain download.  Each new block will do a get() on the unconnected block so if we haven't seen it yet
    ;; we must efficiently respond.
    ;;
    ;; We don't care about the value in this cache.  It is always notFoundMarker.  Unfortunately LinkedHashSet does
    ;; not provide the removeEldestEntry control.
    #_private
    #_static
    (§ def- #_"Object" (§ name SPVBlockStore/NOT_FOUND_MARKER) (§ new #_"Object"))
    #_protected
    (§ field #_"LinkedHashMap<Sha256Hash, Object>" (§ name notFoundCache) (§ new #_"LinkedHashMap<Sha256Hash, Object>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" (§ fn removeEldestEntry) [#_"Map.Entry<Sha256Hash, Object>" __entry])
            (§ block
                (< 100 (.. this (size))) ;; This was chosen arbitrarily.
            )
        )))

    ;; Used to stop other applications/processes from opening the store.
    #_protected
    (§ field #_"FileLock" (§ name fileLock))
    #_protected
    (§ field #_"RandomAccessFile" (§ name randomAccessFile))

    ;;;
     ; Creates and initializes an SPV block store that can hold {@link #DEFAULT_CAPACITY} blocks.
     ; Will create the given file if it's missing.  This operation will block on disk.
     ; @param file File to use for the block store.
     ; @throws BlockStoreException if something goes wrong.
     ;;
    #_public
    (§ constructor #_"SPVBlockStore" [#_"NetworkParameters" __params, #_"File" __file])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this __params, __file, SPVBlockStore/DEFAULT_CAPACITY)
        this
    )

    ;;;
     ; Creates and initializes an SPV block store that can hold a given amount of blocks.
     ; Will create the given file if it's missing.  This operation will block on disk.
     ; @param file File to use for the block store.
     ; @param capacity Custom capacity.
     ; @throws BlockStoreException if something goes wrong.
     ;;
    #_public
    (§ constructor #_"SPVBlockStore" [#_"NetworkParameters" __params, #_"File" __file, #_"int" __capacity])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull __file)
        (§ ass (§ name (.. this params)) (Preconditions/checkNotNull __params))
        (Preconditions/checkArgument (> __capacity 0))

        (§ ass (§ name (.. this capacity)) __capacity)
        (§ try )
        (§ block
            (§ let #_"boolean" __exists (.. __file (exists)))
            (§ block
                ;; Set up the backing file.
                (§ ass (§ name randomAccessFile) (§ new #_"RandomAccessFile" __file, "rw"))
                (§ let #_"long" __fileSize (SPVBlockStore/getFileSize __capacity))
                (§ block
                    (§ if (not __exists))
                    (§ block
                        (.. SPVBlockStore/log (info (str "Creating new SPV block chain file " __file)))
                        (.. randomAccessFile (setLength __fileSize))
                    )
                    (§ elseif (!= (.. randomAccessFile (length)) __fileSize))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" (str "File size on disk does not match expected size: " (.. randomAccessFile (length)) " vs " __fileSize)))
                    )

                    (§ let #_"FileChannel" __channel (.. randomAccessFile (getChannel)))
                    (§ block
                        (§ ass (§ name fileLock) (.. __channel (tryLock)))
                        (§ if (nil? fileLock))
                        (§ block
                            (§ throw (§ new #_"ChainFileLockedException" "Store file is already locked by another process"))
                        )

                        ;; Map it into memory read/write.  The kernel will take care of flushing writes to disk at the most
                        ;; efficient times, which may mean that until the map is deallocated the data on disk is randomly
                        ;; inconsistent.  However the only process accessing it is us, via this mapping, so our own view will
                        ;; always be correct.  Once we establish the mmap the underlying file and channel can go away.  Note
                        ;; that the details of mmapping vary between platforms.
                        (§ ass (§ name buffer) (.. __channel (map FileChannel/MapMode/READ_WRITE, 0, __fileSize)))

                        ;; Check or initialize the header bytes to ensure we don't try to open some random file.
                        (§ let #_"byte[]" __header)
                        (§ block
                            (§ if __exists)
                            (§ block
                                (§ ass __header (§ new #_"byte[]" (§ count 4)))
                                (.. buffer (get __header))
                                (§ if (not (.. (§ new #_"String" __header, Charsets/US_ASCII) (equals SPVBlockStore/HEADER_MAGIC))))
                                (§ block
                                    (§ throw (§ new #_"BlockStoreException" (str "Header bytes do not equal " SPVBlockStore/HEADER_MAGIC)))
                                )
                            )
                            (§ else )
                            (§ block
                                (.. this (initNewStore __params))
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" __e1)
        (§ block
            (§ try )
            (§ block
                (§ if (some? randomAccessFile))
                (§ block
                    (.. randomAccessFile (close))
                )
            )
            (§ catch #_"IOException" __e2)
            (§ block
                (§ throw (§ new #_"BlockStoreException" __e2))
            )
            (§ throw (§ new #_"BlockStoreException" __e1))
        )
        this
    )

    #_private
    (§ method- #_"void" (§ fn initNewStore) [#_"NetworkParameters" __params])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"byte[]" __header (.. SPVBlockStore/HEADER_MAGIC (getBytes "US-ASCII")))
        (§ block
            (.. buffer (put __header))
            ;; Insert the genesis block.
            (.. lock (lock))
            (§ try )
            (§ block
                (.. this (setRingCursor buffer, SPVBlockStore/FILE_PROLOGUE_BYTES))
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            (§ let #_"Block" __genesis (.. __params (getGenesisBlock) (cloneAsHeader)))
            (§ block
                (§ let #_"StoredBlock" __storedGenesis (§ new #_"StoredBlock" __genesis, (.. __genesis (getWork)), 0))
                (§ block
                    (.. this (put __storedGenesis))
                    (.. this (setChainHead __storedGenesis))
                    nil
                )
            )
        )
    )

    ;;; Returns the size in bytes of the file that is used to store the chain with the current parameters. ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn SPVBlockStore/getFileSize) [#_"int" __capacity])
    (§ block
        (+ (* SPVBlockStore/RECORD_SIZE __capacity) SPVBlockStore/FILE_PROLOGUE_BYTES) ;; extra kilobyte for stuff
    )

    #_override
    #_public
    (§ method #_"void" (§ fn put) [#_"StoredBlock" __block])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" __buffer (.. this buffer))
        (§ block
            (§ if (nil? __buffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ let #_"int" __cursor (.. this (getRingCursor __buffer)))
                (§ block
                    ;; Wrapped around.
                    (§ if (== __cursor (SPVBlockStore/getFileSize capacity)))
                    (§ block
                        (§ ass __cursor SPVBlockStore/FILE_PROLOGUE_BYTES)
                    )
                    (.. __buffer (position __cursor))
                    (§ let #_"Sha256Hash" __hash (.. __block (getHeader) (getHash)))
                    (§ block
                        (.. notFoundCache (remove __hash))
                        (.. __buffer (put (.. __hash (getBytes))))
                        (.. __block (serializeCompact __buffer))
                        (.. this (setRingCursor __buffer, (.. __buffer (position))))
                        (.. blockCache (put __hash, __block))
                    )
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            nil
        )
    )

    #_override
    #_nilable
    #_public
    (§ method #_"StoredBlock" (§ fn get) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" __buffer (.. this buffer))
        (§ block
            (§ if (nil? __buffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ let #_"StoredBlock" __cacheHit (.. blockCache (get __hash)))
                (§ block
                    (§ if (some? __cacheHit))
                    (§ block
                        (§ return __cacheHit)
                    )
                    (§ if (!= (.. notFoundCache (get __hash)) nil))
                    (§ block
                        (§ return nil)
                    )

                    ;; Starting from the current tip of the ring work backwards until we have either
                    ;; found the block or wrapped around.
                    (§ let #_"int" __cursor (.. this (getRingCursor __buffer)))
                    (§ block
                        (§ let #_"int" __startingPoint __cursor)
                        (§ block
                            (§ let #_"int" __fileSize (SPVBlockStore/getFileSize capacity))
                            (§ block
                                (§ let #_"byte[]" __targetHashBytes (.. __hash (getBytes)))
                                (§ block
                                    (§ let #_"byte[]" __scratch (§ new #_"byte[]" (§ count 32)))
                                    (§ block
                                        (§ do )
                                        (§ block
                                            (§ ass __cursor (- __cursor SPVBlockStore/RECORD_SIZE))
                                            ;; We hit the start, so wrap around.
                                            (§ if (< __cursor SPVBlockStore/FILE_PROLOGUE_BYTES))
                                            (§ block
                                                (§ ass __cursor (- __fileSize SPVBlockStore/RECORD_SIZE))
                                            )
                                            ;; Cursor is now at the start of the next record to check, so read the hash and compare it.
                                            (.. __buffer (position __cursor))
                                            (.. __buffer (get __scratch))
                                            (§ if (Arrays/equals __scratch, __targetHashBytes))
                                            (§ block
                                                ;; Found the target.
                                                (§ let #_"StoredBlock" __storedBlock (StoredBlock/deserializeCompact params, __buffer))
                                                (§ block
                                                    (.. blockCache (put __hash, __storedBlock))
                                                    (§ return __storedBlock)
                                                )
                                            )
                                        )
                                        (§ again (!= __cursor __startingPoint))

                                        ;; Not found.
                                        (.. notFoundCache (put __hash, SPVBlockStore/NOT_FOUND_MARKER))
                                        (§ return nil)
                                    )
                                )
                            )
                        )
                    )
                )
            )
            (§ catch #_"ProtocolException" __e)
            (§ block
                (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
        )
    )

    #_protected
    (§ field #_"StoredBlock" (§ name lastChainHead))

    #_override
    #_public
    (§ method #_"StoredBlock" (§ fn getChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" __buffer (.. this buffer))
        (§ block
            (§ if (nil? __buffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ if (nil? lastChainHead))
                (§ block
                    (§ let #_"byte[]" __headHash (§ new #_"byte[]" (§ count 32)))
                    (§ block
                        (.. __buffer (position 8))
                        (.. __buffer (get __headHash))
                        (§ let #_"Sha256Hash" __hash (Sha256Hash/wrap __headHash))
                        (§ block
                            (§ let #_"StoredBlock" __block (.. this (get __hash)))
                            (§ block
                                (§ if (nil? __block))
                                (§ block
                                    (§ throw (§ new #_"BlockStoreException" (str "Corrupted block store: could not find chain head: " __hash)))
                                )

                                (§ ass (§ name lastChainHead) __block)
                            )
                        )
                    )
                )
                (§ return lastChainHead)
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn setChainHead) [#_"StoredBlock" __chainHead])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ let #_"MappedByteBuffer" __buffer (.. this buffer))
        (§ block
            (§ if (nil? __buffer))
            (§ block
                (§ throw (§ new #_"BlockStoreException" "Store closed"))
            )

            (.. lock (lock))
            (§ try )
            (§ block
                (§ ass (§ name lastChainHead) __chainHead)
                (§ let #_"byte[]" __headHash (.. __chainHead (getHeader) (getHash) (getBytes)))
                (§ block
                    (.. __buffer (position 8))
                    (.. __buffer (put __headHash))
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            nil
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn close) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ try )
        (§ block
            (.. buffer (force))
            (§ ass (§ name buffer) nil) ;; Allow it to be GCd and the underlying file mapping to go away.
            (.. randomAccessFile (close))
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"BlockStoreException" __e))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )

    #_protected
    #_static
    (§ def #_"int" (§ name SPVBlockStore/RECORD_SIZE) (+ 32 StoredBlock/COMPACT_SERIALIZED_SIZE)) ;; hash

    ;; File format:
    ;;   4 header bytes = "SPVB"
    ;;   4 cursor bytes, which indicate the offset from the first kb where the next block header should be written
    ;;   32 bytes for the hash of the chain head
    ;;
    ;; For each header (128 bytes)
    ;;   32 bytes hash of the header
    ;;   12 bytes of chain work
    ;;    4 bytes of height
    ;;   80 bytes of block header data
    #_protected
    #_static
    (§ def #_"int" (§ name SPVBlockStore/FILE_PROLOGUE_BYTES) 1024)

    ;;; Returns the offset from the file start where the latest block should be written (end of prev block). ;;
    #_private
    (§ method- #_"int" (§ fn getRingCursor) [#_"ByteBuffer" __buffer])
    (§ block
        (§ let #_"int" __c (.. __buffer (getInt 4)))
        (§ block
            (Preconditions/checkState (<= SPVBlockStore/FILE_PROLOGUE_BYTES __c), "Integer overflow")
            __c
        )
    )

    #_private
    (§ method- #_"void" (§ fn setRingCursor) [#_"ByteBuffer" __buffer, #_"int" __newCursor])
    (§ block
        (Preconditions/checkArgument (<= 0 __newCursor))
        (.. __buffer (putInt 4, __newCursor))
        nil
    )
)

#_(ns org.bitcoinj.uri #_"BitcoinURI"
    (:import [java.io UnsupportedEncodingException]
             [java.net URI URISyntaxException URLDecoder URLEncoder]
             [java.util ArrayList Collections LinkedHashMap List Locale Map])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Address AddressFormatException Coin NetworkParameters]
             [org.bitcoinj.params AbstractBitcoinNetParams]))

;;;
 ; <p>Provides a standard implementation of a Bitcoin URI with support for the following:</p>
 ;
 ; <ul>
 ; <li>URLEncoded URIs (as passed in by IE on the command line)</li>
 ; <li>BIP21 names (including the "req-" prefix handling requirements)</li>
 ; </ul>
 ;
 ; <h2>Accepted formats</h2>
 ;
 ; <p>The following input forms are accepted:</p>
 ;
 ; <ul>
 ; <li>{@code bitcoin:<address>}</li>
 ; <li>{@code bitcoin:<address>?<name1>=<value1>&<name2>=<value2>} with multiple additional name/value pairs</li>
 ; </ul>
 ;
 ; <p>The name/value pairs are processed as follows.</p>
 ; <ol>
 ; <li>URL encoding is stripped and treated as UTF-8</li>
 ; <li>names prefixed with {@code req-} are treated as required and if unknown or conflicting cause a parse exception</li>
 ; <li>Unknown names not prefixed with {@code req-} are added to a Map, accessible by parameter name</li>
 ; <li>Known names not prefixed with {@code req-} are processed unless they are malformed</li>
 ; </ol>
 ;
 ; <p>The following names are known and have the following formats:</p>
 ; <ul>
 ; <li>{@code amount} decimal value to 8 dp (e.g. 0.12345678) <b>Note that the exponent notation is not supported any more.</b></li>
 ; <li>{@code label} any URL encoded alphanumeric</li>
 ; <li>{@code message} any URL encoded alphanumeric</li>
 ; </ul>
 ;
 ; @author Andreas Schildbach (initial code)
 ; @author Jim Burton (enhancements for MultiBit)
 ; @author Gary Rowe (BIP21 support)
 ; @see <a href="https://github.com/bitcoin/bips/blob/master/bip-0021.mediawiki">BIP 0021</a>
 ;;
#_public
(§ class #_"BitcoinURI"
    ;; Not worth turning into an enum.
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/FIELD_MESSAGE) "message")
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/FIELD_LABEL) "label")
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/FIELD_AMOUNT) "amount")
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/FIELD_ADDRESS) "address")
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/FIELD_PAYMENT_REQUEST_URL) "r")

    ;;;
     ; URI for Bitcoin network.  Use {@link org.bitcoinj.params.AbstractBitcoinNetParams#BITCOIN_SCHEME}
     ; if you specifically need Bitcoin, or use {@link org.bitcoinj.core.NetworkParameters#getUriScheme}
     ; to get the scheme from network parameters.
     ;;
    #_deprecated
    #_public
    #_static
    (§ def #_"String" (§ name BitcoinURI/BITCOIN_SCHEME) "bitcoin")
    #_private
    #_static
    (§ def- #_"String" (§ name BitcoinURI/ENCODED_SPACE_CHARACTER) "%20")
    #_private
    #_static
    (§ def- #_"String" (§ name BitcoinURI/AMPERSAND_SEPARATOR) "&")
    #_private
    #_static
    (§ def- #_"String" (§ name BitcoinURI/QUESTION_MARK_SEPARATOR) "?")

    ;;;
     ; Contains all the parameters in the order in which they were processed.
     ;;
    #_private
    (§ field- #_"Map<String, Object>" (§ name parameterMap) (§ new #_"LinkedHashMap<>"))

    ;;;
     ; Constructs a new BitcoinURI from the given string.  Can be for any network.
     ;
     ; @param uri The raw URI data to be parsed (see class comments for accepted formats).
     ; @throws BitcoinURIParseException if the URI is not syntactically or semantically valid.
     ;;
    #_public
    (§ constructor #_"BitcoinURI" [#_"String" __uri])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (§ this nil, __uri)
        this
    )

    ;;;
     ; Constructs a new object by trying to parse the input as a valid Bitcoin URI.
     ;
     ; @param params The network parameters that determine which network the URI is from, or null if you
     ;               don't have any expectation about what network the URI is for and wish to check yourself.
     ; @param input The raw URI data to be parsed (see class comments for accepted formats).
     ;
     ; @throws BitcoinURIParseException if the input fails Bitcoin URI syntax and semantic checks.
     ;;
    #_public
    (§ constructor #_"BitcoinURI" [#_nilable #_"NetworkParameters" __params, #_"String" __input])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (Preconditions/checkNotNull __input)

        (§ let #_"String" __scheme (if (some? __params) (.. __params (getUriScheme)) AbstractBitcoinNetParams/BITCOIN_SCHEME))
        (§ block

            ;; Attempt to form the URI (fail fast syntax checking to official standards).
            (§ let #_"URI" __uri)
            (§ block
                (§ try )
                (§ block
                    (§ ass __uri (§ new #_"URI" __input))
                )
                (§ catch #_"URISyntaxException" __e)
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" "Bad URI syntax", __e))
                )

                ;; URI is formed as  bitcoin:<address>?<query parameters>
                ;; blockchain.info generates URIs of non-BIP compliant form bitcoin://address?....
                ;; We support both until Ben fixes his code.

                ;; Remove the bitcoin scheme.
                ;; (Note: getSchemeSpecificPart() is not used as it unescapes the label and parse then fails.
                ;; For instance with  bitcoin:129mVqKUmJ9uwPxKJBnNdABbuaaNfho4Ha?amount=0.06&label=Tom%20%26%20Jerry
                ;; the & (%26) in Tom and Jerry gets interpreted as a separator and the label then gets parsed
                ;; as 'Tom ' instead of 'Tom & Jerry'.)
                (§ let #_"String" __blockchainInfoScheme (str __scheme "://"))
                (§ block
                    (§ let #_"String" __correctScheme (str __scheme ":"))
                    (§ block
                        (§ let #_"String" __schemeSpecificPart)
                        (§ block
                            (§ if (.. __input (startsWith __blockchainInfoScheme)))
                            (§ block
                                (§ ass __schemeSpecificPart (.. __input (substring (.. __blockchainInfoScheme (length)))))
                            )
                            (§ elseif (.. __input (startsWith __correctScheme)))
                            (§ block
                                (§ ass __schemeSpecificPart (.. __input (substring (.. __correctScheme (length)))))
                            )
                            (§ else )
                            (§ block
                                (§ throw (§ new #_"BitcoinURIParseException" (str "Unsupported URI scheme: " (.. __uri (getScheme)))))
                            )

                            ;; Split off the address from the rest of the query parameters.
                            (§ let #_"String[]" __addressSplitTokens (.. __schemeSpecificPart (split "\\?", 2)))
                            (§ block
                                (§ if (== (.. __addressSplitTokens length) 0))
                                (§ block
                                    (§ throw (§ new #_"BitcoinURIParseException" "No data found after the bitcoin: prefix"))
                                )

                                (§ let #_"String" __addressToken (§ ai __addressSplitTokens 0)) ;; may be empty!
                                (§ block

                                    (§ let #_"String[]" __nameValuePairTokens)
                                    (§ block
                                        (§ if (== (.. __addressSplitTokens length) 1))
                                        (§ block
                                            ;; Only an address is specified - use an empty '<name>=<value>' token array.
                                            (§ ass __nameValuePairTokens (§ new #_"String[]"))
                                        )
                                        (§ else )
                                        (§ block
                                            ;; Split into '<name>=<value>' tokens.
                                            (§ ass __nameValuePairTokens (.. (§ ai __addressSplitTokens 1) (split "&")))
                                        )

                                        ;; Attempt to parse the rest of the URI parameters.
                                        (.. this (parseParameters __params, __addressToken, __nameValuePairTokens))

                                        (§ if (not (.. __addressToken (isEmpty))))
                                        (§ block
                                            ;; Attempt to parse the addressToken as a Bitcoin address for this network.
                                            (§ try )
                                            (§ block
                                                (§ let #_"Address" __address (Address/fromBase58 __params, __addressToken))
                                                (§ block
                                                    (.. this (putWithValidation BitcoinURI/FIELD_ADDRESS, __address))
                                                )
                                            )
                                            (§ catch [#_"AddressFormatException" __e])
                                            (§ block
                                                (§ throw (§ new #_"BitcoinURIParseException" "Bad address", __e))
                                            )
                                        )

                                        (§ if (and (.. __addressToken (isEmpty)) (nil? (.. this (getPaymentRequestUrl)))))
                                        (§ block
                                            (§ throw (§ new #_"BitcoinURIParseException" "No address and no r= parameter found"))
                                        )
                                        this
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; @param params The network parameters or null.
     ; @param nameValuePairTokens The tokens representing the name value pairs (assumed to be separated by '=' e.g. 'amount=0.2').
     ;;
    #_private
    (§ method- #_"void" (§ fn parseParameters) [#_nilable #_"NetworkParameters" __params, #_"String" __addressToken, #_"String[]" __nameValuePairTokens])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        ;; Attempt to decode the rest of the tokens into a parameter map.
        (§ for (§ var #_"String" __nameValuePairToken) :for __nameValuePairTokens)
        (§ block
            (§ let #_"int" __sepIndex (.. __nameValuePairToken (indexOf "=")))
            (§ block
                (§ if (== __sepIndex -1))
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" (str "Malformed Bitcoin URI - no separator in '" __nameValuePairToken "'")))
                )
                (§ if (== __sepIndex 0))
                (§ block
                    (§ throw (§ new #_"BitcoinURIParseException" (str "Malformed Bitcoin URI - empty name '" __nameValuePairToken "'")))
                )

                (§ let #_"String" __nameToken (.. __nameValuePairToken (substring 0, __sepIndex) (toLowerCase Locale/ENGLISH)))
                (§ block
                    (§ let #_"String" __valueToken (.. __nameValuePairToken (substring (inc __sepIndex))))
                    (§ block

                        ;; Parse the amount.
                        (§ if (.. BitcoinURI/FIELD_AMOUNT (equals __nameToken)))
                        (§ block
                            ;; Decode the amount (contains an optional decimal component to 8dp).
                            (§ try )
                            (§ block
                                (§ let #_"Coin" __amount (Coin/parseCoin __valueToken))
                                (§ block
                                    (§ if (and (some? __params) (.. __amount (isGreaterThan (.. __params (getMaxMoney))))))
                                    (§ block
                                        (§ throw (§ new #_"BitcoinURIParseException" "Max number of coins exceeded"))
                                    )
                                    (§ if (< (.. __amount (signum)) 0))
                                    (§ block
                                        (§ throw (§ new #_"ArithmeticException" "Negative coins specified"))
                                    )

                                    (.. this (putWithValidation BitcoinURI/FIELD_AMOUNT, __amount))
                                )
                            )
                            (§ catch #_"IllegalArgumentException" __e)
                            (§ block
                                (§ throw (§ new #_"OptionalFieldValidationException" (String/format Locale/US, "'%s' is not a valid amount", __valueToken), __e))
                            )
                            (§ catch #_"ArithmeticException" __e)
                            (§ block
                                (§ throw (§ new #_"OptionalFieldValidationException" (String/format Locale/US, "'%s' has too many decimal places", __valueToken), __e))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ if (.. __nameToken (startsWith "req-")))
                            (§ block
                                ;; A required parameter that we do not know about.
                                (§ throw (§ new #_"RequiredFieldValidationException" (str "'" __nameToken "' is required but not known, this URI is not valid")))
                            )

                            ;; Known fields and unknown parameters that are optional.
                            (§ try )
                            (§ block
                                (§ if (< 0 (.. __valueToken (length))))
                                (§ block
                                    (.. this (putWithValidation __nameToken, (URLDecoder/decode __valueToken, "UTF-8")))
                                )
                            )
                            (§ catch #_"UnsupportedEncodingException" __e)
                            (§ block
                                (§ throw (§ new #_"RuntimeException" __e)) ;; Can't happen.
                            )
                        )
                    )
                )
            )
        )

        ;; Note to the future:
        ;; when you want to implement 'req-expires', have a look at commit 410a53791841 which had it in.
        nil
    )

    ;;;
     ; Put the value against the key in the map checking for duplication.  This avoids address field overwrite etc.
     ;
     ; @param key The key for the map.
     ; @param value The value to store.
     ;;
    #_private
    (§ method- #_"void" (§ fn putWithValidation) [#_"String" __key, #_"Object" __value])
        (§ throws #_"BitcoinURIParseException")
    (§ block
        (§ if (.. parameterMap (containsKey __key)))
        (§ block
            (§ throw (§ new #_"BitcoinURIParseException" (String/format Locale/US, "'%s' is duplicated, URI is invalid", __key)))
        )

        (.. parameterMap (put __key, __value))
        nil
    )

    ;;;
     ; The Bitcoin Address from the URI, if one was present.  It's possible to have Bitcoin URI's with no address
     ; if a r= payment protocol parameter is specified, though this form is not recommended as older wallets
     ; can't understand it.
     ;;
    #_nilable
    #_public
    (§ method #_"Address" (§ fn getAddress) [])
    (§ block
        (§ cast #_"Address" (.. parameterMap (get BitcoinURI/FIELD_ADDRESS)))
    )

    ;;;
     ; @return The amount name encoded using a pure integer value based at 10,000,000 units is 1 BTC.
     ;         May be null if no amount is specified.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getAmount) [])
    (§ block
        (§ cast #_"Coin" (.. parameterMap (get BitcoinURI/FIELD_AMOUNT)))
    )

    ;;;
     ; @return the label from the URI.
     ;;
    #_public
    (§ method #_"String" (§ fn getLabel) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get BitcoinURI/FIELD_LABEL)))
    )

    ;;;
     ; @return the message from the URI.
     ;;
    #_public
    (§ method #_"String" (§ fn getMessage) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get BitcoinURI/FIELD_MESSAGE)))
    )

    ;;;
     ; @return the URL where a payment request (as specified in BIP 70) may be fetched.
     ;;
    #_public
    (§ method #_"String" (§ fn getPaymentRequestUrl) [])
    (§ block
        (§ cast #_"String" (.. parameterMap (get BitcoinURI/FIELD_PAYMENT_REQUEST_URL)))
    )

    ;;;
     ; Returns the URLs where a payment request (as specified in BIP 70) may be fetched.
     ; The first URL is the main URL, all subsequent URLs are fallbacks.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn getPaymentRequestUrls) [])
    (§ block
        (§ let #_"ArrayList<String>" __urls (§ new #_"ArrayList<>"))
        (§ block
            (§ while true)
            (§ block
                (§ let #_"int" __i (.. __urls (size)))
                (§ block
                    (§ let #_"String" __paramName (str BitcoinURI/FIELD_PAYMENT_REQUEST_URL (if (< 0 __i) (Integer/toString __i) "")))
                    (§ block
                        (§ let #_"String" __url (§ cast #_"String" (.. parameterMap (get __paramName))))
                        (§ block
                            (§ if (nil? __url))
                            (§ block
                                (§ break )
                            )
                            (.. __urls (add __url))
                        )
                    )
                )
            )
            (Collections/reverse __urls)
            __urls
        )
    )

    ;;;
     ; @param name The name of the parameter.
     ; @return the parameter value, or null if not present.
     ;;
    #_public
    (§ method #_"Object" (§ fn getParameterByName) [#_"String" __name])
    (§ block
        (.. parameterMap (get __name))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder" "BitcoinURI["))
        (§ block
            (§ let #_"boolean" __first true)
            (§ block
                (§ for (§ var #_"Map.Entry<String, Object>" __entry) :for (.. parameterMap (entrySet)))
                (§ block
                    (§ if __first)
                    (§ block
                        (§ ass __first false)
                    )
                    (§ else )
                    (§ block
                        (.. __sb (append ","))
                    )
                    (.. __sb (append "'") (append (.. __entry (getKey))) (append "'='") (append (.. __entry (getValue))) (append "'"))
                )
                (.. __sb (append "]"))
                (.. __sb (toString))
            )
        )
    )

    ;;;
     ; Simple Bitcoin URI builder using known good fields.
     ;
     ; @param address The Bitcoin address.
     ; @param amount The amount.
     ; @param label A label.
     ; @param message A message.
     ; @return a String containing the Bitcoin URI.
     ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn BitcoinURI/convertToBitcoinURI) [#_"Address" __address, #_"Coin" __amount, #_"String" __label, #_"String" __message])
    (§ block
        (BitcoinURI/convertToBitcoinURI (.. __address (getParameters)), (.. __address (toString)), __amount, __label, __message)
    )

    ;;;
     ; Simple Bitcoin URI builder using known good fields.
     ;
     ; @param params The network parameters that determine which network the URI is for.
     ; @param address The Bitcoin address.
     ; @param amount The amount.
     ; @param label A label.
     ; @param message A message.
     ; @return a String containing the Bitcoin URI.
     ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn BitcoinURI/convertToBitcoinURI) [#_"NetworkParameters" __params, #_"String" __address, #_nilable #_"Coin" __amount, #_nilable #_"String" __label, #_nilable #_"String" __message])
    (§ block
        (Preconditions/checkNotNull __params)
        (Preconditions/checkNotNull __address)

        (§ if (and (some? __amount) (< (.. __amount (signum)) 0)))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" "Coin must be positive"))
        )

        (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder"))
        (§ block
            (§ let #_"String" __scheme (.. __params (getUriScheme)))
            (§ block
                (.. __sb (append __scheme) (append ":") (append __address))

                (§ let #_"boolean" __questionMarkHasBeenOutput false)
                (§ block

                    (§ if (some? __amount))
                    (§ block
                        (.. __sb (append BitcoinURI/QUESTION_MARK_SEPARATOR) (append BitcoinURI/FIELD_AMOUNT) (append "="))
                        (.. __sb (append (.. __amount (toPlainString))))
                        (§ ass __questionMarkHasBeenOutput true)
                    )

                    (§ if (and (some? __label) (not (.. "" (equals __label)))))
                    (§ block
                        (§ if __questionMarkHasBeenOutput)
                        (§ block
                            (.. __sb (append BitcoinURI/AMPERSAND_SEPARATOR))
                        )
                        (§ else )
                        (§ block
                            (.. __sb (append BitcoinURI/QUESTION_MARK_SEPARATOR))
                            (§ ass __questionMarkHasBeenOutput true)
                        )
                        (.. __sb (append BitcoinURI/FIELD_LABEL) (append "=") (append (BitcoinURI/encodeURLString __label)))
                    )

                    (§ if (and (some? __message) (not (.. "" (equals __message)))))
                    (§ block
                        (§ if __questionMarkHasBeenOutput)
                        (§ block
                            (.. __sb (append BitcoinURI/AMPERSAND_SEPARATOR))
                        )
                        (§ else )
                        (§ block
                            (.. __sb (append BitcoinURI/QUESTION_MARK_SEPARATOR))
                        )
                        (.. __sb (append BitcoinURI/FIELD_MESSAGE) (append "=") (append (BitcoinURI/encodeURLString __message)))
                    )

                    (.. __sb (toString))
                )
            )
        )
    )

    ;;;
     ; Encode a string using URL encoding.
     ;
     ; @param stringToEncode The string to URL encode.
     ;;
    #_static
    (§ defn #_"String" (§ fn BitcoinURI/encodeURLString) [#_"String" __stringToEncode])
    (§ block
        (§ try )
        (§ block
            (§ return (.. (URLEncoder/encode __stringToEncode, "UTF-8") (replace "+", BitcoinURI/ENCODED_SPACE_CHARACTER)))
        )
        (§ catch #_"UnsupportedEncodingException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Can't happen.
        )
    )
)

#_(ns org.bitcoinj.uri #_"BitcoinURIParseException")

