    #_public
    (§ constructor #_"DaemonThreadFactory" [])
    (§ block
        (§ this nil)
        this
    )

    #_override
    #_public
    (§ method #_"Thread" (§ fn newThread) [#_non-nil #_"Runnable" __runnable])
    (§ block
        (§ let #_"Thread" __thread (.. (Executors/defaultThreadFactory) (newThread __runnable)))
        (§ block
            (.. __thread (setDaemon true))
            (§ if (some? name))
            (§ block
