#_(ns org.bitcoinj.utils #_"ExchangeRate"
    (:import [java.io Serializable]
             [java.math BigInteger])
    (:import [com.google.common.base Objects Preconditions])
   (:require [org.bitcoinj.core Coin]))

;;;
 ; An exchange rate is expressed as a ratio of a {@link Coin} and a {@link Fiat} amount.
 ;;
#_public
(§ class #_"ExchangeRate" (§ implements #_"Serializable")
    #_public
    (§ field #_"Coin" (§ name coin))
    #_public
    (§ field #_"Fiat" (§ name fiat))

    ;;; Construct exchange rate.  This amount of coin is worth that amount of fiat. ;;
    #_public
    (§ constructor #_"ExchangeRate" [#_"Coin" __coin, #_"Fiat" __fiat])
    (§ block
        (Preconditions/checkArgument (.. __coin (isPositive)))
        (Preconditions/checkArgument (.. __fiat (isPositive)))
        (Preconditions/checkArgument (some? (.. __fiat currencyCode)), "currency code required")

        (§ ass (§ name (.. this coin)) __coin)
        (§ ass (§ name (.. this fiat)) __fiat)
        this
    )

    ;;; Construct exchange rate.  One coin is worth this amount of fiat. ;;
    #_public
    (§ constructor #_"ExchangeRate" [#_"Fiat" __fiat])
    (§ block
        (§ this Coin/COIN, __fiat)
        this
    )

    ;;;
     ; Convert a coin amount to a fiat amount using this exchange rate.
     ; @throws ArithmeticException if the converted fiat amount is too high or too low.
     ;;
    #_public
    (§ method #_"Fiat" (§ fn coinToFiat) [#_"Coin" __convertCoin])
    (§ block
        ;; Use BigInteger because it's much easier to maintain full precision without overflowing.
        (§ let #_"BigInteger" __converted (.. (BigInteger/valueOf (.. __convertCoin value)) (multiply (BigInteger/valueOf (.. fiat value))) (divide (BigInteger/valueOf (.. coin value)))))
        (§ block

            (§ if (or (< 0 (.. __converted (compareTo (BigInteger/valueOf Long/MAX_VALUE)))) (< (.. __converted (compareTo (BigInteger/valueOf Long/MIN_VALUE))) 0)))
            (§ block
                (§ throw (§ new #_"ArithmeticException" "Overflow"))
            )

            (Fiat/valueOf (.. fiat currencyCode), (.. __converted (longValue)))
        )
    )

    ;;;
     ; Convert a fiat amount to a coin amount using this exchange rate.
     ; @throws ArithmeticException if the converted coin amount is too high or too low.
     ;;
    #_public
    (§ method #_"Coin" (§ fn fiatToCoin) [#_"Fiat" __convertFiat])
    (§ block
        (Preconditions/checkArgument (.. __convertFiat currencyCode (equals (.. fiat currencyCode))), "Currency mismatch: %s vs %s", (.. __convertFiat currencyCode), (.. fiat currencyCode))

        ;; Use BigInteger because it's much easier to maintain full precision without overflowing.
        (§ let #_"BigInteger" __converted (.. (BigInteger/valueOf (.. __convertFiat value)) (multiply (BigInteger/valueOf (.. coin value))) (divide (BigInteger/valueOf (.. fiat value)))))
        (§ block

            (§ if (or (< 0 (.. __converted (compareTo (BigInteger/valueOf Long/MAX_VALUE)))) (< (.. __converted (compareTo (BigInteger/valueOf Long/MIN_VALUE))) 0)))
            (§ block
                (§ throw (§ new #_"ArithmeticException" "Overflow"))
            )

            (§ try )
            (§ block
                (§ return (Coin/valueOf (.. __converted (longValue))))
            )
            (§ catch #_"IllegalArgumentException" __e)
            (§ block
                (§ throw (§ new #_"ArithmeticException" (str "Overflow: " (.. __e (getMessage)))))
            )
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"ExchangeRate" __other (§ cast #_"ExchangeRate" __o))
        (§ block
            (and (Objects/equal (.. this coin), (.. __other coin)) (Objects/equal (.. this fiat), (.. __other fiat)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode coin, fiat)
    )
)

#_(ns org.bitcoinj.utils #_"ExponentialBackoff"
    (:import [com.google.common.base Preconditions]
             [com.google.common.primitives Longs])
   (:require [org.bitcoinj.core Utils]))

;;;
 ; <p>Tracks successes and failures and calculates a time to retry the operation.</p>
 ;
 ; <p>The retries are exponentially backed off, up to a maximum interval.  On success the back off interval is reset.</p>
 ;;
#_public
(§ class #_"ExponentialBackoff" (§ implements #_"Comparable<ExponentialBackoff>")
    #_public
    #_static
    (§ def #_"int" (§ name ExponentialBackoff/DEFAULT_INITIAL_MILLIS) 100)
    #_public
    #_static
    (§ def #_"float" (§ name ExponentialBackoff/DEFAULT_MULTIPLIER) 1.1)
    #_public
    #_static
    (§ def #_"int" (§ name ExponentialBackoff/DEFAULT_MAXIMUM_MILLIS) (* 30 1000))

    #_private
    (§ field- #_"float" (§ name backoff))
    #_private
    (§ field- #_"long" (§ name retryTime))
    #_private
    (§ field- #_"Params" (§ name params))

    ;;;
     ; Parameters to configure a particular kind of exponential backoff.
     ;;
    #_public
    #_static
    (§ class #_"Params"
        #_private
        (§ field- #_"float" (§ name initial))
        #_private
        (§ field- #_"float" (§ name multiplier))
        #_private
        (§ field- #_"float" (§ name maximum))

        ;;;
         ; @param initialMillis The initial interval to wait, in milliseconds.
         ; @param multiplier The multiplier to apply on each failure.
         ; @param maximumMillis The maximum interval to wait, in milliseconds.
         ;;
        #_public
        (§ constructor #_"Params" [#_"long" __initialMillis, #_"float" __multiplier, #_"long" __maximumMillis])
        (§ block
            (Preconditions/checkArgument (< 1.0 __multiplier), "multiplier must be greater than 1.0")
            (Preconditions/checkArgument (<= __initialMillis __maximumMillis), "maximum must not be less than initial")

            (§ ass (§ name (.. this initial)) __initialMillis)
            (§ ass (§ name (.. this multiplier)) __multiplier)
            (§ ass (§ name (.. this maximum)) __maximumMillis)
            this
        )

        ;;;
         ; Construct params with default values.
         ;;
        #_public
        (§ constructor #_"Params" [])
        (§ block
            (§ ass (§ name initial) ExponentialBackoff/DEFAULT_INITIAL_MILLIS)
            (§ ass (§ name multiplier) ExponentialBackoff/DEFAULT_MULTIPLIER)
            (§ ass (§ name maximum) ExponentialBackoff/DEFAULT_MAXIMUM_MILLIS)
            this
        )
    )

    #_public
    (§ constructor #_"ExponentialBackoff" [#_"Params" __params])
    (§ block
        (§ ass (§ name (.. this params)) __params)
        (.. this (trackSuccess))
        this
    )

    ;;; Track a success - reset back off interval to the initial value. ;;
    #_public
    (§ method #_"void" (§ fn trackSuccess) [])
    (§ block
        (§ ass (§ name backoff) (.. params initial))
        (§ ass (§ name retryTime) (Utils/currentTimeMillis))
        nil
    )

    ;;; Track a failure - multiply the back off interval by the multiplier. ;;
    #_public
    (§ method #_"void" (§ fn trackFailure) [])
    (§ block
        (§ ass (§ name retryTime) (+ (Utils/currentTimeMillis) (§ cast #_"long" backoff)))
        (§ ass (§ name backoff) (Math/min (* backoff (.. params multiplier)), (.. params maximum)))
        nil
    )

    ;;; Get the next time to retry, in milliseconds since the epoch. ;;
    #_public
    (§ method #_"long" (§ fn getRetryTime) [])
    (§ block
        retryTime
    )

    #_override
    #_public
    (§ method #_"int" (§ fn compareTo) [#_"ExponentialBackoff" __other])
    (§ block
        ;; Note that in this implementation compareTo() is not consistent with equals().
        (Longs/compare retryTime, (.. __other retryTime))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "ExponentialBackoff retry=" retryTime " backoff=" backoff)
    )
)

#_(ns org.bitcoinj.utils #_"Fiat"
    (:import [java.io Serializable]
             [java.math BigDecimal])
    (:import [com.google.common.base Objects Preconditions]
             [com.google.common.math LongMath]
             [com.google.common.primitives Longs])
   (:require [org.bitcoinj.core Monetary]))

;;;
 ; Represents a monetary fiat value.  It was decided to not fold this into {@link org.bitcoinj.core.Coin}
 ; because of type safety.  Fiat values always come with an attached currency code.
 ;
 ; This class is immutable.
 ;;
#_public
(§ class #_"Fiat" (§ implements #_"Monetary", #_"Comparable<Fiat>", #_"Serializable")
    ;;;
     ; The absolute value of exponent of the value of a "smallest unit" in scientific notation.
     ; We picked 4 rather than 2, because in financial applications it's common to use sub-cent precision.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name Fiat/SMALLEST_UNIT_EXPONENT) 4)

    ;;;
     ; The number of smallest units of this monetary value.
     ;;
    #_public
    (§ field #_"long" (§ name value))
    #_public
    (§ field #_"String" (§ name currencyCode))

    #_private
    (§ constructor- #_"Fiat" [#_"String" __currencyCode, #_"long" __value])
    (§ block
        (§ ass (§ name (.. this value)) __value)
        (§ ass (§ name (.. this currencyCode)) __currencyCode)
        this
    )

    #_public
    #_static
    (§ defn #_"Fiat" (§ fn Fiat/valueOf) [#_"String" __currencyCode, #_"long" __value])
    (§ block
        (§ new #_"Fiat" __currencyCode, __value)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn smallestUnitExponent) [])
    (§ block
        Fiat/SMALLEST_UNIT_EXPONENT
    )

    ;;;
     ; Returns the number of "smallest units" of this monetary value.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getValue) [])
    (§ block
        value
    )

    #_public
    (§ method #_"String" (§ fn getCurrencyCode) [])
    (§ block
        currencyCode
    )

    ;;;
     ; Parses an amount expressed in the way humans are used to.
     ;
     ; This takes string in a format understood by {@link BigDecimal#BigDecimal(String)},
     ; for example "0", "1", "0.10", "1.23E3", "1234.5E-5".
     ;
     ; @throws IllegalArgumentException if you try to specify more than 4 digits after the comma, or a value out of range.
     ;;
    #_public
    #_static
    (§ defn #_"Fiat" (§ fn Fiat/parseFiat) [#_"String" __currencyCode, #_"String" __str])
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" __val (.. (§ new #_"BigDecimal" __str) (movePointRight Fiat/SMALLEST_UNIT_EXPONENT) (longValueExact)))
            (§ block
                (§ return (Fiat/valueOf __currencyCode, __val))
            )
        )
        (§ catch #_"ArithmeticException" __e)
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" __e))
        )
    )

    ;;;
     ; Parses an amount expressed in the way humans are used to.  The amount is cut to 4 digits after the comma.
     ;
     ; This takes string in a format understood by {@link BigDecimal#BigDecimal(String)},
     ; for example "0", "1", "0.10", "1.23E3", "1234.5E-5".
     ;
     ; @throws IllegalArgumentException if you try to specify a value out of range.
     ;;
    #_public
    #_static
    (§ defn #_"Fiat" (§ fn Fiat/parseFiatInexact) [#_"String" __currencyCode, #_"String" __str])
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" __val (.. (§ new #_"BigDecimal" __str) (movePointRight Fiat/SMALLEST_UNIT_EXPONENT) (longValue)))
            (§ block
                (§ return (Fiat/valueOf __currencyCode, __val))
            )
        )
        (§ catch #_"ArithmeticException" __e)
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" __e))
        )
    )

    #_public
    (§ method #_"Fiat" (§ fn add) [#_"Fiat" __value])
    (§ block
        (Preconditions/checkArgument (.. __value currencyCode (equals currencyCode)))

        (§ new #_"Fiat" currencyCode, (LongMath/checkedAdd (.. this value), (.. __value value)))
    )

    #_public
    (§ method #_"Fiat" (§ fn subtract) [#_"Fiat" __value])
    (§ block
        (Preconditions/checkArgument (.. __value currencyCode (equals currencyCode)))

        (§ new #_"Fiat" currencyCode, (LongMath/checkedSubtract (.. this value), (.. __value value)))
    )

    #_public
    (§ method #_"Fiat" (§ fn multiply) [#_"long" __factor])
    (§ block
        (§ new #_"Fiat" currencyCode, (LongMath/checkedMultiply (.. this value), __factor))
    )

    #_public
    (§ method #_"Fiat" (§ fn divide) [#_"long" __divisor])
    (§ block
        (§ new #_"Fiat" currencyCode, (/ (.. this value) __divisor))
    )

    #_public
    (§ method #_"Fiat[]" (§ fn divideAndRemainder) [#_"long" __divisor])
    (§ block
        (§ new #_"Fiat[]" (§ coll (§ new #_"Fiat" currencyCode, (/ (.. this value) __divisor)), (§ new #_"Fiat" currencyCode, (% (.. this value) __divisor)) ))
    )

    #_public
    (§ method #_"long" (§ fn divide) [#_"Fiat" __divisor])
    (§ block
        (Preconditions/checkArgument (.. __divisor currencyCode (equals currencyCode)))

        (/ (.. this value) (.. __divisor value))
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value greater than zero, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPositive) [])
    (§ block
        (== (.. this (signum)) 1)
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value less than zero, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isNegative) [])
    (§ block
        (== (.. this (signum)) -1)
    )

    ;;;
     ; Returns true if and only if this instance represents zero monetary value, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isZero) [])
    (§ block
        (== (.. this (signum)) 0)
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is greater than that of the given other Fiat,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isGreaterThan) [#_"Fiat" __other])
    (§ block
        (> (.. this (compareTo __other)) 0)
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is less than that of the given other Fiat,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isLessThan) [#_"Fiat" __other])
    (§ block
        (< (.. this (compareTo __other)) 0)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn signum) [])
    (§ block
        (if (== (.. this value) 0) 0 (if (< (.. this value) 0) -1 1))
    )

    #_public
    (§ method #_"Fiat" (§ fn negate) [])
    (§ block
        (§ new #_"Fiat" currencyCode, (- (.. this value)))
    )

    ;;;
     ; Returns the number of "smallest units" of this monetary value.
     ; It's deprecated in favour of accessing {@link #value} directly.
     ;;
    #_public
    (§ method #_"long" (§ fn longValue) [])
    (§ block
        (.. this value)
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name Fiat/FRIENDLY_FORMAT) (.. MonetaryFormat/FIAT (postfixCode)))

    ;;;
     ; Returns the value as a 0.12 type string.
     ; More digits after the decimal place will be used if necessary, but two will always be present.
     ;;
    #_public
    (§ method #_"String" (§ fn toFriendlyString) [])
    (§ block
        (.. Fiat/FRIENDLY_FORMAT (code 0, currencyCode) (format this) (toString))
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name Fiat/PLAIN_FORMAT) (.. MonetaryFormat/FIAT (minDecimals 0) (repeatOptionalDecimals 1, 4) (noCode)))

    ;;;
     ; Returns the value as a plain string.  The result is unformatted with no trailing zeroes.
     ; For instance, a value of 150000 "smallest units" gives an output string of "0.0015".
     ;;
    #_public
    (§ method #_"String" (§ fn toPlainString) [])
    (§ block
        (.. Fiat/PLAIN_FORMAT (format this) (toString))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (Long/toString value)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== __o this))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (.. __o (getClass)) (getClass))))
        (§ block
            (§ return false)
        )
        (§ let #_"Fiat" __other (§ cast #_"Fiat" __o))
        (§ block
            (and (== (.. this value) (.. __other value)) (.. this currencyCode (equals (.. __other currencyCode))))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode value, currencyCode)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn compareTo) [#_"Fiat" __other])
    (§ block
        (if (.. this currencyCode (equals (.. __other currencyCode))) (Longs/compare (.. this value), (.. __other value)) (.. this currencyCode (compareTo (.. __other currencyCode))))
    )
)

#_(ns org.bitcoinj.utils #_"ListenerRegistration"
    (:import [java.util List]
             [java.util.concurrent Executor])
    (:import [com.google.common.base Preconditions]))

;;;
 ; A simple wrapper around a listener and an executor, with some utility methods.
 ;;
#_public
(§ class #_"ListenerRegistration<T>"
    #_public
    (§ field #_"T" (§ name listener))
    #_public
    (§ field #_"Executor" (§ name executor))

    #_public
    (§ constructor #_"ListenerRegistration" [#_"T" __listener, #_"Executor" __executor])
    (§ block
        (§ ass (§ name (.. this listener)) (Preconditions/checkNotNull __listener))
        (§ ass (§ name (.. this executor)) (Preconditions/checkNotNull __executor))
        this
    )

    ;;; Returns true if the listener was removed, else false. ;;
    #_public
    #_static
    (§ defn #_"<T> boolean" (§ fn ListenerRegistration<T>/removeFromList) [#_"T" __listener, #_"List<? extends ListenerRegistration<T>>" __list])
    (§ block
        (Preconditions/checkNotNull __listener)

        (§ let #_"ListenerRegistration<T>" __item nil)
        (§ block
            (§ for (§ var #_"ListenerRegistration<T>" __registration) :for __list)
            (§ block
                (§ if (== (.. __registration listener) __listener))
                (§ block
                    (§ ass __item __registration)
                    (§ break )
                )
            )
            (and (some? __item) (.. __list (remove __item)))
        )
    )
)

#_(ns org.bitcoinj.utils #_"MonetaryFormat"
    (:import [java.math RoundingMode]
             [java.text DecimalFormatSymbols]
             [java.util ArrayList Arrays List Locale])
    (:import [com.google.common.base Preconditions]
             [com.google.common.math LongMath])
   (:require [org.bitcoinj.core Coin Monetary]))

;;;
 ; Utility for formatting and parsing coin values to and from human readable form.
 ;
 ; MonetaryFormat instances are immutable.  Invoking a configuration method has no effect on the receiving instance;
 ; you must store and use the new instance it returns, instead.  Instances are thread safe, so they may be stored safely
 ; as static constants.
 ;;
#_public
(§ class #_"MonetaryFormat"
    ;;; Standard format for the BTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name MonetaryFormat/BTC) (.. (§ new #_"MonetaryFormat") (shift 0) (minDecimals 2) (repeatOptionalDecimals 2, 3)))
    ;;; Standard format for the mBTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name MonetaryFormat/MBTC) (.. (§ new #_"MonetaryFormat") (shift 3) (minDecimals 2) (optionalDecimals 2)))
    ;;; Standard format for the µBTC denomination. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name MonetaryFormat/UBTC) (.. (§ new #_"MonetaryFormat") (shift 6) (minDecimals 0) (optionalDecimals 2)))
    ;;; Standard format for fiat amounts. ;;
    #_public
    #_static
    (§ def #_"MonetaryFormat" (§ name MonetaryFormat/FIAT) (.. (§ new #_"MonetaryFormat") (shift 0) (minDecimals 2) (repeatOptionalDecimals 2, 1)))
    ;;; Currency code for base 1 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name MonetaryFormat/CODE_BTC) "BTC")
    ;;; Currency code for base 1/1000 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name MonetaryFormat/CODE_MBTC) "mBTC")
    ;;; Currency code for base 1/1000000 Bitcoin. ;;
    #_public
    #_static
    (§ def #_"String" (§ name MonetaryFormat/CODE_UBTC) "µBTC")

    #_public
    #_static
    (§ def #_"int" (§ name MonetaryFormat/MAX_DECIMALS) 8)

    #_private
    (§ field- #_"char" (§ name negativeSign))
    #_private
    (§ field- #_"char" (§ name positiveSign))
    #_private
    (§ field- #_"char" (§ name zeroDigit))
    #_private
    (§ field- #_"char" (§ name decimalMark))
    #_private
    (§ field- #_"int" (§ name minDecimals))
    #_private
    (§ field- #_"List<Integer>" (§ name decimalGroups))
    #_private
    (§ field- #_"int" (§ name shift))
    #_private
    (§ field- #_"RoundingMode" (§ name roundingMode))
    #_private
    (§ field- #_"String[]" (§ name codes))
    #_private
    (§ field- #_"char" (§ name codeSeparator))
    #_private
    (§ field- #_"boolean" (§ name codePrefixed))

    #_private
    #_static
    (§ def- #_"String" (§ name MonetaryFormat/DECIMALS_PADDING) "0000000000000000") ;; a few more than necessary for Bitcoin

    ;;;
     ; Set character to prefix negative values.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn negativeSign) [#_"char" __negativeSign])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit __negativeSign)))
        (Preconditions/checkArgument (< 0 __negativeSign))

        (if (== __negativeSign (.. this negativeSign)) this (§ new #_"MonetaryFormat" __negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character to prefix positive values.  A zero value means no sign is used in this case.
     ; For parsing, a missing sign will always be interpreted as if the positive sign was used.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn positiveSign) [#_"char" __positiveSign])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit __positiveSign)))

        (if (== __positiveSign (.. this positiveSign)) this (§ new #_"MonetaryFormat" negativeSign, __positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character range to use for representing digits.  It starts with the specified character representing zero.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn digits) [#_"char" __zeroDigit])
    (§ block
        (if (== __zeroDigit (.. this zeroDigit)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, __zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set character to use as the decimal mark.  If the formatted value does not have any decimals,
     ; no decimal mark is used either.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn decimalMark) [#_"char" __decimalMark])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit __decimalMark)))
        (Preconditions/checkArgument (< 0 __decimalMark))

        (if (== __decimalMark (.. this decimalMark)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, __decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set minimum number of decimals to use for formatting.  If the value precision exceeds all decimals specified
     ; (including additional decimals specified by {@link #optionalDecimals(int...)} or {@link #repeatOptionalDecimals(int, int)}),
     ; the value will be rounded.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn minDecimals) [#_"int" __minDecimals])
    (§ block
        (if (== __minDecimals (.. this minDecimals)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, __minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set additional groups of decimals to use after the minimum decimals, if they are useful for expressing precision.
     ; Each value is a number of decimals in that group.  If the value precision exceeds all decimals specified
     ; (including minimum decimals), the value will be rounded.  This configuration is not relevant for parsing.
     ;
     ; For example, if you pass <tt>4,2</tt> it will add four decimals to your formatted string if needed, and then add
     ; another two decimals if needed.  At this point, rather than adding further decimals the value will be rounded.
     ;
     ; @param groups Any number numbers of decimals, one for each group.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn optionalDecimals) [#_"int..." __groups])
    (§ block
        (§ let #_"List<Integer>" __decimalGroups (§ new #_"ArrayList<>" (.. __groups length)))
        (§ block
            (§ for (§ var #_"int" __group) :for __groups)
            (§ block
                (.. __decimalGroups (add __group))
            )
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, __decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Set repeated additional groups of decimals to use after the minimum decimals, if they are useful for expressing
     ; precision.  If the value precision exceeds all decimals specified (including minimum decimals), the value will be
     ; rounded.  This configuration is not relevant for parsing.
     ;
     ; For example, if you pass <tt>1,8</tt> it will up to eight decimals to your formatted string if needed.
     ; After these have been used up, rather than adding further decimals the value will be rounded.
     ;
     ; @param decimals Value of the group to be repeated.
     ; @param repetitions Number of repetitions.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn repeatOptionalDecimals) [#_"int" __decimals, #_"int" __repetitions])
    (§ block
        (Preconditions/checkArgument (<= 0 __repetitions))

        (§ let #_"List<Integer>" __decimalGroups (§ new #_"ArrayList<>" __repetitions))
        (§ block
            (§ for (§ var #_"int" __i 0) :for (< __i __repetitions) :for (§ recur (inc __i)))
            (§ block
                (.. __decimalGroups (add __decimals))
            )
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, __decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Set number of digits to shift the decimal separator to the right, coming from the standard BTC notation that was
     ; common pre-2014.  Note this will change the currency code if enabled.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn shift) [#_"int" __shift])
    (§ block
        (if (== __shift (.. this shift)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, __shift, roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Set rounding mode to use when it becomes necessary.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn roundingMode) [#_"RoundingMode" __roundingMode])
    (§ block
        (if (== __roundingMode (.. this roundingMode)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, __roundingMode, codes, codeSeparator, codePrefixed))
    )

    ;;;
     ; Don't display currency code when formatting.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn noCode) [])
    (§ block
        (if (nil? codes) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, nil, codeSeparator, codePrefixed))
    )

    ;;;
     ; Configure currency code for given decimal separator shift.  This configuration is not relevant for parsing.
     ;
     ; @param codeShift Decimal separator shift, see {@link #shift}.
     ; @param code Currency code.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn code) [#_"int" __codeShift, #_"String" __code])
    (§ block
        (Preconditions/checkArgument (<= 0 __codeShift))

        (§ let #_"String[]" __codes (if (some? (.. this codes)) (Arrays/copyOf (.. this codes), (.. this codes length)) (§ new #_"String[]" (§ count MonetaryFormat/MAX_DECIMALS))))
        (§ block

            (§ ass (§ name (§ ai __codes __codeShift)) __code)
            (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, __codes, codeSeparator, codePrefixed)
        )
    )

    ;;;
     ; Separator between currency code and formatted value.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn codeSeparator) [#_"char" __codeSeparator])
    (§ block
        (Preconditions/checkArgument (not (Character/isDigit __codeSeparator)))
        (Preconditions/checkArgument (< 0 __codeSeparator))

        (if (== __codeSeparator (.. this codeSeparator)) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, __codeSeparator, codePrefixed))
    )

    ;;;
     ; Prefix formatted output by currency code.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn prefixCode) [])
    (§ block
        (if codePrefixed this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, true))
    )

    ;;;
     ; Postfix formatted output with currency code.  This configuration is not relevant for parsing.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn postfixCode) [])
    (§ block
        (if (not codePrefixed) this (§ new #_"MonetaryFormat" negativeSign, positiveSign, zeroDigit, decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, false))
    )

    ;;;
     ; Configure this instance with values from a {@link Locale}.
     ;;
    #_public
    (§ method #_"MonetaryFormat" (§ fn withLocale) [#_"Locale" __locale])
    (§ block
        (§ let #_"DecimalFormatSymbols" __dfs (§ new #_"DecimalFormatSymbols" __locale))
        (§ block
            (§ let #_"char" __negativeSign (.. __dfs (getMinusSign)))
            (§ block
                (§ let #_"char" __zeroDigit (.. __dfs (getZeroDigit)))
                (§ block
                    (§ let #_"char" __decimalMark (.. __dfs (getMonetaryDecimalSeparator)))
                    (§ block
                        (§ new #_"MonetaryFormat" __negativeSign, positiveSign, __zeroDigit, __decimalMark, minDecimals, decimalGroups, shift, roundingMode, codes, codeSeparator, codePrefixed)
                    )
                )
            )
        )
    )

    #_public
    (§ constructor #_"MonetaryFormat" [])
    (§ block
        ;; defaults
        (§ ass (§ name (.. this negativeSign)) \-)
        (§ ass (§ name (.. this positiveSign)) 0) ;; none
        (§ ass (§ name (.. this zeroDigit)) \0)
        (§ ass (§ name (.. this decimalMark)) \.)
        (§ ass (§ name (.. this minDecimals)) 2)
        (§ ass (§ name (.. this decimalGroups)) nil)
        (§ ass (§ name (.. this shift)) 0)
        (§ ass (§ name (.. this roundingMode)) RoundingMode/HALF_UP)
        (§ ass (§ name (.. this codes)) (§ new #_"String[]" (§ count MonetaryFormat/MAX_DECIMALS)))
        (§ ass (§ name this.codes[0]) MonetaryFormat/CODE_BTC)
        (§ ass (§ name this.codes[3]) MonetaryFormat/CODE_MBTC)
        (§ ass (§ name this.codes[6]) MonetaryFormat/CODE_UBTC)
        (§ ass (§ name (.. this codeSeparator)) \space)
        (§ ass (§ name (.. this codePrefixed)) true)
        this
    )

    #_private
    (§ constructor- #_"MonetaryFormat" [#_"char" __negativeSign, #_"char" __positiveSign, #_"char" __zeroDigit, #_"char" __decimalMark, #_"int" __minDecimals, #_"List<Integer>" __decimalGroups, #_"int" __shift, #_"RoundingMode" __roundingMode, #_"String[]" __codes, #_"char" __codeSeparator, #_"boolean" __codePrefixed])
    (§ block
        (§ ass (§ name (.. this negativeSign)) __negativeSign)
        (§ ass (§ name (.. this positiveSign)) __positiveSign)
        (§ ass (§ name (.. this zeroDigit)) __zeroDigit)
        (§ ass (§ name (.. this decimalMark)) __decimalMark)
        (§ ass (§ name (.. this minDecimals)) __minDecimals)
        (§ ass (§ name (.. this decimalGroups)) __decimalGroups)
        (§ ass (§ name (.. this shift)) __shift)
        (§ ass (§ name (.. this roundingMode)) __roundingMode)
        (§ ass (§ name (.. this codes)) __codes)
        (§ ass (§ name (.. this codeSeparator)) __codeSeparator)
        (§ ass (§ name (.. this codePrefixed)) __codePrefixed)
        this
    )

    ;;;
     ; Format the given monetary value to a human readable form.
     ;;
    #_public
    (§ method #_"CharSequence" (§ fn format) [#_"Monetary" __monetary])
    (§ block
        ;; preparation
        (§ let #_"int" __maxDecimals minDecimals)
        (§ block
            (§ if (some? decimalGroups))
            (§ block
                (§ for (§ var #_"int" __group) :for decimalGroups)
                (§ block
                    (§ ass __maxDecimals (+ __maxDecimals __group))
                )
            )
            (§ let #_"int" __smallestUnitExponent (.. __monetary (smallestUnitExponent)))
            (§ block
                (Preconditions/checkState (<= __maxDecimals __smallestUnitExponent), "The maximum possible number of decimals (%s) cannot exceed %s.", __maxDecimals, __smallestUnitExponent)

                ;; rounding
                (§ let #_"long" __satoshis (Math/abs (.. __monetary (getValue))))
                (§ block
                    (§ let #_"long" __precisionDivisor (LongMath/checkedPow 10, (- __smallestUnitExponent shift __maxDecimals)))
                    (§ block
                        (§ ass __satoshis (LongMath/checkedMultiply (LongMath/divide __satoshis, __precisionDivisor, roundingMode), __precisionDivisor))

                        ;; shifting
                        (§ let #_"long" __shiftDivisor (LongMath/checkedPow 10, (- __smallestUnitExponent shift)))
                        (§ block
                            (§ let #_"long" __numbers (/ __satoshis __shiftDivisor))
                            (§ block
                                (§ let #_"long" __decimals (% __satoshis __shiftDivisor))
                                (§ block

                                    ;; formatting
                                    (§ let #_"String" __decimalsStr (String/format Locale/US, (str "%0" (- __smallestUnitExponent shift) "d"), __decimals))
                                    (§ block
                                        (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder" __decimalsStr))
                                        (§ block
                                            (§ while (and (< minDecimals (.. __sb (length))) (== (.. __sb (charAt (- (.. __sb (length)) 1))) \0)))
                                            (§ block
                                                (.. __sb (setLength (- (.. __sb (length)) 1))) ;; trim trailing zero
                                            )
                                            (§ let #_"int" __i minDecimals)
                                            (§ block
                                                (§ if (some? decimalGroups))
                                                (§ block
                                                    (§ for (§ var #_"int" __group) :for decimalGroups)
                                                    (§ block
                                                        (§ if (and (< __i (.. __sb (length))) (< (.. __sb (length)) (+ __i __group))))
                                                        (§ block
                                                            (§ while (< (.. __sb (length)) (+ __i __group)))
                                                            (§ block
                                                                (.. __sb (append "0"))
                                                            )
                                                            (§ break )
                                                        )
                                                        (§ ass __i (+ __i __group))
                                                    )
                                                )
                                                (§ if (< 0 (.. __sb (length))))
                                                (§ block
                                                    (.. __sb (insert 0, decimalMark))
                                                )
                                                (.. __sb (insert 0, __numbers))
                                                (§ if (< (.. __monetary (getValue)) 0))
                                                (§ block
                                                    (.. __sb (insert 0, negativeSign))
                                                )
                                                (§ elseif (!= positiveSign 0))
                                                (§ block
                                                    (.. __sb (insert 0, positiveSign))
                                                )
                                                (§ if (some? codes))
                                                (§ block
                                                    (§ if codePrefixed)
                                                    (§ block
                                                        (.. __sb (insert 0, codeSeparator) (insert 0, (.. this (code))))
                                                    )
                                                    (§ else )
                                                    (§ block
                                                        (.. __sb (append codeSeparator) (append (.. this (code))))
                                                    )
                                                )

                                                ;; Convert to non-arabic digits.
                                                (§ if (!= zeroDigit \0))
                                                (§ block
                                                    (§ let #_"int" __offset (- zeroDigit \0))
                                                    (§ block
                                                        (§ for (§ var #_"int" __d 0) :for (< __d (.. __sb (length))) :for (§ recur (inc __d)))
                                                        (§ block
                                                            (§ let #_"char" __c (.. __sb (charAt __d)))
                                                            (§ block
                                                                (§ if (Character/isDigit __c))
                                                                (§ block
                                                                    (.. __sb (setCharAt __d, (§ cast #_"char" (+ __c __offset))))
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                                __sb
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Parse a human readable coin value to a {@link org.bitcoinj.core.Coin} instance.
     ;
     ; @throws NumberFormatException if the string cannot be parsed for some reason.
     ;;
    #_public
    (§ method #_"Coin" (§ fn parse) [#_"String" __str])
        (§ throws #_"NumberFormatException")
    (§ block
        (Coin/valueOf (.. this (parseValue __str, Coin/SMALLEST_UNIT_EXPONENT)))
    )

    ;;;
     ; Parse a human readable fiat value to a {@link org.bitcoinj.utils.Fiat} instance.
     ;
     ; @throws NumberFormatException if the string cannot be parsed for some reason.
     ;;
    #_public
    (§ method #_"Fiat" (§ fn parseFiat) [#_"String" __currencyCode, #_"String" __str])
        (§ throws #_"NumberFormatException")
    (§ block
        (Fiat/valueOf __currencyCode, (.. this (parseValue __str, Fiat/SMALLEST_UNIT_EXPONENT)))
    )

    #_private
    (§ method- #_"long" (§ fn parseValue) [#_"String" __str, #_"int" __smallestUnitExponent])
    (§ block
        (Preconditions/checkState (<= __smallestUnitExponent (.. MonetaryFormat/DECIMALS_PADDING (length))))

        (§ if (.. __str (isEmpty)))
        (§ block
            (§ throw (§ new #_"NumberFormatException" "empty string"))
        )

        (§ let #_"char" __first (.. __str (charAt 0)))
        (§ block
            (§ if (or (== __first negativeSign) (== __first positiveSign)))
            (§ block
                (§ ass __str (.. __str (substring 1)))
            )
            (§ let #_"String" __numbers)
            (§ block
                (§ let #_"String" __decimals)
                (§ block
                    (§ let #_"int" __decimalMarkIndex (.. __str (indexOf decimalMark)))
                    (§ block
                        (§ if (!= __decimalMarkIndex -1))
                        (§ block
                            (§ ass __numbers (.. __str (substring 0, __decimalMarkIndex)))
                            (§ ass __decimals (.. (str __str MonetaryFormat/DECIMALS_PADDING) (substring (inc __decimalMarkIndex))))
                            (§ if (!= (.. __decimals (indexOf decimalMark)) -1))
                            (§ block
                                (§ throw (§ new #_"NumberFormatException" "more than one decimal mark"))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass __numbers __str)
                            (§ ass __decimals MonetaryFormat/DECIMALS_PADDING)
                        )

                        (§ let #_"String" __satoshis (+ __numbers (.. __decimals (substring 0, (- __smallestUnitExponent shift)))))
                        (§ block
                            (§ for (§ var #_"char" __c) :for (.. __satoshis (toCharArray)))
                            (§ block
                                (§ if (not (Character/isDigit __c)))
                                (§ block
                                    (§ throw (§ new #_"NumberFormatException" (str "illegal character: " __c)))
                                )
                            )

                            ;; Non-arabic digits allowed here.
                            (§ let #_"long" __value (Long/parseLong __satoshis))
                            (§ block
                                (§ if (== __first negativeSign))
                                (§ block
                                    (§ ass __value (- __value))
                                )
                                __value
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Get currency code that will be used for current shift.
     ;;
    #_public
    (§ method #_"String" (§ fn code) [])
    (§ block
        (§ if (nil? codes))
        (§ block
            (§ return nil)
        )
        (§ if (nil? (§ ai codes shift)))
        (§ block
            (§ throw (§ new #_"NumberFormatException" (str "missing code for shift: " shift)))
        )
        (§ ai codes shift)
    )
)

#_(ns org.bitcoinj.utils #_"Threading"
    (:import [java.util.concurrent *]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.util.concurrent CycleDetectingLockFactory ListeningExecutorService MoreExecutors Uninterruptibles]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]))

;;;
 ; Various threading related utilities.  Provides a wrapper around explicit lock creation that lets you control whether
 ; bitcoinj performs cycle detection or not.  Cycle detection is useful to detect bugs but comes with a small cost.
 ; Also provides a worker thread that is designed for event listeners to be dispatched on.
 ;;
#_public
(§ class #_"Threading"
    ;;;
     ; An executor with one thread that is intended for running event listeners on.  This ensures all event listener
     ; code runs without any locks being held.  It's intended for the API user to run things on.  Callbacks registered
     ; by bitcoinj internally shouldn't normally run here, although currently there are a few exceptions.
     ;;
    #_public
    #_static
    (§ def #_"Executor" (§ name Threading/USER_THREAD))

    ;;;
     ; A dummy executor that just invokes the runnable immediately.  Use this over
     ; {@link com.google.common.util.concurrent.MoreExecutors#sameThreadExecutor()} because the latter creates
     ; a new object each time in order to implement the more complex {@link ExecutorService} interface, which is
     ; overkill for our needs.
     ;;
    #_public
    #_static
    (§ def #_"Executor" (§ name Threading/SAME_THREAD))

    ;;;
     ; Put a dummy task into the queue and wait for it to be run.  Because it's single threaded, this means all
     ; tasks submitted before this point are now completed.  Usually you won't want to use this method - it's a
     ; convenience primarily used in unit testing.  If you want to wait for an event to be called the right thing
     ; to do is usually to create a {@link com.google.common.util.concurrent.SettableFuture} and then call set
     ; on it.  You can then either block on that future, compose it, add listeners to it and so on.
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn Threading/waitForUserCode) [])
    (§ block
        (§ let #_"CountDownLatch" __latch (§ new #_"CountDownLatch" 1))
        (§ block
            (.. Threading/USER_THREAD (execute (§ new #_"Runnable")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (.. __latch (countDown))
                    nil
                )
            )))
            (Uninterruptibles/awaitUninterruptibly __latch)
            nil
        )
    )

    ;;;
     ; An exception handler that will be invoked for any exceptions that occur in the user thread, and any unhandled
     ; exceptions that are caught whilst the framework is processing network traffic or doing other background tasks.
     ; The purpose of this is to allow you to report back unanticipated crashes from your users to a central collection
     ; center for analysis and debugging.  You should configure this <b>before</b> any bitcoinj library code is run,
     ; setting it after you started network traffic and other forms of processing may result in the change not taking effect.
     ;;
    #_nilable
    #_public
    #_static
    #_volatile
    (§ def #_"Thread.UncaughtExceptionHandler" (§ name Threading/UNCAUGHT_EXCEPTION_HANDLER))

    #_public
    #_static
    (§ class #_"UserThread" (§ extends #_"Thread") (§ implements #_"Executor")
        #_private
        #_static
        (§ def- #_"Logger" (§ name Threading/UserThread/log) (LoggerFactory/getLogger (§ klass #_"UserThread")))

        ;; 10,000 pending tasks is entirely arbitrary and may or may not be appropriate for the device we're running on.
        #_public
        #_static
        (§ def #_"int" (§ name Threading/UserThread/WARNING_THRESHOLD) 10000)

        #_private
        (§ field- #_"LinkedBlockingQueue<Runnable>" (§ name tasks))

        #_public
        (§ constructor #_"UserThread" [])
        (§ block
            (§ super "bitcoinj user thread")

            (setDaemon true)
            (§ ass (§ name tasks) (§ new #_"LinkedBlockingQueue<>"))
            (.. this (start))
            this
        )

        #_suppress(§ opt "InfiniteLoopStatement")
        #_override
        #_public
        (§ method #_"void" (§ fn run) [])
        (§ block
            (§ while true)
            (§ block
                (§ let #_"Runnable" __task (Uninterruptibles/takeUninterruptibly tasks))
                (§ block
                    (§ try )
                    (§ block
                        (.. __task (run))
                    )
                    (§ catch #_"Throwable" __t)
                    (§ block
                        (.. UserThread/log (warn "Exception in user thread", __t))
                        (§ let #_"Thread.UncaughtExceptionHandler" __handler Threading/UNCAUGHT_EXCEPTION_HANDLER)
                        (§ block
                            (§ if (some? __handler))
                            (§ block
                                (.. __handler (uncaughtException this, __t))
                            )
                        )
                    )
                )
            )
            nil
        )

        #_override
        #_public
        (§ method #_"void" (§ fn execute) [#_"Runnable" __command])
        (§ block
            (§ let #_"int" __size (.. tasks (size)))
            (§ block
                (§ if (== __size UserThread/WARNING_THRESHOLD))
                (§ block
                    (.. UserThread/log (warn (str "User thread has {} pending tasks, memory exhaustion may occur.\n" "If you see this message, check your memory consumption and see if it's problematic or excessively spikey.\n" "If it is, check for deadlocked or slow event handlers. If it isn't, try adjusting the constant \n" "Threading.UserThread.WARNING_THRESHOLD upwards until it's a suitable level for your app, or Integer.MAX_VALUE to disable."), __size))
                )
                (Uninterruptibles/putUninterruptibly tasks, __command)
                nil
            )
        )
    )

    #_static
    (§ block
        ;; Default policy goes here.  If you want to change this, use one of the static methods before instantiating
        ;; any bitcoinj objects.  The policy change will take effect only on new objects from that point onwards.
        (Threading/throwOnLockCycles)

        (§ ass (§ name Threading/USER_THREAD) (§ new #_"UserThread"))
        (§ ass (§ name Threading/SAME_THREAD) (§ new #_"Executor"
        (§ anon
            #_override
            #_public
            (§ method #_"void" (§ fn execute) [#_non-nil #_"Runnable" __runnable])
            (§ block
                (.. __runnable (run))
                nil
            )
        )))
    )

    #_private
    #_static
    (§ def- #_"CycleDetectingLockFactory.Policy" (§ name Threading/POLICY))
    #_public
    #_static
    (§ def #_"CycleDetectingLockFactory" (§ name Threading/FACTORY))

    #_public
    #_static
    (§ defn #_"ReentrantLock" (§ fn Threading/lock) [#_"String" __name])
    (§ block
        (if (Utils/isAndroidRuntime) (§ new #_"ReentrantLock" true) (.. Threading/FACTORY (newReentrantLock __name)))
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn Threading/warnOnLockCycles) [])
    (§ block
        (Threading/setPolicy CycleDetectingLockFactory/Policies/WARN)
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn Threading/throwOnLockCycles) [])
    (§ block
