#_(ns org.bitcoinj.wallet #_"BasicKeyChain"
    (:import [java.util *]
             [java.util.concurrent CopyOnWriteArrayList Executor]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect ImmutableList Lists]
             [com.google.protobuf ByteString]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core BloomFilter ECKey]
             [org.bitcoinj.crypto *]
             [org.bitcoinj.utils ListenerRegistration Threading]
             [org.bitcoinj.wallet.listeners KeyChainEventListener]))

;;;
 ; A {@link KeyChain} that implements the simplest model possible: it can have keys imported into it, and just
 ; acts as a dumb bag of keys.  It will, left to its own devices, always return the same key for usage by the wallet,
 ; although it will automatically add one to itself if it's empty or if encryption is requested.
 ;;
#_public
(§ class #_"BasicKeyChain" (§ implements #_"EncryptableKeyChain")
    #_private
    (§ field- #_"ReentrantLock" (§ name lock) (Threading/lock "BasicKeyChain"))

    ;; Maps used to let us quickly look up a key given data we find in transcations or the block chain.
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" (§ name hashToKeys))
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" (§ name pubkeyToKeys))
    #_nilable
    #_private
    (§ field- #_"KeyCrypter" (§ name keyCrypter))
    #_private
    (§ field- #_"boolean" (§ name isWatching))

    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<KeyChainEventListener>>" (§ name listeners))

    #_public
    (§ constructor #_"BasicKeyChain" [])
    (§ block
        (§ this nil)
        this
    )

    #_public
    (§ constructor #_"BasicKeyChain" [#_nilable #_"KeyCrypter" __crypter])
    (§ block
        (§ ass (§ name (.. this keyCrypter)) __crypter)
        (§ ass (§ name hashToKeys) (§ new #_"LinkedHashMap<>"))
        (§ ass (§ name pubkeyToKeys) (§ new #_"LinkedHashMap<>"))
        (§ ass (§ name listeners) (§ new #_"CopyOnWriteArrayList<>"))
        this
    )

    ;;; Returns the {@link KeyCrypter} in use or null if the key chain is not encrypted. ;;
    #_override
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return keyCrypter)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"ECKey" (§ fn getKey) [#_nilable #_"KeyPurpose" __ignored])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (.. hashToKeys (isEmpty)))
            (§ block
                ;; We will refuse to encrypt an empty key chain.
                (Preconditions/checkState (nil? keyCrypter))

                (§ let #_"ECKey" __key (§ new #_"ECKey"))
                (§ block
                    (.. this (importKeyLocked __key))
                    (.. this (queueOnKeysAdded (ImmutableList/of __key)))
                )
            )
            (§ return (.. hashToKeys (values) (iterator) (next)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"List<ECKey>" (§ fn getKeys) [#_nilable #_"KeyPurpose" __purpose, #_"int" __numberOfKeys])
    (§ block
        (Preconditions/checkArgument (< 0 __numberOfKeys))

        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (< (.. hashToKeys (size)) __numberOfKeys))
            (§ block
                (Preconditions/checkState (nil? keyCrypter))

                (§ let #_"List<ECKey>" __keys (§ new #_"ArrayList<>"))
                (§ block
                    (§ for (§ var #_"int" __i 0) :for (< __i (- __numberOfKeys (.. hashToKeys (size)))) :for (§ recur (inc __i)))
                    (§ block
                        (.. __keys (add (§ new #_"ECKey")))
                    )

                    (§ let #_"ImmutableList<ECKey>" __immutableKeys (ImmutableList/copyOf __keys))
                    (§ block
                        (.. this (importKeysLocked __immutableKeys))
                        (.. this (queueOnKeysAdded __immutableKeys))
                    )
                )
            )

            (§ let #_"List<ECKey>" __keysToReturn (§ new #_"ArrayList<>"))
            (§ block
                (§ let #_"int" __count 0)
                (§ block
                    (§ while (and (.. hashToKeys (values) (iterator) (hasNext)) (!= __numberOfKeys __count)))
                    (§ block
                        (.. __keysToReturn (add (.. hashToKeys (values) (iterator) (next))))
                        (§ ass __count (inc __count))
                    )
                    (§ return __keysToReturn)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;; Returns a copy of the list of keys that this chain is managing. ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getKeys) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (§ new #_"ArrayList<>" (.. hashToKeys (values))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"int" (§ fn importKeys) [#_"ECKey..." __keys])
    (§ block
        (.. this (importKeys (ImmutableList/copyOf __keys)))
    )

    #_public
    (§ method #_"int" (§ fn importKeys) [#_"List<? extends ECKey>" __keys])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; Check that if we're encrypted, the keys are all encrypted, and if we're not, that none are.
            ;; We are NOT checking that the actual password matches here because we don't have access to the
            ;; password at this point: if you screw up and import keys with mismatched passwords, you lose!
            ;; So make sure the password is checked first.
            (§ for (§ var #_"ECKey" __key) :for __keys)
            (§ block
                (.. this (checkKeyEncryptionStateMatches __key))
            )

            (§ let #_"List<ECKey>" __actuallyAdded (§ new #_"ArrayList<>" (.. __keys (size))))
            (§ block
                (§ for (§ var ECKey key) :for __keys)
                (§ block
                    (§ if (not (.. this (hasKey key))))
                    (§ block
                        (.. __actuallyAdded (add key))
                        (.. this (importKeyLocked key))
                    )
                )
                (§ if (< 0 (.. __actuallyAdded (size))))
                (§ block
                    (.. this (queueOnKeysAdded __actuallyAdded))
                )
                (§ return (.. __actuallyAdded (size)))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkKeyEncryptionStateMatches) [#_"ECKey" __key])
    (§ block
        (§ if (and (nil? keyCrypter) (.. __key (isEncrypted))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key is encrypted but chain is not"))
        )
        (§ if (and (some? keyCrypter) (not (.. __key (isEncrypted)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key is not encrypted but chain is"))
        )
        (§ if (and (some? keyCrypter) (some? (.. __key (getKeyCrypter))) (not (.. __key (getKeyCrypter) (equals keyCrypter)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Key encrypted under different parameters to chain"))
        )
        nil
    )

    #_private
    (§ method- #_"void" (§ fn importKeyLocked) [#_"ECKey" __key])
    (§ block
        (§ if (.. hashToKeys (isEmpty)))
        (§ block
            (§ ass (§ name isWatching) (.. __key (isWatching)))
        )
        (§ else )
        (§ block
            (§ if (and (.. __key (isWatching)) (not isWatching)))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" "Key is watching but chain is not"))
            )
            (§ if (and (not (.. __key (isWatching))) isWatching))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" "Key is not watching but chain is"))
            )
        )
        (§ let #_"ECKey" __previousKey (.. pubkeyToKeys (put (ByteString/copyFrom (.. __key (getPubKey))), __key)))
        (§ block
            (.. hashToKeys (put (ByteString/copyFrom (.. __key (getPubKeyHash))), __key))
            (Preconditions/checkState (nil? __previousKey))
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn importKeysLocked) [#_"List<ECKey>" __keys])
    (§ block
        (§ for (§ var #_"ECKey" __key) :for __keys)
        (§ block
            (.. this (importKeyLocked __key))
        )
        nil
    )

    ;;;
     ; Imports a key to the key chain.  If key is present in the key chain, ignore it.
     ;;
    #_public
    (§ method #_"void" (§ fn importKey) [#_"ECKey" __key])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (.. this (checkKeyEncryptionStateMatches __key))
            (§ if (.. this (hasKey __key)))
            (§ block
                (§ return nil)
            )

            (.. this (importKeyLocked __key))
            (.. this (queueOnKeysAdded (ImmutableList/of __key)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" __pubkeyHash])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (.. hashToKeys (get (ByteString/copyFrom __pubkeyHash))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubKey) [#_"byte[]" __pubkey])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (.. pubkeyToKeys (get (ByteString/copyFrom __pubkey))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn hasKey) [#_"ECKey" __key])
    (§ block
        (!= (.. this (findKeyFromPubKey (.. __key (getPubKey)))) nil)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn numKeys) [])
    (§ block
        (.. pubkeyToKeys (size))
    )

    ;;; Whether this basic key chain is empty, full of regular (usable for signing) keys, or full of watching keys. ;;
    #_public
    (§ enum #_"State"
        (§ item EMPTY)
        (§ item WATCHING)
        (§ item REGULAR)
    )

    ;;;
     ; Returns whether this chain consists of pubkey only (watching) keys, regular keys (usable for signing),
     ; or has no keys in it yet at all (thus we cannot tell).
     ;;
    #_public
    (§ method #_"State" (§ fn isWatching) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (if (.. hashToKeys (isEmpty)) State/EMPTY (if isWatching State/WATCHING State/REGULAR)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Removes the given key from the keychain.  Be very careful with this - losing a private key
     ; <b>destroys the money associated with it</b>.
     ; @return whether the key was removed or not.
     ;;
    #_public
    (§ method #_"boolean" (§ fn removeKey) [#_"ECKey" __key])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" __a (!= (.. hashToKeys (remove (ByteString/copyFrom (.. __key (getPubKeyHash))))) nil))
            (§ block
                (§ let #_"boolean" __b (!= (.. pubkeyToKeys (remove (ByteString/copyFrom (.. __key (getPubKey))))) nil))
                (§ block
                    (Preconditions/checkState (== __a __b)) ;; Should be in both maps or neither.
                    (§ return __a)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"long" (§ fn getEarliestKeyCreationTime) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"long" __time Long/MAX_VALUE)
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (§ ass __time (Math/min (.. __key (getCreationTimeSeconds)), __time))
                )
                (§ return __time)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_public
    (§ method #_"List<ListenerRegistration<KeyChainEventListener>>" (§ fn getListeners) [])
    (§ block
        (§ new #_"ArrayList<>" listeners)
    )

    (§ method #_"Map<ECKey, Protos.Key.Builder>" (§ fn serializeToEditableProtobufs) [])
    (§ block
        (§ let #_"Map<ECKey, Protos.Key.Builder>" __result (§ new #_"LinkedHashMap<>"))
        (§ block
            (§ for (§ var #_"ECKey" __ecKey) :for (.. hashToKeys (values)))
            (§ block
                (§ let #_"Protos.Key.Builder" __protoKey (BasicKeyChain/serializeEncryptableItem __ecKey))
                (§ block
                    (.. __protoKey (setPublicKey (ByteString/copyFrom (.. __ecKey (getPubKey)))))
                    (.. __result (put __ecKey, __protoKey))
                )
            )
            __result
        )
    )

    #_override
    #_public
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])
    (§ block
        (§ let #_"Collection<Protos.Key.Builder>" __builders (.. this (serializeToEditableProtobufs) (values)))
        (§ block
            (§ let #_"List<Protos.Key>" __result (§ new #_"ArrayList<>" (.. __builders (size))))
            (§ block
                (§ for (§ var #_"Protos.Key.Builder" __builder) :for __builders)
                (§ block
                    (.. __result (add (.. __builder (build))))
                )
                __result
            )
        )
    )

    #_static
    (§ defn #_"Protos.Key.Builder" (§ fn BasicKeyChain/serializeEncryptableItem) [#_"EncryptableItem" __item])
    (§ block
        (§ let #_"Protos.Key.Builder" __proto (.. Protos/Key (newBuilder)))
        (§ block
            (.. __proto (setCreationTimestamp (* (.. __item (getCreationTimeSeconds)) 1000)))
            (§ if (and (.. __item (isEncrypted)) (some? (.. __item (getEncryptedData)))))
            (§ block
                ;; The encrypted data can be missing for an "encrypted" key in the case of a deterministic wallet
                ;; for which the leaf keys chain to an encrypted parent and rederive their private keys on the fly.
                ;; In that case the caller in DeterministicKeyChain will take care of setting the type.
                (§ let #_"EncryptedData" __data (.. __item (getEncryptedData)))
                (§ block
                    (.. __proto (getEncryptedDataBuilder) (setEncryptedPrivateKey (ByteString/copyFrom (.. __data encryptedBytes))) (setInitialisationVector (ByteString/copyFrom (.. __data initialisationVector))))
                    ;; We don't allow mixing of encryption types at the moment.
                    (Preconditions/checkState (== (.. __item (getEncryptionType)) Protos/Wallet/EncryptionType/ENCRYPTED_SCRYPT_AES))
                    (.. __proto (setType Protos/Key/Type/ENCRYPTED_SCRYPT_AES))
                )
            )
            (§ else )
            (§ block
                (§ let #_"byte[]" __secret (.. __item (getSecretBytes)))
                (§ block
                    ;; The secret might be missing in the case of a watching wallet, or a key for which the private key
                    ;; is expected to be rederived on the fly from its parent.
                    (§ if (some? __secret))
                    (§ block
                        (.. __proto (setSecretBytes (ByteString/copyFrom __secret)))
                    )
                    (.. __proto (setType Protos/Key/Type/ORIGINAL))
                )
            )
            __proto
        )
    )

    ;;;
     ; Returns a new BasicKeyChain that contains all basic, ORIGINAL type keys extracted from the list.
     ; Unrecognised key types are ignored.
     ;;
    #_public
    #_static
    (§ defn #_"BasicKeyChain" (§ fn BasicKeyChain/fromProtobufUnencrypted) [#_"List<Protos.Key>" __keys])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"BasicKeyChain" __chain (§ new #_"BasicKeyChain"))
        (§ block
            (.. __chain (deserializeFromProtobuf __keys))
            __chain
        )
    )

    ;;;
     ; Returns a new BasicKeyChain that contains all basic, ORIGINAL type keys and also any encrypted keys
     ; extracted from the list.  Unrecognised key types are ignored.
     ; @throws org.bitcoinj.wallet.UnreadableWalletException.BadPassword if the password doesn't seem to match.
     ; @throws org.bitcoinj.wallet.UnreadableWalletException if the data structures are corrupted/inconsistent.
     ;;
    #_public
    #_static
    (§ defn #_"BasicKeyChain" (§ fn BasicKeyChain/fromProtobufEncrypted) [#_"List<Protos.Key>" __keys, #_"KeyCrypter" __crypter])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"BasicKeyChain" __chain (§ new #_"BasicKeyChain" (Preconditions/checkNotNull __crypter)))
        (§ block
            (.. __chain (deserializeFromProtobuf __keys))
            __chain
        )
    )

    #_private
    (§ method- #_"void" (§ fn deserializeFromProtobuf) [#_"List<Protos.Key>" __keys])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (.. hashToKeys (isEmpty)), "Tried to deserialize into a non-empty chain")
            (§ for (§ var #_"Protos.Key" __key) :for __keys)
            (§ block
                (§ if (and (!= (.. __key (getType)) Protos/Key/Type/ORIGINAL) (!= (.. __key (getType)) Protos/Key/Type/ENCRYPTED_SCRYPT_AES)))
                (§ block
                    (§ continue )
                )

                (§ let #_"boolean" __encrypted (== (.. __key (getType)) Protos/Key/Type/ENCRYPTED_SCRYPT_AES))
                (§ block
                    (§ let #_"byte[]" __priv (when (.. __key (hasSecretBytes)) (.. __key (getSecretBytes) (toByteArray))))
                    (§ block
                        (§ if (not (.. __key (hasPublicKey))))
                        (§ block
                            (§ throw (§ new #_"UnreadableWalletException" "Public key missing"))
                        )

                        (§ let #_"byte[]" __pub (.. __key (getPublicKey) (toByteArray)))
                        (§ block
                            (§ let #_"ECKey" __ecKey)
                            (§ block
                                (§ if __encrypted)
                                (§ block
                                    (Preconditions/checkState (some? keyCrypter), "This wallet is encrypted but encrypt() was not called prior to deserialization")
                                    (§ if (not (.. __key (hasEncryptedData))))
                                    (§ block
                                        (§ throw (§ new #_"UnreadableWalletException" "Encrypted private key data missing"))
                                    )

                                    (§ let #_"Protos.EncryptedData" __proto (.. __key (getEncryptedData)))
                                    (§ block
                                        (§ let #_"EncryptedData" __e (§ new #_"EncryptedData" (.. __proto (getInitialisationVector) (toByteArray)), (.. __proto (getEncryptedPrivateKey) (toByteArray))))
                                        (§ block
                                            (§ ass __ecKey (ECKey/fromEncrypted __e, keyCrypter, __pub))
                                        )
                                    )
                                )
                                (§ else )
                                (§ block
                                    (§ ass __ecKey (if (some? __priv) (ECKey/fromPrivateAndPrecalculatedPublic __priv, __pub) (ECKey/fromPublicOnly __pub)))
                                )
                                (.. __ecKey (setCreationTimeSeconds (/ (.. __key (getCreationTimestamp)) 1000)))
                                (.. this (importKeyLocked __ecKey))
                            )
