    ;;;
     ; Convenience wrapper around {@link #toEncrypted(org.bitcoinj.crypto.KeyCrypter, org.spongycastle.crypto.params.KeyParameter)}
     ; which uses the default Scrypt key derivation algorithm and parameters, derives a key from the given password and returns
     ; the created key.
     ;;
    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toEncrypted) [#_"CharSequence" __password])
    (§ block
        (Preconditions/checkNotNull __password)
        (Preconditions/checkArgument (< 0 (.. __password (length))))

        (§ let #_"KeyCrypter" __scrypt (§ new #_"KeyCrypterScrypt"))
        (§ block
            (§ let #_"KeyParameter" __derivedKey (.. __scrypt (deriveKey __password)))
            (§ block
                (.. this (toEncrypted __scrypt, __derivedKey))
            )
        )
    )

    ;;;
     ; Encrypt the wallet using the KeyCrypter and the AES key.  A good default KeyCrypter to use is
     ; {@link org.bitcoinj.crypto.KeyCrypterScrypt}.
     ;
     ; @param keyCrypter The KeyCrypter that specifies how to encrypt/ decrypt a key.
     ; @param aesKey AES key to use (normally created using KeyCrypter#deriveKey and cached as it is time consuming
     ;               to create from a password).
     ; @throws KeyCrypterException if the wallet encryption fails.  If so, the wallet state is unchanged.
     ;;
    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toEncrypted) [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkNotNull __keyCrypter)
            (Preconditions/checkState (nil? (.. this keyCrypter)), "Key chain is already encrypted")

            (§ let #_"BasicKeyChain" __encrypted (§ new #_"BasicKeyChain" __keyCrypter))
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"ECKey" __encryptedKey (.. __key (encrypt __keyCrypter, __aesKey)))
                    (§ block
                        ;; Check that the encrypted key can be successfully decrypted.
                        ;; This is done as it is a critical failure if the private key cannot be decrypted successfully
                        ;; (all bitcoin controlled by that private key is lost forever).
                        ;; For a correctly constructed keyCrypter the encryption should always be reversible so it is just
                        ;; being as cautious as possible.
                        (§ if (not (ECKey/encryptionIsReversible __key, __encryptedKey, __keyCrypter, __aesKey)))
                        (§ block
                            (§ throw (§ new #_"KeyCrypterException" (str "The key " __key " cannot be successfully decrypted after encryption so aborting wallet encryption.")))
                        )
                        (.. __encrypted (importKeyLocked __encryptedKey))
                    )
                )
                (§ return __encrypted)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toDecrypted) [#_"CharSequence" __password])
    (§ block
        (Preconditions/checkNotNull keyCrypter, "Wallet is already decrypted")

        (.. this (toDecrypted (.. keyCrypter (deriveKey __password))))
    )

    #_override
    #_public
    (§ method #_"BasicKeyChain" (§ fn toDecrypted) [#_"KeyParameter" __aesKey])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (some? keyCrypter), "Wallet is already decrypted")

            ;; Do an up-front check.
            (§ if (and (< 0 (.. this (numKeys))) (not (.. this (checkAESKey __aesKey)))))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "Password/key was incorrect."))
            )

            (§ let #_"BasicKeyChain" __decrypted (§ new #_"BasicKeyChain"))
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (.. __decrypted (importKeyLocked (.. __key (decrypt __aesKey))))
                )
                (§ return __decrypted)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns whether the given password is correct for this key chain.
     ; @throws IllegalStateException if the chain is not encrypted at all.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn checkPassword) [#_"CharSequence" __password])
    (§ block
        (Preconditions/checkNotNull __password)
        (Preconditions/checkState (some? keyCrypter), "Key chain not encrypted")

        (.. this (checkAESKey (.. keyCrypter (deriveKey __password))))
    )

    ;;;
     ; Check whether the AES key can decrypt the first encrypted key in the wallet.
     ;
     ; @return true if AES key supplied can decrypt the first encrypted private key in the wallet, false otherwise.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" __aesKey])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            ;; If no keys then cannot decrypt.
            (§ if (.. hashToKeys (isEmpty)))
            (§ block
                (§ return false)
            )

            (Preconditions/checkState (some? keyCrypter), "Key chain is not encrypted")

            ;; Find the first encrypted key in the wallet.
            (§ let #_"ECKey" __first nil)
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (§ if (.. __key (isEncrypted)))
                    (§ block
                        (§ ass __first __key)
                        (§ break )
                    )
                )
                (Preconditions/checkState (some? __first), "No encrypted keys in the wallet")

                (§ try )
                (§ block
                    (§ let #_"ECKey" __rebornKey (.. __first (decrypt __aesKey)))
                    (§ block
                        (§ return (Arrays/equals (.. __first (getPubKey)), (.. __rebornKey (getPubKey))))
                    )
                )
                (§ catch #_"KeyCrypterException" (§ name _))
                (§ block
                    ;; The AES key supplied is incorrect.
                    (§ return false)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"BloomFilter" (§ fn getFilter) [#_"int" __size, #_"double" __falsePositiveRate, #_"long" __tweak])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"BloomFilter" __filter (§ new #_"BloomFilter" __size, __falsePositiveRate, __tweak))
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (.. __filter (insert __key))
                )
                (§ return __filter)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn numBloomFilterEntries) [])
    (§ block
        (* (.. this (numKeys)) 2)
    )

    ;;; Returns the first ECKey created after the given UNIX time, or null if there is none. ;;
    #_nilable
    #_public
    (§ method #_"ECKey" (§ fn findOldestKeyAfter) [#_"long" __timeSecs])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"ECKey" __oldest nil)
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"long" __keyTime (.. __key (getCreationTimeSeconds)))
                    (§ block
                        (§ if (and (< __timeSecs __keyTime) (or (nil? __oldest) (< __keyTime (.. __oldest (getCreationTimeSeconds))))))
                        (§ block
                            (§ ass __oldest __key)
                        )
                    )
                )
                (§ return __oldest)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;; Returns a list of all ECKeys created after the given UNIX time. ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn findKeysBefore) [#_"long" __timeSecs])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"List<ECKey>" __results (Lists/newLinkedList))
            (§ block
                (§ for (§ var #_"ECKey" __key) :for (.. hashToKeys (values)))
                (§ block
                    (§ let #_"long" __keyTime (.. __key (getCreationTimeSeconds)))
                    (§ block
                        (§ if (< __keyTime __timeSecs))
                        (§ block
                            (.. __results (add __key))
                        )
                    )
                )
                (§ return __results)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )
)

