#_public
(§ class #_"DecryptingKeyBag" (§ implements #_"KeyBag")
    #_protected
    (§ field #_"KeyBag" (§ name target))
    #_protected
    (§ field #_"KeyParameter" (§ name aesKey))

    #_public
    (§ constructor #_"DecryptingKeyBag" [#_"KeyBag" __target, #_nilable #_"KeyParameter" __aesKey])
    (§ block
        (§ ass (§ name (.. this target)) (Preconditions/checkNotNull __target))
        (§ ass (§ name (.. this aesKey)) __aesKey)
        this
    )

    #_nilable
    #_private
    (§ method- #_"ECKey" (§ fn maybeDecrypt) [#_"ECKey" __key])
    (§ block
        (§ if (nil? __key))
        (§ block
            (§ return nil)
        )

        (§ if (.. __key (isEncrypted)))
        (§ block
            (§ if (nil? aesKey))
            (§ block
                (§ throw (§ new #_"ECKey.KeyIsEncryptedException"))
            )
            (§ return (.. __key (decrypt aesKey)))
        )

        __key
    )

    #_private
    (§ method- #_"RedeemData" (§ fn maybeDecrypt) [#_"RedeemData" __redeemData])
