            (§ block
                (.. __decryptedKeys (add (.. this (maybeDecrypt __key))))
            )
            (RedeemData/of __decryptedKeys, (.. __redeemData redeemScript))
        )
    )

    #_nilable
    #_override
    #_public
    (§ method #_"ECKey" (§ fn findKeyFromPubHash) [#_"byte[]" __pubkeyHash])
    (§ block
        (.. this (maybeDecrypt (.. target (findKeyFromPubHash __pubkeyHash))))
    )

