     ; keychain follows some other keychain.  In a married wallet following keychain represents "spouse's" keychain.</p>
     ; <p>Watch key has to be an account key.</p>
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicKey" __watchKey, #_"boolean" __isFollowing])
    (§ block
        (§ this __watchKey)
        (§ ass (§ name (.. this isFollowing)) __isFollowing)
        this
    )

