    ;;;
     ; For use in {@link KeyChainFactory} during deserialization.
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"DeterministicSeed" __seed, #_nilable #_"KeyCrypter" __crypter])
    (§ block
        (§ ass (§ name (.. this seed)) __seed)
        (§ ass (§ name basicKeyChain) (§ new #_"BasicKeyChain" __crypter))
        (§ if (not (.. __seed (isEncrypted))))
        (§ block
            (§ ass (§ name rootKey) (HDKeyDerivation/createMasterPrivateKey (Preconditions/checkNotNull (.. __seed (getSeedBytes)))))
            (.. rootKey (setCreationTimeSeconds (.. __seed (getCreationTimeSeconds))))
            (.. basicKeyChain (importKey rootKey))
            (§ ass (§ name hierarchy) (§ new #_"DeterministicHierarchy" rootKey))
            (§ for (§ var #_"int" __i 1) :for (<= __i (.. this (getAccountPath) (size))) :for (§ recur (inc __i)))
            (§ block
                (.. basicKeyChain (importKey (.. hierarchy (get (.. this (getAccountPath) (subList 0, __i)), false, true))))
            )
            (.. this (initializeHierarchyUnencrypted rootKey))
        )
        ;; Else...
        ;; We can't initialize ourselves with just an encrypted seed, so we expected deserialization code
        ;; to do the rest of the setup (loading the root key).
        this
    )

    ;;;
     ; For use in encryption when {@link #toEncrypted(KeyCrypter, KeyParameter)} is called,
     ; so that subclasses can override that method and create an instance of the right class.
     ;
     ; See also {@link #makeKeyChainFromSeed(DeterministicSeed)}.
     ;;
    #_protected
    (§ constructor #_"DeterministicKeyChain" [#_"KeyCrypter" __crypter, #_"KeyParameter" __aesKey, #_"DeterministicKeyChain" __chain])
    (§ block
        ;; Can't encrypt a watching chain.
        (Preconditions/checkNotNull (.. __chain rootKey))
        (Preconditions/checkNotNull (.. __chain seed))

        (Preconditions/checkArgument (not (.. __chain rootKey (isEncrypted))), "Chain already encrypted")

        (§ ass (§ name (.. this issuedExternalKeys)) (.. __chain issuedExternalKeys))
        (§ ass (§ name (.. this issuedInternalKeys)) (.. __chain issuedInternalKeys))

        (§ ass (§ name (.. this lookaheadSize)) (.. __chain lookaheadSize))
        (§ ass (§ name (.. this lookaheadThreshold)) (.. __chain lookaheadThreshold))

        (§ ass (§ name (.. this seed)) (.. __chain seed (encrypt __crypter, __aesKey)))
        (§ ass (§ name basicKeyChain) (§ new #_"BasicKeyChain" __crypter))
        ;; The first number is the "account number" but we don't use that feature.
        (§ ass (§ name rootKey) (.. __chain rootKey (encrypt __crypter, __aesKey, nil)))
        (§ ass (§ name hierarchy) (§ new #_"DeterministicHierarchy" rootKey))
        (.. basicKeyChain (importKey rootKey))

        (§ for (§ var #_"int" __i 1) :for (< __i (.. this (getAccountPath) (size))) :for (§ recur (inc __i)))
        (§ block
            (.. this (encryptNonLeaf __aesKey, __chain, rootKey, (.. this (getAccountPath) (subList 0, __i))))
        )

        (§ let #_"DeterministicKey" __account (.. this (encryptNonLeaf __aesKey, __chain, rootKey, (.. this (getAccountPath)))))
        (§ block
            (§ ass (§ name externalParentKey) (.. this (encryptNonLeaf __aesKey, __chain, __account, (HDUtils/concat (.. this (getAccountPath)), DeterministicKeyChain/EXTERNAL_SUBPATH))))
            (§ ass (§ name internalParentKey) (.. this (encryptNonLeaf __aesKey, __chain, __account, (HDUtils/concat (.. this (getAccountPath)), DeterministicKeyChain/INTERNAL_SUBPATH))))

            ;; Now copy the (pubkey only) leaf keys across to avoid rederiving them.  The private key bytes are missing
            ;; anyway so there's nothing to encrypt.
            (§ for (§ var #_"ECKey" __eckey) :for (.. __chain basicKeyChain (getKeys)))
            (§ block
                (§ let #_"DeterministicKey" __key (§ cast #_"DeterministicKey" __eckey))
                (§ block
                    (§ if (!= (.. __key (getPath) (size)) (+ (.. this (getAccountPath) (size)) 2)))
                    (§ block
                        (§ continue ) ;; Not a leaf key.
                    )

                    (§ let #_"DeterministicKey" __parent (.. hierarchy (get (.. (Preconditions/checkNotNull (.. __key (getParent))) (getPath)), false, false)))
                    (§ block
                        ;; Clone the key to the new encrypted hierarchy.
                        (§ ass __key (§ new #_"DeterministicKey" (.. __key (dropPrivateBytes)), __parent))
                        (.. hierarchy (putKey __key))
                        (.. basicKeyChain (importKey __key))
                    )
                )
            )
            this
        )
    )

    ;;; Override in subclasses to use a different account derivation path. ;;
    #_protected
    (§ method #_"ImmutableList<ChildNumber>" (§ fn getAccountPath) [])
    (§ block
        DeterministicKeyChain/ACCOUNT_ZERO_PATH
    )

    #_private
    (§ method- #_"DeterministicKey" (§ fn encryptNonLeaf) [#_"KeyParameter" __aesKey, #_"DeterministicKeyChain" __chain, #_"DeterministicKey" __parent, #_"ImmutableList<ChildNumber>" __path])
    (§ block
        (§ let #_"DeterministicKey" __key (.. __chain hierarchy (get __path, false, false)))
        (§ block
            (§ ass __key (.. __key (encrypt (Preconditions/checkNotNull (.. basicKeyChain (getKeyCrypter))), __aesKey, __parent)))
            (.. hierarchy (putKey __key))
            (.. basicKeyChain (importKey __key))
            __key
        )
    )

    ;; Derives the account path keys and inserts them into the basic key chain.
    ;; This is important to preserve their order for serialization, amongst other things.
    #_private
    (§ method- #_"void" (§ fn initializeHierarchyUnencrypted) [#_"DeterministicKey" __baseKey])
    (§ block
        (§ ass (§ name externalParentKey) (.. hierarchy (deriveChild (.. this (getAccountPath)), false, false, ChildNumber/ZERO)))
        (§ ass (§ name internalParentKey) (.. hierarchy (deriveChild (.. this (getAccountPath)), false, false, ChildNumber/ONE)))
        (.. basicKeyChain (importKey externalParentKey))
        (.. basicKeyChain (importKey internalParentKey))
        nil
    )

    ;;; Returns a freshly derived key that has not been returned by this method before. ;;
    #_override
    #_public
    (§ method #_"DeterministicKey" (§ fn getKey) [#_"KeyPurpose" __purpose])
    (§ block
        (.. this (getKeys __purpose, 1) (get 0))
    )

    ;;; Returns freshly derived key/s that have not been returned by this method before. ;;
    #_override
    #_public
    (§ method #_"List<DeterministicKey>" (§ fn getKeys) [#_"KeyPurpose" __purpose, #_"int" __numberOfKeys])
    (§ block
        (Preconditions/checkArgument (< 0 __numberOfKeys))
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"DeterministicKey" __parentKey)
            (§ block
                (§ let #_"int" __index)
                (§ block
                    (§ switch __purpose)
                    (§ block
                        ;; Map both REFUND and RECEIVE_KEYS to the same branch for now.  Refunds are a feature of the BIP 70
                        ;; payment protocol.  Later we may wish to map it to a different branch (in a new wallet version?).
                        ;; This would allow a watching wallet to only be able to see inbound payments, but not change
                        ;; (i.e. spends) or refunds.  Might be useful for auditing ...
                        (§ case RECEIVE_FUNDS)
                        (§ case REFUND)
                        (§ block
                            (§ ass (§ name issuedExternalKeys) (+ issuedExternalKeys __numberOfKeys))
                            (§ ass __index issuedExternalKeys)
                            (§ ass __parentKey externalParentKey)
                            (§ break )
                        )
                        (§ case AUTHENTICATION)
                        (§ case CHANGE)
                        (§ block
                            (§ ass (§ name issuedInternalKeys) (+ issuedInternalKeys __numberOfKeys))
                            (§ ass __index issuedInternalKeys)
                            (§ ass __parentKey internalParentKey)
                            (§ break )
                        )
                        (§ default )
                        (§ block
                            (§ throw (§ new #_"UnsupportedOperationException"))
                        )
                    )
                    ;; Optimization: potentially do a very quick key generation for just the number of keys we need if we
                    ;; didn't already create them, ignoring the configured lookahead size.  This ensures we'll be able to
                    ;; retrieve the keys in the following loop, but if we're totally fresh and didn't get a chance to
                    ;; calculate the lookahead keys yet, this will not block waiting to calculate 100+ EC point multiplies.
                    ;; On slow/crappy Android phones looking ahead 100 keys can take ~5 seconds but the OS will kill us
                    ;; if we block for just one second on the UI thread.  Because UI threads may need an address in order
                    ;; to render the screen, we need getKeys to be fast even if the wallet is totally brand new and lookahead
                    ;; didn't happen yet.
                    ;;
                    ;; It's safe to do this because when a network thread tries to calculate a Bloom filter, we'll go ahead
                    ;; and calculate the full lookahead zone there, so network requests will always use the right amount.
                    (§ let #_"List<DeterministicKey>" __lookahead (.. this (maybeLookAhead __parentKey, __index, 0, 0)))
                    (§ block
                        (.. basicKeyChain (importKeys __lookahead))
                        (§ let #_"List<DeterministicKey>" __keys (§ new #_"ArrayList<>" __numberOfKeys))
                        (§ block
                            (§ for (§ var #_"int" __i 0) :for (< __i __numberOfKeys) :for (§ recur (inc __i)))
                            (§ block
                                (§ let #_"ImmutableList<ChildNumber>" __path (HDUtils/append (.. __parentKey (getPath)), (§ new #_"ChildNumber" (+ (- __index __numberOfKeys) __i), false)))
                                (§ block
                                    (§ let #_"DeterministicKey" __k (.. hierarchy (get __path, false, false)))
                                    (§ block
                                        ;; Just a last minute sanity check before we hand the key out to the app for usage.  This isn't
                                        ;; inspired by any real problem reports from bitcoinj users, but I've heard of cases via the grapevine
                                        ;; of places that lost money due to bitflips causing addresses to not match keys.  Of course in an
                                        ;; environment with flaky RAM there's no real way to always win: bitflips could be introduced at any
                                        ;; other layer.  But as we're potentially retrieving from long term storage here, check anyway.
                                        (.. this (checkForBitFlip __k))
                                        (.. __keys (add __k))
                                    )
                                )
                            )
                            (§ return __keys)
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkForBitFlip) [#_"DeterministicKey" __k])
    (§ block
        (§ let #_"DeterministicKey" __parent (Preconditions/checkNotNull (.. __k (getParent))))
        (§ block
            (§ let #_"byte[]" __rederived (.. (HDKeyDerivation/deriveChildKeyBytesFromPublic __parent, (.. __k (getChildNumber)), HDKeyDerivation/PublicDeriveMode/WITH_INVERSION) keyBytes))
            (§ block
                (§ let #_"byte[]" __actual (.. __k (getPubKey)))
                (§ block
                    (§ if (not (Arrays/equals __rederived, __actual)))
                    (§ block
                        (§ throw (§ new #_"IllegalStateException" (String/format Locale/US, "Bit-flip check failed: %s vs %s", (Arrays/toString __rederived), (Arrays/toString __actual))))
                    )
                    nil
                )
            )
        )
    )

    ;;;
     ; Mark the DeterministicKey as used.
     ; Also correct the issued{Internal|External}Keys counter, because all lower children seem to be requested already.
     ; If the counter was updated, we also might trigger lookahead.
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn markKeyAsUsed) [#_"DeterministicKey" __k])
    (§ block
        (§ let #_"int" __numChildren (+ (.. __k (getChildNumber) (i)) 1))
        (§ block

            (§ if (== (.. __k (getParent)) internalParentKey))
            (§ block
                (§ if (< issuedInternalKeys __numChildren))
                (§ block
                    (§ ass (§ name issuedInternalKeys) __numChildren)
                    (.. this (maybeLookAhead))
                )
            )
            (§ elseif (== (.. __k (getParent)) externalParentKey))
            (§ block
                (§ if (< issuedExternalKeys __numChildren))
                (§ block
                    (§ ass (§ name issuedExternalKeys) __numChildren)
                    (.. this (maybeLookAhead))
                )
            )
            __k
        )
    )

