    #_override
    #_public
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])
    (§ block
        (§ let #_"List<Protos.Key>" __result (Lists/newArrayList))
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (.. __result (addAll (.. this (serializeMyselfToProtobuf))))
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            __result
        )
