    #_protected
    (§ method #_"List<Protos.Key>" (§ fn serializeMyselfToProtobuf) [])
    (§ block
        ;; Most of the serialization work is delegated to the basic key chain, which will serialize the bulk of the
        ;; data (handling encryption along the way), and letting us patch it up with the extra data we care about.
        (§ let #_"LinkedList<Protos.Key>" __entries (Lists/newLinkedList))
        (§ block
            (§ if (some? seed))
            (§ block
                (§ let #_"Protos.Key.Builder" __mnemonicEntry (BasicKeyChain/serializeEncryptableItem seed))
                (§ block
                    (.. __mnemonicEntry (setType Protos/Key/Type/DETERMINISTIC_MNEMONIC))
                    (DeterministicKeyChain/serializeSeedEncryptableItem seed, __mnemonicEntry)
                    (.. __entries (add (.. __mnemonicEntry (build))))
                )
            )
            (§ let #_"Map<ECKey, Protos.Key.Builder>" __keys (.. basicKeyChain (serializeToEditableProtobufs)))
            (§ block
                (§ for (§ var #_"Map.Entry<ECKey, Protos.Key.Builder>" __entry) :for (.. __keys (entrySet)))
                (§ block
                    (§ let #_"DeterministicKey" __key (§ cast #_"DeterministicKey" (.. __entry (getKey))))
                    (§ block
                        (§ let #_"Protos.Key.Builder" __proto (.. __entry (getValue)))
                        (§ block
                            (.. __proto (setType Protos/Key/Type/DETERMINISTIC_KEY))
                            (§ let #_"Protos.DeterministicKey.Builder" __detKey (.. __proto (getDeterministicKeyBuilder)))
                            (§ block
                                (.. __detKey (setChainCode (ByteString/copyFrom (.. __key (getChainCode)))))
                                (§ for (§ var #_"ChildNumber" __num) :for (.. __key (getPath)))
                                (§ block
                                    (.. __detKey (addPath (.. __num (i))))
                                )
                                (§ if (.. __key (equals externalParentKey)))
                                (§ block
                                    (.. __detKey (setIssuedSubkeys issuedExternalKeys))
                                    (.. __detKey (setLookaheadSize lookaheadSize))
                                    (.. __detKey (setSigsRequiredToSpend (.. this (getSigsRequiredToSpend))))
                                )
                                (§ elseif (.. __key (equals internalParentKey)))
                                (§ block
                                    (.. __detKey (setIssuedSubkeys issuedInternalKeys))
                                    (.. __detKey (setLookaheadSize lookaheadSize))
                                    (.. __detKey (setSigsRequiredToSpend (.. this (getSigsRequiredToSpend))))
                                )
                                ;; Flag the very first key of following keychain.
                                (§ if (and (.. __entries (isEmpty)) (.. this (isFollowing))))
                                (§ block
                                    (.. __detKey (setIsFollowing true))
                                )
                                ;; HD keys inherit the timestamp of their parent if they have one, so no need to serialize it.
                                (§ if (!= (.. __key (getParent)) nil))
                                (§ block
                                    (.. __proto (clearCreationTimestamp))
                                )
                                (.. __entries (add (.. __proto (build))))
                            )
                        )
                    )
                )
                __entries
            )
        )
    )

