    ;;;
     ; Returns all the key chains found in the given list of keys.  Typically there will only be one,
     ; but in the case of key rotation it can happen that there are multiple chains found.
     ;;
    #_public
    #_static
    (§ defn #_"List<DeterministicKeyChain>" (§ fn DeterministicKeyChain/fromProtobuf) [#_"List<Protos.Key>" __keys, #_nilable #_"KeyCrypter" __crypter, #_"KeyChainFactory" __factory])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"List<DeterministicKeyChain>" __chains (Lists/newLinkedList))
        (§ block
            (§ let #_"DeterministicSeed" __seed nil)
            (§ block
                (§ let #_"DeterministicKeyChain" __chain nil)
                (§ block

                    (§ let #_"int" __lookaheadSize -1)
                    (§ block
                        (§ let #_"int" __sigsRequiredToSpend 1)
                        (§ block

                            (§ let #_"PeekingIterator<Protos.Key>" __iter (Iterators/peekingIterator (.. __keys (iterator))))
                            (§ block
                                (§ while (.. __iter (hasNext)))
                                (§ block
                                    (§ let #_"Protos.Key" __key (.. __iter (next)))
                                    (§ block
                                        (§ let #_"Protos.Key.Type" __t (.. __key (getType)))
                                        (§ block
                                            (§ if (== __t Protos/Key/Type/DETERMINISTIC_MNEMONIC))
                                            (§ block
                                                (§ if (some? __chain))
                                                (§ block
                                                    (Preconditions/checkState (<= 0 __lookaheadSize))

                                                    (.. __chain (setLookaheadSize __lookaheadSize))
                                                    (.. __chain (setSigsRequiredToSpend __sigsRequiredToSpend))
                                                    (.. __chain (maybeLookAhead))
                                                    (.. __chains (add __chain))
                                                    (§ ass __chain nil)
                                                )
                                                (§ let #_"long" __timestamp (/ (.. __key (getCreationTimestamp)) 1000))
                                                (§ block
                                                    (§ let #_"String" __passphrase DeterministicKeyChain/DEFAULT_PASSPHRASE_FOR_MNEMONIC) ;; FIXME allow non-empty passphrase
                                                    (§ block
                                                        (§ if (.. __key (hasSecretBytes)))
                                                        (§ block
                                                            (§ if (.. __key (hasEncryptedDeterministicSeed)))
                                                            (§ block
                                                                (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " __key)))
                                                            )

                                                            (§ let #_"byte[]" __seedBytes nil)
                                                            (§ block
                                                                (§ if (.. __key (hasDeterministicSeed)))
                                                                (§ block
                                                                    (§ ass __seedBytes (.. __key (getDeterministicSeed) (toByteArray)))
                                                                )
                                                                (§ ass __seed (§ new #_"DeterministicSeed" (.. __key (getSecretBytes) (toStringUtf8)), __seedBytes, __passphrase, __timestamp))
                                                            )
                                                        )
                                                        (§ elseif (.. __key (hasEncryptedData)))
                                                        (§ block
                                                            (§ if (.. __key (hasDeterministicSeed)))
                                                            (§ block
                                                                (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " __key)))
                                                            )

                                                            (§ let #_"EncryptedData" __data (§ new #_"EncryptedData" (.. __key (getEncryptedData) (getInitialisationVector) (toByteArray)), (.. __key (getEncryptedData) (getEncryptedPrivateKey) (toByteArray))))
                                                            (§ block
                                                                (§ let #_"EncryptedData" __encryptedSeedBytes nil)
                                                                (§ block
                                                                    (§ if (.. __key (hasEncryptedDeterministicSeed)))
                                                                    (§ block
                                                                        (§ let #_"Protos.EncryptedData" __encryptedSeed (.. __key (getEncryptedDeterministicSeed)))
                                                                        (§ block
                                                                            (§ ass __encryptedSeedBytes (§ new #_"EncryptedData" (.. __encryptedSeed (getInitialisationVector) (toByteArray)), (.. __encryptedSeed (getEncryptedPrivateKey) (toByteArray))))
                                                                        )
                                                                    )
                                                                    (§ ass __seed (§ new #_"DeterministicSeed" __data, __encryptedSeedBytes, __timestamp))
                                                                )
                                                            )
                                                        )
                                                        (§ else )
                                                        (§ block
                                                            (§ throw (§ new #_"UnreadableWalletException" (str "Malformed key proto: " __key)))
                                                        )

                                                        (§ if (.. DeterministicKeyChain/log (isDebugEnabled)))
                                                        (§ block
                                                            (.. DeterministicKeyChain/log (debug "Deserializing: DETERMINISTIC_MNEMONIC: {}", __seed))
                                                        )
                                                    )
                                                )
                                            )
                                            (§ elseif (== __t Protos/Key/Type/DETERMINISTIC_KEY))
                                            (§ block
                                                (§ if (not (.. __key (hasDeterministicKey))))
                                                (§ block
                                                    (§ throw (§ new #_"UnreadableWalletException" (str "Deterministic key missing extra data: " __key)))
                                                )

                                                (§ let #_"byte[]" __chainCode (.. __key (getDeterministicKey) (getChainCode) (toByteArray)))
                                                (§ block
                                                    ;; Deserialize the path through the tree.
                                                    (§ let #_"LinkedList<ChildNumber>" __path (Lists/newLinkedList))
                                                    (§ block
                                                        (§ for (§ var #_"int" __i) :for (.. __key (getDeterministicKey) (getPathList)))
                                                        (§ block
                                                            (.. __path (add (§ new #_"ChildNumber" __i)))
                                                        )

                                                        ;; Deserialize the public key and path.
                                                        (§ let #_"LazyECPoint" __pubkey (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), (.. __key (getPublicKey) (toByteArray))))
                                                        (§ block
                                                            (§ let #_"ImmutableList<ChildNumber>" __immutablePath (ImmutableList/copyOf __path))
                                                            (§ block
                                                                ;; Possibly create the chain, if we didn't already do so yet.
                                                                (§ let #_"boolean" __isWatchingAccountKey false)
                                                                (§ block
                                                                    (§ let #_"boolean" __isFollowingKey false)
                                                                    (§ block
                                                                        ;; Save previous chain if any if the key is marked as following.  Current key and the next ones
                                                                        ;; are to be placed in new following key chain.
                                                                        (§ if (.. __key (getDeterministicKey) (getIsFollowing)))
                                                                        (§ block
                                                                            (§ if (some? __chain))
                                                                            (§ block
                                                                                (Preconditions/checkState (<= 0 __lookaheadSize))

                                                                                (.. __chain (setLookaheadSize __lookaheadSize))
                                                                                (.. __chain (setSigsRequiredToSpend __sigsRequiredToSpend))
                                                                                (.. __chain (maybeLookAhead))
                                                                                (.. __chains (add __chain))
                                                                                (§ ass __chain nil)
                                                                                (§ ass __seed nil)
                                                                            )
                                                                            (§ ass __isFollowingKey true)
                                                                        )

                                                                        (§ if (nil? __chain))
                                                                        (§ block
                                                                            ;; If this is not a following chain and previous was, this must be married.
                                                                            (§ let #_"boolean" __isMarried (and (not __isFollowingKey) (not (.. __chains (isEmpty))) (.. __chains (get (- (.. __chains (size)) 1)) (isFollowing))))
                                                                            (§ block
                                                                                (§ if (nil? __seed))
                                                                                (§ block
                                                                                    (§ let #_"DeterministicKey" __accountKey (§ new #_"DeterministicKey" __immutablePath, __chainCode, __pubkey, nil, nil))
                                                                                    (§ block
                                                                                        (.. __accountKey (setCreationTimeSeconds (/ (.. __key (getCreationTimestamp)) 1000)))
                                                                                        (§ ass __chain (.. __factory (makeWatchingKeyChain __key, (.. __iter (peek)), __accountKey, __isFollowingKey, __isMarried)))
                                                                                        (§ ass __isWatchingAccountKey true)
                                                                                    )
                                                                                )
                                                                                (§ else )
                                                                                (§ block
                                                                                    (§ ass __chain (.. __factory (makeKeyChain __key, (.. __iter (peek)), __seed, __crypter, __isMarried)))
                                                                                    (§ ass (§ name (.. __chain lookaheadSize)) DeterministicKeyChain/LAZY_CALCULATE_LOOKAHEAD)
                                                                                    ;; If the seed is encrypted, then the chain is incomplete at this point.  However, we will load
                                                                                    ;; it up below as we parse in the keys.  We just need to check at the end that we've loaded
                                                                                    ;; everything afterwards.
                                                                                )
                                                                            )
                                                                        )
                                                                        ;; Find the parent key assuming this is not the root key, and not an account key for a watching chain.
                                                                        (§ let #_"DeterministicKey" __parent nil)
                                                                        (§ block
                                                                            (§ if (and (not (.. __path (isEmpty))) (not __isWatchingAccountKey)))
                                                                            (§ block
                                                                                (§ let #_"ChildNumber" __index (.. __path (removeLast)))
                                                                                (§ block
                                                                                    (§ ass __parent (.. __chain hierarchy (get __path, false, false)))
                                                                                    (.. __path (add __index))
                                                                                )
                                                                            )
                                                                            (§ let #_"DeterministicKey" __detkey)
                                                                            (§ block
                                                                                (§ if (.. __key (hasSecretBytes)))
                                                                                (§ block
                                                                                    ;; Not encrypted: private key is available.
                                                                                    (§ let #_"BigInteger" __priv (§ new #_"BigInteger" 1, (.. __key (getSecretBytes) (toByteArray))))
                                                                                    (§ block
                                                                                        (§ ass __detkey (§ new #_"DeterministicKey" __immutablePath, __chainCode, __pubkey, __priv, __parent))
                                                                                    )
                                                                                )
                                                                                (§ else )
                                                                                (§ block
                                                                                    (§ if (.. __key (hasEncryptedData)))
                                                                                    (§ block
                                                                                        (§ let #_"Protos.EncryptedData" __proto (.. __key (getEncryptedData)))
                                                                                        (§ block
                                                                                            (§ let #_"EncryptedData" __data (§ new #_"EncryptedData" (.. __proto (getInitialisationVector) (toByteArray)), (.. __proto (getEncryptedPrivateKey) (toByteArray))))
                                                                                            (§ block
                                                                                                (Preconditions/checkNotNull __crypter, "Encountered an encrypted key but no key crypter provided")
                                                                                                (§ ass __detkey (§ new #_"DeterministicKey" __immutablePath, __chainCode, __crypter, __pubkey, __data, __parent))
                                                                                            )
                                                                                        )
                                                                                    )
                                                                                    (§ else )
                                                                                    (§ block
                                                                                        ;; No secret key bytes and key is not encrypted: either a watching key or private key bytes
                                                                                        ;; will be rederived on the fly from the parent.
                                                                                        (§ ass __detkey (§ new #_"DeterministicKey" __immutablePath, __chainCode, __pubkey, nil, __parent))
                                                                                    )
                                                                                )
                                                                                (§ if (.. __key (hasCreationTimestamp)))
                                                                                (§ block
                                                                                    (.. __detkey (setCreationTimeSeconds (/ (.. __key (getCreationTimestamp)) 1000)))
                                                                                )
                                                                                (§ if (.. DeterministicKeyChain/log (isDebugEnabled)))
                                                                                (§ block
                                                                                    (.. DeterministicKeyChain/log (debug "Deserializing: DETERMINISTIC_KEY: {}", __detkey))
                                                                                )
                                                                                (§ if (not __isWatchingAccountKey))
                                                                                (§ block
                                                                                    ;; If the non-encrypted case, the non-leaf keys (account, internal, external) have already
                                                                                    ;; been rederived and inserted at this point.  In the encrypted case though, we can't
                                                                                    ;; rederive and we must reinsert, potentially building the heirarchy object if need be.
                                                                                    (§ if (== (.. __path (size)) 0))
                                                                                    (§ block
                                                                                        ;; Master key.
                                                                                        (§ if (== (.. __chain rootKey) nil))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. __chain rootKey)) __detkey)
                                                                                            (§ ass (§ name (.. __chain hierarchy)) (§ new #_"DeterministicHierarchy" __detkey))
                                                                                        )
                                                                                    )
                                                                                    (§ elseif (== (.. __path (size)) (+ (.. __chain (getAccountPath) (size)) 1)))
                                                                                    (§ block
                                                                                        (§ if (== (.. __detkey (getChildNumber) (num)) 0))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. __chain externalParentKey)) __detkey)
                                                                                            (§ ass (§ name (.. __chain issuedExternalKeys)) (.. __key (getDeterministicKey) (getIssuedSubkeys)))
                                                                                            (§ ass __lookaheadSize (Math/max __lookaheadSize, (.. __key (getDeterministicKey) (getLookaheadSize))))
                                                                                            (§ ass __sigsRequiredToSpend (.. __key (getDeterministicKey) (getSigsRequiredToSpend)))
                                                                                        )
                                                                                        (§ elseif (== (.. __detkey (getChildNumber) (num)) 1))
                                                                                        (§ block
                                                                                            (§ ass (§ name (.. __chain internalParentKey)) __detkey)
                                                                                            (§ ass (§ name (.. __chain issuedInternalKeys)) (.. __key (getDeterministicKey) (getIssuedSubkeys)))
                                                                                        )
                                                                                    )
                                                                                )
                                                                                (.. __chain hierarchy (putKey __detkey))
                                                                                (.. __chain basicKeyChain (importKey __detkey))
                                                                            )
                                                                        )
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                                (§ if (some? __chain))
                                (§ block
                                    (Preconditions/checkState (>= __lookaheadSize 0))
                                    (.. __chain (setLookaheadSize __lookaheadSize))
                                    (.. __chain (setSigsRequiredToSpend __sigsRequiredToSpend))
                                    (.. __chain (maybeLookAhead))
                                    (.. __chains (add __chain))
                                )
                                __chains
                            )
                        )
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toEncrypted) [#_"CharSequence" __password])
    (§ block
        (Preconditions/checkNotNull __password)
        (Preconditions/checkArgument (< 0 (.. __password (length))))
        (Preconditions/checkState (some? seed), "Attempt to encrypt a watching chain.")
        (Preconditions/checkState (not (.. seed (isEncrypted))))

        (§ let #_"KeyCrypter" __scrypt (§ new #_"KeyCrypterScrypt"))
        (§ block
            (§ let #_"KeyParameter" __derivedKey (.. __scrypt (deriveKey __password)))
            (§ block
                (.. this (toEncrypted __scrypt, __derivedKey))
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toEncrypted) [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])
