    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toDecrypted) [#_"CharSequence" __password])
    (§ block
        (Preconditions/checkNotNull __password)
        (Preconditions/checkArgument (< 0 (.. __password (length))))

        (§ let #_"KeyCrypter" __crypter (.. this (getKeyCrypter)))
        (§ block
            (Preconditions/checkState (some? __crypter), "Chain not encrypted")
            (§ let #_"KeyParameter" __derivedKey (.. __crypter (deriveKey __password)))
            (§ block
                (.. this (toDecrypted __derivedKey))
            )
        )
    )

    #_override
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn toDecrypted) [#_"KeyParameter" __aesKey])
    (§ block
        (Preconditions/checkState (some? (.. this (getKeyCrypter))), "Key chain not encrypted")
        (Preconditions/checkState (some? seed), "Can't decrypt a watching chain")
        (Preconditions/checkState (.. seed (isEncrypted)))

        (§ let #_"String" __passphrase DeterministicKeyChain/DEFAULT_PASSPHRASE_FOR_MNEMONIC) ;; FIXME allow non-empty passphrase
        (§ block
            (§ let #_"DeterministicSeed" __decSeed (.. seed (decrypt (.. this (getKeyCrypter)), __passphrase, __aesKey)))
            (§ block
                (§ let #_"DeterministicKeyChain" __chain (.. this (makeKeyChainFromSeed __decSeed)))
                (§ block
                    ;; Now double check that the keys match to catch the case where the key is wrong but padding didn't catch it.
                    (§ if (not (.. __chain (getWatchingKey) (getPubKeyPoint) (equals (.. this (getWatchingKey) (getPubKeyPoint))))))
                    (§ block
                        (§ throw (§ new #_"KeyCrypterException" "Provided AES key is wrong"))
                    )

                    (§ ass (§ name (.. __chain lookaheadSize)) lookaheadSize)
                    ;; Now copy the (pubkey only) leaf keys across to avoid rederiving them.  The private key bytes are missing
                    ;; anyway so there's nothing to decrypt.
                    (§ for (§ var #_"ECKey" __eckey) :for (.. basicKeyChain (getKeys)))
                    (§ block
                        (§ let #_"DeterministicKey" __key (§ cast #_"DeterministicKey" __eckey))
                        (§ block
                            (§ if (!= (.. __key (getPath) (size)) (+ (.. this (getAccountPath) (size)) 2)))
                            (§ block
                                (§ continue ) ;; Not a leaf key.
                            )

                            (Preconditions/checkState (.. __key (isEncrypted)))
                            (§ let #_"DeterministicKey" __parent (.. __chain hierarchy (get (.. (Preconditions/checkNotNull (.. __key (getParent))) (getPath)), false, false)))
                            (§ block
                                ;; Clone the key to the new decrypted hierarchy.
                                (§ ass __key (§ new #_"DeterministicKey" (.. __key (dropPrivateBytes)), __parent))
                                (.. __chain hierarchy (putKey __key))
                                (.. __chain basicKeyChain (importKey __key))
                            )
                        )
                    )
                    (§ ass (§ name (.. __chain issuedExternalKeys)) issuedExternalKeys)
                    (§ ass (§ name (.. __chain issuedInternalKeys)) issuedInternalKeys)
                    __chain
                )
            )
        )
    )

