        (Preconditions/checkState (some? (.. this (getKeyCrypter))), "Key chain not encrypted")

        (.. this (checkAESKey (.. this (getKeyCrypter) (deriveKey __password))))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn checkAESKey) [#_"KeyParameter" __aesKey])
    (§ block
