        (Preconditions/checkNotNull __aesKey)
        (Preconditions/checkState (some? (.. this (getKeyCrypter))), "Key chain not encrypted")

        (§ try )
        (§ block
            (§ return (.. rootKey (decrypt __aesKey) (getPubKeyPoint) (equals (.. rootKey (getPubKeyPoint)))))
