        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkArgument (<= (.. this (numBloomFilterEntries)) __size))
            (.. this (maybeLookAhead))
            (§ return (.. basicKeyChain (getFilter __size, __falsePositiveRate, __tweak)))
