    (§ block
        (Preconditions/checkNotNull __keyCrypter)
        (Preconditions/checkNotNull __aesKey)

        ;; This code must be exception safe.
        (§ let #_"BasicKeyChain" __newBasic (.. basic (toEncrypted __keyCrypter, __aesKey)))
        (§ block
            (§ let #_"List<DeterministicKeyChain>" __newChains (§ new #_"ArrayList<>" (.. chains (size))))
            (§ block
                (§ if (and (.. chains (isEmpty)) (== (.. basic (numKeys)) 0)))
                (§ block
                    ;; No HD chains and no random keys: encrypting an entirely empty keychain group.
                    ;; But we can't do that, we must have something to encrypt: so instantiate a new HD chain here.
                    (.. this (createAndActivateNewHDChain))
                )
                (§ for (§ var #_"DeterministicKeyChain" __chain) :for chains)
                (§ block
                    (.. __newChains (add (.. __chain (toEncrypted __keyCrypter, __aesKey))))
                )
                (§ ass (§ name (.. this keyCrypter)) __keyCrypter)
                (§ ass (§ name basic) __newBasic)
                (.. chains (clear))
                (.. chains (addAll __newChains))
                nil
