        (§ let #_"BasicKeyChain" __newBasic (.. basic (toDecrypted __aesKey)))
        (§ block
            (§ let #_"List<DeterministicKeyChain>" __newChains (§ new #_"ArrayList<>" (.. chains (size))))
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" __chain) :for chains)
                (§ block
                    (.. __newChains (add (.. __chain (toDecrypted __aesKey))))
                )

                (§ ass (§ name (.. this keyCrypter)) nil)
                (§ ass (§ name basic) __newBasic)
                (.. chains (clear))
                (.. chains (addAll __newChains))
                nil
            )
        )
    )

