    (§ method #_"boolean" (§ fn isWatching) [])
    (§ block
        (§ let #_"BasicKeyChain.State" __activeState BasicKeyChain/State/EMPTY)
        (§ block
            (§ if (not (.. chains (isEmpty))))
            (§ block
                (§ ass __activeState (if (.. this (getActiveKeyChain) (isWatching)) BasicKeyChain/State/WATCHING BasicKeyChain/State/REGULAR))
            )

            (§ let #_"BasicKeyChain.State" __basicState (.. basic (isWatching)))
            (§ block
                (§ if (== __basicState BasicKeyChain/State/EMPTY))
                (§ block
                    (§ if (== __activeState BasicKeyChain/State/EMPTY))
                    (§ block
                        (§ throw (§ new #_"IllegalStateException" "Empty key chain group: cannot answer isWatching() query"))
                    )

                    (§ return (== __activeState BasicKeyChain/State/WATCHING))
                )

                (§ if (== __activeState BasicKeyChain/State/EMPTY))
                (§ block
                    (§ return (== __basicState BasicKeyChain/State/WATCHING))
                )

                (§ if (!= __activeState __basicState))
                (§ block
                    (§ throw (§ new #_"IllegalStateException" "Mix of watching and non-watching keys in wallet"))
                )

                (== __activeState BasicKeyChain/State/WATCHING)
