     ; based on the oldest non-rotating private key (i.e. the seed is derived from the old wallet).
     ;
     ; @param keyRotationTimeSecs If non-zero, UNIX time for which keys created before this are assumed to be
     ;                            compromised or weak, those keys will not be used for deterministic upgrade.
     ; @param aesKey If non-null, the encryption key the keychain is encrypted under.  If the keychain is encrypted
     ;               and this is not supplied, an exception is thrown letting you know you should ask the user for
     ;               their password, turn it into a key, and then try again.
     ; @throws java.lang.IllegalStateException if there is already a deterministic key chain present or if there are
     ;                                         no random keys (i.e. this is not an upgrade scenario), or if aesKey is
     ;                                         provided but the wallet is not encrypted.
     ; @throws java.lang.IllegalArgumentException if the rotation time specified excludes all keys.
     ; @throws DeterministicUpgradeRequiresPassword if the key chain group is encrypted
     ;         and you should provide the users encryption key.
     ; @return the DeterministicKeyChain that was created by the upgrade.
     ;;
    #_public
    (§ method #_"DeterministicKeyChain" (§ fn upgradeToDeterministic) [#_"long" __keyRotationTimeSecs, #_nilable #_"KeyParameter" __aesKey])
        (§ throws #_"DeterministicUpgradeRequiresPassword", #_"AllRandomKeysRotating")
    (§ block
        (Preconditions/checkState (< 0 (.. basic (numKeys))))
        (Preconditions/checkArgument (<= 0 __keyRotationTimeSecs))

        ;; Subtract one because the key rotation time might have been set to the creation time of the first known
        ;; good key, in which case, that's the one we want to find.
        (§ let #_"ECKey" __keyToUse (.. basic (findOldestKeyAfter (dec __keyRotationTimeSecs))))
        (§ block
            (§ if (nil? __keyToUse))
            (§ block
                (§ throw (§ new #_"AllRandomKeysRotating"))
            )

            (§ if (.. __keyToUse (isEncrypted)))
            (§ block
                (§ if (nil? __aesKey))
                (§ block
                    ;; We can't auto upgrade because we don't know the users password at this point.  We throw an exception
                    ;; so the calling code knows to abort the load and ask the user for their password, they can then try
                    ;; loading the wallet again passing in the AES key.
                    ;;
                    ;; There are a few different approaches we could have used here, but they all suck.  The most obvious
                    ;; is to try and be as lazy as possible, running in the old random-wallet mode until the user enters
                    ;; their password for some other reason and doing the upgrade then.  But this could result in strange
                    ;; and unexpected UI flows for the user, as well as complicating the job of wallet developers who then
                    ;; have to support both "old" and "new" UI modes simultaneously, switching them on the fly.  Given that
                    ;; this is a one-off transition, it seems more reasonable to just ask the user for their password
                    ;; on startup, and then the wallet app can have all the widgets for accessing seed words etc active
                    ;; all the time.
                    (§ throw (§ new #_"DeterministicUpgradeRequiresPassword"))
                )
                (§ ass __keyToUse (.. __keyToUse (decrypt __aesKey)))
            )
            (§ elseif (some? __aesKey))
            (§ block
                (§ throw (§ new #_"IllegalStateException" "AES Key was provided but wallet is not encrypted."))
            )

            (§ if (.. chains (isEmpty)))
            (§ block
                (.. KeyChainGroup/log (info "Auto-upgrading pre-HD wallet to HD!"))
            )
            (§ else )
            (§ block
                (.. KeyChainGroup/log (info "Wallet with existing HD chain is being re-upgraded due to change in key rotation time."))
            )

            (.. KeyChainGroup/log (info "Instantiating new HD chain using oldest non-rotating private key (address: {})", (.. __keyToUse (toAddress params))))
            (§ let #_"byte[]" __entropy (Preconditions/checkNotNull (.. __keyToUse (getSecretBytes))))
            (§ block
                ;; Private keys should be at least 128 bits long.
                (Preconditions/checkState (<= (/ DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS 8) (.. __entropy length)))
                ;; We reduce the entropy here to 128 bits because people like to write their seeds down on paper,
                ;; and 128 bits should be sufficient forever unless the laws of the universe change or ECC is broken;
                ;; in either case we all have bigger problems.
                (§ ass __entropy (Arrays/copyOfRange __entropy, 0, (/ DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS 8))) ;; Final argument is exclusive range.
                (Preconditions/checkState (== (.. __entropy length) (/ DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS 8)))
                (§ let #_"String" __passphrase "") ;; FIXME allow non-empty passphrase
                (§ block
                    (§ let #_"DeterministicKeyChain" __chain (§ new #_"DeterministicKeyChain" __entropy, __passphrase, (.. __keyToUse (getCreationTimeSeconds))))
                    (§ block
                        (§ if (some? __aesKey))
                        (§ block
                            (§ ass __chain (.. __chain (toEncrypted (Preconditions/checkNotNull (.. basic (getKeyCrypter))), __aesKey)))
                        )
                        (.. chains (add __chain))
                        __chain
                    )
                )
            )
        )
    )

    ;;; Returns true if the group contains random keys but no HD chains. ;;
    #_public
    (§ method #_"boolean" (§ fn isDeterministicUpgradeRequired) [])
    (§ block
        (and (< 0 (.. basic (numKeys))) (.. chains (isEmpty)))
    )

    #_private
    #_static
    (§ defn- #_"EnumMap<KeyChain.KeyPurpose, DeterministicKey>" (§ fn KeyChainGroup/createCurrentKeysMap) [#_"List<DeterministicKeyChain>" __chains])
    (§ block
        (§ let #_"DeterministicKeyChain" __activeChain (.. __chains (get (- (.. __chains (size)) 1))))
        (§ block

            (§ let #_"EnumMap<KeyChain.KeyPurpose, DeterministicKey>" __currentKeys (§ new #_"EnumMap<>" (§ klass #_"KeyChain.KeyPurpose")))
            (§ block

                ;; Assuming that only RECEIVE and CHANGE keys are being used at the moment, we will treat the latest issued
                ;; external key as current RECEIVE key and latest issued internal key as CHANGE key.  This should be changed
                ;; as soon as other kinds of KeyPurpose are introduced.
                (§ if (< 0 (.. __activeChain (getIssuedExternalKeys))))
                (§ block
                    (§ let #_"DeterministicKey" __currentExternalKey (.. __activeChain (getKeyByPath (HDUtils/append (HDUtils/concat (.. __activeChain (getAccountPath)), DeterministicKeyChain/EXTERNAL_SUBPATH), (§ new #_"ChildNumber" (- (.. __activeChain (getIssuedExternalKeys)) 1))))))
                    (§ block
                        (.. __currentKeys (put KeyChain/KeyPurpose/RECEIVE_FUNDS, __currentExternalKey))
                    )
                )

                (§ if (< 0 (.. __activeChain (getIssuedInternalKeys))))
                (§ block
                    (§ let #_"DeterministicKey" __currentInternalKey (.. __activeChain (getKeyByPath (HDUtils/append (HDUtils/concat (.. __activeChain (getAccountPath)), DeterministicKeyChain/INTERNAL_SUBPATH), (§ new #_"ChildNumber" (- (.. __activeChain (getIssuedInternalKeys)) 1))))))
                    (§ block
                        (.. __currentKeys (put KeyChain/KeyPurpose/CHANGE, __currentInternalKey))
                    )
                )

                __currentKeys
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn KeyChainGroup/extractFollowingKeychains) [#_"List<DeterministicKeyChain>" __chains])
    (§ block
        ;; Look for following key chains and map them to the watch keys of followed keychains.
        (§ let #_"List<DeterministicKeyChain>" __followingChains (Lists/newArrayList))
        (§ block
            (§ for (§ var #_"Iterator<DeterministicKeyChain>" __it (.. __chains (iterator))) :for (.. __it (hasNext)) :for )
            (§ block
                (§ let #_"DeterministicKeyChain" __chain (.. __it (next)))
                (§ block
                    (§ if (.. __chain (isFollowing)))
                    (§ block
                        (.. __followingChains (add __chain))
                        (.. __it (remove))
                    )
                    (§ elseif (not (.. __followingChains (isEmpty))))
                    (§ block
                        (§ if (not (§ insta __chain #_"MarriedKeyChain")))
                        (§ block
                            (§ throw (§ new #_"IllegalStateException"))
                        )

                        (.. (§ cast #_"MarriedKeyChain" __chain) (setFollowingKeyChains __followingChains))
                        (§ ass __followingChains (Lists/newArrayList))
                    )
                )
            )
            nil
        )
    )

    #_public
    (§ method #_"String" (§ fn toString) [#_"boolean" __includePrivateKeys])
    (§ block
        (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder"))
        (§ block
            (§ if (some? basic))
            (§ block
                (§ let #_"List<ECKey>" __keys (.. basic (getKeys)))
                (§ block
                    (Collections/sort __keys, ECKey/AGE_COMPARATOR)
                    (§ for (§ var #_"ECKey" __key) :for __keys)
                    (§ block
                        (.. __key (formatKeyWithAddress __includePrivateKeys, __sb, params))
                    )
                )
            )
            (§ for (§ var #_"DeterministicKeyChain" __chain) :for chains)
            (§ block
                (.. __sb (append (.. __chain (toString __includePrivateKeys, params))) (append "\n"))
            )
            (.. __sb (toString))
        )
    )

    ;;; Returns a copy of the current list of chains. ;;
    #_public
    (§ method #_"List<DeterministicKeyChain>" (§ fn getDeterministicKeyChains) [])
    (§ block
        (§ new #_"ArrayList<>" chains)
    )

    ;;;
     ; Returns a counter that increases (by an arbitrary amount) each time new keys have been calculated
     ; due to lookahead and thus the Bloom filter that was previously calculated has become stale.
     ;;
    #_public
    (§ method #_"int" (§ fn getCombinedKeyLookaheadEpochs) [])
    (§ block
        (§ let #_"int" __epoch 0)
        (§ block
            (§ for (§ var #_"DeterministicKeyChain" __chain) :for chains)
            (§ block
                (§ ass __epoch (+ __epoch (.. __chain (getKeyLookaheadEpoch))))
            )
            __epoch
        )
    )
)

#_(ns org.bitcoinj.wallet #_"KeyTimeCoinSelector"
    (:import [java.util LinkedList List])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.script Script]))

;;;
 ; A coin selector that takes all coins assigned to keys created before the given timestamp.
 ; Used as part of the implementation of {@link Wallet#setKeyRotationTime(java.util.Date)}.
 ;;
#_public
(§ class #_"KeyTimeCoinSelector" (§ implements #_"CoinSelector")
    #_private
    #_static
    (§ def- #_"Logger" (§ name KeyTimeCoinSelector/log) (LoggerFactory/getLogger (§ klass #_"KeyTimeCoinSelector")))

    ;;; A number of inputs chosen to avoid hitting {@link org.bitcoinj.core.Transaction#MAX_STANDARD_TX_SIZE}. ;;
    #_public
    #_static
    (§ def #_"int" (§ name KeyTimeCoinSelector/MAX_SIMULTANEOUS_INPUTS) 600)

    #_private
    (§ field- #_"long" (§ name unixTimeSeconds))
    #_private
    (§ field- #_"Wallet" (§ name wallet))
    #_private
    (§ field- #_"boolean" (§ name ignorePending))

    #_public
    (§ constructor #_"KeyTimeCoinSelector" [#_"Wallet" __wallet, #_"long" __unixTimeSeconds, #_"boolean" __ignorePending])
    (§ block
        (§ ass (§ name (.. this unixTimeSeconds)) __unixTimeSeconds)
        (§ ass (§ name (.. this wallet)) __wallet)
        (§ ass (§ name (.. this ignorePending)) __ignorePending)
        this
    )

    #_override
    #_public
    (§ method #_"CoinSelection" (§ fn select) [#_"Coin" __target, #_"List<TransactionOutput>" __candidates])
    (§ block
        (§ try )
        (§ block
            (§ let #_"LinkedList<TransactionOutput>" __gathered (Lists/newLinkedList))
            (§ block
                (§ let #_"Coin" __valueGathered Coin/ZERO)
                (§ block
                    (§ for (§ var #_"TransactionOutput" __output) :for __candidates)
                    (§ block
                        (§ if (and ignorePending (not (.. this (isConfirmed __output)))))
                        (§ block
                            (§ continue )
                        )

                        ;; Find the key that controls output, assuming it's a regular pay-to-pubkey or pay-to-address output.
                        ;; We ignore any other kind of exotic output on the assumption we can't spend it ourselves.
                        (§ let #_"Script" __scriptPubKey (.. __output (getScriptPubKey)))
                        (§ block
                            (§ let #_"ECKey" __controllingKey)
                            (§ block
                                (§ if (.. __scriptPubKey (isSentToRawPubKey)))
                                (§ block
                                    (§ ass __controllingKey (.. wallet (findKeyFromPubKey (.. __scriptPubKey (getPubKey)))))
                                )
                                (§ elseif (.. __scriptPubKey (isSentToAddress)))
                                (§ block
                                    (§ ass __controllingKey (.. wallet (findKeyFromPubHash (.. __scriptPubKey (getPubKeyHash)))))
                                )
                                (§ else )
                                (§ block
                                    (.. KeyTimeCoinSelector/log (info "Skipping tx output {} because it's not of simple form.", __output))
                                    (§ continue )
                                )

                                (Preconditions/checkNotNull __controllingKey, "Coin selector given output as candidate for which we lack the key")
                                (§ if (<= unixTimeSeconds (.. __controllingKey (getCreationTimeSeconds))))
                                (§ block
                                    (§ continue )
                                )

                                ;; It's older than the cutoff time so select.
                                (§ ass __valueGathered (.. __valueGathered (add (.. __output (getValue)))))
                                (.. __gathered (push __output))
                                (§ if (<= KeyTimeCoinSelector/MAX_SIMULTANEOUS_INPUTS (.. __gathered (size))))
                                (§ block
                                    (.. KeyTimeCoinSelector/log (warn "Reached {} inputs, going further would yield a tx that is too large, stopping here.", (.. __gathered (size))))
                                    (§ break )
                                )
                            )
                        )
                    )
                    (§ return (§ new #_"CoinSelection" __valueGathered, __gathered))
                )
            )
        )
        (§ catch #_"ScriptException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; We should never have problems understanding scripts in our wallet.
        )
    )

    #_private
    (§ method- #_"boolean" (§ fn isConfirmed) [#_"TransactionOutput" __output])
    (§ block
        (.. __output (getParentTransaction) (getConfidence) (getConfidenceType) (equals TransactionConfidence/ConfidenceType/BUILDING))
    )
)

#_(ns org.bitcoinj.wallet #_"MarriedKeyChain"
    (:import [java.security SecureRandom]
