             [com.google.protobuf ByteString])
   (:require [org.bitcoinj.core BloomFilter ECKey NetworkParameters Utils]
             [org.bitcoinj.crypto DeterministicKey KeyCrypter]
             [org.bitcoinj.script Script ScriptBuilder]))

;;;
 ; <p>A multi-signature keychain using synchronized HD keys (a.k.a HDM).</p>
 ;
 ; <p>This keychain keeps track of following keychains that follow the account key of this keychain.  You can get P2SH
 ; addresses to receive coins to from this chain.  The threshold - sigsRequiredToSpend specifies how many signatures
 ; required to spend transactions for this married keychain.  This value should not exceed the total number of keys
 ; involved (one followed key plus number of following keys), otherwise IllegalArgumentException will be thrown.</p>
 ;
 ; <p>IMPORTANT: As of Bitcoin Core 0.9 all multisig transactions which require more than 3 public keys are non-standard and
 ; such spends won't be processed by peers with default settings, essentially making such transactions almost nonspendable.</p>
 ;
 ; <p>This method will throw an IllegalStateException, if the keychain is already married or already has leaf keys issued.</p>
 ;;
#_public
(§ class #_"MarriedKeyChain" (§ extends #_"DeterministicKeyChain")
    ;; The map holds P2SH redeem script and corresponding ECKeys issued by this KeyChainGroup (including lookahead)
    ;; mapped to redeem script hashes.
    #_private
    (§ field- #_"LinkedHashMap<ByteString, RedeemData>" (§ name marriedKeysRedeemData) (§ new #_"LinkedHashMap<>"))

    #_private
    (§ field- #_"List<DeterministicKeyChain>" (§ name followingKeyChains))

    ;;; Builds a {@link MarriedKeyChain}. ;;
    #_public
    #_static
    (§ class #_"Builder<T extends Builder<T>>" (§ extends #_"DeterministicKeyChain.Builder<T>")
        #_private
        (§ field- #_"List<DeterministicKey>" (§ name followingKeys))
        #_private
        (§ field- #_"int" (§ name threshold))

        #_protected
        (§ constructor #_"Builder" [])
        (§ block
            this
        )

        #_public
        (§ method #_"T" (§ fn followingKeys) [#_"List<DeterministicKey>" __followingKeys])
        (§ block
            (§ ass (§ name (.. this followingKeys)) __followingKeys)
            (.. this (self))
        )

        #_public
        (§ method #_"T" (§ fn followingKeys) [#_"DeterministicKey" __followingKey, #_"DeterministicKey" __followingKeys ...])
        (§ block
            (§ ass (§ name (.. this __followingKeys)) (Lists/asList __followingKey, __followingKeys))
            (.. this (self))
        )

        ;;;
         ; Threshold, or <code>((followingKeys.size() + 1) / 2 + 1)</code> (majority) if unspecified.
         ;
         ; IMPORTANT: As of Bitcoin Core 0.9 all multisig transactions which require more than 3 public keys are non-standard and
         ; such spends won't be processed by peers with default settings, essentially making such transactions almost nonspendable.
         ;;
        #_public
        (§ method #_"T" (§ fn threshold) [#_"int" __threshold])
        (§ block
            (§ ass (§ name (.. this threshold)) __threshold)
            (.. this (self))
        )

        #_override
        #_public
        (§ method #_"MarriedKeyChain" (§ fn build) [])
        (§ block
            (Preconditions/checkState (or (some? random) (some? entropy) (some? seed) (some? watchingKey)), "Must provide either entropy or random or seed or watchingKey")
            (Preconditions/checkNotNull followingKeys, "followingKeys must be provided")

            (§ let #_"MarriedKeyChain" __chain)
            (§ block
                (§ if (== threshold 0))
                (§ block
                    (§ ass (§ name threshold) (+ (/ (+ (.. followingKeys (size)) 1) 2) 1))
                )

                (§ if (some? random))
                (§ block
                    (§ ass __chain (§ new #_"MarriedKeyChain" random, bits, (.. this (getPassphrase)), seedCreationTimeSecs))
                )
                (§ elseif (some? entropy))
                (§ block
                    (§ ass __chain (§ new #_"MarriedKeyChain" entropy, (.. this (getPassphrase)), seedCreationTimeSecs))
                )
                (§ elseif (some? seed))
                (§ block
                    (.. seed (setCreationTimeSeconds seedCreationTimeSecs))
                    (§ ass __chain (§ new #_"MarriedKeyChain" seed))
                )
                (§ else )
                (§ block
                    (.. watchingKey (setCreationTimeSeconds seedCreationTimeSecs))
                    (§ ass __chain (§ new #_"MarriedKeyChain" watchingKey))
                )

                (.. __chain (addFollowingAccountKeys followingKeys, threshold))
                __chain
            )
        )
    )

    #_public
    #_static
    (§ defn #_"Builder<?>" (§ fn MarriedKeyChain/builder) [])
    (§ block
        (§ new #_"Builder")
    )

    ;; Protobuf deserialization constructors.
    (§ constructor #_"MarriedKeyChain" [#_"DeterministicKey" __accountKey])
    (§ block
        (§ super __accountKey, false)
        this
    )

    (§ constructor #_"MarriedKeyChain" [#_"DeterministicSeed" __seed, #_"KeyCrypter" __crypter])
    (§ block
        (§ super __seed, __crypter)
        this
    )

    ;; Builder constructors.
    #_private
    (§ constructor- #_"MarriedKeyChain" [#_"SecureRandom" __random, #_"int" __bits, #_"String" __passphrase, #_"long" __seedCreationTimeSecs])
    (§ block
        (§ super __random, __bits, __passphrase, __seedCreationTimeSecs)
        this
    )

    #_private
    (§ constructor- #_"MarriedKeyChain" [#_"byte[]" __entropy, #_"String" __passphrase, #_"long" __seedCreationTimeSecs])
    (§ block
        (§ super __entropy, __passphrase, __seedCreationTimeSecs)
        this
    )

    #_private
    (§ constructor- #_"MarriedKeyChain" [#_"DeterministicSeed" __seed])
    (§ block
        (§ super __seed)
        this
    )

    (§ method #_"void" (§ fn setFollowingKeyChains) [#_"List<DeterministicKeyChain>" __followingKeyChains])
    (§ block
        (Preconditions/checkArgument (not (.. __followingKeyChains (isEmpty))))
        (§ ass (§ name (.. this followingKeyChains)) __followingKeyChains)
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn isMarried) [])
    (§ block
        true
    )

    ;;; Create a new married key and return the matching output script. ;;
    #_override
    #_public
    (§ method #_"Script" (§ fn freshOutputScript) [#_"KeyPurpose" __purpose])
    (§ block
        (§ let #_"DeterministicKey" __followedKey (.. this (getKey __purpose)))
        (§ block
            (§ let #_"ImmutableList.Builder<ECKey>" __keys (.. (ImmutableList/builder #_"ImmutableList<ECKey>") (add __followedKey)))
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" __keyChain) :for followingKeyChains)
                (§ block
                    (§ let #_"DeterministicKey" __followingKey (.. __keyChain (getKey __purpose)))
                    (§ block
                        (Preconditions/checkState (.. __followedKey (getChildNumber) (equals (.. __followingKey (getChildNumber)))), "Following keychains should be in sync")
                        (.. __keys (add __followingKey))
                    )
                )
                (§ let #_"List<ECKey>" __marriedKeys (.. __keys (build)))
                (§ block
                    (§ let #_"Script" __redeemScript (ScriptBuilder/createRedeemScript sigsRequiredToSpend, __marriedKeys))
                    (§ block
                        (ScriptBuilder/createP2SHOutputScript __redeemScript)
                    )
                )
            )
        )
    )

    #_private
    (§ method- #_"List<ECKey>" (§ fn getMarriedKeysWithFollowed) [#_"DeterministicKey" __followedKey])
    (§ block
        (§ let #_"ImmutableList.Builder<ECKey>" __keys (ImmutableList/builder))
        (§ block
            (§ for (§ var #_"DeterministicKeyChain" __keyChain) :for followingKeyChains)
            (§ block
                (.. __keyChain (maybeLookAhead))
                (.. __keys (add (.. __keyChain (getKeyByPath (.. __followedKey (getPath))))))
            )
            (.. __keys (add __followedKey))
            (.. __keys (build))
        )
    )

    ;;; Get the redeem data for a key in this married chain. ;;
    #_override
    #_public
    (§ method #_"RedeemData" (§ fn getRedeemData) [#_"DeterministicKey" __followedKey])
    (§ block
        (§ let #_"List<ECKey>" __marriedKeys (.. this (getMarriedKeysWithFollowed __followedKey)))
        (§ block
            (§ let #_"Script" __redeemScript (ScriptBuilder/createRedeemScript sigsRequiredToSpend, __marriedKeys))
            (§ block
                (RedeemData/of __marriedKeys, __redeemScript)
            )
        )
    )

    #_private
    (§ method- #_"void" (§ fn addFollowingAccountKeys) [#_"List<DeterministicKey>" __followingAccountKeys, #_"int" __sigsRequiredToSpend])
    (§ block
        (Preconditions/checkArgument (<= __sigsRequiredToSpend (+ (.. __followingAccountKeys (size)) 1)), "Multisig threshold can't exceed total number of keys")
        (Preconditions/checkState (== (.. this (numLeafKeysIssued)) 0), "Active keychain already has keys in use")
        (Preconditions/checkState (nil? followingKeyChains))

        (§ let #_"List<DeterministicKeyChain>" __followingKeyChains (Lists/newArrayList))
        (§ block

            (§ for (§ var #_"DeterministicKey" __key) :for __followingAccountKeys)
            (§ block
                (Preconditions/checkArgument (== (.. __key (getPath) (size)) (.. this (getAccountPath) (size))), "Following keys have to be account keys")

                (§ let #_"DeterministicKeyChain" __chain (DeterministicKeyChain/watchAndFollow __key))
                (§ block
                    (§ if (<= 0 lookaheadSize))
                    (§ block
                        (.. __chain (setLookaheadSize lookaheadSize))
                    )
                    (§ if (<= 0 lookaheadThreshold))
                    (§ block
                        (.. __chain (setLookaheadThreshold lookaheadThreshold))
                    )
                    (.. __followingKeyChains (add __chain))
                )
            )

            (§ ass (§ name (.. this sigsRequiredToSpend)) __sigsRequiredToSpend)
            (§ ass (§ name (.. this followingKeyChains)) __followingKeyChains)
            nil
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn setLookaheadSize) [#_"int" __lookaheadSize])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (.. super (setLookaheadSize __lookaheadSize))

            (§ if (some? followingKeyChains))
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" __followingChain) :for followingKeyChains)
                (§ block
                    (.. __followingChain (setLookaheadSize __lookaheadSize))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"List<Protos.Key>" (§ fn serializeToProtobuf) [])
    (§ block
        (§ let #_"List<Protos.Key>" __result (Lists/newArrayList))
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (§ for (§ var #_"DeterministicKeyChain" __chain) :for followingKeyChains)
                (§ block
                    (.. __result (addAll (.. __chain (serializeMyselfToProtobuf))))
                )
                (.. __result (addAll (.. this (serializeMyselfToProtobuf))))
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            __result
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn formatAddresses) [#_"boolean" __includePrivateKeys, #_"NetworkParameters" __params, #_"StringBuilder" __sb])
    (§ block
        (§ for (§ var #_"DeterministicKeyChain" __followingChain) :for followingKeyChains)
        (§ block
            (.. __sb (append "Following chain:  ") (append (.. __followingChain (getWatchingKey) (serializePubB58 __params))) (append "\n"))
        )
        (.. __sb (append "\n"))
        (§ for (§ var #_"RedeemData" __redeemData) :for (.. marriedKeysRedeemData (values)))
        (§ block
            (.. this (formatScript (ScriptBuilder/createP2SHOutputScript (.. __redeemData redeemScript)), __sb, __params))
        )
        nil
    )

    #_private
    (§ method- #_"void" (§ fn formatScript) [#_"Script" __script, #_"StringBuilder" __sb, #_"NetworkParameters" __params])
    (§ block
        (.. __sb (append "  addr:") (append (.. __script (getToAddress __params))))
        (.. __sb (append "  hash160:") (append (.. Utils/HEX (encode (.. __script (getPubKeyHash))))))
        (§ if (< 0 (.. __script (getCreationTimeSeconds))))
        (§ block
            (.. __sb (append "  creationTimeSeconds:") (append (.. __script (getCreationTimeSeconds))))
        )
        (.. __sb (append "\n"))
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn maybeLookAheadScripts) [])
    (§ block
        (.. super (maybeLookAheadScripts))

        (§ let #_"int" __numLeafKeys (.. this (getLeafKeys) (size)))
        (§ block
            (Preconditions/checkState (<= (.. marriedKeysRedeemData (size)) __numLeafKeys), "Number of scripts is greater than number of leaf keys")

            (§ if (!= (.. marriedKeysRedeemData (size)) __numLeafKeys))
            (§ block
                (.. this (maybeLookAhead))
                (§ for (§ var #_"DeterministicKey" __followedKey) :for (.. this (getLeafKeys)))
                (§ block
                    (§ let #_"RedeemData" __redeemData (.. this (getRedeemData __followedKey)))
                    (§ block
                        (§ let #_"Script" __scriptPubKey (ScriptBuilder/createP2SHOutputScript (.. __redeemData redeemScript)))
                        (§ block
                            (.. marriedKeysRedeemData (put (ByteString/copyFrom (.. __scriptPubKey (getPubKeyHash))), __redeemData))
                        )
                    )
                )
            )
            nil
        )
    )

    #_nilable
    #_override
    #_public
    (§ method #_"RedeemData" (§ fn findRedeemDataByScriptHash) [#_"ByteString" __bytes])
    (§ block
        (.. marriedKeysRedeemData (get __bytes))
    )

    #_override
    #_public
    (§ method #_"BloomFilter" (§ fn getFilter) [#_"int" __size, #_"double" __falsePositiveRate, #_"long" __tweak])
    (§ block
        (.. lock (lock))
        (§ let #_"BloomFilter" __filter)
        (§ block
            (§ try )
            (§ block
                (§ ass __filter (§ new #_"BloomFilter" __size, __falsePositiveRate, __tweak))
                (§ for (§ var #_"Map.Entry<ByteString, RedeemData>" __entry) :for (.. marriedKeysRedeemData (entrySet)))
                (§ block
                    (.. __filter (insert (.. __entry (getKey) (toByteArray))))
                    (.. __filter (insert (.. __entry (getValue) redeemScript (getProgram))))
