    ;;;
     ; Creates a wallet containing a given set of keys.  All further keys will be derived from the oldest key.
     ;;
    #_public
    #_static
    (§ defn #_"Wallet" (§ fn Wallet/fromKeys) [#_"NetworkParameters" __params, #_"List<ECKey>" __keys])
    (§ block
        (§ for (§ var #_"ECKey" __key) :for __keys)
        (§ block
            (Preconditions/checkArgument (not (§ insta __key #_"DeterministicKey")))
        )

        (§ let #_"KeyChainGroup" __group (§ new #_"KeyChainGroup" __params))
        (§ block
            (.. __group (importKeys __keys))
            (§ new #_"Wallet" __params, __group)
        )
    )

