    #_public
    (§ method #_"List<DeterministicKey>" (§ fn freshKeys) [#_"KeyChain.KeyPurpose" __purpose, #_"int" __numberOfKeys])
    (§ block
        (§ let #_"List<DeterministicKey>" __keys)
        (§ block
            (.. keyChainGroupLock (lock))
            (§ try )
            (§ block
                (.. this (maybeUpgradeToHD))
                (§ ass __keys (.. keyChainGroup (freshKeys __purpose, __numberOfKeys)))
            )
            (§ finally )
            (§ block
