     ; to do anything that would require a new address or key.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isDeterministicUpgradeRequired) [])
    (§ block
        (.. keyChainGroupLock (lock))
        (§ try )
        (§ block
            (§ return (.. keyChainGroup (isDeterministicUpgradeRequired)))
        )
        (§ finally )
        (§ block
            (.. keyChainGroupLock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn maybeUpgradeToHD) [])
        (§ throws #_"DeterministicUpgradeRequiresPassword")
    (§ block
        (.. this (maybeUpgradeToHD nil))
        nil
    )

    #_guarded-by(§ opt "keyChainGroupLock")
    #_private
    (§ method- #_"void" (§ fn maybeUpgradeToHD) [#_nilable #_"KeyParameter" __aesKey])
        (§ throws #_"DeterministicUpgradeRequiresPassword")
    (§ block
        (Preconditions/checkState (.. keyChainGroupLock (isHeldByCurrentThread)))
        (§ if (.. keyChainGroup (isDeterministicUpgradeRequired)))
        (§ block
            (.. Wallet/log (info "Upgrade to HD wallets is required, attempting to do so."))
            (§ try )
            (§ block
                (.. this (upgradeToDeterministic __aesKey))
            )
            (§ catch #_"DeterministicUpgradeRequiresPassword" __e)
            (§ block
                (.. Wallet/log (error "Failed to auto upgrade due to encryption. You should call wallet.upgradeToDeterministic with the users AES key to avoid this error."))
                (§ throw __e)
            )
        )
        nil
    )

