            ;; Now for each pending transaction, see if it shares any outpoints with this tx.
            (§ let #_"Set<Transaction>" __doubleSpendTxns (Sets/newHashSet))
            (§ block
                (§ for (§ var #_"Transaction" __p) :for (.. __candidates (values)))
                (§ block
                    (§ if (.. __p (equals __tx)))
