    ;;;
     ; Returns a set of all transactions in the wallet.
     ; @param includeDead If true, transactions that were overridden by a double spend are included.
     ;;
    #_public
    (§ method #_"Set<Transaction>" (§ fn getTransactions) [#_"boolean" __includeDead])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"Set<Transaction>" __all (§ new #_"HashSet<>"))
            (§ block
                (.. __all (addAll (.. unspent (values))))
                (.. __all (addAll (.. spent (values))))
                (.. __all (addAll (.. pending (values))))
                (§ if __includeDead)
                (§ block
                    (.. __all (addAll (.. dead (values))))
                )
                (§ return __all)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns a set of all WalletTransactions in the wallet.
     ;;
    #_public
    (§ method #_"Iterable<WalletTransaction>" (§ fn getWalletTransactions) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"Set<WalletTransaction>" __all (§ new #_"HashSet<>"))
            (§ block
                (Wallet/addWalletTransactionsToSet __all, Pool/UNSPENT, (.. unspent (values)))
                (Wallet/addWalletTransactionsToSet __all, Pool/SPENT, (.. spent (values)))
                (Wallet/addWalletTransactionsToSet __all, Pool/DEAD, (.. dead (values)))
                (Wallet/addWalletTransactionsToSet __all, Pool/PENDING, (.. pending (values)))
                (§ return __all)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn Wallet/addWalletTransactionsToSet) [#_"Set<WalletTransaction>" __txns, #_"Pool" __poolType, #_"Collection<Transaction>" __pool])
    (§ block
        (§ for (§ var #_"Transaction" __tx) :for __pool)
        (§ block
            (.. __txns (add (§ new #_"WalletTransaction" __poolType, __tx)))
        )
        nil
    )

    ;;;
     ; Adds a transaction that has been associated with a particular wallet pool.  This is intended for usage by
     ; deserialization code, such as the {@link WalletProtobufSerializer} class.  It isn't normally useful for
     ; applications.  It does not trigger auto saving.
     ;;
    #_public
    (§ method #_"void" (§ fn addWalletTransaction) [#_"WalletTransaction" __wtx])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (.. this (addWalletTransaction (.. __wtx (getPool)), (.. __wtx (getTransaction))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Adds the given transaction to the given pools and registers a confidence change listener on it.
     ;;
    #_private
    (§ method- #_"void" (§ fn addWalletTransaction) [#_"Pool" __pool, #_"Transaction" __tx])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (.. transactions (put (.. __tx (getHash)), __tx))
        (§ switch __pool)
        (§ block
            (§ case UNSPENT)
            (§ block
                (Preconditions/checkState (== (.. unspent (put (.. __tx (getHash)), __tx)) nil))
                (§ break )
            )
            (§ case SPENT)
            (§ block
                (Preconditions/checkState (== (.. spent (put (.. __tx (getHash)), __tx)) nil))
                (§ break )
            )
            (§ case PENDING)
            (§ block
                (Preconditions/checkState (== (.. pending (put (.. __tx (getHash)), __tx)) nil))
                (§ break )
            )
            (§ case DEAD)
            (§ block
                (Preconditions/checkState (== (.. dead (put (.. __tx (getHash)), __tx)) nil))
                (§ break )
            )
            (§ default )
            (§ block
                (§ throw (§ new #_"RuntimeException" (str "Unknown wallet transaction type " __pool)))
            )
        )

        (§ if (or (== __pool Pool/UNSPENT) (== __pool Pool/PENDING)))
        (§ block
            (§ for (§ var #_"TransactionOutput" __output) :for (.. __tx (getOutputs)))
            (§ block
                (§ if (and (.. __output (isAvailableForSpending)) (.. __output (isMine this))))
                (§ block
                    (.. myUnspents (add __output))
                )
            )
        )
        ;; This is safe even if the listener has been added before, as TransactionConfidence ignores duplicate
        ;; registration requests.  That makes the code in the wallet simpler.
        (.. __tx (getConfidence) (addEventListener Threading/SAME_THREAD, txConfidenceListener))
        nil
    )

    ;;;
     ; Returns all non-dead, active transactions ordered by recency.
     ;;
    #_public
    (§ method #_"List<Transaction>" (§ fn getTransactionsByTime) [])
    (§ block
        (.. this (getRecentTransactions 0, false))
    )

    ;;;
     ; Returns an list of N transactions, ordered by increasing age.  Transactions on side chains are not included.
     ; Dead transactions (overridden by double spends) are optionally included.
     ;
     ; Note: the current implementation is O(num transactions in wallet).  Regardless of how many transactions are
     ; requested, the cost is always the same.  In future, requesting smaller numbers of transactions may be faster
     ; depending on how the wallet is implemented (e.g. if backed by a database).
     ;;
    #_public
    (§ method #_"List<Transaction>" (§ fn getRecentTransactions) [#_"int" __numTransactions, #_"boolean" __includeDead])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkArgument (<= 0 __numTransactions))

            ;; Firstly, put all transactions into an array.
            (§ let #_"int" __size (+ (.. unspent (size)) (.. spent (size)) (.. pending (size))))
            (§ block
                (§ if (or (< __size __numTransactions) (== __numTransactions 0)))
                (§ block
                    (§ ass __numTransactions __size)
                )

                (§ let #_"ArrayList<Transaction>" __all (§ new #_"ArrayList<>" (.. this (getTransactions __includeDead))))
                (§ block
                    ;; Order by update time.
                    (Collections/sort __all, Transaction/SORT_TX_BY_UPDATE_TIME)
                    (§ if (== __numTransactions (.. __all (size))))
                    (§ block
                        (§ return __all)
                    )

                    (.. __all (subList __numTransactions, (.. __all (size))) (clear))
                    (§ return __all)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

