    ;;;
     ; Prepares the wallet for a blockchain replay.  Removes all transactions (as they would
     ; get in the way of the replay) and makes the wallet think it has never seen a block.
     ;;
    #_public
    (§ method #_"void" (§ fn reset) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (.. this (clearTransactions))
            (§ ass (§ name lastBlockSeenHash) nil)
            (§ ass (§ name lastBlockSeenHeight) -1) ;; Magic value for 'never'.
            (§ ass (§ name lastBlockSeenTimeSecs) 0)
            (.. this (saveLater))
            (.. this (maybeQueueOnWalletChanged))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Deletes transactions which appeared above the given block height from the wallet, but does not touch the keys.
     ; This is useful if you have some keys and wish to replay the block chain into the wallet in order to pick them up.
     ; Triggers auto saving.
     ;;
    #_public
    (§ method #_"void" (§ fn clearTransactions) [#_"int" __fromHeight])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (== __fromHeight 0))
            (§ block
                (.. this (clearTransactions))
                (.. this (saveLater))
            )
            (§ else )
            (§ block
                (§ throw (§ new #_"UnsupportedOperationException"))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_private
    (§ method- #_"void" (§ fn clearTransactions) [])
    (§ block
        (.. unspent (clear))
        (.. spent (clear))
        (.. pending (clear))
        (.. dead (clear))
        (.. transactions (clear))
        (.. myUnspents (clear))
        nil
    )

    ;;;
     ; Clean up the wallet.  Currently, it only removes risky pending transaction from the wallet and only
     ; if their outputs have not been spent.
     ;;
    #_public
    (§ method #_"void" (§ fn cleanup) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" __dirty false)
            (§ block
                (§ for (§ var #_"Iterator<Transaction>" __i (.. pending (values) (iterator))) :for (.. __i (hasNext)) :for )
                (§ block
                    (§ let #_"Transaction" __tx (.. __i (next)))
                    (§ block
                        (§ if (and (.. this (isTransactionRisky __tx, nil)) (not acceptRiskyTransactions)))
                        (§ block
                            (.. Wallet/log (debug "Found risky transaction {} in wallet during cleanup.", (.. __tx (getHashAsString))))
                            (§ if (not (.. __tx (isAnyOutputSpent))))
                            (§ block
                                ;; Sync myUnspents with the change.
                                (§ for (§ var #_"TransactionInput" __input) :for (.. __tx (getInputs)))
                                (§ block
                                    (§ let #_"TransactionOutput" __output (.. __input (getConnectedOutput)))
                                    (§ block
                                        (§ if (nil? __output))
                                        (§ block
                                            (§ continue )
                                        )
                                        (§ if (.. __output (isMine this)))
                                        (§ block
                                            (Preconditions/checkState (.. myUnspents (add __output)))
                                        )
                                        (.. __input (disconnect))
                                    )
                                )
                                (§ for (§ var #_"TransactionOutput" __output) :for (.. __tx (getOutputs)))
                                (§ block
                                    (.. myUnspents (remove __output))
                                )

                                (.. __i (remove))
                                (.. transactions (remove (.. __tx (getHash))))
                                (§ ass __dirty true)
                                (.. Wallet/log (info "Removed transaction {} from pending pool during cleanup.", (.. __tx (getHashAsString))))
                            )
                            (§ else )
                            (§ block
                                (.. Wallet/log (info "Cannot remove transaction {} from pending pool during cleanup, as it's already spent partially.", (.. __tx (getHashAsString))))
                            )
                        )
                    )
                )
                (§ if __dirty)
                (§ block
                    (.. this (isConsistentOrThrow))
                    (.. this (saveLater))
                    (§ if (.. Wallet/log (isInfoEnabled)))
                    (§ block
                        (.. Wallet/log (info "Estimated balance is now: {}", (.. this (getBalance BalanceType/ESTIMATED) (toFriendlyString))))
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    (§ method #_"EnumSet<Pool>" (§ fn getContainingPools) [#_"Transaction" __tx])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"EnumSet<Pool>" __result (EnumSet/noneOf (§ klass #_"Pool")))
            (§ block
                (§ let #_"Sha256Hash" __txHash (.. __tx (getHash)))
                (§ block
                    (§ if (.. unspent (containsKey __txHash)))
                    (§ block
                        (.. __result (add Pool/UNSPENT))
                    )
                    (§ if (.. spent (containsKey __txHash)))
                    (§ block
                        (.. __result (add Pool/SPENT))
                    )
                    (§ if (.. pending (containsKey __txHash)))
                    (§ block
                        (.. __result (add Pool/PENDING))
                    )
                    (§ if (.. dead (containsKey __txHash)))
                    (§ block
                        (.. __result (add Pool/DEAD))
                    )
                    (§ return __result)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

