    ;;;
     ; Formats the wallet as a human readable piece of text.  Intended for debugging, the format is
     ; not meant to be stable or human readable.
     ; @param includePrivateKeys Whether raw private key data should be included.
     ; @param includeTransactions Whether to print transaction data.
     ; @param chain If set, will be used to estimate lock times for block timelocked transactions.
     ;;
    #_public
    (§ method #_"String" (§ fn toString) [#_"boolean" __includePrivateKeys, #_"boolean" __includeTransactions, #_nilable #_"AbstractBlockChain" __chain])
    (§ block
        (.. lock (lock))
        (.. keyChainGroupLock (lock))
        (§ try )
        (§ block
            (§ let #_"StringBuilder" __sb (§ new #_"StringBuilder"))
            (§ block
                (§ let #_"Coin" __estimatedBalance (.. this (getBalance BalanceType/ESTIMATED)))
                (§ block
                    (§ let #_"Coin" __availableBalance (.. this (getBalance BalanceType/AVAILABLE_SPENDABLE)))
                    (§ block
                        (.. __sb (append "Wallet containing ") (append (.. __estimatedBalance (toFriendlyString))) (append " (spendable: ") (append (.. __availableBalance (toFriendlyString))) (append ") in:\n"))
                        (.. __sb (append "  ") (append (.. pending (size))) (append " pending transactions\n"))
                        (.. __sb (append "  ") (append (.. unspent (size))) (append " unspent transactions\n"))
                        (.. __sb (append "  ") (append (.. spent (size))) (append " spent transactions\n"))
                        (.. __sb (append "  ") (append (.. dead (size))) (append " dead transactions\n"))
                        (§ let #_"Date" __lastBlockSeenTime (.. this (getLastBlockSeenTime)))
                        (§ block
                            (.. __sb (append "Last seen best block: ") (append (.. this (getLastBlockSeenHeight))) (append " (") (append (if (some? __lastBlockSeenTime) (Utils/dateTimeFormat __lastBlockSeenTime) "time unknown")) (append "): ") (append (.. this (getLastBlockSeenHash))) (append "\n"))
                            (§ let #_"KeyCrypter" __crypter (.. keyChainGroup (getKeyCrypter)))
                            (§ block
                                (§ if (some? __crypter))
                                (§ block
                                    (.. __sb (append "Encryption: ") (append __crypter) (append "\n"))
                                )
                                (§ if (.. this (isWatching)))
                                (§ block
                                    (.. __sb (append "Wallet is watching.\n"))
                                )

                                ;; Do the keys.
                                (.. __sb (append "\nKeys:\n"))
                                (.. __sb (append "Earliest creation time: ") (append (Utils/dateTimeFormat (* (.. this (getEarliestKeyCreationTime)) 1000))) (append "\n"))
                                (§ let #_"Date" __keyRotationTime (.. this (getKeyRotationTime)))
                                (§ block
                                    (§ if (some? __keyRotationTime))
                                    (§ block
                                        (.. __sb (append "Key rotation time:      ") (append (Utils/dateTimeFormat __keyRotationTime)) (append "\n"))
                                    )
                                    (.. __sb (append (.. keyChainGroup (toString __includePrivateKeys))))

                                    (§ if __includeTransactions)
                                    (§ block
                                        ;; Print the transactions themselves.
                                        (§ if (< 0 (.. pending (size))))
                                        (§ block
                                            (.. __sb (append "\n>>> PENDING:\n"))
                                            (.. this (toStringHelper __sb, pending, __chain, Transaction/SORT_TX_BY_UPDATE_TIME))
                                        )
                                        (§ if (< 0 (.. unspent (size))))
                                        (§ block
                                            (.. __sb (append "\n>>> UNSPENT:\n"))
                                            (.. this (toStringHelper __sb, unspent, __chain, Transaction/SORT_TX_BY_HEIGHT))
                                        )
                                        (§ if (< 0 (.. spent (size))))
                                        (§ block
                                            (.. __sb (append "\n>>> SPENT:\n"))
                                            (.. this (toStringHelper __sb, spent, __chain, Transaction/SORT_TX_BY_HEIGHT))
                                        )
                                        (§ if (< 0 (.. dead (size))))
                                        (§ block
                                            (.. __sb (append "\n>>> DEAD:\n"))
                                            (.. this (toStringHelper __sb, dead, __chain, Transaction/SORT_TX_BY_UPDATE_TIME))
                                        )
                                    )
                                    (§ return (.. __sb (toString)))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. keyChainGroupLock (unlock))
            (.. lock (unlock))
        )
    )

    #_private
    (§ method- #_"void" (§ fn toStringHelper) [#_"StringBuilder" __sb, #_"Map<Sha256Hash, Transaction>" __transactionMap, #_nilable #_"AbstractBlockChain" __chain, #_nilable #_"Comparator<Transaction>" __sortOrder])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ let #_"Collection<Transaction>" __txns)
        (§ block
            (§ if (some? __sortOrder))
            (§ block
                (§ ass __txns (§ new #_"TreeSet<>" __sortOrder))
                (.. __txns (addAll (.. __transactionMap (values))))
            )
            (§ else )
            (§ block
                (§ ass __txns (.. __transactionMap (values)))
            )

            (§ for (§ var #_"Transaction" __tx) :for __txns)
            (§ block
                (§ try )
                (§ block
                    (.. __sb (append (.. __tx (getValue this) (toFriendlyString))))
                    (.. __sb (append " total value (sends "))
                    (.. __sb (append (.. __tx (getValueSentFromMe this) (toFriendlyString))))
                    (.. __sb (append " and receives "))
                    (.. __sb (append (.. __tx (getValueSentToMe this) (toFriendlyString))))
                    (.. __sb (append ")\n"))
                )
                (§ catch #_"ScriptException" (§ name _))
                (§ block
                    ;; Ignore and don't print this line.
                )
                (§ if (.. __tx (hasConfidence)))
                (§ block
                    (.. __sb (append "  confidence: ") (append (.. __tx (getConfidence))) (append "\n"))
                )
                (.. __sb (append (.. __tx (toString __chain))))
            )
            nil
        )
    )

