                ;; Make sure the object can be re-used to read another wallet without corruption.
                (.. txMap (clear))

                __wallet
            )
        )
    )

    ;;;
     ; Returns the loaded protocol buffer from the given byte stream.  You normally want
     ; {@link Wallet#loadFromFile(java.io.File)} instead - this method is designed for
     ; low level work involving the wallet file format itself.
     ;;
    #_public
    #_static
    (§ defn #_"Protos.Wallet" (§ fn WalletProtobufSerializer/parseToProto) [#_"InputStream" __input])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"CodedInputStream" __codedInput (CodedInputStream/newInstance __input))
        (§ block
            (.. __codedInput (setSizeLimit WalletProtobufSerializer/WALLET_SIZE_LIMIT))
            (.. Protos/Wallet (parseFrom __codedInput))
        )
    )

    #_private
    (§ method- #_"void" (§ fn readTransaction) [#_"Protos.Transaction" __txProto, #_"NetworkParameters" __params])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"Transaction" __tx (§ new #_"Transaction" __params))
        (§ block

            (.. __tx (setVersion (.. __txProto (getVersion))))

            (§ if (.. __txProto (hasUpdatedAt)))
            (§ block
                (.. __tx (setUpdateTime (§ new #_"Date" (.. __txProto (getUpdatedAt)))))
            )

            (§ for (§ var #_"Protos.TransactionOutput" __outputProto) :for (.. __txProto (getTransactionOutputList)))
            (§ block
                (§ let #_"Coin" __value (Coin/valueOf (.. __outputProto (getValue))))
                (§ block
                    (§ let #_"byte[]" __scriptBytes (.. __outputProto (getScriptBytes) (toByteArray)))
                    (§ block
                        (§ let #_"TransactionOutput" __output (§ new #_"TransactionOutput" __params, __tx, __value, __scriptBytes))
                        (§ block
                            (.. __tx (addOutput __output))
                        )
                    )
                )
            )

            (§ for (§ var #_"Protos.TransactionInput" __inputProto) :for (.. __txProto (getTransactionInputList)))
            (§ block
                (§ let #_"byte[]" __scriptBytes (.. __inputProto (getScriptBytes) (toByteArray)))
                (§ block
                    (§ let #_"TransactionOutPoint" __outpoint (§ new #_"TransactionOutPoint" __params, (& 0xffffffff (.. __inputProto (getTransactionOutPointIndex))), (WalletProtobufSerializer/byteStringToHash (.. __inputProto (getTransactionOutPointHash)))))
                    (§ block
                        (§ let #_"Coin" __value (when (.. __inputProto (hasValue)) (Coin/valueOf (.. __inputProto (getValue)))))
                        (§ block
                            (§ let #_"TransactionInput" __input (§ new #_"TransactionInput" __params, __tx, __scriptBytes, __outpoint, __value))
                            (§ block
                                (§ if (.. __inputProto (hasSequence)))
                                (§ block
                                    (.. __input (setSequenceNumber (& 0xffffffff (.. __inputProto (getSequence)))))
                                )
                                (.. __tx (addInput __input))
                            )
                        )
                    )
                )
            )

            (§ for (§ var #_"int" __i 0) :for (< __i (.. __txProto (getBlockHashCount))) :for (§ recur (inc __i)))
            (§ block
                (§ let #_"ByteString" __blockHash (.. __txProto (getBlockHash __i)))
                (§ block
                    (§ let #_"int" __relativityOffset 0)
                    (§ block
                        (§ if (< 0 (.. __txProto (getBlockRelativityOffsetsCount))))
                        (§ block
                            (§ ass __relativityOffset (.. __txProto (getBlockRelativityOffsets __i)))
                        )
                        (.. __tx (addBlockAppearance (WalletProtobufSerializer/byteStringToHash __blockHash), __relativityOffset))
                    )
                )
            )

            (§ if (.. __txProto (hasLockTime)))
            (§ block
                (.. __tx (setLockTime (& 0xffffffff (.. __txProto (getLockTime)))))
            )

            (§ if (.. __txProto (hasPurpose)))
            (§ block
                (§ switch (.. __txProto (getPurpose)))
                (§ block
                    (§ case UNKNOWN)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/UNKNOWN))
                        (§ break )
                    )
                    (§ case USER_PAYMENT)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/USER_PAYMENT))
                        (§ break )
                    )
                    (§ case KEY_ROTATION)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/KEY_ROTATION))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_CLAIM)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_CLAIM))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_PLEDGE)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_PLEDGE))
                        (§ break )
                    )
                    (§ case ASSURANCE_CONTRACT_STUB)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/ASSURANCE_CONTRACT_STUB))
                        (§ break )
                    )
                    (§ case RAISE_FEE)
                    (§ block
                        (.. __tx (setPurpose Transaction/Purpose/RAISE_FEE))
                        (§ break )
                    )
                    (§ default )
                    (§ block
                        (§ throw (§ new #_"RuntimeException" "New purpose serialization not implemented"))
                    )
                )
            )
            (§ else )
            (§ block
                ;; Old wallet: assume a user payment as that's the only reason a new tx would have been created back then.
                (.. __tx (setPurpose Transaction/Purpose/USER_PAYMENT))
            )

            (§ if (.. __txProto (hasExchangeRate)))
            (§ block
                (§ let #_"Protos.ExchangeRate" __exchangeRateProto (.. __txProto (getExchangeRate)))
                (§ block
                    (.. __tx (setExchangeRate (§ new #_"ExchangeRate" (Coin/valueOf (.. __exchangeRateProto (getCoinValue))), (Fiat/valueOf (.. __exchangeRateProto (getFiatCurrencyCode)), (.. __exchangeRateProto (getFiatValue))))))
                )
            )

            (§ if (.. __txProto (hasMemo)))
            (§ block
                (.. __tx (setMemo (.. __txProto (getMemo))))
            )

            ;; Transaction should now be complete.
            (§ let #_"Sha256Hash" __protoHash (WalletProtobufSerializer/byteStringToHash (.. __txProto (getHash))))
            (§ block
                (§ if (not (.. __tx (getHash) (equals __protoHash))))
                (§ block
                    (§ throw (§ new #_"UnreadableWalletException" (String/format Locale/US, "Transaction did not deserialize completely: %s vs %s", (.. __tx (getHash)), __protoHash)))
                )
                (§ if (.. txMap (containsKey (.. __txProto (getHash)))))
                (§ block
                    (§ throw (§ new #_"UnreadableWalletException" (str "Wallet contained duplicate transaction " (WalletProtobufSerializer/byteStringToHash (.. __txProto (getHash))))))
                )
                (.. txMap (put (.. __txProto (getHash)), __tx))
                nil
            )
        )
    )

    #_private
    (§ method- #_"WalletTransaction" (§ fn connectTransactionOutputs) [#_"NetworkParameters" __params, #_"org.bitcoinj.wallet.Protos.Transaction" __txProto])
        (§ throws #_"UnreadableWalletException")
    (§ block
        (§ let #_"Transaction" __tx (.. txMap (get (.. __txProto (getHash)))))
        (§ block
            (§ let #_"WalletTransaction.Pool" __pool)
            (§ block
                (§ switch (.. __txProto (getPool)))
                (§ block
                    (§ case DEAD)
                    (§ block
                        (§ ass __pool WalletTransaction/Pool/DEAD)
                        (§ break )
                    )
                    (§ case PENDING)
                    (§ block
                        (§ ass __pool WalletTransaction/Pool/PENDING)
                        (§ break )
                    )
                    (§ case SPENT)
                    (§ block
                        (§ ass __pool WalletTransaction/Pool/SPENT)
                        (§ break )
                    )
                    (§ case UNSPENT)
                    (§ block
                        (§ ass __pool WalletTransaction/Pool/UNSPENT)
                        (§ break )
                    )
                    ;; Upgrade old wallets: inactive pool has been merged with the pending pool.
                    ;; Remove this some time after 0.9 is old and everyone has upgraded.
                    ;; There should not be any spent outputs in this tx as old wallets would not allow them to be spent in this state.
                    (§ case INACTIVE)
                    (§ case PENDING_INACTIVE)
                    (§ block
                        (§ ass __pool WalletTransaction/Pool/PENDING)
                        (§ break )
                    )
                    (§ default )
                    (§ block
                        (§ throw (§ new #_"UnreadableWalletException" (str "Unknown transaction pool: " (.. __txProto (getPool)))))
                    )
                )

                (§ for (§ var #_"int" __i 0) :for (< __i (.. __tx (getOutputs) (size))) :for (§ recur (inc __i)))
                (§ block
                    (§ let #_"TransactionOutput" __output (.. __tx (getOutputs) (get __i)))
                    (§ block
                        (§ let #_"Protos.TransactionOutput" __transactionOutput (.. __txProto (getTransactionOutput __i)))
                        (§ block
                            (§ if (.. __transactionOutput (hasSpentByTransactionHash)))
                            (§ block
                                (§ let #_"ByteString" __spentByTransactionHash (.. __transactionOutput (getSpentByTransactionHash)))
                                (§ block
                                    (§ let #_"Transaction" __spendingTx (.. txMap (get __spentByTransactionHash)))
                                    (§ block
                                        (§ if (nil? __spendingTx))
                                        (§ block
                                            (§ throw (§ new #_"UnreadableWalletException" (String/format Locale/US, "Could not connect %s to %s", (.. __tx (getHashAsString)), (WalletProtobufSerializer/byteStringToHash __spentByTransactionHash))))
                                        )

                                        (§ let #_"int" __spendingIndex (.. __transactionOutput (getSpentByTransactionIndex)))
                                        (§ block
                                            (§ let #_"TransactionInput" __input (Preconditions/checkNotNull (.. __spendingTx (getInput __spendingIndex))))
                                            (§ block
                                                (.. __input (connect __output))
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )

                (§ if (.. __txProto (hasConfidence)))
                (§ block
                    (§ let #_"Protos.TransactionConfidence" __confidenceProto (.. __txProto (getConfidence)))
                    (§ block
                        (§ let #_"TransactionConfidence" __confidence (.. __tx (getConfidence)))
                        (§ block
                            (.. this (readConfidence __params, __tx, __confidenceProto, __confidence))
                        )
                    )
                )

                (§ new #_"WalletTransaction" __pool, __tx)
            )
        )
    )

    #_private
    (§ method- #_"void" (§ fn readConfidence) [#_"NetworkParameters" __params, #_"Transaction" __tx, #_"Protos.TransactionConfidence" __confidenceProto, #_"TransactionConfidence" __confidence])
        (§ throws #_"UnreadableWalletException")
    (§ block
        ;; We are lenient here because tx confidence is not an essential part of the wallet.
        ;; If the tx has an unknown type of confidence, ignore.
        (§ if (not (.. __confidenceProto (hasType))))
        (§ block
            (.. WalletProtobufSerializer/log (warn "Unknown confidence type for tx {}", (.. __tx (getHashAsString))))
            (§ return nil)
        )

        (§ let #_"ConfidenceType" __confidenceType)
        (§ block
            (§ switch (.. __confidenceProto (getType)))
            (§ block
                (§ case BUILDING)
                (§ block
                    (§ ass __confidenceType ConfidenceType/BUILDING)
                    (§ break )
                )
                (§ case DEAD)
                (§ block
                    (§ ass __confidenceType ConfidenceType/DEAD)
                    (§ break )
                )
                ;; These two are equivalent (must be able to read old wallets).
                (§ case NOT_IN_BEST_CHAIN)
                (§ block
                    (§ ass __confidenceType ConfidenceType/PENDING)
                    (§ break )
                )
                (§ case PENDING)
                (§ block
                    (§ ass __confidenceType ConfidenceType/PENDING)
                    (§ break )
                )
                (§ case IN_CONFLICT)
                (§ block
                    (§ ass __confidenceType ConfidenceType/IN_CONFLICT)
                    (§ break )
                )
                (§ case UNKNOWN)
                (§ block
                    ;; Fall through.
                )
                (§ default )
                (§ block
                    (§ ass __confidenceType ConfidenceType/UNKNOWN)
                    (§ break )
                )
            )
            (.. __confidence (setConfidenceType __confidenceType))
            (§ if (.. __confidenceProto (hasAppearedAtHeight)))
            (§ block
                (§ if (!= (.. __confidence (getConfidenceType)) ConfidenceType/BUILDING))
                (§ block
                    (.. WalletProtobufSerializer/log (warn "Have appearedAtHeight but not BUILDING for tx {}", (.. __tx (getHashAsString))))
                    (§ return nil)
                )
                (.. __confidence (setAppearedAtChainHeight (.. __confidenceProto (getAppearedAtHeight))))
            )

            (§ if (.. __confidenceProto (hasDepth)))
            (§ block
                (§ if (!= (.. __confidence (getConfidenceType)) ConfidenceType/BUILDING))
                (§ block
                    (.. WalletProtobufSerializer/log (warn "Have depth but not BUILDING for tx {}", (.. __tx (getHashAsString))))
                    (§ return nil)
                )
                (.. __confidence (setDepthInBlocks (.. __confidenceProto (getDepth))))
            )

            (§ if (.. __confidenceProto (hasOverridingTransaction)))
            (§ block
                (§ if (!= (.. __confidence (getConfidenceType)) ConfidenceType/DEAD))
                (§ block
                    (.. WalletProtobufSerializer/log (warn "Have overridingTransaction but not OVERRIDDEN for tx {}", (.. __tx (getHashAsString))))
                    (§ return nil)
                )
                (§ let #_"Transaction" __overridingTransaction (.. txMap (get (.. __confidenceProto (getOverridingTransaction)))))
                (§ block
                    (§ if (nil? __overridingTransaction))
                    (§ block
                        (.. WalletProtobufSerializer/log (warn "Have overridingTransaction that is not in wallet for tx {}", (.. __tx (getHashAsString))))
                        (§ return nil)
                    )
                    (.. __confidence (setOverridingTransaction __overridingTransaction))
                )
            )

            (§ for (§ var #_"Protos.PeerAddress" __proto) :for (.. __confidenceProto (getBroadcastByList)))
            (§ block
                (§ let #_"InetAddress" __ip)
                (§ block
                    (§ try )
                    (§ block
                        (§ ass __ip (InetAddress/getByAddress (.. __proto (getIpAddress) (toByteArray))))
                    )
                    (§ catch #_"UnknownHostException" __e)
                    (§ block
                        (§ throw (§ new #_"UnreadableWalletException" "Peer IP address does not have the right length", __e))
                    )

                    (§ let #_"int" __port (.. __proto (getPort)))
                    (§ block
                        (§ let #_"int" __protocolVersion (.. __params (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)))
                        (§ block
                            (§ let #_"BigInteger" __services (BigInteger/valueOf (.. __proto (getServices))))
                            (§ block
                                (§ let #_"PeerAddress" __address (§ new #_"PeerAddress" __params, __ip, __port, __protocolVersion, __services))
                                (§ block
                                    (.. __confidence (markBroadcastBy __address))
                                )
                            )
                        )
                    )
                )
            )

            (§ if (.. __confidenceProto (hasLastBroadcastedAt)))
            (§ block
                (.. __confidence (setLastBroadcastedAt (§ new #_"Date" (.. __confidenceProto (getLastBroadcastedAt)))))
            )

            (§ switch (.. __confidenceProto (getSource)))
            (§ block
                (§ case SOURCE_SELF)
                (§ block
                    (.. __confidence (setSource TransactionConfidence/Source/SELF))
                    (§ break )
                )
                (§ case SOURCE_NETWORK)
                (§ block
                    (.. __confidence (setSource TransactionConfidence/Source/NETWORK))
                    (§ break )
                )
                (§ case SOURCE_UNKNOWN)
                (§ block
                    ;; Fall through.
                )
                (§ default )
                (§ block
                    (.. __confidence (setSource TransactionConfidence/Source/UNKNOWN))
                    (§ break )
                )
            )
            nil
        )
    )

    ;;;
     ; Cheap test to see if input stream is a wallet.  This checks for a magic value at the beginning of the stream.
     ;
     ; @param is Input stream to test.
     ; @return true if input stream is a wallet.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn WalletProtobufSerializer/isWallet) [#_"InputStream" __is])
    (§ block
        (§ try )
        (§ block
            (§ let #_"CodedInputStream" __cis (CodedInputStream/newInstance __is))
            (§ block
                (§ let #_"int" __tag (.. __cis (readTag)))
                (§ block
                    (§ let #_"int" __field (WireFormat/getTagFieldNumber __tag))
                    (§ block
                        (§ if (!= __field 1)) ;; network_identifier
                        (§ block
                            (§ return false)
                        )

                        (§ let #_"String" __network (.. __cis (readString)))
                        (§ block
                            (§ return (some? (NetworkParameters/fromID __network)))
                        )
                    )
                )
            )
        )
        (§ catch #_"IOException" (§ name _))
        (§ block
            (§ return false)
        )
    )
)

#_(ns org.bitcoinj.wallet #_"WalletTransaction"
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Transaction]))

;;;
 ; Stores data about a transaction that is only relevant to the {@link org.bitcoinj.wallet.Wallet} class.
 ;;
#_public
(§ class #_"WalletTransaction"
    #_public
    (§ enum #_"Pool"
        (§ item UNSPENT) ;; unspent in best chain
        (§ item SPENT) ;; spent in best chain
        (§ item DEAD) ;; double-spend in alt chain
        (§ item PENDING) ;; a pending tx we would like to go into the best chain
    )
    #_private
    (§ field- #_"Transaction" (§ name transaction))
    #_private
    (§ field- #_"Pool" (§ name pool))

    #_public
    (§ constructor #_"WalletTransaction" [#_"Pool" __pool, #_"Transaction" __transaction])
    (§ block
        (§ ass (§ name (.. this pool)) (Preconditions/checkNotNull __pool))
        (§ ass (§ name (.. this transaction)) __transaction)
        this
    )

    #_public
    (§ method #_"Transaction" (§ fn getTransaction) [])
    (§ block
        transaction
    )

    #_public
    (§ method #_"Pool" (§ fn getPool) [])
    (§ block
        pool
    )
)

#_(ns org.bitcoinj.wallet.listeners #_"KeyChainEventListener"
    (:import [java.util List])
   (:require [org.bitcoinj.core ECKey]
             [org.bitcoinj.wallet KeyChain]))

#_public
(§ interface #_"KeyChainEventListener"
    ;;;
     ; Called whenever a new key is added to the key chain, whether that be via an explicit addition or due to some
     ; other automatic derivation.  See the documentation for your {@link KeyChain} implementation for details on
     ; what can trigger this event.
     ;;
    (§ method #_"void" (§ fn onKeysAdded) [#_"List<ECKey>" __keys])
)

#_(ns org.bitcoinj.wallet.listeners #_"WalletChangeEventListener"
   (:require [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>Implementors are called when the contents of the wallet changes, for instance due to receiving/sending money
 ; or a block chain re-organize.</p>
 ;;
#_public
(§ interface #_"WalletChangeEventListener"
    ;;;
     ; <p>Designed for GUI applications to refresh their transaction lists.  This callback is invoked in the following
     ; situations:</p>
     ;
     ; <ol>
     ;     <li>A new block is received (and thus building transactions got more confidence).</li>
     ;     <li>A pending transaction is received.</li>
     ;     <li>A pending transaction changes confidence due to some non-new-block related event,
     ;     such as being announced by more peers or by  a double-spend conflict being observed.</li>
     ;     <li>A re-organize occurs. Call occurs only if the re-org modified any of our transactions.</li>
     ;     <li>A new spend is committed to the wallet.</li>
     ;     <li>The wallet is reset and all transactions removed.<li>
     ; </ol>
     ;
     ; <p>When this is called you can refresh the UI contents from the wallet contents.  It's more efficient to use
     ; this rather than onTransactionConfidenceChanged() + onReorganize() because you only get one callback per block
     ; rather than one per transaction per block.  Note that this is <b>not</b> called when a key is added.</p>
     ;;
    (§ method #_"void" (§ fn onWalletChanged) [#_"Wallet" __wallet])
)

#_(ns org.bitcoinj.wallet.listeners #_"WalletCoinsReceivedEventListener"
   (:require [org.bitcoinj.core Coin Transaction]
             [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>Implementors are called when the contents of the wallet changes, for instance due to receiving/sending money
 ; or a block chain re-organize.</p>
 ;;
#_public
(§ interface #_"WalletCoinsReceivedEventListener"
    ;;;
     ; This is called when a transaction is seen that sends coins <b>to</b> this wallet, either because it was
     ; broadcast across the network or because a block was received.  If a transaction is seen when it was broadcast,
     ; onCoinsReceived won't be called again when a block containing it is received.  If you want to know when such
     ; a transaction receives its first confirmation, register a {@link TransactionConfidence} event listener using
     ; the object retrieved via {@link org.bitcoinj.core.Transaction#getConfidence()}.  It's safe to modify the
     ; wallet in this callback, for example, by spending the transaction just received.
     ;
     ; @param wallet The wallet object that received the coins.
     ; @param tx The transaction which sent us the coins.
     ; @param prevBalance Balance before the coins were received.
     ; @param newBalance This is the 'estimated' balance.
     ;;
    (§ method #_"void" (§ fn onCoinsReceived) [#_"Wallet" __wallet, #_"Transaction" __tx, #_"Coin" __prevBalance, #_"Coin" __newBalance])
)

#_(ns org.bitcoinj.wallet.listeners #_"WalletCoinsSentEventListener"
   (:require [org.bitcoinj.core Coin Transaction]
             [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>Implementors are called when the contents of the wallet changes, for instance due to receiving/sending money
 ; or a block chain re-organize.</p>
 ;;
#_public
(§ interface #_"WalletCoinsSentEventListener"
    ;;;
     ; This is called when a transaction is seen that sends coins <b>from</b> this wallet, either because it
     ; was broadcast across the network or because a block was received.  This may at first glance seem useless,
     ; because in the common case you already know about such transactions because you created them with the
     ; Wallets createSend/sendCoins methods.  However when you have a wallet containing only keys, and you wish
     ; to replay the block chain to fill it with transactions, it's useful to find out when a transaction is
     ; discovered that sends coins from the wallet.
     ;
     ; It's safe to modify the wallet from inside this callback, but if you're replaying the block chain you should
     ; be careful to avoid such modifications.  Otherwise your changes may be overridden by new data from the chain.
     ;
     ; @param wallet The wallet object that this callback relates to (that sent the coins).
     ; @param tx The transaction that sent the coins to someone else.
     ; @param prevBalance The wallets balance before this transaction was seen.
     ; @param newBalance The wallets balance after this transaction was seen.  This is the 'estimated' balance.
     ;;
    (§ method #_"void" (§ fn onCoinsSent) [#_"Wallet" __wallet, #_"Transaction" __tx, #_"Coin" __prevBalance, #_"Coin" __newBalance])
)

#_(ns org.bitcoinj.wallet.listeners #_"WalletReorganizeEventListener"
   (:require [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>Implementors are called when the wallet is reorganized.</p>
 ;;
#_public
(§ interface #_"WalletReorganizeEventListener"
    ;; TODO: Finish onReorganize to be more useful.
    ;;;
     ; <p>This is called when a block is received that triggers a block chain re-organization.</p>
     ;
     ; <p>A re-organize means that the consensus (chain) of the network has diverged and now changed from
     ; what we believed it was previously.  Usually this won't matter because the new consensus will include
     ; all our old transactions assuming we are playing by the rules.  However it's theoretically possible
     ; for our balance to change in arbitrary ways, most likely, we could lose some money we thought we had.</p>
     ;
     ; <p>It is safe to use methods of wallet whilst inside this callback.</p>
     ;;
    (§ method #_"void" (§ fn onReorganize) [#_"Wallet" __wallet])
)
